        duration,
        status: 'success',
        baseline: this.baselines.lintTime,
        regression: duration / this.baselines.lintTime,
      };
    } catch (error) {
      console.log(`‚ùå ESLint Ê£ÄÊü•Â§±Ë¥•: ${error.message}`);
      return {
        duration: 0,
        status: 'failed',
        error: error.message,
        baseline: this.baselines.lintTime,
        regression: 0,
      };
    }
  }

  /**
   * ÊµãËØïÂü∫ÂáÜÊµãËØï
   */
  async benchmarkTest() {
    console.log('üß™ ÊµãËØïÂü∫ÂáÜÊµãËØï...');

    try {
      const startTime = Date.now();
      execSync('pnpm test --run --reporter=json', {
        stdio: 'pipe',
        timeout: 180000,
      });
      const endTime = Date.now();
