}

module.exports = { PerformanceBenchmarkMonitor };

#!/usr/bin/env node

/**
 * È°πÁõÆÂÅ•Â∫∑Ê£ÄÊü•Âô® - Ë∂ÖÁÆÄÂçïÁöÑÁî®Êà∑ÁïåÈù¢
 * Project Health Checker - Ultra-simple user interface
 */

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

class ProjectHealthChecker {
  constructor() {
    this.healthStatus = {
      overall: 'unknown',
      score: 0,
      issues: [],
      recommendations: [],
      canDeploy: false,
    };
  }

  /**
   * ÊòæÁ§∫Âä†ËΩΩÂä®Áîª
   */
  showLoading(message) {
    const frames = ['‚†ã', '‚†ô', '‚†π', '‚†∏', '‚†º', '‚†¥', '‚†¶', '‚†ß', '‚†á', '‚†è'];
    let i = 0;

    const interval = setInterval(() => {
      process.stdout.write(`\r${frames[i]} ${message}`);
      i = (i + 1) % frames.length;
    }, 100);

    return interval;
  }

  /**
   * ÂÅúÊ≠¢Âä†ËΩΩÂä®Áîª
   */
  stopLoading(interval, finalMessage) {
    clearInterval(interval);
    process.stdout.write(`\r${finalMessage}\n`);
  }

  /**
   * ËøêË°åÂêéÂè∞Â§çÊùÇÊ£ÄÊü•
   */
  async runComplexChecks() {
    const loader = this.showLoading('üîç AIÊ≠£Âú®ËøõË°åÂÖ®Èù¢ÂÅ•Â∫∑Ê£ÄÊü•...');

    try {
      // ËøêË°åÂÆåÊï¥ÁöÑË¥®ÈáèÊ£ÄÊü•ÔºàAIÂ±ÇÁöÑÂ§çÊùÇÈÄªËæëÔºâ
      const output = execSync('pnpm quality:report', {
        stdio: 'pipe',
        encoding: 'utf8',
        timeout: 120000, // 2ÂàÜÈíüË∂ÖÊó∂
      });

      this.stopLoading(loader, '‚úÖ ÂÅ•Â∫∑Ê£ÄÊü•ÂÆåÊàê');

      // ËØªÂèñËØ¶ÁªÜÊä•Âëä
      await this.parseDetailedReport();

      return true;
    } catch (error) {
      this.stopLoading(loader, '‚ùå ÂÅ•Â∫∑Ê£ÄÊü•ÈÅáÂà∞ÈóÆÈ¢ò');

      // Âç≥‰ΩøÂá∫Èîô‰πüÂ∞ùËØïËß£ÊûêÂ∑≤ÊúâÊä•Âëä
      await this.parseDetailedReport();

      return false;
    }
  }

  /**
   * Ëß£ÊûêËØ¶ÁªÜÊä•ÂëäÔºåÊèêÂèñÂÖ≥ÈîÆ‰ø°ÊÅØ
   */
  async parseDetailedReport() {
    try {
      const reportPath = path.join(
        process.cwd(),
        'reports',
        'simple-quality-report.json',
      );

      if (fs.existsSync(reportPath)) {
        const report = JSON.parse(fs.readFileSync(reportPath, 'utf8'));

        // ÊèêÂèñÂÖ≥ÈîÆ‰ø°ÊÅØ
        this.healthStatus.score = report.summary.overallScore || 0;
        this.healthStatus.issues = this.extractCriticalIssues(report);
        this.healthStatus.recommendations =
          this.extractTopRecommendations(report);
        this.healthStatus.canDeploy = this.assessDeploymentReadiness(report);
        this.healthStatus.overall = this.determineOverallHealth(report);
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è  Êó†Ê≥ïËØªÂèñËØ¶ÁªÜÊä•ÂëäÔºå‰ΩøÁî®Âü∫Á°ÄÊ£ÄÊü•ÁªìÊûú');
      this.healthStatus.overall = 'unknown';
    }
  }

  /**
   * ÊèêÂèñÂÖ≥ÈîÆÈóÆÈ¢ò
   */
  extractCriticalIssues(report) {
    const issues = [];

    // ‰ªéÊ£ÄÊü•ÁªìÊûú‰∏≠ÊèêÂèñÂ§±Ë¥•È°π
    if (report.checks) {
      report.checks.forEach((check) => {
        if (check.status === 'FAIL') {
          issues.push({
            type: 'quality',
            message: `${check.name  }Â§±Ë¥•`,
            severity: 'high',
          });
        }
      });
    }

    // ‰ªéÂÆâÂÖ®Êä•Âëä‰∏≠ÊèêÂèñÊºèÊ¥û
    if (report.security && report.security.vulnerabilities > 0) {
      issues.push({
        type: 'security',
        message: `ÂèëÁé∞ ${report.security.vulnerabilities} ‰∏™ÂÆâÂÖ®ÊºèÊ¥û`,
        severity: 'critical',
      });
    }

    // ‰ªéÊÄßËÉΩÊä•Âëä‰∏≠ÊèêÂèñÈóÆÈ¢ò
    if (report.performance && report.performance.issues > 0) {
      issues.push({
        type: 'performance',
        message: `ÂèëÁé∞ ${report.performance.issues} ‰∏™ÊÄßËÉΩÈóÆÈ¢ò`,
        severity: 'medium',
      });
    }

    return issues.slice(0, 3); // Âè™ÊòæÁ§∫Ââç3‰∏™ÊúÄÈáçË¶ÅÁöÑÈóÆÈ¢ò
  }

  /**
   * ÊèêÂèñÈ°∂Á∫ßÂª∫ËÆÆ
   */
  extractTopRecommendations(report) {
    if (!report.recommendations) return [];

    return report.recommendations
      .filter((rec) => rec.priority === 'HIGH')
      .slice(0, 2)
      .map((rec) => rec.title);
  }

  /**
   * ËØÑ‰º∞ÈÉ®ÁΩ≤Â∞±Áª™ÊÄß
   */
  assessDeploymentReadiness(report) {
    // Âü∫‰∫éÂàÜÊï∞ÂíåÂÖ≥ÈîÆÈóÆÈ¢òÂà§Êñ≠
    const score = report.summary.overallScore || 0;
    const criticalIssues = this.healthStatus.issues.filter(
      (issue) => issue.severity === 'critical',
    ).length;

    return score >= 70 && criticalIssues === 0;
  }

  /**
   * Á°ÆÂÆöÊï¥‰ΩìÂÅ•Â∫∑Áä∂ÂÜµ
   */
  determineOverallHealth(report) {
    const score = report.summary.overallScore || 0;
    const failedChecks = report.summary.failedChecks || 0;

    if (score >= 90 && failedChecks === 0) return 'excellent';
    if (score >= 80 && failedChecks <= 1) return 'good';
    if (score >= 70 && failedChecks <= 2) return 'fair';
    if (score >= 50) return 'poor';
    return 'critical';
  }

  /**
   * ÊòæÁ§∫ÁÆÄÂçïÁöÑÂÅ•Â∫∑Áä∂ÊÄÅ
   */
  displaySimpleHealth() {
    console.log('\nüè• È°πÁõÆÂÅ•Â∫∑Áä∂ÂÜµ');
    console.log('='.repeat(30));

    // ÊòæÁ§∫Êï¥‰ΩìÁä∂ÊÄÅ
    const statusIcons = {
      excellent: 'üü¢ ‰ºòÁßÄ',
      good: 'üü¢ ËâØÂ•Ω',
      fair: 'üü° ‰∏ÄËà¨',
      poor: 'üü† ËæÉÂ∑Æ',
      critical: 'üî¥ Âç±Èô©',
      unknown: '‚ö™ Êú™Áü•',
    };

    console.log(`Áä∂ÊÄÅ: ${statusIcons[this.healthStatus.overall]}`);
    console.log(`ËØÑÂàÜ: ${this.healthStatus.score}/100`);

    // ÊòæÁ§∫ÂÖ≥ÈîÆÈóÆÈ¢ò
    if (this.healthStatus.issues.length > 0) {
      console.log('\nüö® ÈúÄË¶ÅÂÖ≥Ê≥®ÁöÑÈóÆÈ¢ò:');
      this.healthStatus.issues.forEach((issue, index) => {
        const severityIcon = {
          critical: 'üî¥',
          high: 'üü†',
          medium: 'üü°',
          low: 'üü¢',
        }[issue.severity];

        console.log(`${index + 1}. ${severityIcon} ${issue.message}`);
      });
    }

    // ÊòæÁ§∫ÈÉ®ÁΩ≤Áä∂ÊÄÅ
    console.log(
      `\nüöÄ ÈÉ®ÁΩ≤Áä∂ÊÄÅ: ${this.healthStatus.canDeploy ? '‚úÖ ÂèØ‰ª•ÈÉ®ÁΩ≤' : '‚ùå ÊöÇ‰∏çÂª∫ËÆÆÈÉ®ÁΩ≤'}`,
    );

    // ÊòæÁ§∫Âª∫ËÆÆ
    if (this.healthStatus.recommendations.length > 0) {
      console.log('\nüí° Âª∫ËÆÆ‰ºòÂÖàÂ§ÑÁêÜ:');
      this.healthStatus.recommendations.forEach((rec, index) => {
        console.log(`${index + 1}. ${rec}`);
      });
    }

    // ÊòæÁ§∫ËØ¶ÁªÜÊä•ÂëäÊèêÁ§∫
    console.log('\nüìä Êü•ÁúãËØ¶ÁªÜÊä•Âëä: pnpm report');
    console.log('üöÄ Ê£ÄÊü•ÈÉ®ÁΩ≤Â∞±Áª™: pnpm ready');
  }

  /**
   * ÊâßË°åÂÅ•Â∫∑Ê£ÄÊü•
   */
  async checkHealth() {
    console.log('üéØ È°πÁõÆÂÅ•Â∫∑Âø´ÈÄüÊ£ÄÊü•');
    console.log('='.repeat(30));

    const success = await this.runComplexChecks();
    this.displaySimpleHealth();

    // ËøîÂõûÁÆÄÂçïÁöÑÈÄÄÂá∫Á†Å
    const isHealthy =
      this.healthStatus.overall === 'excellent' ||
      this.healthStatus.overall === 'good';

    return isHealthy;
  }
}

// ÂëΩ‰ª§Ë°åÊé•Âè£
if (require.main === module) {
  const checker = new ProjectHealthChecker();
  checker
    .checkHealth()
    .then((healthy) => {
      process.exit(healthy ? 0 : 1);
    })
    .catch((error) => {
      console.error(`‚ùå ÂÅ•Â∫∑Ê£ÄÊü•Â§±Ë¥•: ${error.message}`);
      process.exit(1);
    });
}

module.exports = ProjectHealthChecker;

#!/usr/bin/env node

/**
 * Ë¥®ÈáèÈó®Á¶ÅÁ≥ªÁªü
 *
 * Âú®CI/CDÊµÅÁ®ã‰∏≠ÊâßË°åË¥®ÈáèÊ£ÄÊü•ÔºåÁ°Æ‰øù‰ª£Á†ÅË¥®ÈáèÊ†áÂáÜ
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

class QualityGate {
  constructor() {
    this.config = {
      // Ë¥®ÈáèÈó®Á¶ÅÊ†áÂáÜ
      gates: {
        coverage: {
          enabled: true,
          thresholds: {
            lines: 85,
            functions: 85,
            branches: 80,
            statements: 85,
          },
          blocking: true, // ÊòØÂê¶ÈòªÂ°ûÊûÑÂª∫
        },
        codeQuality: {
          enabled: true,
          thresholds: {
            eslintErrors: 0,
            eslintWarnings: 10,
            typeErrors: 0,
          },
          blocking: true,
        },
        performance: {
          enabled: true,
          thresholds: {
            buildTime: 120000, // 2ÂàÜÈíü
            testTime: 180000, // 3ÂàÜÈíü
          },
          blocking: false, // ÊÄßËÉΩÈóÆÈ¢ò‰∏çÈòªÂ°ûÔºå‰ΩÜ‰ºöË≠¶Âëä
        },
        security: {
          enabled: true,
          thresholds: {
            vulnerabilities: 0,
            highSeverity: 0,
          },
          blocking: true,
        },
      },
      // ÁéØÂ¢ÉÈÖçÁΩÆ
      environment: process.env.NODE_ENV || 'development',
      ciMode: process.env.CI === 'true',
      branch: process.env.GITHUB_REF_NAME || 'unknown',
    };

    this.results = {
      gates: {},
      summary: {
        passed: 0,
        failed: 0,
        warnings: 0,
        blocked: false,
      },
    };
  }

  /**
   * ÊâßË°åÊâÄÊúâË¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•
   */
  async executeQualityGates() {
    console.log('üö™ ÂºÄÂßãÊâßË°åË¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•...\n');
    console.log(`üåø ÂàÜÊîØ: ${this.config.branch}`);
    console.log(`üèóÔ∏è  ÁéØÂ¢É: ${this.config.environment}`);
    console.log(`ü§ñ CIÊ®°Âºè: ${this.config.ciMode ? 'ÊòØ' : 'Âê¶'}\n`);

    // ÊâßË°åÂêÑÈ°πÈó®Á¶ÅÊ£ÄÊü•
    if (this.config.gates.codeQuality.enabled) {
      this.results.gates.codeQuality = await this.checkCodeQuality();
    }

    if (this.config.gates.coverage.enabled) {
      this.results.gates.coverage = await this.checkCoverage();
    }

    if (this.config.gates.performance.enabled) {
      this.results.gates.performance = await this.checkPerformance();
    }

    if (this.config.gates.security.enabled) {
      this.results.gates.security = await this.checkSecurity();
    }

    // Ê±áÊÄªÁªìÊûú
    this.summarizeResults();

    // ÁîüÊàêÊä•Âëä
    this.generateGateReport();

    // ËøîÂõûÁªìÊûú
    return this.results;
  }

  /**
   * ‰ª£Á†ÅË¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•
   */
  async checkCodeQuality() {
    console.log('üîç ÊâßË°å‰ª£Á†ÅË¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•...');

    const gate = {
      name: 'Code Quality',
      status: 'unknown',
      checks: {},
      blocking: this.config.gates.codeQuality.blocking,
      issues: [],
    };

    try {
      // TypeScript Á±ªÂûãÊ£ÄÊü•
      gate.checks.typeCheck = await this.runTypeCheck();

      // ESLint Ê£ÄÊü•
      gate.checks.eslint = await this.runESLintCheck();

      // Ê±áÊÄª‰ª£Á†ÅË¥®ÈáèÁªìÊûú
      const hasErrors =
        gate.checks.typeCheck.errors > 0 ||
        gate.checks.eslint.errors >
          this.config.gates.codeQuality.thresholds.eslintErrors;

      const hasWarnings =
        gate.checks.eslint.warnings >
        this.config.gates.codeQuality.thresholds.eslintWarnings;

      if (hasErrors) {
        gate.status = 'failed';
        gate.issues.push('‰ª£Á†ÅË¥®ÈáèÊ£ÄÊü•ÂèëÁé∞ÈîôËØØ');
      } else if (hasWarnings) {
        gate.status = 'warning';
        gate.issues.push('‰ª£Á†ÅË¥®ÈáèÊ£ÄÊü•ÂèëÁé∞Ë≠¶Âëä');
      } else {
        gate.status = 'passed';
      }
    } catch (error) {
      gate.status = 'error';
      gate.issues.push(`‰ª£Á†ÅË¥®ÈáèÊ£ÄÊü•Â§±Ë¥•: ${error.message}`);
    }

    console.log(
      `${this.getStatusEmoji(gate.status)} ‰ª£Á†ÅË¥®ÈáèÈó®Á¶Å: ${gate.status}`,
    );
    return gate;
  }

  /**
   * Ë¶ÜÁõñÁéáÈó®Á¶ÅÊ£ÄÊü•
   */
  async checkCoverage() {
    console.log('üìä ÊâßË°åË¶ÜÁõñÁéáÈó®Á¶ÅÊ£ÄÊü•...');

    const gate = {
      name: 'Coverage',
      status: 'unknown',
      checks: {},
      blocking: this.config.gates.coverage.blocking,
      issues: [],
    };

    try {
      // ËøêË°åË¶ÜÁõñÁéáÊµãËØï
      console.log('üß™ ËøêË°åÊµãËØï‰ª•ÁîüÊàêË¶ÜÁõñÁéá...');
      execSync('pnpm test:coverage --run --reporter=json', {
        stdio: 'pipe',
        timeout: 180000,
      });

      // ËØªÂèñË¶ÜÁõñÁéáÊï∞ÊçÆ
      const coverageJsonPath = path.join(
        process.cwd(),
        'coverage',
        'coverage-summary.json',
      );

      if (fs.existsSync(coverageJsonPath)) {
        const rawData = fs.readFileSync(coverageJsonPath, 'utf8');
        const coverageData = JSON.parse(rawData);
        gate.checks.coverage = coverageData.total;

        // Ê£ÄÊü•Ë¶ÜÁõñÁéáÈòàÂÄº
        const {thresholds} = this.config.gates.coverage;
        const failedMetrics = [];

        Object.keys(thresholds).forEach((metric) => {
          const current = gate.checks.coverage[metric]?.pct || 0;
          const threshold = thresholds[metric];

          if (current < threshold) {
            failedMetrics.push(`${metric}: ${current}% < ${threshold}%`);
          }
        });

        if (failedMetrics.length > 0) {
          gate.status = 'failed';
          gate.issues.push(`Ë¶ÜÁõñÁéá‰∏çËææÊ†á: ${failedMetrics.join(', ')}`);
        } else {
          gate.status = 'passed';
        }
      } else {
        gate.status = 'error';
        gate.issues.push('Ë¶ÜÁõñÁéáÊä•ÂëäÊñá‰ª∂‰∏çÂ≠òÂú®');
      }
    } catch (error) {
      gate.status = 'error';
      gate.issues.push(`Ë¶ÜÁõñÁéáÊ£ÄÊü•Â§±Ë¥•: ${error.message}`);
    }

    console.log(
      `${this.getStatusEmoji(gate.status)} Ë¶ÜÁõñÁéáÈó®Á¶Å: ${gate.status}`,
    );
    return gate;
  }

  /**
   * ÊÄßËÉΩÈó®Á¶ÅÊ£ÄÊü•
   */
  async checkPerformance() {
    console.log('‚ö° ÊâßË°åÊÄßËÉΩÈó®Á¶ÅÊ£ÄÊü•...');

    const gate = {
      name: 'Performance',
      status: 'unknown',
      checks: {},
      blocking: this.config.gates.performance.blocking,
      issues: [],
    };

    try {
      // ÊûÑÂª∫ÊÄßËÉΩÊ£ÄÊü•
      const buildStart = Date.now();
      execSync('pnpm build', { stdio: 'pipe', timeout: 180000 });
      const buildTime = Date.now() - buildStart;

      gate.checks.buildTime = buildTime;

      // ÊµãËØïÊÄßËÉΩÊ£ÄÊü•
      const testStart = Date.now();
      execSync('pnpm test --run --reporter=json', {
        stdio: 'pipe',
        timeout: 240000,
      });
      const testTime = Date.now() - testStart;

      gate.checks.testTime = testTime;

      // Ê£ÄÊü•ÊÄßËÉΩÈòàÂÄº
      const issues = [];
      if (buildTime > this.config.gates.performance.thresholds.buildTime) {
        issues.push(
          `ÊûÑÂª∫Êó∂Èó¥ ${Math.round(buildTime / 1000)}s Ë∂ÖËøáÈòàÂÄº ${Math.round(this.config.gates.performance.thresholds.buildTime / 1000)}s`,
        );
      }

      if (testTime > this.config.gates.performance.thresholds.testTime) {
        issues.push(
          `ÊµãËØïÊó∂Èó¥ ${Math.round(testTime / 1000)}s Ë∂ÖËøáÈòàÂÄº ${Math.round(this.config.gates.performance.thresholds.testTime / 1000)}s`,
        );
      }

      if (issues.length > 0) {
        gate.status = gate.blocking ? 'failed' : 'warning';
        gate.issues.push(...issues);
      } else {
        gate.status = 'passed';
      }
    } catch (error) {
      gate.status = 'error';
      gate.issues.push(`ÊÄßËÉΩÊ£ÄÊü•Â§±Ë¥•: ${error.message}`);
    }

    console.log(`${this.getStatusEmoji(gate.status)} ÊÄßËÉΩÈó®Á¶Å: ${gate.status}`);
    return gate;
  }

  /**
   * ÂÆâÂÖ®Èó®Á¶ÅÊ£ÄÊü•
   */
  async checkSecurity() {
    console.log('üîí ÊâßË°åÂÆâÂÖ®Èó®Á¶ÅÊ£ÄÊü•...');

    const gate = {
      name: 'Security',
      status: 'unknown',
      checks: {},
      blocking: this.config.gates.security.blocking,
      issues: [],
    };

    try {
      // npm audit Ê£ÄÊü•
      gate.checks.audit = await this.runSecurityAudit();

      // Ê£ÄÊü•ÂÆâÂÖ®ÈòàÂÄº
      const vulnerabilities = gate.checks.audit.vulnerabilities || 0;
      const highSeverity = gate.checks.audit.high || 0;

      if (
        vulnerabilities >
          this.config.gates.security.thresholds.vulnerabilities ||
        highSeverity > this.config.gates.security.thresholds.highSeverity
      ) {
        gate.status = 'failed';
        gate.issues.push(
          `ÂèëÁé∞ ${vulnerabilities} ‰∏™ÂÆâÂÖ®ÊºèÊ¥ûÔºåÂÖ∂‰∏≠ ${highSeverity} ‰∏™È´òÂç±`,
        );
      } else {
        gate.status = 'passed';
      }
    } catch (error) {
      gate.status = 'warning'; // ÂÆâÂÖ®Ê£ÄÊü•Â§±Ë¥•‰∏çÈòªÂ°ûÔºå‰ΩÜÂèëÂá∫Ë≠¶Âëä
      gate.issues.push(`ÂÆâÂÖ®Ê£ÄÊü•Â§±Ë¥•: ${error.message}`);
    }

    console.log(`${this.getStatusEmoji(gate.status)} ÂÆâÂÖ®Èó®Á¶Å: ${gate.status}`);
    return gate;
  }

  /**
   * ËøêË°å TypeScript Á±ªÂûãÊ£ÄÊü•
   */
  async runTypeCheck() {
    try {
      execSync('pnpm type-check', { stdio: 'pipe' });
      return { errors: 0, status: 'passed' };
    } catch (error) {
      return { errors: 1, status: 'failed', message: error.message };
    }
  }

  /**
   * ËøêË°å ESLint Ê£ÄÊü•
   */
  async runESLintCheck() {
    try {
      execSync('pnpm lint:check', { stdio: 'pipe' });
      return { errors: 0, warnings: 0, status: 'passed' };
    } catch (error) {
      const output = error.stdout || error.stderr || '';
      const errorMatch = output.match(/(\d+) error/);
      const warningMatch = output.match(/(\d+) warning/);

      return {
        errors: errorMatch ? parseInt(errorMatch[1]) : 0,
        warnings: warningMatch ? parseInt(warningMatch[1]) : 0,
        status: 'failed',
        output,
      };
    }
  }

  /**
   * ËøêË°åÂÆâÂÖ®ÂÆ°ËÆ°
   */
  async runSecurityAudit() {
    try {
      const output = execSync('pnpm audit --json', {
        encoding: 'utf8',
        stdio: 'pipe',
      });
      const auditData = JSON.parse(output);

      return {
        vulnerabilities: auditData.metadata?.vulnerabilities?.total || 0,
        high: auditData.metadata?.vulnerabilities?.high || 0,
        critical: auditData.metadata?.vulnerabilities?.critical || 0,
        status: 'completed',
      };
    } catch (error) {
      // npm audit Âú®ÂèëÁé∞ÊºèÊ¥ûÊó∂‰ºöËøîÂõûÈùûÈõ∂ÈÄÄÂá∫Á†Å
      try {
        const output = error.stdout || '';
        if (output) {
          const auditData = JSON.parse(output);
          return {
            vulnerabilities: auditData.metadata?.vulnerabilities?.total || 0,
            high: auditData.metadata?.vulnerabilities?.high || 0,
            critical: auditData.metadata?.vulnerabilities?.critical || 0,
            status: 'completed',
          };
        }
      } catch (parseError) {
        // Ëß£ÊûêÂ§±Ë¥•ÔºåËøîÂõûÈªòËÆ§ÂÄº
      }

      return {
        vulnerabilities: 0,
        high: 0,
        critical: 0,
        status: 'failed',
        error: error.message,
      };
    }
  }

  /**
   * Ê±áÊÄªÁªìÊûú
   */
  summarizeResults() {
    Object.values(this.results.gates).forEach((gate) => {
      switch (gate.status) {
        case 'passed':
          this.results.summary.passed++;
          break;
        case 'failed':
          this.results.summary.failed++;
          if (gate.blocking) {
            this.results.summary.blocked = true;
          }
          break;
        case 'warning':
          this.results.summary.warnings++;
          break;
      }
    });
  }

  /**
   * ÁîüÊàêÈó®Á¶ÅÊä•Âëä
   */
  generateGateReport() {
    console.log('\nüö™ Ë¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•Êä•Âëä');
    console.log('='.repeat(50));

    console.log(`‚úÖ ÈÄöËøá: ${this.results.summary.passed}`);
    console.log(`‚ùå Â§±Ë¥•: ${this.results.summary.failed}`);
    console.log(`‚ö†Ô∏è  Ë≠¶Âëä: ${this.results.summary.warnings}`);
    console.log(`üö´ ÈòªÂ°ûÊûÑÂª∫: ${this.results.summary.blocked ? 'ÊòØ' : 'Âê¶'}`);

    console.log('\nüìã ËØ¶ÁªÜÁªìÊûú:');
    Object.values(this.results.gates).forEach((gate) => {
      console.log(
        `${this.getStatusEmoji(gate.status)} ${gate.name}: ${gate.status}`,
      );
      if (gate.issues.length > 0) {
        gate.issues.forEach((issue) => {
          console.log(`   - ${issue}`);
        });
      }
    });

    // ‰øùÂ≠òÊä•Âëä
    const reportPath = path.join(
      process.cwd(),
      'reports',
      `quality-gate-${Date.now()}.json`,
    );
    fs.mkdirSync(path.dirname(reportPath), { recursive: true });
    fs.writeFileSync(
      reportPath,
      JSON.stringify(
        {
          timestamp: new Date().toISOString(),
          config: this.config,
          results: this.results,
        },
        null,
        2,
      ),
    );

    console.log(`\nüíæ Êä•ÂëäÂ∑≤‰øùÂ≠ò: ${reportPath}`);

    // CI ÁéØÂ¢É‰∏ãÁöÑÁâπÊÆäÂ§ÑÁêÜ
    if (this.config.ciMode) {
      this.handleCIOutput();
    }
  }

  /**
   * Â§ÑÁêÜ CI ÁéØÂ¢ÉËæìÂá∫
   */
  handleCIOutput() {
    // GitHub Actions Ê≥®Ëß£
    if (process.env.GITHUB_ACTIONS) {
      Object.values(this.results.gates).forEach((gate) => {
        if (gate.status === 'failed' && gate.blocking) {
          console.log(
            `::error::Ë¥®ÈáèÈó®Á¶ÅÂ§±Ë¥•: ${gate.name} - ${gate.issues.join(', ')}`,
          );
        } else if (gate.status === 'warning') {
          console.log(
            `::warning::Ë¥®ÈáèÈó®Á¶ÅË≠¶Âëä: ${gate.name} - ${gate.issues.join(', ')}`,
          );
        }
      });
    }

    // ËÆæÁΩÆËæìÂá∫ÂèòÈáè
    console.log(
      `::set-output name=quality-gate-passed::${!this.results.summary.blocked}`,
    );
    console.log(
      `::set-output name=quality-gate-score::${this.calculateQualityScore()}`,
    );
  }

  /**
   * ËÆ°ÁÆóË¥®ÈáèËØÑÂàÜ
   */
  calculateQualityScore() {
    const totalGates = Object.keys(this.results.gates).length;
    if (totalGates === 0) return 0;

    const score = (this.results.summary.passed / totalGates) * 100;
    return Math.round(score);
  }

  getStatusEmoji(status) {
    switch (status) {
      case 'passed':
        return '‚úÖ';
      case 'failed':
        return '‚ùå';
      case 'warning':
        return '‚ö†Ô∏è';
      case 'error':
        return 'üí•';
      default:
        return '‚ùì';
    }
  }
}

// ‰∏ªÊâßË°åÂáΩÊï∞
async function main() {
  const gate = new QualityGate();

  try {
    const results = await gate.executeQualityGates();

    if (results.summary.blocked) {
      console.log('\nüö´ Ë¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•Â§±Ë¥•ÔºåÊûÑÂª∫Ë¢´ÈòªÂ°ûÔºÅ');
      process.exit(1);
    } else if (results.summary.failed > 0 || results.summary.warnings > 0) {
      console.log('\n‚ö†Ô∏è  Ë¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•ÂèëÁé∞ÈóÆÈ¢òÔºå‰ΩÜ‰∏çÈòªÂ°ûÊûÑÂª∫');
      console.log('ËØ∑ÂèäÊó∂‰øÆÂ§çÁõ∏ÂÖ≥ÈóÆÈ¢ò‰ª•ÊèêÈ´ò‰ª£Á†ÅË¥®Èáè');
    } else {
      console.log('\nüéâ ÊâÄÊúâË¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•ÈÄöËøáÔºÅ');
    }
  } catch (error) {
    console.error('‚ùå Ë¥®ÈáèÈó®Á¶ÅÊ£ÄÊü•Â§±Ë¥•:', error.message);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

module.exports = { QualityGate };

#!/usr/bin/env node

/**
 * ‰ºÅ‰∏öÁ∫ß‰ª£Á†ÅË¥®ÈáèÁõëÊéßÂ∑•ÂÖ∑
 *
 * ÂäüËÉΩÔºö
 * - ÂÆûÊó∂ÁõëÊéßESLintÈîôËØØÂíåË≠¶ÂëäÊï∞Èáè
 * - ÁîüÊàêË¥®ÈáèÊåáÊ†áÊä•Âëä
 * - ËÆæÁΩÆË¥®ÈáèÈó®Á¶ÅÈòàÂÄº
 * - ÈõÜÊàêCI/CDË¥®ÈáèÊ£ÄÊü•
 */

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

// Ë¥®ÈáèÊåáÊ†áÈòàÂÄºÈÖçÁΩÆ
const QUALITY_THRESHOLDS = {
  // ÈîôËØØÊï∞ÈáèÈòàÂÄº
  maxErrors: 0, // ‰ºÅ‰∏öÁ∫ßÊ†áÂáÜÔºöÈõ∂ÈîôËØØÂÆπÂøç
  maxWarnings: 500, // ÂΩìÂâçÁõÆÊ†áÔºö500‰∏™Ë≠¶Âëä‰ª•‰∏ã

  // ÁâπÂÆöËßÑÂàôÈòàÂÄº
  maxAnyTypeUsage: 0, // ‰∏•Ê†ºÁ¶ÅÊ≠¢anyÁ±ªÂûã
  maxComplexityViolations: 10, // Â§çÊùÇÂ∫¶ËøùËßÑÊúÄÂ§ö10‰∏™
  maxFunctionLengthViolations: 20, // ÂáΩÊï∞ÈïøÂ∫¶ËøùËßÑÊúÄÂ§ö20‰∏™
  maxSecurityWarnings: 30, // ÂÆâÂÖ®Ë≠¶ÂëäÊúÄÂ§ö30‰∏™

  // Ë¥®ÈáèË∂ãÂäøÈòàÂÄº
  maxQualityRegression: 50, // Ë¥®ÈáèÂõûÈÄÄÊúÄÂ§ö50‰∏™ÈóÆÈ¢ò
};

// Ë¥®ÈáèÊåáÊ†áÊî∂ÈõÜÂô®
class QualityMonitor {
  constructor() {
    this.reportDir = path.join(process.cwd(), 'reports', 'quality');
    this.ensureReportDir();
  }

  ensureReportDir() {
    if (!fs.existsSync(this.reportDir)) {
      fs.mkdirSync(this.reportDir, { recursive: true });
    }
  }

  /**
   * ËøêË°åESLintÂπ∂Êî∂ÈõÜË¥®ÈáèÊåáÊ†á
   */
  async collectQualityMetrics() {
    console.log('üîç Êî∂ÈõÜ‰ª£Á†ÅË¥®ÈáèÊåáÊ†á...');

    try {
      // Áõ¥Êé•ËøêË°åESLintÂëΩ‰ª§ÔºåÈÅøÂÖçpnpmËæìÂá∫Âπ≤Êâ∞
      // Â§çÁî®È°πÁõÆËÑöÊú¨ÔºåÁ°Æ‰øù‰∏é lint:check ÂÆåÂÖ®‰∏ÄËá¥ÔºàÂπ∂ÂºÄÂêØÁºìÂ≠òÔºâÔºå‰ªÖË¶ÜÁõñËæìÂá∫Ê†ºÂºè‰∏∫ JSON
      const lintOutput = execSync('pnpm run -s lint:check -- --format json', {
        encoding: 'utf8',
        maxBuffer: 10 * 1024 * 1024, // 10MB buffer
        stdio: ['pipe', 'pipe', 'pipe'], // Á°Æ‰øùÂè™Ëé∑Âèñstdout
      });

      const lintResults = JSON.parse(lintOutput);
      return this.analyzeLintResults(lintResults);
    } catch (error) {
      // ESLintÊúâÈîôËØØÊó∂‰πü‰ºöÊäõÂá∫ÂºÇÂ∏∏Ôºå‰ΩÜÊàë‰ª¨‰ªçÈúÄË¶ÅËß£ÊûêÁªìÊûú
      if (error.stdout) {
        try {
          // Ê∏ÖÁêÜËæìÂá∫ÔºåÁßªÈô§ÈùûJSONÂÜÖÂÆπ
          const cleanOutput = error.stdout.trim();
          const jsonStart = cleanOutput.indexOf('[');
          const jsonEnd = cleanOutput.lastIndexOf(']') + 1;

          if (jsonStart >= 0 && jsonEnd > jsonStart) {
            const jsonOutput = cleanOutput.substring(jsonStart, jsonEnd);
            const lintResults = JSON.parse(jsonOutput);
            return this.analyzeLintResults(lintResults);
          } 
            console.error('‚ùå Êú™ÊâæÂà∞ÊúâÊïàÁöÑJSONËæìÂá∫');
            throw new Error('ESLintËæìÂá∫Ê†ºÂºèÊó†Êïà');

        } catch (parseError) {
          console.error('‚ùå Ëß£ÊûêESLintËæìÂá∫Â§±Ë¥•:', parseError.message);
          console.error('ÂéüÂßãËæìÂá∫:', error.stdout.substring(0, 500));
          throw parseError;
        }
      }

      // Â¶ÇÊûúÊ≤°ÊúâstdoutÔºåÂèØËÉΩÊòØ‰∏•ÈáçÈîôËØØ
      console.error('‚ùå ESLintÊâßË°åÂ§±Ë¥•:', error.message);
      throw error;
    }
  }

  /**
   * ÂàÜÊûêESLintÁªìÊûú
   */
  analyzeLintResults(lintResults) {
    const metrics = {
      timestamp: new Date().toISOString(),
      totalFiles: lintResults.length,
      totalErrors: 0,
      totalWarnings: 0,
      ruleViolations: {},
      fileMetrics: [],
      qualityScore: 0,
    };

    // ÂàÜÊûêÊØè‰∏™Êñá‰ª∂ÁöÑÁªìÊûú
    lintResults.forEach((fileResult) => {
      const fileMetric = {
        filePath: fileResult.filePath,
        errorCount: fileResult.errorCount,
        warningCount: fileResult.warningCount,
        messages: fileResult.messages,
      };

      metrics.totalErrors += fileResult.errorCount;
      metrics.totalWarnings += fileResult.warningCount;

      // ÁªüËÆ°ËßÑÂàôËøùËßÑÊÉÖÂÜµ
      fileResult.messages.forEach((message) => {
        const ruleId = message.ruleId || 'unknown';
        if (!metrics.ruleViolations[ruleId]) {
          metrics.ruleViolations[ruleId] = {
            count: 0,
            severity: message.severity === 2 ? 'error' : 'warning',
          };
        }
        metrics.ruleViolations[ruleId].count++;
      });

      metrics.fileMetrics.push(fileMetric);
    });

    // ËÆ°ÁÆóË¥®ÈáèÂàÜÊï∞ (0-100)
    metrics.qualityScore = this.calculateQualityScore(metrics);

    return metrics;
  }

  /**
   * ËÆ°ÁÆóË¥®ÈáèÂàÜÊï∞
   */
  calculateQualityScore(metrics) {
    const baseScore = 100;

    // ÈîôËØØÊâ£ÂàÜÔºöÊØè‰∏™ÈîôËØØÊâ£5ÂàÜ
    const errorPenalty = metrics.totalErrors * 5;

    // Ë≠¶ÂëäÊâ£ÂàÜÔºöÊØè‰∏™Ë≠¶ÂëäÊâ£0.1ÂàÜ
    const warningPenalty = metrics.totalWarnings * 0.1;

    // ÁâπÂÆöËßÑÂàôÈ¢ùÂ§ñÊâ£ÂàÜ
    const anyTypePenalty =
      (metrics.ruleViolations['@typescript-eslint/no-explicit-any']?.count ||
        0) * 2;
    const securityPenalty =
      Object.keys(metrics.ruleViolations)
        .filter((rule) => rule.startsWith('security/'))
        .reduce((sum, rule) => sum + metrics.ruleViolations[rule].count, 0) * 1;

    const totalPenalty =
      errorPenalty + warningPenalty + anyTypePenalty + securityPenalty;

    return Math.max(0, Math.round(baseScore - totalPenalty));
  }

  /**
   * ÁîüÊàêË¥®ÈáèÊä•Âëä
   */
  generateQualityReport(metrics) {
    const reportPath = path.join(
      this.reportDir,
      `quality-report-${Date.now()}.json`,
    );

    // ‰øùÂ≠òËØ¶ÁªÜÊä•Âëä
    fs.writeFileSync(reportPath, JSON.stringify(metrics, null, 2));

    // ÁîüÊàêÊëòË¶ÅÊä•Âëä
    const summary = {
      timestamp: metrics.timestamp,
      qualityScore: metrics.qualityScore,
      totalErrors: metrics.totalErrors,
      totalWarnings: metrics.totalWarnings,
      totalFiles: metrics.totalFiles,
      topViolations: Object.entries(metrics.ruleViolations)
        .sort(([, a], [, b]) => b.count - a.count)
        .slice(0, 10)
        .map(([rule, data]) => ({
          rule,
          count: data.count,
          severity: data.severity,
        })),
      thresholdStatus: this.checkThresholds(metrics),
    };

    const summaryPath = path.join(
      this.reportDir,
      'latest-quality-summary.json',
    );
    fs.writeFileSync(summaryPath, JSON.stringify(summary, null, 2));

    return { reportPath, summaryPath, summary };
  }

  /**
   * Ê£ÄÊü•Ë¥®ÈáèÈó®Á¶ÅÈòàÂÄº
   */
  checkThresholds(metrics) {
    const status = {
      passed: true,
      violations: [],
    };

    // Ê£ÄÊü•ÈîôËØØÊï∞Èáè
    if (metrics.totalErrors > QUALITY_THRESHOLDS.maxErrors) {
      status.passed = false;
      status.violations.push({
        type: 'errors',
        current: metrics.totalErrors,
        threshold: QUALITY_THRESHOLDS.maxErrors,
        message: `ÈîôËØØÊï∞ÈáèË∂ÖËøáÈòàÂÄº: ${metrics.totalErrors} > ${QUALITY_THRESHOLDS.maxErrors}`,
      });
    }

    // Ê£ÄÊü•Ë≠¶ÂëäÊï∞Èáè
    if (metrics.totalWarnings > QUALITY_THRESHOLDS.maxWarnings) {
      status.passed = false;
      status.violations.push({
        type: 'warnings',
        current: metrics.totalWarnings,
        threshold: QUALITY_THRESHOLDS.maxWarnings,
        message: `Ë≠¶ÂëäÊï∞ÈáèË∂ÖËøáÈòàÂÄº: ${metrics.totalWarnings} > ${QUALITY_THRESHOLDS.maxWarnings}`,
      });
    }

    // Ê£ÄÊü•anyÁ±ªÂûã‰ΩøÁî®
    const anyTypeCount =
      metrics.ruleViolations['@typescript-eslint/no-explicit-any']?.count || 0;
    if (anyTypeCount > QUALITY_THRESHOLDS.maxAnyTypeUsage) {
      status.passed = false;
      status.violations.push({
        type: 'any-type',
        current: anyTypeCount,
        threshold: QUALITY_THRESHOLDS.maxAnyTypeUsage,
        message: `anyÁ±ªÂûã‰ΩøÁî®Ë∂ÖËøáÈòàÂÄº: ${anyTypeCount} > ${QUALITY_THRESHOLDS.maxAnyTypeUsage}`,
      });
    }

    return status;
  }

  /**
   * ÊâìÂç∞Ë¥®ÈáèÊä•ÂëäÊëòË¶Å
   */
  printQualitySummary(summary) {
    console.log('\nüìä ‰ª£Á†ÅË¥®ÈáèÊä•ÂëäÊëòË¶Å');
    console.log('='.repeat(50));
    console.log(`üéØ Ë¥®ÈáèÂàÜÊï∞: ${summary.qualityScore}/100`);
    console.log(`üìÅ Ê£ÄÊü•Êñá‰ª∂: ${summary.totalFiles} ‰∏™`);
    console.log(`‚ùå ÈîôËØØÊï∞Èáè: ${summary.totalErrors} ‰∏™`);
    console.log(`‚ö†Ô∏è  Ë≠¶ÂëäÊï∞Èáè: ${summary.totalWarnings} ‰∏™`);

    console.log('\nüîù ‰∏ªË¶ÅÈóÆÈ¢òÁ±ªÂûã:');
    summary.topViolations.slice(0, 5).forEach((violation, index) => {
      const icon = violation.severity === 'error' ? '‚ùå' : '‚ö†Ô∏è';
      console.log(
        `  ${index + 1}. ${icon} ${violation.rule}: ${violation.count} ‰∏™`,
      );
    });

    console.log('\nüö™ Ë¥®ÈáèÈó®Á¶ÅÁä∂ÊÄÅ:');
    if (summary.thresholdStatus.passed) {
      console.log('‚úÖ ÈÄöËøá - ÊâÄÊúâË¥®ÈáèÊåáÊ†áÁ¨¶Âêà‰ºÅ‰∏öÁ∫ßÊ†áÂáÜ');
    } else {
      console.log('‚ùå Êú™ÈÄöËøá - ÂèëÁé∞‰ª•‰∏ãÈóÆÈ¢ò:');
      summary.thresholdStatus.violations.forEach((violation) => {
        console.log(`  ‚Ä¢ ${violation.message}`);
      });
    }

    console.log('='.repeat(50));
  }

  /**
   * ‰∏ªÊâßË°åÂáΩÊï∞
   */
  async run() {
    try {
      console.log('üöÄ ÂêØÂä®‰ºÅ‰∏öÁ∫ß‰ª£Á†ÅË¥®ÈáèÁõëÊéß...\n');

      const metrics = await this.collectQualityMetrics();
      const { summaryPath, summary } = this.generateQualityReport(metrics);

      this.printQualitySummary(summary);

      console.log(`\nüìÑ ËØ¶ÁªÜÊä•ÂëäÂ∑≤‰øùÂ≠òËá≥: ${summaryPath}`);

      // Â¶ÇÊûúË¥®ÈáèÈó®Á¶ÅÊú™ÈÄöËøáÔºåÈÄÄÂá∫Á†Å‰∏∫1
      if (!summary.thresholdStatus.passed) {
        console.log('\nüí° Âª∫ËÆÆ‰ºòÂÖà‰øÆÂ§çerrorÁ∫ßÂà´ÈóÆÈ¢òÔºåÁÑ∂ÂêéÈÄêÊ≠•ÂáèÂ∞ëwarningÊï∞Èáè');
        process.exit(1);
      }

      console.log('\nüéâ ‰ª£Á†ÅË¥®ÈáèÁõëÊéßÂÆåÊàêÔºÅ');
    } catch (error) {
      console.error('‚ùå Ë¥®ÈáèÁõëÊéßÊâßË°åÂ§±Ë¥•:', error.message);
      process.exit(1);
    }
  }
}

// Â¶ÇÊûúÁõ¥Êé•ËøêË°åÊ≠§ËÑöÊú¨
if (require.main === module) {
  const monitor = new QualityMonitor();
  monitor.run();
}

module.exports = QualityMonitor;

#!/usr/bin/env node
/*
 * Âø´ÈÄüË¥®ÈáèÊ£ÄÊü•Ôºà‰ªÖÈíàÂØπÊöÇÂ≠òÊñá‰ª∂Ôºâ
 * - ËØªÂèñ Git ÊöÇÂ≠òÂå∫ÁöÑÊñá‰ª∂ÂàóË°®
 * - ËøáÊª§Âá∫ .js/.jsx/.ts/.tsx Êñá‰ª∂
 * - ‰ªÖÂØπËøô‰∫õÊñá‰ª∂ËøêË°å ESLintÔºà‰ΩøÁî®ÂΩìÂâçÈ°πÁõÆÁöÑ eslint.config.mjsÔºâ
 */

const { execSync, spawnSync } = require('child_process');

function getStagedFiles() {
  try {
    const output = execSync(
      'git diff --name-only --cached --diff-filter=ACMR',
      { stdio: ['ignore', 'pipe', 'ignore'] },
    )
      .toString()
      .trim();
    if (!output) return [];
    return output.split('\n').filter((f) => /\.(js|jsx|ts|tsx)$/i.test(f));
  } catch {
    return [];
  }
}

function runESLint(files) {
  if (files.length === 0) {
    console.log('[quality:quick:staged] Êó†ÈúÄÊ£ÄÊü•ÔºöÊ≤°ÊúâÊöÇÂ≠òÁöÑ JS/TS Êñá‰ª∂');
    return 0;
  }

  // ‰ΩøÁî®pnpm execË∞ÉÁî®ESLintÔºåÈÅøÂÖçË∑ØÂæÑËß£ÊûêÈóÆÈ¢ò
  const args = [
    'exec',
    'eslint',
    ...files,
    '--config',
    'eslint.config.mjs',
    '--max-warnings',
    '0',
  ];

  const res = spawnSync('pnpm', args, { stdio: 'inherit' });
  return res.status ?? 1;
}

const files = getStagedFiles();
const code = runESLint(files);
process.exit(code);

#!/usr/bin/env node
/*
 * ÂÆâÂÖ®È≠îÊ≥ïÊï∞Â≠ó‰øÆÂ§çÂô® (Âü∫‰∫éCODEXÂª∫ËÆÆ)
 * - ‰øÆÂ§çÂéüËÑöÊú¨ÁöÑÂØºÂÖ•ÈÄªËæëÁº∫Èô∑
 * - Ê∑ªÂä†È¢ÑÊ£ÄÊü•È™åËØÅÊú∫Âà∂
 * - ÂÆûÊñΩÂàÜÊâπÂ§ÑÁêÜÂíåËá™Âä®È™åËØÅ
 * - Èò≤Ê≠¢‰∏ä‰∏ãÊñáÊõøÊç¢ÈîôËØØ
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// ‰ªémagic-numbers.tsÊèêÂèñÊâÄÊúâÂèØÁî®Â∏∏Èáè
function getAvailableConstants() {
  const constantsFile = 'src/constants/magic-numbers.ts';
  const content = fs.readFileSync(constantsFile, 'utf8');
  const matches = content.match(/export const (\w+)/g) || [];
  return matches.map(match => match.replace('export const ', ''));
}

// Ëé∑ÂèñÁé∞ÊúâÂØºÂÖ•Â∏∏Èáè
function getExistingImports(filePath) {
  const content = fs.readFileSync(filePath, 'utf8');
  const importMatch = content.match(/import\s*{\s*([^}]+)\s*}\s*from\s*['"]@\/constants\/magic-numbers['"]/);

  if (!importMatch) return [];

  return importMatch[1]
    .split(',')
    .map(item => item.trim())
    .filter(item => item.length > 0);
}

// Êô∫ËÉΩÊõ¥Êñ∞ÂØºÂÖ•ËØ≠Âè• (‰øÆÂ§çÂéüËÑöÊú¨Áº∫Èô∑)
function updateImports(filePath, newConstants) {
  if (newConstants.length === 0) return false;

  const existingImports = getExistingImports(filePath);
  // ‰ΩøÁî®SetÂéªÈáçÂπ∂ÊåâÂ≠óÊØçÈ°∫Â∫èÊéíÂ∫è (CODEXÂª∫ËÆÆ)
  const allConstants = [...new Set([...existingImports, ...newConstants])].sort();

  let content = fs.readFileSync(filePath, 'utf8');

  // ÁîüÊàêÊñ∞ÁöÑÂØºÂÖ•ËØ≠Âè•
  const newImportStatement = `import { ${allConstants.join(', ')} } from '@/constants/magic-numbers';`;

  // ÁßªÈô§ÊóßÁöÑÂØºÂÖ•
  content = content.replace(
    /import\s*{\s*[^}]+\s*}\s*from\s*['"]@\/constants\/magic-numbers['"];?\n?/g,
    ''
  );

  // Êô∫ËÉΩÊ∑ªÂä†Êñ∞ÂØºÂÖ• (Â§ÑÁêÜÂêÑÁßçÊñá‰ª∂ÂºÄÂ§¥ÊÉÖÂÜµ)
  if (content.includes('import')) {
    // Âú®Á¨¨‰∏Ä‰∏™importËØ≠Âè•ÂêéÊ∑ªÂä†
    const firstImportMatch = content.match(/^import .+;$/m);
    if (firstImportMatch) {
      content = content.replace(
        firstImportMatch[0],
        `${firstImportMatch[0]}\n${newImportStatement}`
      );
    }
  } else {
    // Âú®Êñá‰ª∂ÂºÄÂ§¥Ê∑ªÂä† (Â§ÑÁêÜÊ≤°ÊúâÂÖ∂‰ªñimportÁöÑÊÉÖÂÜµ)
    content = `${newImportStatement}\n\n${content}`;
  }

  fs.writeFileSync(filePath, content);
  return true;
}

// È¢ÑÊ£ÄÊü•È™åËØÅ (CODEXÂª∫ËÆÆ)
function validateConstants(errors) {
  const availableConstants = getAvailableConstants();
  const missingConstants = errors.filter(e => 
    !availableConstants.includes(e.constantName)
  );

  if (missingConstants.length > 0) {
    console.error('‚ùå È¢ÑÊ£ÄÊü•Â§±Ë¥•Ôºö‰ª•‰∏ãÂ∏∏ÈáèÊú™ÂÆö‰πâ');
    missingConstants.forEach(c => {
      console.error(`  - ${c.constantName} (Áî®‰∫éÊï∞Â≠ó ${c.number})`);
    });
    throw new Error(`Áº∫Â§±Â∏∏ÈáèÂÆö‰πâ: ${missingConstants.map(c => c.constantName).join(', ')}`);
  }

  console.log('‚úÖ È¢ÑÊ£ÄÊü•ÈÄöËøáÔºöÊâÄÊúâÂ∏∏ÈáèÈÉΩÂ∑≤ÂÆö‰πâ');
}

// ÂÆâÂÖ®ÁöÑÊï∞Â≠óÊõøÊç¢ (Èò≤Ê≠¢‰∏ä‰∏ãÊñáÈîôËØØ)
function safeNumberReplace(content, number, constantName) {
  // ÊûÑÂª∫Êõ¥ÂÆâÂÖ®ÁöÑÊ≠£ÂàôË°®ËææÂºè
  let regex;

  if (number.startsWith('-')) {
    // Ë¥üÊï∞Â§ÑÁêÜ
    regex = new RegExp(`(?<!\\d)${number.replace('.', '\\.')}(?!\\d)`, 'g');
  } else if (number.startsWith('0x')) {
    // ÂçÅÂÖ≠ËøõÂà∂Â§ÑÁêÜ
    regex = new RegExp(`\\b${number}\\b`, 'gi');
  } else {
    // ÊôÆÈÄöÊï∞Â≠ó - Ê∑ªÂä†Êõ¥‰∏•Ê†ºÁöÑËæπÁïåÊ£ÄÊü•
    const escapedNumber = number.replace('.', '\\.');
    regex = new RegExp(`(?<![\\.\\d])\\b${escapedNumber}\\b(?![\\d])`, 'g');
  }

  // ÊâßË°åÊõøÊç¢Âπ∂ËÆ∞ÂΩï
  const newContent = content.replace(regex, constantName);
  const replacements = (content.match(regex) || []).length;

  if (replacements > 0) {
    console.log(`    ÊõøÊç¢ ${number} ‚Üí ${constantName} (${replacements}Ê¨°)`);
  }

  return newContent;
}

// ÊâπÂ§ÑÁêÜ‰øÆÂ§ç (CODEXÂª∫ËÆÆ)
function processBatch(errors, batchSize = 5) {
  const fileGroups = new Map();

  // ÊåâÊñá‰ª∂ÂàÜÁªÑÈîôËØØ
  for (const error of errors) {
    if (!fileGroups.has(error.file)) {
      fileGroups.set(error.file, []);
    }
    fileGroups.get(error.file).push(error);
  }

  const files = Array.from(fileGroups.keys());
  let processedFiles = 0;

  for (let i = 0; i < files.length; i += batchSize) {
    const batch = files.slice(i, i + batchSize);
    console.log(`\nüì¶ Â§ÑÁêÜÊâπÊ¨° ${Math.floor(i/batchSize) + 1}/${Math.ceil(files.length/batchSize)}`);

    for (const filePath of batch) {
      try {
        const fileErrors = fileGroups.get(filePath);
        console.log(`üîß Â§ÑÁêÜÊñá‰ª∂: ${path.relative(process.cwd(), filePath)}`);

        // Êõ¥Êñ∞ÂØºÂÖ•
        const constantNames = fileErrors.map(e => e.constantName);
        updateImports(filePath, constantNames);

        // ÂÆâÂÖ®ÊõøÊç¢Êï∞Â≠ó
        let content = fs.readFileSync(filePath, 'utf8');
        let modified = false;

        for (const error of fileErrors) {
          const newContent = safeNumberReplace(content, error.number, error.constantName);
          if (newContent !== content) {
            content = newContent;
            modified = true;
          }
        }

        if (modified) {
          fs.writeFileSync(filePath, content);
        }

        processedFiles++;

      } catch (error) {
        console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
        throw error; // Âø´ÈÄüÂ§±Ë¥•
      }
    }

    // ÊâπÊ¨°È™åËØÅ (CODEXÂª∫ËÆÆ)
    console.log('üîç È™åËØÅÊâπÊ¨°ÁªìÊûú...');
    try {
      execSync('pnpm run type-check', { stdio: 'pipe' });
      console.log('‚úÖ ÊâπÊ¨°È™åËØÅÈÄöËøá');
    } catch (error) {
      console.error('‚ùå ÊâπÊ¨°È™åËØÅÂ§±Ë¥•ÔºåÂÅúÊ≠¢Â§ÑÁêÜ');
      throw new Error('TypeScriptÁºñËØëÈîôËØØÔºåËØ∑Ê£ÄÊü•‰øÆÂ§çÁªìÊûú');
    }
  }

  return processedFiles;
}

// Ëé∑ÂèñÈ≠îÊ≥ïÊï∞Â≠óÈîôËØØ
function getMagicNumberErrors() {
  let output = '';
  try {
    output = execSync('pnpm lint:check 2>&1', {
      encoding: 'utf8',
      shell: true,
      maxBuffer: 1024 * 1024 * 10,
    });
  } catch (error) {
    output = error.stdout || error.output?.join('') || '';
  }

  const errors = [];
  const lines = output.split('\n');
  let currentFile = '';

  for (const line of lines) {
    // Ê£ÄÊü•Êñá‰ª∂Ë∑ØÂæÑ
    const fileMatch = line.match(/^\/.*\.(ts|tsx|js|jsx)$/);
    if (fileMatch) {
      currentFile = line.trim();
      continue;
    }

    // Ê£ÄÊü•È≠îÊ≥ïÊï∞Â≠óÈîôËØØ
    const errorMatch = line.match(
      /^\s*(\d+):(\d+)\s+(error|warning)\s+No magic number:\s+([-]?(?:0x)?[0-9a-fA-F.]+)\s+no-magic-numbers/
    );

    if (errorMatch && currentFile) {
      const [, lineNum, colNum, severity, number] = errorMatch;
      const constantName = getConstantName(number);

      errors.push({
        file: currentFile,
        line: parseInt(lineNum),
        column: parseInt(colNum),
        number: number,
        constantName: constantName
      });
    }
  }

  return errors;
}

// ÁîüÊàêÂ∏∏ÈáèÂêç
function getConstantName(number) {
  const constantMap = {
    '0.5': 'MAGIC_0_5',
    '0.9': 'MAGIC_0_9', 
    '1.5': 'MAGIC_1_5',
    '131': 'MAGIC_131',
    '132': 'MAGIC_132',
    '133': 'MAGIC_133',
    '136': 'MAGIC_136',
    '190': 'MAGIC_190',
    '368': 'MAGIC_368'
  };

  return constantMap[number] || `MAGIC_${number.replace(/[.-]/g, '_').replace(/^0x/, 'HEX_')}`;
}

// ‰∏ªÂáΩÊï∞
function main() {
  console.log('üîç ÂÆâÂÖ®È≠îÊ≥ïÊï∞Â≠ó‰øÆÂ§çÂô®ÂêØÂä®...');

  const errors = getMagicNumberErrors();
  if (errors.length === 0) {
    console.log('‚úÖ Êú™ÂèëÁé∞ÈúÄË¶Å‰øÆÂ§çÁöÑÈ≠îÊ≥ïÊï∞Â≠ó');
    return;
  }

  console.log(`üìä ÂèëÁé∞ ${errors.length} ‰∏™È≠îÊ≥ïÊï∞Â≠óÈîôËØØ`);

  try {
    // È¢ÑÊ£ÄÊü•È™åËØÅ
    validateConstants(errors);

    // ÂàÜÊâπÂ§ÑÁêÜ
    const processedFiles = processBatch(errors, 3); // Â∞èÊâπÊ¨°ÔºåÊõ¥ÂÆâÂÖ®

    console.log(`\nüéâ ‰øÆÂ§çÂÆåÊàêÔºÅÂ§ÑÁêÜ‰∫Ü ${processedFiles} ‰∏™Êñá‰ª∂`);

    // ÊúÄÁªàÈ™åËØÅ
    console.log('üîç ÊâßË°åÊúÄÁªàÈ™åËØÅ...');
    execSync('pnpm run type-check', { stdio: 'inherit' });

    const remainingErrors = getMagicNumberErrors().length;
    console.log(`üìä Ââ©‰ΩôÈ≠îÊ≥ïÊï∞Â≠óÈîôËØØ: ${remainingErrors} ‰∏™`);

  } catch (error) {
    console.error('‚ùå ‰øÆÂ§çËøáÁ®ã‰∏≠Âá∫Áé∞ÈîôËØØ:', error.message);
    console.log('\nüí° Âª∫ËÆÆÊ£ÄÊü•ÊúÄËøëÁöÑ‰øÆÊîπÂπ∂ËøêË°å pnpm run type-check');
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

#!/usr/bin/env node

/**
 * Security Configuration Check Script
 *
 * This script validates the security configuration of the application
 * and provides recommendations for improvement.
 */

const fs = require('fs');
const path = require('path');

class SecurityChecker {
  constructor() {
    this.results = {
      passed: true,
      checks: [],
      warnings: [],
      recommendations: [],
    };
  }

  /**
   * Run all security checks
   */
  async runChecks() {
    console.log('üîí Running security configuration checks...\n');

    this.checkEnvironmentVariables();
    this.checkSecurityHeaders();
    this.checkCSPConfiguration();
    this.checkDependencies();
    this.checkFilePermissions();
    this.checkGitIgnore();
    this.checkTurnstileConfiguration();

    this.generateReport();
  }

  /**
   * Check environment variables configuration
   */
  checkEnvironmentVariables() {
    console.log('üîç Checking environment variables...');

    // Check .env.example exists
    this.addCheck(
      'Environment template exists',
      fs.existsSync('.env.example'),
      '.env.example file should exist as a template',
    );

    // Check .env files are not committed
    const envFiles = [
      '.env',
      '.env.local',
      '.env.production',
      '.env.development',
    ];
    envFiles.forEach((file) => {
      if (fs.existsSync(file)) {
        this.addWarning(
          `${file} exists - ensure it's not committed to version control`,
        );
      }
    });

    // Check env.mjs exists
    this.addCheck(
      'Environment validation exists',
      fs.existsSync('env.mjs'),
      'env.mjs file should exist for environment variable validation',
    );

    // Check for sensitive data in .env.example
    if (fs.existsSync('.env.example')) {
      const envExample = fs.readFileSync('.env.example', 'utf8');

      // Look for uncommented lines with sensitive values that don't look like placeholders
      const lines = envExample.split('\n');
      const sensitiveLines = lines.filter((line) => {
        // Skip comments
        if (line.trim().startsWith('#')) return false;

        // Check for sensitive keys with non-placeholder values
        const sensitivePattern = /(password|secret|key|token)=/i;
        const placeholderPattern =
          /(your_|test_|example_|placeholder|changeme|replace)/i;

        if (sensitivePattern.test(line)) {
          const value = line.split('=')[1]?.trim();
          if (value && !placeholderPattern.test(value) && value.length > 10) {
            return true;
          }
        }
        return false;
      });

      this.addCheck(
        'No sensitive data in .env.example',
        sensitiveLines.length === 0,
        '.env.example should not contain real sensitive data',
      );

      if (sensitiveLines.length > 0) {
        console.log(
          `     Found potentially sensitive lines: ${sensitiveLines.length}`,
        );
      }
    }
  }

  /**
   * Check security headers configuration
   */
  checkSecurityHeaders() {
    console.log('üõ°Ô∏è  Checking security headers...');

    // Check security config exists
    const securityConfigPath = 'src/config/security.ts';
    this.addCheck(
      'Security configuration exists',
      fs.existsSync(securityConfigPath),
      'Security configuration file should exist',
    );

    // Check next.config.ts for security headers
    if (fs.existsSync('next.config.ts')) {
      const nextConfig = fs.readFileSync('next.config.ts', 'utf8');

      this.addCheck(
        'Security headers configured',
        nextConfig.includes('getSecurityHeaders'),
        'next.config.ts should use getSecurityHeaders function',
      );

      this.addCheck(
        'CSP configured',
        nextConfig.includes('Content-Security-Policy') ||
          nextConfig.includes('getSecurityHeaders'),
        'Content Security Policy should be configured',
      );
    }
  }

  /**
   * Check CSP configuration
   */
  checkCSPConfiguration() {
    console.log('üîê Checking CSP configuration...');

    // Check CSP report endpoint exists
    const cspReportPath = 'src/app/api/csp-report/route.ts';
    this.addCheck(
      'CSP report endpoint exists',
      fs.existsSync(cspReportPath),
      'CSP report endpoint should exist for violation reporting',
    );

    // Check middleware includes security
    if (fs.existsSync('middleware.ts')) {
      const middleware = fs.readFileSync('middleware.ts', 'utf8');

      this.addCheck(
        'Middleware includes security',
        middleware.includes('getSecurityHeaders') ||
          middleware.includes('generateNonce'),
        'Middleware should include security headers',
      );
    }
  }

  /**
   * Check dependencies for security vulnerabilities
   */
  checkDependencies() {
    console.log('üì¶ Checking dependencies...');

    // Check if security-related packages are installed
    if (fs.existsSync('package.json')) {
      const packageJson = JSON.parse(fs.readFileSync('package.json', 'utf8'));
      const dependencies = {
        ...packageJson.dependencies,
        ...packageJson.devDependencies,
      };

      this.addCheck(
        'Environment validation package installed',
        '@t3-oss/env-nextjs' in dependencies,
        '@t3-oss/env-nextjs should be installed for environment validation',
      );

      this.addCheck(
        'Bot protection package installed',
        '@marsidev/react-turnstile' in dependencies,
        'Turnstile package should be installed for bot protection',
      );

      this.addCheck(
        'Security linting installed',
        'eslint-plugin-security' in dependencies,
        'Security ESLint plugin should be installed',
      );

      // Check for known vulnerable packages (basic check)
      const vulnerablePackages = ['lodash', 'moment', 'request'];
      vulnerablePackages.forEach((pkg) => {
        if (pkg in dependencies) {
          this.addWarning(
            `${pkg} is installed - consider using modern alternatives`,
          );
        }
      });
    }
  }

  /**
   * Check file permissions and sensitive files
   */
  checkFilePermissions() {
    console.log('üìÅ Checking file permissions...');

    // Check for sensitive files that shouldn't exist
    const sensitiveFiles = [
      '.env',
      '.env.local',
      '.env.production',
      'private.key',
      'server.key',
      'id_rsa',
      'id_dsa',
    ];

    sensitiveFiles.forEach((file) => {
      if (fs.existsSync(file)) {
        this.addWarning(
          `Sensitive file ${file} exists - ensure it's properly secured`,
        );
      }
    });
  }

  /**
   * Check .gitignore configuration
   */
  checkGitIgnore() {
    console.log('üìù Checking .gitignore...');

    if (fs.existsSync('.gitignore')) {
      const gitignore = fs.readFileSync('.gitignore', 'utf8');

      const requiredEntries = [
        { pattern: '.env', alternatives: ['.env', '.env*'] },
        { pattern: '.env.local', alternatives: ['.env.local', '.env*'] },
        { pattern: '.env.*.local', alternatives: ['.env.*.local', '.env*'] },
        {
          pattern: 'node_modules',
          alternatives: ['node_modules', '/node_modules'],
        },
      ];

      requiredEntries.forEach(({ pattern, alternatives }) => {
        const isIncluded = alternatives.some((alt) => gitignore.includes(alt));
        this.addCheck(
          `.gitignore includes ${pattern}`,
          isIncluded,
          `${pattern} should be in .gitignore`,
        );
      });
    } else {
      this.addCheck('.gitignore exists', false, '.gitignore file should exist');
    }
  }

  /**
   * Check Turnstile configuration
   */
  checkTurnstileConfiguration() {
    console.log('ü§ñ Checking bot protection...');

    // Check Turnstile component exists
    const turnstilePath = 'src/components/security/turnstile.tsx';
    this.addCheck(
      'Turnstile component exists',
      fs.existsSync(turnstilePath),
      'Turnstile component should exist for bot protection',
    );

    // Check Turnstile verification endpoint exists
    const verifyPath = 'src/app/api/verify-turnstile/route.ts';
    this.addCheck(
      'Turnstile verification endpoint exists',
      fs.existsSync(verifyPath),
      'Turnstile verification endpoint should exist',
    );
  }

  /**
   * Add a check result
   */
  addCheck(name, passed, message) {
    this.results.checks.push({
      name,
      passed,
      message,
    });

    if (!passed) {
      this.results.passed = false;
    }

    const status = passed ? '‚úÖ' : '‚ùå';
    console.log(`  ${status} ${name}`);
    if (!passed) {
      console.log(`     ${message}`);
    }
  }

  /**
   * Add a warning
   */
  addWarning(message) {
    this.results.warnings.push(message);
    console.log(`  ‚ö†Ô∏è  ${message}`);
  }

  /**
   * Add a recommendation
   */
  addRecommendation(message) {
    this.results.recommendations.push(message);
  }

  /**
   * Generate final report
   */
  generateReport() {
    console.log('\nüìä Security Check Report');
    console.log('========================');

    const totalChecks = this.results.checks.length;
    const passedChecks = this.results.checks.filter((c) => c.passed).length;
    const failedChecks = totalChecks - passedChecks;

    console.log(`Total checks: ${totalChecks}`);
    console.log(`Passed: ${passedChecks}`);
    console.log(`Failed: ${failedChecks}`);
    console.log(`Warnings: ${this.results.warnings.length}`);

    if (this.results.passed) {
      console.log('\nüéâ All security checks passed!');
    } else {
      console.log(
        '\n‚ö†Ô∏è  Some security checks failed. Please review and fix the issues above.',
      );
    }

    // Add general recommendations
    this.addRecommendation(
      'Regularly update dependencies to patch security vulnerabilities',
    );
    this.addRecommendation('Use strong, unique secrets in production');
    this.addRecommendation('Enable security monitoring and alerting');
    this.addRecommendation(
      'Regularly review and update security configurations',
    );

    if (this.results.recommendations.length > 0) {
      console.log('\nüí° Recommendations:');
      this.results.recommendations.forEach((rec) => {
        console.log(`  ‚Ä¢ ${rec}`);
      });
    }

    // Save report to file
    const reportPath = 'reports/security-check-report.json';
    const reportsDir = path.dirname(reportPath);

    if (!fs.existsSync(reportsDir)) {
      fs.mkdirSync(reportsDir, { recursive: true });
    }

    fs.writeFileSync(reportPath, JSON.stringify(this.results, null, 2));
    console.log(`\nüìÑ Report saved to ${reportPath}`);

    // Exit with appropriate code
    process.exit(this.results.passed ? 0 : 1);
  }
}

// Run the security check
if (require.main === module) {
  const checker = new SecurityChecker();
  checker.runChecks().catch((error) => {
    console.error('Error running security checks:', error);
    process.exit(1);
  });
}

module.exports = SecurityChecker;

#!/usr/bin/env node

/**
 * ÊµãËØïÊúçÂä°Âô®ÂêØÂä®ËÑöÊú¨
 *
 * ‰∏ìÈó®‰∏∫ E2E ÊµãËØïÂêØÂä®ÂºÄÂèëÊúçÂä°Âô®ÔºåÁ°Æ‰øùÔºö
 * 1. React Scan Ë¢´Á¶ÅÁî®‰ª•ÈÅøÂÖçÊµãËØïÂπ≤Êâ∞
 * 2. ÂÖ∂‰ªñÂºÄÂèëÂ∑•ÂÖ∑Ë¢´ÈÄÇÂΩìÈÖçÁΩÆ
 * 3. ÊµãËØïÁéØÂ¢ÉÂèòÈáèË¢´Ê≠£Á°ÆËÆæÁΩÆ
 */

const { spawn } = require('child_process');
const fs = require('fs');
const path = require('path');

class TestServerManager {
  constructor() {
    this.serverProcess = null;
    this.isShuttingDown = false;
  }

  /**
   * ËÆæÁΩÆÊµãËØïÁéØÂ¢ÉÂèòÈáè
   */
  setupTestEnvironment() {
    console.log('üß™ Setting up test environment...');

    // ËÆæÁΩÆÊµãËØïÁéØÂ¢ÉÂèòÈáè
    process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN = 'true';
    process.env.NEXT_PUBLIC_DISABLE_DEV_TOOLS = 'true';
    process.env.NEXT_PUBLIC_TEST_MODE = 'true';
    process.env.PLAYWRIGHT_TEST = 'true';

    console.log('   ‚úÖ NEXT_PUBLIC_DISABLE_REACT_SCAN=true');
    console.log('   ‚úÖ NEXT_PUBLIC_DISABLE_DEV_TOOLS=true');
    console.log('   ‚úÖ NEXT_PUBLIC_TEST_MODE=true');
    console.log('   ‚úÖ PLAYWRIGHT_TEST=true');
  }

  /**
   * ÂêØÂä®ÂºÄÂèëÊúçÂä°Âô®
   */
  async startServer() {
    return new Promise((resolve, reject) => {
      console.log('üöÄ Starting test server...');

      this.setupTestEnvironment();

      // ÂêØÂä® Next.js ÂºÄÂèëÊúçÂä°Âô®
      this.serverProcess = spawn('pnpm', ['dev'], {
        stdio: ['pipe', 'pipe', 'pipe'],
        env: {
          ...process.env,
          // Á°Æ‰øùÊµãËØïÁéØÂ¢ÉÂèòÈáèË¢´‰º†ÈÄí
          NEXT_PUBLIC_DISABLE_REACT_SCAN: 'true',
          NEXT_PUBLIC_TEST_MODE: 'true',
        },
      });

      let serverReady = false;
      let output = '';

      this.serverProcess.stdout.on('data', (data) => {
        const text = data.toString();
        output += text;

        // Ê£ÄÊü•ÊúçÂä°Âô®ÊòØÂê¶ÂáÜÂ§áÂ∞±Áª™
        if (text.includes('Ready') || text.includes('localhost:3000')) {
          if (!serverReady) {
            serverReady = true;
            console.log('‚úÖ Test server is ready');
            resolve(this.serverProcess);
          }
        }

        // ËæìÂá∫ÊúçÂä°Âô®Êó•ÂøóÔºàËøáÊª§ÊéâÂô™Èü≥Ôºâ
        if (!text.includes('webpack') && !text.includes('Compiled')) {
          process.stdout.write(text);
        }
      });

      this.serverProcess.stderr.on('data', (data) => {
        const text = data.toString();

        // ËøáÊª§ÊéâÂ∑≤Áü•ÁöÑÊó†ÂÆ≥Ë≠¶Âëä
        if (
          !text.includes('ExperimentalWarning') &&
          !text.includes('punycode') &&
          !text.includes('deprecated')
        ) {
          process.stderr.write(text);
        }
      });

      this.serverProcess.on('error', (error) => {
        console.error('‚ùå Failed to start test server:', error);
        reject(error);
      });

      this.serverProcess.on('exit', (code) => {
        if (!this.isShuttingDown) {
          console.log(`üîÑ Test server exited with code ${code}`);
        }
      });

      // Ë∂ÖÊó∂Â§ÑÁêÜ
      setTimeout(() => {
        if (!serverReady) {
          console.error('‚ùå Test server failed to start within timeout');
          reject(new Error('Server startup timeout'));
        }
      }, 30000); // 30ÁßíË∂ÖÊó∂
    });
  }

  /**
   * ÂÅúÊ≠¢ÊúçÂä°Âô®
   */
  async stopServer() {
    if (this.serverProcess && !this.isShuttingDown) {
      console.log('üõë Stopping test server...');
      this.isShuttingDown = true;

      this.serverProcess.kill('SIGTERM');

      // Á≠âÂæÖËøõÁ®ãÁªìÊùü
      await new Promise((resolve) => {
        this.serverProcess.on('exit', resolve);

        // Âº∫Âà∂ÁªìÊùüË∂ÖÊó∂
        setTimeout(() => {
          if (this.serverProcess) {
            this.serverProcess.kill('SIGKILL');
          }
          resolve(null);
        }, 5000);
      });

      console.log('‚úÖ Test server stopped');
    }
  }

  /**
   * È™åËØÅÊúçÂä°Âô®Áä∂ÊÄÅ
   */
  async verifyServer(url = 'http://localhost:3000') {
    console.log(`üîç Verifying server at ${url}...`);

    try {
      const response = await fetch(url);
      const isHealthy = response.ok;

      if (isHealthy) {
        console.log('‚úÖ Server is healthy');

        // Ê£ÄÊü•ÂìçÂ∫î‰∏≠ÊòØÂê¶ÂåÖÂê´ React Scan Áõ∏ÂÖ≥ÂÜÖÂÆπ
        const html = await response.text();
        const hasReactScan =
          html.includes('react-scan') || html.includes('React Scan');

        if (hasReactScan) {
          console.warn('‚ö†Ô∏è  Server response contains React Scan content');
          return { healthy: true, hasInterference: true };
        } 
          console.log('‚úÖ Server response is clean (no React Scan content)');
          return { healthy: true, hasInterference: false };

      } 
        console.error(`‚ùå Server returned status ${response.status}`);
        return { healthy: false, hasInterference: false };

    } catch (error) {
      console.error('‚ùå Server verification failed:', error.message);
      return { healthy: false, hasInterference: false };
    }
  }

  /**
   * ÂÆåÊï¥ÁöÑÊµãËØïÊúçÂä°Âô®ÁîüÂëΩÂë®ÊúüÁÆ°ÁêÜ
   */
  async runWithServer(testFunction) {
    try {
      await this.startServer();

      // Á≠âÂæÖÊúçÂä°Âô®ÂÆåÂÖ®ÂêØÂä®
      await new Promise((resolve) => setTimeout(resolve, 3000));

      // È™åËØÅÊúçÂä°Âô®Áä∂ÊÄÅ
      const status = await this.verifyServer();
      if (!status.healthy) {
        throw new Error('Server is not healthy');
      }

      if (status.hasInterference) {
        console.warn(
          '‚ö†Ô∏è  Detected potential interference, but proceeding with tests',
        );
      }

      // ËøêË°åÊµãËØïÂáΩÊï∞
      await testFunction();
    } finally {
      await this.stopServer();
    }
  }
}

// Â¶ÇÊûúÁõ¥Êé•ËøêË°åÊ≠§ËÑöÊú¨
if (require.main === module) {
  const manager = new TestServerManager();

  // Â§ÑÁêÜÈÄÄÂá∫‰ø°Âè∑
  process.on('SIGINT', async () => {
    console.log('\nüõë Received SIGINT, shutting down...');
    await manager.stopServer();
    process.exit(0);
  });

  process.on('SIGTERM', async () => {
    console.log('\nüõë Received SIGTERM, shutting down...');
    await manager.stopServer();
    process.exit(0);
  });

  // ÂêØÂä®ÊúçÂä°Âô®Âπ∂‰øùÊåÅËøêË°å
  manager
    .startServer()
    .then(() => {
      console.log('üéØ Test server is running. Press Ctrl+C to stop.');
    })
    .catch((error) => {
      console.error('‚ùå Failed to start test server:', error);
      process.exit(1);
    });
}

module.exports = TestServerManager;

#!/usr/bin/env node
/*
 * Summarize ESLint JSON output (reports/eslint-report.json)
 * Outputs a concise JSON summary to stdout for the CLI to capture.
 */

const fs = require('fs');
const path = require('path');

const reportPath = path.resolve(process.cwd(), 'reports/eslint-report.json');
if (!fs.existsSync(reportPath)) {
  console.error('Missing reports/eslint-report.json');
  process.exit(1);
}

/** @type {Array<{filePath:string,errorCount:number,warningCount:number,fixableErrorCount:number,fixableWarningCount:number,source?:string,messages:Array<{ruleId:string|null,severity:number,message:string,line:number,column:number}>>}> */
const files = JSON.parse(fs.readFileSync(reportPath, 'utf8'));

const total = { errors: 0, warnings: 0 };
const byRule = new Map();
const messageSamples = new Map();

for (const f of files) {
  total.errors += f.errorCount || 0;
  total.warnings += f.warningCount || 0;
  for (const m of f.messages) {
    const key = m.ruleId || 'unknown';
    const entry = byRule.get(key) || { errors: 0, warnings: 0 };
    if (m.severity === 2) entry.errors += 1;
    else entry.warnings += 1;
    byRule.set(key, entry);
    if (!messageSamples.has(key) && m.message)
      messageSamples.set(key, m.message);
  }
}

// Helpers
const get = (ruleId) => byRule.get(ruleId) || { errors: 0, warnings: 0 };
const countMsgIncludes = (substr, filterRulePrefix = null) => {
  let n = 0;
  for (const f of files) {
    for (const m of f.messages) {
      if (filterRulePrefix && !(m.ruleId || '').startsWith(filterRulePrefix))
        continue;
      if ((m.message || '').includes(substr)) n++;
    }
  }
  return n;
};

// Category 1: any Á±ªÂûã
const anyRule = '@typescript-eslint/no-explicit-any';
const anyStats = get(anyRule);

// Category 2: ÂÆâÂÖ®Áõ∏ÂÖ≥Ôºàsecurity*Ôºâ
let securityErrors = 0,
  securityWarnings = 0;
for (const [ruleId, stat] of byRule.entries()) {
  if (ruleId.startsWith('security/') || ruleId.startsWith('security-node/')) {
    securityErrors += stat.errors;
    securityWarnings += stat.warnings;
  }
}
const securityTotal = securityErrors + securityWarnings;

// Specific security message splits (best-effort by message text)
const genericObjectInjection = countMsgIncludes(
  'Generic Object Injection Sink',
);
const variableAssignedToInjection = countMsgIncludes(
  'Variable Assigned to Object Injection Sink',
);

// Category 3: console
const consoleStats = get('no-console');

// Category 4: È≠îÊ≥ïÊï∞Â≠ó
const magicStats = get('no-magic-numbers');
// Breakdown by typical numbers (parse from message text)
const magicNumbers = new Map();
for (const f of files) {
  for (const m of f.messages) {
    if (m.ruleId === 'no-magic-numbers') {
      const match = m.message.match(/No magic number:?\s*(-?\d+)/i);
      if (match) {
        const num = match[1];
        magicNumbers.set(num, (magicNumbers.get(num) || 0) + 1);
      }
    }
  }
}
// sort top items
const magicBreakdown = Array.from(magicNumbers.entries())
  .sort((a, b) => b[1] - a[1])
  .slice(0, 12)
  .map(([num, cnt]) => ({ number: Number(num), count: cnt }));

// Category 5: Â§çÊùÇÂ∫¶
const complexityStats = {
  complexity: get('complexity'),
  maxDepth: get('max-depth'),
  maxNestedCallbacks: get('max-nested-callbacks'),
  noElseReturn: get('no-else-return'),
  noPlusPlus: get('no-plusplus'),
};

// Category 6: Êú™‰ΩøÁî®ÂèòÈáè
const unusedCombined = {
  js: get('no-unused-vars'),
  ts: get('@typescript-eslint/no-unused-vars'),
};
// Extract common variable names
const unusedNameCounts = new Map();
for (const f of files) {
  for (const m of f.messages) {
    if (
      m.ruleId === 'no-unused-vars' ||
      m.ruleId === '@typescript-eslint/no-unused-vars'
    ) {
      const mm = m.message.match(/'([^']+)' is defined but never used/);
      if (mm) {
        const name = mm[1];
        unusedNameCounts.set(name, (unusedNameCounts.get(name) || 0) + 1);
      }
    }
  }
}
const unusedTop = Array.from(unusedNameCounts.entries())
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10)
  .map(([name, count]) => ({ name, count }));

// Category 7: ÂºÄÂèëÂ∑•ÂÖ∑Áõ∏ÂÖ≥
const tsNoCheck = get('@typescript-eslint/ban-ts-comment');
const tNotDefined = (() => {
  let n = 0;
  for (const f of files) {
    for (const m of f.messages) {
      if (
        m.ruleId === 'no-undef' &&
        (m.message || '').includes("'t' is not defined")
      )
        n++;
    }
  }
  return n;
})();
const nextImg = get('@next/next/no-img-element');

const summary = {
  timestamp: new Date().toISOString(),
  totals: {
    errors: total.errors,
    warnings: total.warnings,
    total: total.errors + total.warnings,
  },
  anyType: { rule: anyRule, ...anyStats },
  security: {
    total: securityTotal,
    errors: securityErrors,
    warnings: securityWarnings,
    breakdown: {
      genericObjectInjection,
      variableAssignedToInjection,
    },
  },
  console: consoleStats,
  magicNumbers: {
    total: magicStats.errors + magicStats.warnings,
    top: magicBreakdown,
  },
  complexity: {
    complexity: complexityStats.complexity,
    maxDepth: complexityStats.maxDepth,
    maxNestedCallbacks: complexityStats.maxNestedCallbacks,
    noElseReturn: complexityStats.noElseReturn,
    noPlusPlus: complexityStats.noPlusPlus,
  },
  unused: {
    total:
      unusedCombined.js.errors +
      unusedCombined.js.warnings +
      unusedCombined.ts.errors +
      unusedCombined.ts.warnings,
    js: unusedCombined.js,
    ts: unusedCombined.ts,
    topNames: unusedTop,
  },
  devTools: {
    tsNoCheck: tsNoCheck,
    tNotDefinedCount: tNotDefined,
    nextImg: nextImg,
  },
};

console.log(JSON.stringify(summary, null, 2));

#!/usr/bin/env tsx
/**
 * ESLintÈîôËØØÁªüËÆ°ÂàÜÊûêÂ∑•ÂÖ∑
 * ËØªÂèñESLint JSONËæìÂá∫ÔºåÊåâËßÑÂàôËÅöÂêàËÆ°Êï∞‰∏éÊñá‰ª∂ÂàÜÂ∏É
 */

import { readFileSync } from 'fs';
import { join } from 'path';

interface ESLintMessage {
  ruleId: string | null;
  severity: number;
  message: string;
  line: number;
  column: number;
  nodeType?: string;
  messageId?: string;
  endLine?: number;
  endColumn?: number;
}

interface ESLintResult {
  filePath: string;
  messages: ESLintMessage[];
  errorCount: number;
  warningCount: number;
  fixableErrorCount: number;
  fixableWarningCount: number;
  source?: string;
}

interface RuleSummary {
  ruleId: string;
  errorCount: number;
  warningCount: number;
  totalCount: number;
  files: Map<string, number>;
}

function main() {
  try {
    const eslintJsonPath = join(process.cwd(), '.tmp/eslint.json');
    const eslintData: ESLintResult[] = JSON.parse(readFileSync(eslintJsonPath, 'utf-8'));

    console.log('üîç ESLintÈîôËØØÁªüËÆ°ÂàÜÊûêÊä•Âëä');
    console.log('=' .repeat(50));

    // ÁªüËÆ°ÊÄª‰ΩìÊÉÖÂÜµ
    const totalFiles = eslintData.length;
    const filesWithIssues = eslintData.filter(result => result.messages.length > 0).length;
    const totalErrors = eslintData.reduce((sum, result) => sum + result.errorCount, 0);
    const totalWarnings = eslintData.reduce((sum, result) => sum + result.warningCount, 0);

    console.log(`üìä ÊÄª‰ΩìÁªüËÆ°:`);
    console.log(`  Ê£ÄÊü•Êñá‰ª∂: ${totalFiles}`);
    console.log(`  ÊúâÈóÆÈ¢òÊñá‰ª∂: ${filesWithIssues}`);
    console.log(`  ÈîôËØØÊÄªÊï∞: ${totalErrors}`);
    console.log(`  Ë≠¶ÂëäÊÄªÊï∞: ${totalWarnings}`);
    console.log(`  ÈóÆÈ¢òÊÄªËÆ°: ${totalErrors + totalWarnings}`);
    console.log();

    // ÊåâËßÑÂàôËÅöÂêà
    const ruleSummaries = new Map<string, RuleSummary>();

    for (const result of eslintData) {
      for (const message of result.messages) {
        const ruleId = message.ruleId || 'no-rule';

        if (!ruleSummaries.has(ruleId)) {
          ruleSummaries.set(ruleId, {
            ruleId,
            errorCount: 0,
            warningCount: 0,
            totalCount: 0,
            files: new Map()
          });
        }

        const summary = ruleSummaries.get(ruleId)!;

        if (message.severity === 2) {
          summary.errorCount++;
        } else {
          summary.warningCount++;
        }
        summary.totalCount++;

        // ÁªüËÆ°Êñá‰ª∂ÂàÜÂ∏É
        const fileName = result.filePath.replace(process.cwd(), '').replace(/^\//, '');
        const currentCount = summary.files.get(fileName) || 0;
        summary.files.set(fileName, currentCount + 1);
      }
    }

    // ÊåâÊÄªÊï∞ÊéíÂ∫è
    const sortedRules = Array.from(ruleSummaries.values())
      .sort((a, b) => b.totalCount - a.totalCount);

    console.log(`üî• Top 10 ESLintËßÑÂàô (ÊåâÈóÆÈ¢òÊï∞ÈáèÊéíÂ∫è):`);
