    console.log('-'.repeat(60));

    // ÈáçÁÇπËßÑÂàôËØ¶ÁªÜÂàÜÊûê
    const focusRules = [
      'no-restricted-imports',
      'security/detect-object-injection', 
      '@typescript-eslint/no-unused-vars',
