        }
      } else {
        console.log(`\nüìå ${ruleId}: 0 ‰∏™ÈóÆÈ¢ò ‚úÖ`);
      }
    }

    console.log();
    console.log('‚úÖ ÁªüËÆ°ÂÆåÊàêÔºÅ');

  } catch (error) {
    console.error('‚ùå ÁªüËÆ°Â§±Ë¥•:', error);
    process.exit(1);
  }
}

if (require.main === module) {
