// ËΩ¨Êç¢ÈÖçÁΩÆ
const TRANSFORM_CONFIG = {
  // Êâ´ÊèèÊ®°Âºè
  scanPatterns: [
    'src/**/*.{ts,tsx,js,jsx}',
    '!src/**/*.{test,spec}.{ts,tsx,js,jsx}',
    '!src/**/*.d.ts',
  ],

  // ËæìÂá∫ÁõÆÂΩï
  outputDir: path.join(process.cwd(), 'reports', 'transforms'),

  // Â§á‰ªΩÁõÆÂΩï
  backupDir: path.join(process.cwd(), 'backups', 'barrel-exports'),

  // Ëß£ÊûêÂô®ÈÄâÈ°π
  parserOptions: {
    sourceType: 'module',
    allowImportExportEverywhere: true,
    allowReturnOutsideFunction: true,
    plugins: [
      'typescript',
      'jsx',
      'decorators-legacy',
      'classProperties',
      'objectRestSpread',
      'asyncGenerators',
      'functionBind',
      'exportDefaultFrom',
      'exportNamespaceFrom',
      'dynamicImport',
    ],
  },

  // ËΩ¨Êç¢ÈÄâÈ°π
  transformOptions: {
    createBackup: true,
    dryRun: false,
    verbose: true,
    preserveComments: true,
  },
};

class BarrelExportTransformer {
  constructor(options = {}) {
    this.options = { ...TRANSFORM_CONFIG.transformOptions, ...options };
    this.outputDir = TRANSFORM_CONFIG.outputDir;
    this.backupDir = TRANSFORM_CONFIG.backupDir;

    // Á°Æ‰øùtransformStatsÂú®ÊâÄÊúâÊÉÖÂÜµ‰∏ãÈÉΩÊ≠£Á°ÆÂàùÂßãÂåñ
    this.initializeStats();

    this.ensureDirectories();
  }

  ensureDirectories() {
    [this.outputDir, this.backupDir].forEach((dir) => {
      if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir, { recursive: true });
      }
    });
  }

  /**
   * ÂàùÂßãÂåñÁªüËÆ°‰ø°ÊÅØ
   */
  initializeStats() {
    this.transformStats = {
      filesProcessed: 0,
      filesTransformed: 0,
      exportStarRemoved: 0,
      namedExportsAdded: 0,
      errors: [],
    };
  }

  /**
   * ÂàÜÊûêÊñá‰ª∂‰∏≠ÁöÑexport *ËØ≠Âè•
   */
  analyzeExportStar(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const ast = parse(content, TRANSFORM_CONFIG.parserOptions);

      const exportStarNodes = [];
      const existingExports = new Set();

      traverse(ast, {
        ExportAllDeclaration(nodePath) {
          exportStarNodes.push({
            node: nodePath.node,
            source: nodePath.node.source.value,
            line: nodePath.node.loc?.start.line,
          });
        },
        ExportNamedDeclaration(nodePath) {
          if (nodePath.node.specifiers) {
            nodePath.node.specifiers.forEach((spec) => {
              if (spec.exported) {
                existingExports.add(spec.exported.name);
              }
            });
          }
        },
      });

      return { exportStarNodes, existingExports, ast, content };
    } catch (error) {
      // ËÆ∞ÂΩïÈîôËØØ‰ΩÜ‰∏çË¶ÅÂú®Â§ÑÁêÜ‰∏≠ÈáçÁΩÆÁªüËÆ°ÂØπË±°ÔºåÈÅøÂÖçÂºïÁî®‰∏¢Â§±
      (
        this.transformStats ||
        (this.transformStats = {
          filesProcessed: 0,
          filesTransformed: 0,
          exportStarRemoved: 0,
          namedExportsAdded: 0,
          errors: [],
        })
      ).errors.push({
        file: filePath,
        error: error.message,
        type: 'parse_error',
      });

      console.error(`‚ùå Ëß£ÊûêÂ§±Ë¥•: ${filePath} - ${error.message}`);
      return null;
    }
  }

  /**
   * Ëß£ÊûêÁõÆÊ†áÊñá‰ª∂ÁöÑÂØºÂá∫
   */
  async resolveExportsFromModule(modulePath, currentFilePath) {
    try {
      // Ëß£ÊûêÁõ∏ÂØπË∑ØÂæÑ
      const resolvedPath = this.resolveModulePath(modulePath, currentFilePath);

      if (!resolvedPath || !fs.existsSync(resolvedPath)) {
        return [];
      }

      const content = fs.readFileSync(resolvedPath, 'utf8');
      const ast = parse(content, TRANSFORM_CONFIG.parserOptions);

      const exports = [];

      traverse(ast, {
        ExportNamedDeclaration(nodePath) {
          const { node } = nodePath;

          // Â§ÑÁêÜ export { a, b }
          if (node.specifiers && node.specifiers.length > 0) {
            node.specifiers.forEach((spec) => {
              if (spec.exported) {
                exports.push(spec.exported.name);
              }
            });
          }

          // Â§ÑÁêÜ export const/function/class
          if (node.declaration) {
            if (node.declaration.id) {
              exports.push(node.declaration.id.name);
            } else if (node.declaration.declarations) {
              node.declaration.declarations.forEach((decl) => {
                if (decl.id && decl.id.name) {
                  exports.push(decl.id.name);
                }
              });
            }
          }
        },

        ExportDefaultDeclaration(nodePath) {
          exports.push('default');
        },
      });

      return exports;
    } catch (error) {
      if (this.options.verbose) {
        console.warn(`‚ö†Ô∏è Êó†Ê≥ïËß£ÊûêÊ®°ÂùóÂØºÂá∫: ${modulePath} - ${error.message}`);
      }
      return [];
    }
  }

  /**
   * Ëß£ÊûêÊ®°ÂùóË∑ØÂæÑ
   */
  resolveModulePath(modulePath, currentFilePath) {
    if (!modulePath.startsWith('.')) {
      return null; // Ë∑≥Ëøá node_modules
    }

    const currentDir = path.dirname(currentFilePath);
    const resolvedPath = path.resolve(currentDir, modulePath);

    // Â∞ùËØï‰∏çÂêåÁöÑÊâ©Â±ïÂêç
    const extensions = [
      '.ts',
      '.tsx',
      '.js',
      '.jsx',
      '/index.ts',
      '/index.tsx',
      '/index.js',
      '/index.jsx',
    ];

    for (const ext of extensions) {
      const testPath = resolvedPath + ext;
      if (fs.existsSync(testPath)) {
        return testPath;
      }
    }

    return null;
  }

  /**
   * ËΩ¨Êç¢Âçï‰∏™Êñá‰ª∂
   */
  async transformFile(filePath) {
    try {
      if (this.options.verbose) {
        console.log(`üîÑ Â§ÑÁêÜÊñá‰ª∂: ${filePath}`);
      }

      // ÊçïËé∑Á®≥ÂÆöÂºïÁî®ÔºåÈÅøÂÖçÂú® traverse ÂõûË∞É‰∏≠‰∏¢Â§± this ‰∏ä‰∏ãÊñá
      const stats =
        this.transformStats ||
        (this.transformStats = {
          filesProcessed: 0,
          filesTransformed: 0,
          exportStarRemoved: 0,
          namedExportsAdded: 0,
          errors: [],
        });

      stats.filesProcessed++;

      const analysis = this.analyzeExportStar(filePath);
      if (!analysis || analysis.exportStarNodes.length === 0) {
        return false; // Ê≤°ÊúâÈúÄË¶ÅËΩ¨Êç¢ÁöÑÂÜÖÂÆπ
      }

      const { exportStarNodes, existingExports, ast, content } = analysis;
      let hasChanges = false;

      // ÂàõÂª∫Â§á‰ªΩ
      if (this.options.createBackup && !this.options.dryRun) {
        this.createBackup(filePath, content);
      }

      // Â§ÑÁêÜÊØè‰∏™ export * ËØ≠Âè•
      for (const exportInfo of exportStarNodes) {
        try {
          const { node, source } = exportInfo;

          // Ëé∑ÂèñÁõÆÊ†áÊ®°ÂùóÁöÑÂØºÂá∫
          const moduleExports = await this.resolveExportsFromModule(
            source,
            filePath,
          );

          if (moduleExports.length === 0) {
            if (this.options.verbose) {
              console.warn(`‚ö†Ô∏è Êó†Ê≥ïËß£ÊûêÊ®°ÂùóÂØºÂá∫: ${source}`);
            }
            continue;
          }

          // ËøáÊª§ÊéâÂ∑≤Â≠òÂú®ÁöÑÂØºÂá∫
          const newExports = moduleExports.filter(
            (exp) => !existingExports.has(exp),
          );

          if (newExports.length === 0) {
            continue;
          }

          // ËΩ¨Êç¢ AST
          traverse(ast, {
            // ‰ΩøÁî®ÁÆ≠Â§¥ÂáΩÊï∞‰ª•ÊçïËé∑Â§ñÂ±Ç‰ΩúÁî®ÂüüÔºåÈÅøÂÖç this ÂèòÊõ¥
            ExportAllDeclaration: (nodePath) => {
              if (nodePath.node === node) {
                // ÂàõÂª∫ÂëΩÂêçÂØºÂá∫ËäÇÁÇπ
                const specifiers = newExports.map((exportName) => {
                  return {
                    type: 'ExportSpecifier',
                    local: { type: 'Identifier', name: exportName },
                    exported: { type: 'Identifier', name: exportName },
                  };
                });

                const namedExportNode = {
                  type: 'ExportNamedDeclaration',
                  declaration: null,
                  specifiers: specifiers,
                  source: node.source,
                };

                // ÊõøÊç¢ËäÇÁÇπ
                nodePath.replaceWith(namedExportNode);
                hasChanges = true;

                // ÁªüËÆ°‰ø°ÊÅØÊõ¥Êñ∞‰ΩøÁî®Á®≥ÂÆöÂºïÁî®
                stats.exportStarRemoved++;
                stats.namedExportsAdded += newExports.length;
              }
            },
          });
        } catch (exportError) {
          // Â§ÑÁêÜÂçï‰∏™ export Â§ÑÁêÜÈîôËØØÔºà‰∏çÈáçÁΩÆÁªüËÆ°ÂØπË±°Ôºâ
          stats.errors.push({
            file: filePath,
            error: `Export processing error: ${exportError.message}`,
            type: 'export_processing_error',
          });

          if (this.options.verbose) {
            console.warn(
              `‚ö†Ô∏è Â§ÑÁêÜexportÊó∂Âá∫Èîô: ${filePath} - ${exportError.message}`,
            );
          }
          continue; // ÁªßÁª≠Â§ÑÁêÜÂÖ∂‰ªñexport
        }
      }

      // ÁîüÊàêÊñ∞‰ª£Á†Å
      if (hasChanges && !this.options.dryRun) {
        const result = generate(ast, {
          retainLines: true,
          comments: this.options.preserveComments,
        });

        fs.writeFileSync(filePath, result.code);

        stats.filesTransformed++;

        if (this.options.verbose) {
          console.log(`‚úÖ Â∑≤ËΩ¨Êç¢: ${filePath}`);
        }
      }

      return hasChanges;
    } catch (error) {
      // Êï¥‰∏™ÊñπÊ≥ïÁöÑÈîôËØØÂ§ÑÁêÜÔºà‰∏çÈáçÁΩÆÁªüËÆ°ÂØπË±°Ôºâ
      const stats =
        this.transformStats ||
        (this.transformStats = {
          filesProcessed: 0,
          filesTransformed: 0,
          exportStarRemoved: 0,
          namedExportsAdded: 0,
          errors: [],
        });

      stats.errors.push({
        file: filePath,
        error: `Transform file error: ${error.message}`,
        type: 'transform_file_error',
      });

      if (this.options.verbose) {
        console.error(`‚ùå ËΩ¨Êç¢Êñá‰ª∂Â§±Ë¥•: ${filePath} - ${error.message}`);
      }

      return false;
    }
  }

  /**
   * ÂàõÂª∫Êñá‰ª∂Â§á‰ªΩ
   */
  createBackup(filePath, content) {
    const relativePath = path.relative(process.cwd(), filePath);
    const backupPath = path.join(this.backupDir, relativePath);
    const backupDir = path.dirname(backupPath);

    if (!fs.existsSync(backupDir)) {
      fs.mkdirSync(backupDir, { recursive: true });
    }

    fs.writeFileSync(backupPath, content);
  }

  /**
   * ÊâπÈáèËΩ¨Êç¢Êñá‰ª∂
   */
  async transformFiles(patterns = TRANSFORM_CONFIG.scanPatterns) {
    console.log('üöÄ ÂºÄÂßã Export * ËΩ¨Êç¢...\n');

    // Ëé∑ÂèñË¶ÅÂ§ÑÁêÜÁöÑÊñá‰ª∂
    const files = [];
    for (const pattern of patterns) {
      const matchedFiles = glob.sync(pattern, { cwd: process.cwd() });
      files.push(...matchedFiles);
    }

    console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÂæÖÂ§ÑÁêÜ\n`);

    // Â§ÑÁêÜÊñá‰ª∂
    for (const file of files) {
      try {
        await this.transformFile(file);
      } catch (error) {
        (
          this.transformStats ||
          (this.transformStats = {
            filesProcessed: 0,
            filesTransformed: 0,
            exportStarRemoved: 0,
            namedExportsAdded: 0,
            errors: [],
          })
        ).errors.push({
          file,
          error: error.message,
          type: 'transform_error',
        });
        console.error(`‚ùå ËΩ¨Êç¢Â§±Ë¥•: ${file} - ${error.message}`);
      }
    }

    // ÁîüÊàêÊä•Âëä
    await this.generateReport();

    console.log('\nüìä ËΩ¨Êç¢ÂÆåÊàê!');
    console.log(`Â§ÑÁêÜÊñá‰ª∂: ${this.transformStats.filesProcessed}`);
    console.log(`ËΩ¨Êç¢Êñá‰ª∂: ${this.transformStats.filesTransformed}`);
    console.log(`ÁßªÈô§ export *: ${this.transformStats.exportStarRemoved}`);
    console.log(`Ê∑ªÂä†ÂëΩÂêçÂØºÂá∫: ${this.transformStats.namedExportsAdded}`);

    if (this.transformStats.errors.length > 0) {
      console.log(`ÈîôËØØÊï∞Èáè: ${this.transformStats.errors.length}`);
    }

    return this.transformStats;
  }

  /**
   * ÁîüÊàêËΩ¨Êç¢Êä•Âëä
   */
  async generateReport() {
    const report = {
      timestamp: new Date().toISOString(),
      options: this.options,
      stats: this.transformStats,
      summary: {
        success: this.transformStats.errors.length === 0,
        filesProcessed: this.transformStats.filesProcessed,
        filesTransformed: this.transformStats.filesTransformed,
        transformationRate:
          this.transformStats.filesProcessed > 0
            ? `${(
                (this.transformStats.filesTransformed /
                  this.transformStats.filesProcessed) *
                100
              ).toFixed(2)  }%`
            : '0%',
      },
    };

    const reportPath = path.join(
      this.outputDir,
      `barrel-transform-${Date.now()}.json`,
    );
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

    console.log(`üìÑ Êä•ÂëäÂ∑≤ÁîüÊàê: ${reportPath}`);
    return report;
  }
}

// ÂëΩ‰ª§Ë°åÊé•Âè£
async function main() {
  const args = process.argv.slice(2);
  const options = {};

  // Ëß£ÊûêÂëΩ‰ª§Ë°åÂèÇÊï∞
  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--dry-run':
        options.dryRun = true;
        break;
      case '--no-backup':
        options.createBackup = false;
        break;
      case '--quiet':
        options.verbose = false;
        break;
      case '--no-comments':
        options.preserveComments = false;
        break;
    }
  }

  const transformer = new BarrelExportTransformer(options);

  try {
    await transformer.transformFiles();
  } catch (error) {
    console.error('‚ùå ËΩ¨Êç¢ËøáÁ®ã‰∏≠Âá∫Áé∞ÈîôËØØ:', error);
    process.exit(1);
  }
}

// Â¶ÇÊûúÁõ¥Êé•ËøêË°åÊ≠§ËÑöÊú¨
if (require.main === module) {
  main();
}

module.exports = BarrelExportTransformer;

#!/usr/bin/env node

/**
 * ÁøªËØëÈîÆËá™Âä®Êâ´ÊèèÂ∑•ÂÖ∑
 * ‰ΩøÁî®ASTËß£ÊûêÊâ´Êèè‰ª£Á†Å‰∏≠ÁöÑÁøªËØëÈîÆÔºåÁ°Æ‰øùÁøªËØëÂÆåÊï¥ÊÄß
 */

const fs = require('fs');
const path = require('path');
const { parse } = require('@babel/parser');
const traverse = require('@babel/traverse').default;
const glob = require('glob');

console.log('üîç ÂºÄÂßãÁøªËØëÈîÆÊâ´Êèè...\n');

// ÈÖçÁΩÆ
const CONFIG = {
  // Êâ´ÊèèÁöÑÊñá‰ª∂Ê®°Âºè
  SCAN_PATTERNS: [
    'src/**/*.{ts,tsx,js,jsx}',
    'app/**/*.{ts,tsx,js,jsx}',
    '!src/**/*.test.{ts,tsx,js,jsx}',
    '!src/**/*.spec.{ts,tsx,js,jsx}',
    '!**/*.d.ts',
  ],

  // ÁøªËØëÂáΩÊï∞Âêç
  TRANSLATION_FUNCTIONS: ['t', 'useTranslations', 'getTranslations'],

  // ËæìÂá∫ÁõÆÂΩï
  OUTPUT_DIR: path.join(process.cwd(), 'reports'),

  // ÁøªËØëÊñá‰ª∂ÁõÆÂΩï
  MESSAGES_DIR: path.join(process.cwd(), 'messages'),

  // ÊîØÊåÅÁöÑËØ≠Ë®Ä
  LOCALES: ['en', 'zh'],
};

const scanResults = {
  totalFiles: 0,
  scannedFiles: 0,
  translationKeys: new Set(),
  keyUsages: new Map(),
  errors: [],
  warnings: [],
  statistics: {
    totalKeys: 0,
    uniqueKeys: 0,
    missingKeys: [],
    unusedKeys: [],
  },
};

/**
 * Êâ´ÊèèÊñá‰ª∂‰∏≠ÁöÑÁøªËØëÈîÆ
 */
function scanFile(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    const ast = parse(content, {
      sourceType: 'module',
      plugins: [
        'typescript',
        'jsx',
        'decorators-legacy',
        'classProperties',
        'objectRestSpread',
        'asyncGenerators',
        'functionBind',
        'exportDefaultFrom',
        'exportNamespaceFrom',
        'dynamicImport',
        'nullishCoalescingOperator',
        'optionalChaining',
      ],
    });

    const fileKeys = new Set();

    traverse(ast, {
      // Êâ´Êèè useTranslations hook
      CallExpression(nodePath) {
        const { node } = nodePath;

        if (
          node.callee.name === 'useTranslations' &&
          node.arguments.length > 0 &&
          node.arguments[0].type === 'StringLiteral'
        ) {
          const namespace = node.arguments[0].value;
          fileKeys.add(namespace);
          recordKeyUsage(namespace, filePath, node.loc);
        }
      },

      // Êâ´Êèè t() ÂáΩÊï∞Ë∞ÉÁî®
      MemberExpression(nodePath) {
        const { node } = nodePath;

        if (
          node.property &&
          node.property.type === 'Identifier' &&
          node.property.name === 't' &&
          nodePath.parent.type === 'CallExpression'
        ) {
          const callExpression = nodePath.parent;
          if (
            callExpression.arguments.length > 0 &&
            callExpression.arguments[0].type === 'StringLiteral'
          ) {
            const key = callExpression.arguments[0].value;
            fileKeys.add(key);
            recordKeyUsage(key, filePath, callExpression.loc);
          }
        }
      },

      // Êâ´ÊèèÁõ¥Êé•ÁöÑ t() Ë∞ÉÁî®
      CallExpression(nodePath) {
        const { node } = nodePath;

        if (
          node.callee.type === 'Identifier' &&
          node.callee.name === 't' &&
          node.arguments.length > 0 &&
          node.arguments[0].type === 'StringLiteral'
        ) {
          const key = node.arguments[0].value;
          fileKeys.add(key);
          recordKeyUsage(key, filePath, node.loc);
        }
      },
    });

    scanResults.scannedFiles++;
    fileKeys.forEach((key) => scanResults.translationKeys.add(key));

    console.log(`‚úÖ Êâ´ÊèèÂÆåÊàê: ${filePath} (ÂèëÁé∞ ${fileKeys.size} ‰∏™ÁøªËØëÈîÆ)`);
  } catch (error) {
    scanResults.errors.push({
      file: filePath,
      error: error.message,
      type: 'parse_error',
    });
    console.error(`‚ùå Êâ´ÊèèÂ§±Ë¥•: ${filePath} - ${error.message}`);
  }
}

/**
 * ËÆ∞ÂΩïÁøªËØëÈîÆ‰ΩøÁî®ÊÉÖÂÜµ
 */
function recordKeyUsage(key, filePath, location) {
  if (!scanResults.keyUsages.has(key)) {
    scanResults.keyUsages.set(key, []);
  }

  scanResults.keyUsages.get(key).push({
    file: filePath,
    line: location ? location.start.line : null,
    column: location ? location.start.column : null,
  });
}

/**
 * Âä†ËΩΩÁé∞ÊúâÁøªËØëÊñá‰ª∂
 */
function loadExistingTranslations() {
  const translations = {};

  for (const locale of CONFIG.LOCALES) {
    const filePath = path.join(CONFIG.MESSAGES_DIR, `${locale}.json`);
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      translations[locale] = JSON.parse(content);
      console.log(`üìñ Âä†ËΩΩÁøªËØëÊñá‰ª∂: ${locale}.json`);
    } catch (error) {
      console.warn(`‚ö†Ô∏è  Êó†Ê≥ïÂä†ËΩΩÁøªËØëÊñá‰ª∂: ${locale}.json - ${error.message}`);
      translations[locale] = {};
    }
  }

  return translations;
}

/**
 * Ëé∑ÂèñÂµåÂ•óÂØπË±°ÁöÑÊâÄÊúâÈîÆ
 */
function getAllKeys(obj, prefix = '') {
  const keys = new Set();

  for (const [key, value] of Object.entries(obj)) {
    const fullKey = prefix ? `${prefix}.${key}` : key;

    if (typeof value === 'object' && value !== null) {
      getAllKeys(value, fullKey).forEach((k) => keys.add(k));
    } else {
      keys.add(fullKey);
    }
  }

  return keys;
}

/**
 * ÂàÜÊûêÁøªËØëÈîÆ‰ΩøÁî®ÊÉÖÂÜµ
 */
function analyzeTranslationUsage(translations) {
  const allTranslationKeys = new Set();

  // Êî∂ÈõÜÊâÄÊúâÁøªËØëÊñá‰ª∂‰∏≠ÁöÑÈîÆ
  for (const locale of CONFIG.LOCALES) {
    if (translations[locale]) {
      getAllKeys(translations[locale]).forEach((key) =>
        allTranslationKeys.add(key),
      );
    }
  }

  // ÊâæÂá∫Áº∫Â§±ÁöÑÈîÆÔºà‰ª£Á†Å‰∏≠‰ΩøÁî®‰ΩÜÁøªËØëÊñá‰ª∂‰∏≠Ê≤°ÊúâÔºâ
  const missingKeys = [];
  scanResults.translationKeys.forEach((key) => {
    if (!allTranslationKeys.has(key)) {
      missingKeys.push(key);
    }
  });

  // ÊâæÂá∫Êú™‰ΩøÁî®ÁöÑÈîÆÔºàÁøªËØëÊñá‰ª∂‰∏≠Êúâ‰ΩÜ‰ª£Á†Å‰∏≠Ê≤°Êúâ‰ΩøÁî®Ôºâ
  const unusedKeys = [];
  allTranslationKeys.forEach((key) => {
    if (!scanResults.translationKeys.has(key)) {
      unusedKeys.push(key);
    }
  });

  scanResults.statistics = {
    totalKeys: allTranslationKeys.size,
    uniqueKeys: scanResults.translationKeys.size,
    missingKeys,
    unusedKeys,
  };

  return { missingKeys, unusedKeys };
}

/**
 * ÁîüÊàêÊâ´ÊèèÊä•Âëä
 */
function generateScanReport(translations, analysis) {
  const report = {
    timestamp: new Date().toISOString(),
    summary: {
      totalFiles: scanResults.totalFiles,
      scannedFiles: scanResults.scannedFiles,
      errorCount: scanResults.errors.length,
      warningCount: scanResults.warnings.length,
      ...scanResults.statistics,
    },
    translationKeys: Array.from(scanResults.translationKeys).sort(),
    keyUsages: Object.fromEntries(
      Array.from(scanResults.keyUsages.entries()).map(([key, usages]) => [
        key,
        usages,
      ]),
    ),
    analysis,
    errors: scanResults.errors,
    warnings: scanResults.warnings,
  };

  // Á°Æ‰øùËæìÂá∫ÁõÆÂΩïÂ≠òÂú®
  if (!fs.existsSync(CONFIG.OUTPUT_DIR)) {
    fs.mkdirSync(CONFIG.OUTPUT_DIR, { recursive: true });
  }

  // ÂÜôÂÖ•Êä•ÂëäÊñá‰ª∂
  const reportPath = path.join(
    CONFIG.OUTPUT_DIR,
    'translation-scan-report.json',
  );
  fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

  console.log(`üìä Êâ´ÊèèÊä•ÂëäÂ∑≤ÁîüÊàê: ${reportPath}`);
  return report;
}

/**
 * ÊòæÁ§∫Êâ´ÊèèÁªìÊûú
 */
function displayResults(analysis) {
  console.log('\nüìä Êâ´ÊèèÁªüËÆ°:\n');
  console.log(
    `   Êâ´ÊèèÊñá‰ª∂: ${scanResults.scannedFiles}/${scanResults.totalFiles}`,
  );
  console.log(`   ÂèëÁé∞ÁøªËØëÈîÆ: ${scanResults.statistics.uniqueKeys}`);
  console.log(`   ÁøªËØëÊñá‰ª∂ÊÄªÈîÆÊï∞: ${scanResults.statistics.totalKeys}`);
  console.log(`   Áº∫Â§±ÈîÆ: ${analysis.missingKeys.length}`);
  console.log(`   Êú™‰ΩøÁî®ÈîÆ: ${analysis.unusedKeys.length}`);
  console.log(`   ÈîôËØØ: ${scanResults.errors.length}`);
  console.log(`   Ë≠¶Âëä: ${scanResults.warnings.length}\n`);

  // ÊòæÁ§∫Áº∫Â§±ÁöÑÈîÆ
  if (analysis.missingKeys.length > 0) {
    console.log('‚ùå Áº∫Â§±ÁöÑÁøªËØëÈîÆ:');
    analysis.missingKeys.slice(0, 10).forEach((key) => {
      const usages = scanResults.keyUsages.get(key) || [];
      console.log(`   - ${key} (‰ΩøÁî® ${usages.length} Ê¨°)`);
    });
    if (analysis.missingKeys.length > 10) {
      console.log(`   ... ËøòÊúâ ${analysis.missingKeys.length - 10} ‰∏™ÈîÆ`);
    }
    console.log();
  }

  // ÊòæÁ§∫Êú™‰ΩøÁî®ÁöÑÈîÆ
  if (analysis.unusedKeys.length > 0) {
    console.log('‚ö†Ô∏è  Êú™‰ΩøÁî®ÁöÑÁøªËØëÈîÆ:');
    analysis.unusedKeys.slice(0, 10).forEach((key) => {
      console.log(`   - ${key}`);
    });
    if (analysis.unusedKeys.length > 10) {
      console.log(`   ... ËøòÊúâ ${analysis.unusedKeys.length - 10} ‰∏™ÈîÆ`);
    }
    console.log();
  }
}

/**
 * ‰∏ªÂáΩÊï∞
 */
async function main() {
  try {
    // Ëé∑ÂèñË¶ÅÊâ´ÊèèÁöÑÊñá‰ª∂
    const files = [];
    for (const pattern of CONFIG.SCAN_PATTERNS) {
      const matchedFiles = glob.sync(pattern, { cwd: process.cwd() });
      files.push(...matchedFiles);
    }

    scanResults.totalFiles = files.length;
    console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÂæÖÊâ´Êèè\n`);

    // Êâ´ÊèèÊñá‰ª∂
    for (const file of files) {
      scanFile(file);
    }

    // Âä†ËΩΩÁé∞ÊúâÁøªËØë
    const translations = loadExistingTranslations();

    // ÂàÜÊûê‰ΩøÁî®ÊÉÖÂÜµ
    const analysis = analyzeTranslationUsage(translations);

    // ÁîüÊàêÊä•Âëä
    generateScanReport(translations, analysis);

    // ÊòæÁ§∫ÁªìÊûú
    displayResults(analysis);

    // Âà§Êñ≠ÊòØÂê¶ÈÄöËøá
    const hasErrors = scanResults.errors.length > 0;
    const hasMissingKeys = analysis.missingKeys.length > 0;

    if (!hasErrors && !hasMissingKeys) {
      console.log('‚úÖ ÁøªËØëÈîÆÊâ´ÊèèÈÄöËøáÔºÅÊâÄÊúâÁøªËØëÈîÆÈÉΩÂ∑≤Ê≠£Á°ÆÂÆö‰πâ„ÄÇ\n');
      process.exit(0);
    } else if (!hasErrors && hasMissingKeys) {
      console.log('‚ö†Ô∏è  ÁøªËØëÈîÆÊâ´ÊèèÂÆåÊàêÔºå‰ΩÜÂèëÁé∞Áº∫Â§±ÁöÑÁøªËØëÈîÆ„ÄÇ\n');
      process.exit(1);
    } else {
      console.log('‚ùå ÁøªËØëÈîÆÊâ´ÊèèÂ§±Ë¥•ÔºÅÂ≠òÂú®Ëß£ÊûêÈîôËØØ„ÄÇ\n');
      process.exit(1);
    }
  } catch (error) {
    console.error('üí• ÁøªËØëÈîÆÊâ´ÊèèÂ§±Ë¥•:', error.message);
    process.exit(1);
  }
}

