#!/usr/bin/env node

/**
 * Â¢ûÂº∫ÁöÑÁøªËØëÂêåÊ≠•ÂíåÊõ¥Êñ∞Â∑•ÂÖ∑
 * Ëá™Âä®ÂêåÊ≠•ÁøªËØëÊñá‰ª∂ÔºåÊîØÊåÅÂ¢ûÈáèÊõ¥Êñ∞ÂíåÊô∫ËÉΩÂêàÂπ∂
 */

const fs = require('fs');
const path = require('path');

console.log('üîÑ ÂºÄÂßãÁøªËØëÂêåÊ≠•ÂíåÊõ¥Êñ∞...\n');

// ÈÖçÁΩÆ
const CONFIG = {
  LOCALES: ['en', 'zh'],
  MESSAGES_DIR: path.join(process.cwd(), 'messages'),
  BACKUP_DIR: path.join(process.cwd(), 'backups', 'translations'),
  OUTPUT_DIR: path.join(process.cwd(), 'reports'),

  // ÂêåÊ≠•ÈÄâÈ°π
  SYNC_OPTIONS: {
    createMissingKeys: true,
    removeUnusedKeys: false, // Ë∞®ÊÖéÂà†Èô§ÔºåÈªòËÆ§‰∏çÂà†Èô§
    backupBeforeSync: true,
    validateAfterSync: true,
  },
};

const syncResults = {
  processed: 0,
  created: 0,
  updated: 0,
  removed: 0,
  errors: [],
  warnings: [],
  backups: [],
};

/**
 * ÂàõÂª∫Â§á‰ªΩ
 */
function createBackup() {
  if (!CONFIG.SYNC_OPTIONS.backupBeforeSync) return;

  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const backupDir = path.join(CONFIG.BACKUP_DIR, timestamp);

  if (!fs.existsSync(backupDir)) {
    fs.mkdirSync(backupDir, { recursive: true });
  }

  for (const locale of CONFIG.LOCALES) {
    const sourceFile = path.join(CONFIG.MESSAGES_DIR, `${locale}.json`);
    const backupFile = path.join(backupDir, `${locale}.json`);

    if (fs.existsSync(sourceFile)) {
      fs.copyFileSync(sourceFile, backupFile);
      syncResults.backups.push(backupFile);
      console.log(`üíæ Â§á‰ªΩÂàõÂª∫: ${locale}.json -> ${backupFile}`);
    }
  }

  console.log(`üì¶ Â§á‰ªΩÁõÆÂΩï: ${backupDir}\n`);
}

/**
 * Âä†ËΩΩÁøªËØëÊñá‰ª∂
 */
function loadTranslations() {
  const translations = {};

  for (const locale of CONFIG.LOCALES) {
    const filePath = path.join(CONFIG.MESSAGES_DIR, `${locale}.json`);
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      translations[locale] = JSON.parse(content);
      console.log(`üìñ Âä†ËΩΩÁøªËØëÊñá‰ª∂: ${locale}.json`);
    } catch (error) {
      console.warn(`‚ö†Ô∏è  Êó†Ê≥ïÂä†ËΩΩÁøªËØëÊñá‰ª∂: ${locale}.json - ${error.message}`);
      translations[locale] = {};
    }
  }

  return translations;
}

/**
 * Ëé∑ÂèñÊâÄÊúâÁøªËØëÈîÆÁöÑÂπ∂ÈõÜ
 */
function getAllTranslationKeys(translations) {
  const allKeys = new Set();

  function extractKeys(obj, prefix = '') {
    for (const [key, value] of Object.entries(obj)) {
      const fullKey = prefix ? `${prefix}.${key}` : key;

      if (typeof value === 'object' && value !== null) {
        extractKeys(value, fullKey);
      } else {
        allKeys.add(fullKey);
      }
    }
  }

  for (const locale of CONFIG.LOCALES) {
    if (translations[locale]) {
      extractKeys(translations[locale]);
    }
  }

  return Array.from(allKeys).sort();
}

/**
 * Ëé∑ÂèñÂµåÂ•óÂØπË±°ÁöÑÂÄº
 */
function getNestedValue(obj, path) {
  return path.split('.').reduce((current, key) => {
    return current && current[key] !== undefined ? current[key] : undefined;
  }, obj);
}

/**
 * ËÆæÁΩÆÂµåÂ•óÂØπË±°ÁöÑÂÄº
 */
function setNestedValue(obj, path, value) {
  const keys = path.split('.');
  const lastKey = keys.pop();

  let current = obj;
  for (const key of keys) {
    if (!current[key] || typeof current[key] !== 'object') {
      current[key] = {};
    }
    current = current[key];
  }

  current[lastKey] = value;
}

/**
 * ÂêåÊ≠•ÁøªËØëÈîÆ
 */
function syncTranslationKeys(translations) {
  const allKeys = getAllTranslationKeys(translations);
  const syncedTranslations = JSON.parse(JSON.stringify(translations));

  console.log(`üîç ÂèëÁé∞ ${allKeys.length} ‰∏™ÂîØ‰∏ÄÁøªËØëÈîÆ\n`);

  for (const key of allKeys) {
    for (const locale of CONFIG.LOCALES) {
      const currentValue = getNestedValue(syncedTranslations[locale], key);

      if (currentValue === undefined) {
        if (CONFIG.SYNC_OPTIONS.createMissingKeys) {
          // Â∞ùËØï‰ªéÂÖ∂‰ªñËØ≠Ë®ÄÂ§çÂà∂ÊàñÂàõÂª∫Âç†‰ΩçÁ¨¶
          let fallbackValue = null;

          // È¶ñÂÖàÂ∞ùËØï‰ªéËã±ÊñáÂ§çÂà∂
          if (locale !== 'en') {
            fallbackValue = getNestedValue(syncedTranslations['en'], key);
          }

          // Â¶ÇÊûúÊ≤°ÊúâËã±ÊñáÔºåÂ∞ùËØï‰ªéÂÖ∂‰ªñËØ≠Ë®ÄÂ§çÂà∂
          if (!fallbackValue) {
            for (const otherLocale of CONFIG.LOCALES) {
              if (otherLocale !== locale) {
                const otherValue = getNestedValue(
                  syncedTranslations[otherLocale],
                  key,
                );
                if (otherValue && typeof otherValue === 'string') {
                  fallbackValue = otherValue;
                  break;
                }
              }
            }
          }

          // Â¶ÇÊûúËøòÊòØÊ≤°ÊúâÔºåÂàõÂª∫Âç†‰ΩçÁ¨¶
          if (!fallbackValue) {
            fallbackValue = `[TODO: ${key}]`;
          }

          setNestedValue(syncedTranslations[locale], key, fallbackValue);
          syncResults.created++;

          console.log(`‚ûï ÂàõÂª∫Áº∫Â§±ÈîÆ: ${locale}.${key} = "${fallbackValue}"`);
        }
      }
    }
  }

  return syncedTranslations;
}

/**
 * È™åËØÅÁøªËØëÂÆåÊï¥ÊÄß
 */
function validateTranslations(translations) {
  const issues = [];
  const allKeys = getAllTranslationKeys(translations);

  for (const key of allKeys) {
    const values = {};

    for (const locale of CONFIG.LOCALES) {
      values[locale] = getNestedValue(translations[locale], key);
    }

    // Ê£ÄÊü•Áº∫Â§±ÂÄº
    const missingLocales = CONFIG.LOCALES.filter(
      (locale) => values[locale] === undefined || values[locale] === '',
    );

    if (missingLocales.length > 0) {
      issues.push({
        type: 'missing_translation',
        key,
        locales: missingLocales,
      });
    }

    // Ê£ÄÊü•ÂèØÁñëÁöÑÊú™ÁøªËØëÂÜÖÂÆπ
    const stringValues = Object.entries(values)
      .filter(([_, value]) => typeof value === 'string' && value.trim() !== '')
      .map(([locale, value]) => ({ locale, value }));

    if (stringValues.length > 1) {
      const firstValue = stringValues[0].value;
      const sameValues = stringValues.filter(
        ({ value }) => value === firstValue,
      );

      if (
        sameValues.length === stringValues.length &&
        !key.includes('url') &&
        firstValue.length > 3 &&
        !firstValue.startsWith('[TODO:')
      ) {
        issues.push({
          type: 'suspicious_translation',
          key,
          value: firstValue,
        });
      }
    }
  }

  return issues;
}

/**
 * ‰øùÂ≠òÁøªËØëÊñá‰ª∂
 */
function saveTranslations(translations) {
  for (const locale of CONFIG.LOCALES) {
    const filePath = path.join(CONFIG.MESSAGES_DIR, `${locale}.json`);

    try {
      const content = `${JSON.stringify(translations[locale], null, 2)  }\n`;
      fs.writeFileSync(filePath, content, 'utf8');
      syncResults.updated++;
      console.log(`üíæ ‰øùÂ≠òÁøªËØëÊñá‰ª∂: ${locale}.json`);
    } catch (error) {
      syncResults.errors.push({
        type: 'save_error',
        locale,
        error: error.message,
      });
      console.error(`‚ùå ‰øùÂ≠òÂ§±Ë¥•: ${locale}.json - ${error.message}`);
    }
  }
}

/**
 * ÁîüÊàêÂêåÊ≠•Êä•Âëä
 */
function generateSyncReport(validationIssues) {
  const report = {
    timestamp: new Date().toISOString(),
    summary: {
      processed: syncResults.processed,
      created: syncResults.created,
      updated: syncResults.updated,
      removed: syncResults.removed,
      errorCount: syncResults.errors.length,
      warningCount: syncResults.warnings.length,
      backupCount: syncResults.backups.length,
    },
    validationIssues,
    errors: syncResults.errors,
    warnings: syncResults.warnings,
    backups: syncResults.backups,
  };

  // Á°Æ‰øùËæìÂá∫ÁõÆÂΩïÂ≠òÂú®
  if (!fs.existsSync(CONFIG.OUTPUT_DIR)) {
    fs.mkdirSync(CONFIG.OUTPUT_DIR, { recursive: true });
  }

  const reportPath = path.join(
    CONFIG.OUTPUT_DIR,
    'translation-sync-report.json',
  );
  fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

  console.log(`üìä ÂêåÊ≠•Êä•ÂëäÂ∑≤ÁîüÊàê: ${reportPath}`);
  return report;
}

/**
 * ÊòæÁ§∫ÂêåÊ≠•ÁªìÊûú
 */
function displayResults(validationIssues) {
  console.log('\nüìä ÂêåÊ≠•ÁªüËÆ°:\n');
  console.log(`   Â§ÑÁêÜÊñá‰ª∂: ${syncResults.processed}`);
  console.log(`   ÂàõÂª∫ÈîÆ: ${syncResults.created}`);
  console.log(`   Êõ¥Êñ∞Êñá‰ª∂: ${syncResults.updated}`);
  console.log(`   Âà†Èô§ÈîÆ: ${syncResults.removed}`);
  console.log(`   ÈîôËØØ: ${syncResults.errors.length}`);
  console.log(`   Ë≠¶Âëä: ${syncResults.warnings.length}`);
  console.log(`   Â§á‰ªΩ: ${syncResults.backups.length}\n`);

  // ÊòæÁ§∫È™åËØÅÈóÆÈ¢ò
  if (validationIssues.length > 0) {
    console.log('‚ö†Ô∏è  ÂèëÁé∞ÁöÑÈóÆÈ¢ò:');

    const missingTranslations = validationIssues.filter(
      (issue) => issue.type === 'missing_translation',
    );

    const suspiciousTranslations = validationIssues.filter(
      (issue) => issue.type === 'suspicious_translation',
    );

    if (missingTranslations.length > 0) {
      console.log(`   Áº∫Â§±ÁøªËØë: ${missingTranslations.length} ‰∏™ÈîÆ`);
    }

    if (suspiciousTranslations.length > 0) {
      console.log(`   ÂèØÁñëÁøªËØë: ${suspiciousTranslations.length} ‰∏™ÈîÆ`);
    }

    console.log();
  }
}

/**
 * ‰∏ªÂáΩÊï∞
 */
async function main() {
  try {
    // ÂàõÂª∫Â§á‰ªΩ
    createBackup();

    // Âä†ËΩΩÁøªËØëÊñá‰ª∂
    const translations = loadTranslations();
    syncResults.processed = CONFIG.LOCALES.length;

    // ÂêåÊ≠•ÁøªËØëÈîÆ
    const syncedTranslations = syncTranslationKeys(translations);

    // ‰øùÂ≠òÁøªËØëÊñá‰ª∂
    saveTranslations(syncedTranslations);

    // È™åËØÅÁøªËØëÂÆåÊï¥ÊÄß
    let validationIssues = [];
    if (CONFIG.SYNC_OPTIONS.validateAfterSync) {
      console.log('\nüîç È™åËØÅÁøªËØëÂÆåÊï¥ÊÄß...');
      validationIssues = validateTranslations(syncedTranslations);
    }

    // ÁîüÊàêÊä•Âëä
    generateSyncReport(validationIssues);

    // ÊòæÁ§∫ÁªìÊûú
    displayResults(validationIssues);

    // Âà§Êñ≠ÊòØÂê¶ÈÄöËøá
    const hasErrors = syncResults.errors.length > 0;
    const hasCriticalIssues = validationIssues.some(
      (issue) => issue.type === 'missing_translation',
    );

    if (!hasErrors && !hasCriticalIssues) {
      console.log('‚úÖ ÁøªËØëÂêåÊ≠•ÂÆåÊàêÔºÅÊâÄÊúâÁøªËØëÊñá‰ª∂Â∑≤ÊàêÂäüÂêåÊ≠•„ÄÇ\n');
      process.exit(0);
    } else if (!hasErrors && hasCriticalIssues) {
      console.log('‚ö†Ô∏è  ÁøªËØëÂêåÊ≠•ÂÆåÊàêÔºå‰ΩÜÂ≠òÂú®ÈúÄË¶ÅÊ≥®ÊÑèÁöÑÈóÆÈ¢ò„ÄÇ\n');
      process.exit(0);
    } else {
      console.log('‚ùå ÁøªËØëÂêåÊ≠•Â§±Ë¥•ÔºÅÂ≠òÂú®ÈúÄË¶Å‰øÆÂ§çÁöÑÈîôËØØ„ÄÇ\n');
      process.exit(1);
    }
  } catch (error) {
    console.error('üí• ÁøªËØëÂêåÊ≠•Â§±Ë¥•:', error.message);
    process.exit(1);
  }
}

// ËøêË°å‰∏ªÂáΩÊï∞
main();

#!/usr/bin/env node

/**
 * ÁøªËØëÂÆåÊï¥ÊÄßÊ£ÄÊü•Â∑•ÂÖ∑
 * È™åËØÅÁøªËØëÊñá‰ª∂ÁöÑÂÆåÊï¥ÊÄß„ÄÅ‰∏ÄËá¥ÊÄßÂíåË¥®Èáè
 */

const fs = require('fs');
const path = require('path');

console.log('üîç ÂºÄÂßãÁøªËØëÂÆåÊï¥ÊÄßÊ£ÄÊü•...\n');

// ÈÖçÁΩÆ
const CONFIG = {
  LOCALES: ['en', 'zh'],
  MESSAGES_DIR: path.join(process.cwd(), 'messages'),
  OUTPUT_DIR: path.join(process.cwd(), 'reports'),

  // È™åËØÅËßÑÂàô
  VALIDATION_RULES: {
    checkMissingKeys: true,
    checkEmptyValues: true,
    checkTypeConsistency: true,
    checkSuspiciousTranslations: true,
    checkPlaceholderConsistency: true,
    checkTranslationLength: true,
    checkSpecialCharacters: true,
  },

  // Ë¥®ÈáèÈòàÂÄº
  QUALITY_THRESHOLDS: {
    minTranslationCoverage: 95, // ÊúÄÂ∞èÁøªËØëË¶ÜÁõñÁéá (%)
    maxLengthRatio: 6.0, // ÁøªËØëÈïøÂ∫¶ÊØî‰æã‰∏äÈôê (‰∏≠Ëã±ÊñáÂ∑ÆÂºÇËæÉÂ§ßÊòØÊ≠£Â∏∏ÁöÑ)
    minLengthRatio: 0.2, // ÁøªËØëÈïøÂ∫¶ÊØî‰æã‰∏ãÈôê
  },

  // ÁôΩÂêçÂçïÈÖçÁΩÆ
  WHITELIST: {
    // ÂìÅÁâåÂêçÁß∞Âíå‰∏ìÊúâÂêçËØç (ÂÖÅËÆ∏Áõ∏Âêå)
    brandTerms: [
      'Tucsenberg',
      'Next.js',
      'React',
      'TypeScript',
      'GitHub',
      'LinkedIn',
      'Twitter',
      'API',
      'URL',
      'SEO',
      'UI',
      'UX',
      'CSS',
      'HTML',
      'JavaScript',
    ],

    // URLÂíåÈìæÊé• (ÂÖÅËÆ∏Áõ∏Âêå)
    urlPatterns: [
      /^https?:\/\//,
      /^mailto:/,
      /^tel:/,
      /\.com$/,
      /\.org$/,
      /\.net$/,
    ],

    // ÂÖÅËÆ∏Áõ∏ÂêåÂÄºÁöÑÈîÆÊ®°Âºè
    allowSameValueKeys: [
      /\.url$/,
      /\.link$/,
      /\.email$/,
      /\.phone$/,
      /social\./,
      /structured-data\./,
      /schema\./,
    ],
  },
};

const validationResults = {
  totalKeys: 0,
  validatedKeys: 0,
  issues: [],
  warnings: [],
  statistics: {
    coverage: {},
    quality: {},
  },
};

/**
 * Âä†ËΩΩÁøªËØëÊñá‰ª∂
 */
function loadTranslations() {
  const translations = {};

  for (const locale of CONFIG.LOCALES) {
    const filePath = path.join(CONFIG.MESSAGES_DIR, `${locale}.json`);
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      translations[locale] = JSON.parse(content);
      console.log(`üìñ Âä†ËΩΩÁøªËØëÊñá‰ª∂: ${locale}.json`);
    } catch (error) {
      console.error(`‚ùå Êó†Ê≥ïÂä†ËΩΩÁøªËØëÊñá‰ª∂: ${locale}.json - ${error.message}`);
      process.exit(1);
    }
  }

  return translations;
}

/**
 * Ëé∑ÂèñÊâÄÊúâÁøªËØëÈîÆ
 */
function getAllTranslationKeys(translations) {
  const allKeys = new Set();

  function extractKeys(obj, prefix = '') {
    for (const [key, value] of Object.entries(obj)) {
      const fullKey = prefix ? `${prefix}.${key}` : key;

      if (typeof value === 'object' && value !== null) {
        extractKeys(value, fullKey);
      } else {
        allKeys.add(fullKey);
      }
    }
  }

  for (const locale of CONFIG.LOCALES) {
    if (translations[locale]) {
      extractKeys(translations[locale]);
    }
  }

  return Array.from(allKeys).sort();
}

/**
 * Ëé∑ÂèñÂµåÂ•óÂØπË±°ÁöÑÂÄº
 */
function getNestedValue(obj, path) {
  return path.split('.').reduce((current, key) => {
    return current && current[key] !== undefined ? current[key] : undefined;
  }, obj);
}

/**
 * È™åËØÅÁº∫Â§±ÈîÆ
 */
function validateMissingKeys(translations, allKeys) {
  const issues = [];

  for (const key of allKeys) {
    for (const locale of CONFIG.LOCALES) {
      const value = getNestedValue(translations[locale], key);

      if (value === undefined) {
        issues.push({
          type: 'missing_key',
          severity: 'error',
          key,
          locale,
          message: `ÁøªËØëÈîÆ "${key}" Âú® ${locale} ‰∏≠Áº∫Â§±`,
        });
      }
    }
  }

  return issues;
}

/**
 * È™åËØÅÁ©∫ÂÄº
 */
function validateEmptyValues(translations, allKeys) {
  const issues = [];

  for (const key of allKeys) {
    for (const locale of CONFIG.LOCALES) {
      const value = getNestedValue(translations[locale], key);

      if (typeof value === 'string' && value.trim() === '') {
        issues.push({
          type: 'empty_value',
          severity: 'warning',
          key,
          locale,
          message: `ÁøªËØëÈîÆ "${key}" Âú® ${locale} ‰∏≠‰∏∫Á©∫ÂÄº`,
        });
      }
    }
  }

  return issues;
}

/**
 * È™åËØÅÁ±ªÂûã‰∏ÄËá¥ÊÄß
 */
function validateTypeConsistency(translations, allKeys) {
  const issues = [];

  for (const key of allKeys) {
    const types = {};

    for (const locale of CONFIG.LOCALES) {
      const value = getNestedValue(translations[locale], key);
      types[locale] = typeof value;
    }

    const uniqueTypes = new Set(
      Object.values(types).filter((t) => t !== 'undefined'),
    );

    if (uniqueTypes.size > 1) {
      issues.push({
        type: 'type_inconsistency',
        severity: 'error',
        key,
        types,
        message: `ÁøªËØëÈîÆ "${key}" Âú®‰∏çÂêåËØ≠Ë®Ä‰∏≠Á±ªÂûã‰∏ç‰∏ÄËá¥: ${JSON.stringify(types)}`,
      });
    }
  }

  return issues;
}

/**
 * Ê£ÄÊü•ÂÄºÊòØÂê¶Âú®ÁôΩÂêçÂçï‰∏≠
 */
function isWhitelistedValue(key, value) {
  // Ê£ÄÊü•ÂìÅÁâåÊúØËØ≠
  if (CONFIG.WHITELIST.brandTerms.some((term) => value.includes(term))) {
    return true;
  }

  // Ê£ÄÊü•URLÊ®°Âºè
  if (CONFIG.WHITELIST.urlPatterns.some((pattern) => pattern.test(value))) {
    return true;
  }

  // Ê£ÄÊü•ÈîÆÊ®°Âºè
  if (
    CONFIG.WHITELIST.allowSameValueKeys.some((pattern) => pattern.test(key))
  ) {
    return true;
  }

  return false;
}

/**
 * È™åËØÅÂèØÁñëÁøªËØë
 */
function validateSuspiciousTranslations(translations, allKeys) {
  const issues = [];

  for (const key of allKeys) {
    const values = {};

    for (const locale of CONFIG.LOCALES) {
      const value = getNestedValue(translations[locale], key);
      if (typeof value === 'string' && value.trim() !== '') {
        values[locale] = value;
      }
    }

    const stringValues = Object.values(values);

    // Ê£ÄÊü•ÊòØÂê¶ÊâÄÊúâÂÄºÈÉΩÁõ∏Âêå
    if (stringValues.length > 1) {
      const firstValue = stringValues[0];
      const allSame = stringValues.every((value) => value === firstValue);

      if (
        allSame &&
        !firstValue.startsWith('[TODO:') &&
        firstValue.length > 3 &&
        !isWhitelistedValue(key, firstValue)
      ) {
        issues.push({
          type: 'suspicious_translation',
          severity: 'warning',
          key,
          value: firstValue,
          message: `ÁøªËØëÈîÆ "${key}" Âú®ÊâÄÊúâËØ≠Ë®Ä‰∏≠ÈÉΩÊòØÁõ∏ÂêåÁöÑÂÄºÔºåÂèØËÉΩÊú™ÁøªËØë`,
        });
      }
    }
  }

  return issues;
}

/**
 * È™åËØÅÂç†‰ΩçÁ¨¶‰∏ÄËá¥ÊÄß
 */
function validatePlaceholderConsistency(translations, allKeys) {
  const issues = [];

  for (const key of allKeys) {
    const placeholders = {};

    for (const locale of CONFIG.LOCALES) {
      const value = getNestedValue(translations[locale], key);

      if (typeof value === 'string') {
        // ÊèêÂèñÂç†‰ΩçÁ¨¶ {variable}
        const matches = value.match(/\{[^}]+\}/g) || [];
        placeholders[locale] = matches.sort();
      }
    }

    // Ê£ÄÊü•Âç†‰ΩçÁ¨¶ÊòØÂê¶‰∏ÄËá¥
    const localesWithPlaceholders = Object.keys(placeholders).filter(
      (locale) => placeholders[locale].length > 0,
    );

    if (localesWithPlaceholders.length > 1) {
      const firstPlaceholders = placeholders[localesWithPlaceholders[0]];

      for (let i = 1; i < localesWithPlaceholders.length; i++) {
        const currentPlaceholders = placeholders[localesWithPlaceholders[i]];

        if (
          JSON.stringify(firstPlaceholders) !==
          JSON.stringify(currentPlaceholders)
        ) {
          issues.push({
            type: 'placeholder_inconsistency',
            severity: 'error',
            key,
            placeholders,
            message: `ÁøªËØëÈîÆ "${key}" ÁöÑÂç†‰ΩçÁ¨¶Âú®‰∏çÂêåËØ≠Ë®Ä‰∏≠‰∏ç‰∏ÄËá¥`,
          });
          break;
        }
      }
    }
  }

  return issues;
}

/**
 * Ê£ÄÊü•ÊòØÂê¶Â∫îËØ•Ë∑≥ËøáÈïøÂ∫¶Ê£ÄÊü•
 */
function shouldSkipLengthCheck(key, lengths) {
  // Ë∑≥ËøáÂæàÁü≠ÁöÑÊñáÊú¨ (ÂçïËØçÊàñÁü≠ËØ≠)
  const maxLength = Math.max(...Object.values(lengths));
  if (maxLength < 10) {
    return true;
  }

  // Ë∑≥ËøáÁâπÂÆöÁ±ªÂûãÁöÑÈîÆ
  const skipPatterns = [
    /\.title$/,
    /\.name$/,
    /\.label$/,
    /common\./,
    /accessibility\./,
  ];

  return skipPatterns.some((pattern) => pattern.test(key));
}

/**
 * È™åËØÅÁøªËØëÈïøÂ∫¶
 */
function validateTranslationLength(translations, allKeys) {
  const issues = [];

  for (const key of allKeys) {
    const lengths = {};

    for (const locale of CONFIG.LOCALES) {
      const value = getNestedValue(translations[locale], key);

      if (typeof value === 'string') {
        lengths[locale] = value.length;
      }
    }

    const validLengths = Object.values(lengths).filter((len) => len > 0);

    if (validLengths.length > 1) {
      const maxLength = Math.max(...validLengths);
      const minLength = Math.min(...validLengths);
      const ratio = maxLength / minLength;

      // Ë∑≥ËøáÊüê‰∫õÁ±ªÂûãÁöÑÈïøÂ∫¶Ê£ÄÊü•
      if (shouldSkipLengthCheck(key, lengths)) {
        continue;
      }

      if (ratio > CONFIG.QUALITY_THRESHOLDS.maxLengthRatio) {
        issues.push({
          type: 'length_ratio_high',
          severity: 'warning',
          key,
          lengths,
          ratio: ratio.toFixed(2),
          message: `ÁøªËØëÈîÆ "${key}" ÁöÑÈïøÂ∫¶ÊØî‰æãËøáÈ´ò (${ratio.toFixed(2)})ÔºåÂèØËÉΩÂ≠òÂú®ÁøªËØëË¥®ÈáèÈóÆÈ¢ò`,
        });
      } else if (ratio < CONFIG.QUALITY_THRESHOLDS.minLengthRatio) {
        issues.push({
          type: 'length_ratio_low',
          severity: 'warning',
          key,
          lengths,
          ratio: ratio.toFixed(2),
          message: `ÁøªËØëÈîÆ "${key}" ÁöÑÈïøÂ∫¶ÊØî‰æãËøá‰Ωé (${ratio.toFixed(2)})ÔºåÂèØËÉΩÂ≠òÂú®ÁøªËØë‰∏çÂÆåÊï¥`,
        });
      }
    }
  }

  return issues;
}

/**
 * ËÆ°ÁÆóÁøªËØëË¶ÜÁõñÁéá
 */
function calculateCoverage(translations, allKeys) {
  const coverage = {};

  for (const locale of CONFIG.LOCALES) {
    let translatedKeys = 0;

    for (const key of allKeys) {
      const value = getNestedValue(translations[locale], key);

      if (
        value !== undefined &&
        typeof value === 'string' &&
        value.trim() !== '' &&
        !value.startsWith('[TODO:')
      ) {
        translatedKeys++;
      }
    }

    coverage[locale] = {
      total: allKeys.length,
      translated: translatedKeys,
      percentage: ((translatedKeys / allKeys.length) * 100).toFixed(2),
    };
  }

  return coverage;
}

/**
 * ÁîüÊàêÈ™åËØÅÊä•Âëä
 */
function generateValidationReport() {
  const report = {
    timestamp: new Date().toISOString(),
    summary: {
      totalKeys: validationResults.totalKeys,
      validatedKeys: validationResults.validatedKeys,
      issueCount: validationResults.issues.length,
      warningCount: validationResults.warnings.length,
      errorCount: validationResults.issues.filter(
        (issue) => issue.severity === 'error',
      ).length,
    },
    statistics: validationResults.statistics,
    issues: validationResults.issues,
    warnings: validationResults.warnings,
  };

  // Á°Æ‰øùËæìÂá∫ÁõÆÂΩïÂ≠òÂú®
  if (!fs.existsSync(CONFIG.OUTPUT_DIR)) {
    fs.mkdirSync(CONFIG.OUTPUT_DIR, { recursive: true });
  }

  const reportPath = path.join(
    CONFIG.OUTPUT_DIR,
    'translation-validation-report.json',
  );
  fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

  console.log(`üìä È™åËØÅÊä•ÂëäÂ∑≤ÁîüÊàê: ${reportPath}`);
  return report;
}

/**
 * ÊòæÁ§∫È™åËØÅÁªìÊûú
 */
function displayResults() {
  console.log('\nüìä È™åËØÅÁªüËÆ°:\n');
  console.log(`   ÊÄªÈîÆÊï∞: ${validationResults.totalKeys}`);
  console.log(`   È™åËØÅÈîÆÊï∞: ${validationResults.validatedKeys}`);
  console.log(`   ÈóÆÈ¢òÊï∞: ${validationResults.issues.length}`);
  console.log(
    `   ÈîôËØØÊï∞: ${validationResults.issues.filter((i) => i.severity === 'error').length}`,
  );
  console.log(
    `   Ë≠¶ÂëäÊï∞: ${validationResults.issues.filter((i) => i.severity === 'warning').length}\n`,
  );

  // ÊòæÁ§∫Ë¶ÜÁõñÁéá
  console.log('üìà ÁøªËØëË¶ÜÁõñÁéá:');
  for (const [locale, coverage] of Object.entries(
    validationResults.statistics.coverage,
  )) {
    console.log(
      `   ${locale}: ${coverage.percentage}% (${coverage.translated}/${coverage.total})`,
    );
  }
  console.log();

  // ÊòæÁ§∫‰∏ªË¶ÅÈóÆÈ¢ò
  const errorIssues = validationResults.issues.filter(
    (issue) => issue.severity === 'error',
  );
  if (errorIssues.length > 0) {
    console.log('‚ùå ‰∏ªË¶ÅÈîôËØØ:');
    errorIssues.slice(0, 5).forEach((issue) => {
      console.log(`   - ${issue.message}`);
    });
    if (errorIssues.length > 5) {
      console.log(`   ... ËøòÊúâ ${errorIssues.length - 5} ‰∏™ÈîôËØØ`);
    }
    console.log();
  }

  const warningIssues = validationResults.issues.filter(
    (issue) => issue.severity === 'warning',
  );
  if (warningIssues.length > 0) {
    console.log('‚ö†Ô∏è  ‰∏ªË¶ÅË≠¶Âëä:');
    warningIssues.slice(0, 5).forEach((issue) => {
      console.log(`   - ${issue.message}`);
    });
    if (warningIssues.length > 5) {
      console.log(`   ... ËøòÊúâ ${warningIssues.length - 5} ‰∏™Ë≠¶Âëä`);
    }
    console.log();
  }
}

/**
 * ‰∏ªÂáΩÊï∞
 */
async function main() {
  try {
    // Âä†ËΩΩÁøªËØëÊñá‰ª∂
    const translations = loadTranslations();

    // Ëé∑ÂèñÊâÄÊúâÁøªËØëÈîÆ
    const allKeys = getAllTranslationKeys(translations);
    validationResults.totalKeys = allKeys.length;
    validationResults.validatedKeys = allKeys.length;

    console.log(`üîç ÂºÄÂßãÈ™åËØÅ ${allKeys.length} ‰∏™ÁøªËØëÈîÆ...\n`);

    // ÊâßË°åÂêÑÁßçÈ™åËØÅ
    const allIssues = [];

    if (CONFIG.VALIDATION_RULES.checkMissingKeys) {
      console.log('üîç Ê£ÄÊü•Áº∫Â§±ÈîÆ...');
      allIssues.push(...validateMissingKeys(translations, allKeys));
    }

    if (CONFIG.VALIDATION_RULES.checkEmptyValues) {
      console.log('üîç Ê£ÄÊü•Á©∫ÂÄº...');
      allIssues.push(...validateEmptyValues(translations, allKeys));
    }

    if (CONFIG.VALIDATION_RULES.checkTypeConsistency) {
      console.log('üîç Ê£ÄÊü•Á±ªÂûã‰∏ÄËá¥ÊÄß...');
      allIssues.push(...validateTypeConsistency(translations, allKeys));
    }

    if (CONFIG.VALIDATION_RULES.checkSuspiciousTranslations) {
      console.log('üîç Ê£ÄÊü•ÂèØÁñëÁøªËØë...');
      allIssues.push(...validateSuspiciousTranslations(translations, allKeys));
    }

    if (CONFIG.VALIDATION_RULES.checkPlaceholderConsistency) {
      console.log('üîç Ê£ÄÊü•Âç†‰ΩçÁ¨¶‰∏ÄËá¥ÊÄß...');
      allIssues.push(...validatePlaceholderConsistency(translations, allKeys));
    }

    if (CONFIG.VALIDATION_RULES.checkTranslationLength) {
      console.log('üîç Ê£ÄÊü•ÁøªËØëÈïøÂ∫¶...');
      allIssues.push(...validateTranslationLength(translations, allKeys));
    }

    // ËÆ°ÁÆóÁªüËÆ°‰ø°ÊÅØ
    validationResults.statistics.coverage = calculateCoverage(
      translations,
      allKeys,
    );
    validationResults.issues = allIssues;

    // ÁîüÊàêÊä•Âëä
    generateValidationReport();

    // ÊòæÁ§∫ÁªìÊûú
    displayResults();

    // Âà§Êñ≠ÊòØÂê¶ÈÄöËøá
    const errorCount = validationResults.issues.filter(
      (issue) => issue.severity === 'error',
    ).length;
    const minCoverage = Math.min(
      ...Object.values(validationResults.statistics.coverage).map((c) =>
        parseFloat(c.percentage),
      ),
    );

    if (
      errorCount === 0 &&
      minCoverage >= CONFIG.QUALITY_THRESHOLDS.minTranslationCoverage
    ) {
      console.log('‚úÖ ÁøªËØëÂÆåÊï¥ÊÄßÊ£ÄÊü•ÈÄöËøáÔºÅÊâÄÊúâÁøªËØëÊñá‰ª∂Ë¥®ÈáèËâØÂ•Ω„ÄÇ\n');
      process.exit(0);
    } else if (errorCount === 0) {
      console.log('‚ö†Ô∏è  ÁøªËØëÂÆåÊï¥ÊÄßÊ£ÄÊü•Âü∫Êú¨ÈÄöËøáÔºå‰ΩÜË¶ÜÁõñÁéáÈúÄË¶ÅÊèêÂçá„ÄÇ\n');
      process.exit(0);
    } else {
      console.log('‚ùå ÁøªËØëÂÆåÊï¥ÊÄßÊ£ÄÊü•Â§±Ë¥•ÔºÅÂ≠òÂú®ÈúÄË¶Å‰øÆÂ§çÁöÑÈîôËØØ„ÄÇ\n');
      process.exit(1);
    }
  } catch (error) {
    console.error('üí• ÁøªËØëÂÆåÊï¥ÊÄßÊ£ÄÊü•Â§±Ë¥•:', error.message);
    process.exit(1);
  }
}

// ËøêË°å‰∏ªÂáΩÊï∞
main();

#!/usr/bin/env node
/*
 * Â¢ûÈáèTypeScriptÁ±ªÂûãÊ£ÄÊü•Ôºà‰ªÖÈíàÂØπÊöÇÂ≠òÊñá‰ª∂Ôºâ
 * - ËØªÂèñ Git ÊöÇÂ≠òÂå∫ÁöÑ TypeScript Êñá‰ª∂ÂàóË°®
 * - ‰ªÖÂØπËøô‰∫õÊñá‰ª∂ËøêË°å tsc --noEmit
 * - ‰ºòÂåñpre-commitÊÄßËÉΩÔºåÈÅøÂÖçÂÖ®ÈáèÁ±ªÂûãÊ£ÄÊü•
 */

const { execSync, spawnSync } = require('child_process');
const path = require('path');

function getStagedTSFiles() {
  try {
    const output = execSync(
      'git diff --name-only --cached --diff-filter=ACMR',
      { stdio: ['ignore', 'pipe', 'ignore'] },
    )
      .toString()
      .trim();
    if (!output) return [];
    return output.split('\n').filter((f) => /\.(ts|tsx)$/i.test(f));
  } catch {
    return [];
  }
}

function runTypeCheck(files) {
  if (files.length === 0) {
    console.log('[type-check:staged] Êó†ÈúÄÊ£ÄÊü•ÔºöÊ≤°ÊúâÊöÇÂ≠òÁöÑ TypeScript Êñá‰ª∂');
    return 0;
  }

  console.log(`[type-check:staged] Ê£ÄÊü• ${files.length} ‰∏™ TypeScript Êñá‰ª∂...`);

  // ‰ΩøÁî® tsc --noEmit ÂØπÊåáÂÆöÊñá‰ª∂ËøõË°åÁ±ªÂûãÊ£ÄÊü•
  // Ê≥®ÊÑèÔºötsc ‰∏çÊîØÊåÅÁõ¥Êé•ÊåáÂÆöÊñá‰ª∂ÂàóË°®ËøõË°å --noEmit Ê£ÄÊü•
  // ÊâÄ‰ª•Êàë‰ª¨‰ΩøÁî®È°πÁõÆÈÖçÁΩÆÔºå‰ΩÜÂè™Âú®ÊúâTSÊñá‰ª∂ÂèòÊõ¥Êó∂ËøêË°å
  const result = spawnSync('npx', ['tsc', '--noEmit', '--skipLibCheck'], {
    stdio: 'inherit',
    cwd: process.cwd(),
  });

  if (result.status !== 0) {
    console.error('[type-check:staged] TypeScript Á±ªÂûãÊ£ÄÊü•Â§±Ë¥•');
    console.error('ËØ∑‰øÆÂ§çÁ±ªÂûãÈîôËØØÂêéÈáçÊñ∞Êèê‰∫§');
    return result.status;
  }

  console.log('[type-check:staged] ‚úÖ TypeScript Á±ªÂûãÊ£ÄÊü•ÈÄöËøá');
  return 0;
}

function main() {
  const stagedFiles = getStagedTSFiles();
  const exitCode = runTypeCheck(stagedFiles);
  process.exit(exitCode);
}

if (require.main === module) {
  main();
}

module.exports = { getStagedTSFiles, runTypeCheck };

#!/usr/bin/env tsx

/**
 * È¢úËâ≤Á≥ªÁªüÈ™åËØÅËÑöÊú¨
 * Ê£ÄÊü•ÊâÄÊúâÈ¢úËâ≤ÁªÑÂêàÁöÑÂØπÊØîÂ∫¶ÂêàËßÑÊÄß
 */
import {
  ColorSystem,
  darkThemeColors,
  lightThemeColors,
