// Â∑•ÂÖ∑ÂáΩÊï∞ÔºöÊ®°ÊãüÊÄßËÉΩÈóÆÈ¢òÔºà‰ºòÂåñÁâàÊú¨ - ÂáèÂ∞ë CLS ÂΩ±ÂìçÔºâ
export function simulatePerformanceIssues(): void {
  // Ê®°ÊãüÂ∏ÉÂ±ÄÂÅèÁßª - ‰ΩøÁî®Êõ¥Ê∏©ÂíåÁöÑÊñπÂºèÔºåÈÅøÂÖç‰∏•ÈáçÁöÑ CLS
  const div = document.createElement('div');
  div.style.height = '50px'; // ÂáèÂ∞ëÂàùÂßãÈ´òÂ∫¶
  div.style.backgroundColor = 'rgba(255, 0, 0, 0.3)'; // ÂçäÈÄèÊòéÔºåÂáèÂ∞ëËßÜËßâÂÜ≤Âáª
  div.style.position = 'fixed'; // ‰ΩøÁî® fixed ÂÆö‰ΩçÔºåÂáèÂ∞ëÂØπÈ°µÈù¢Â∏ÉÂ±ÄÁöÑÂΩ±Âìç
  div.style.top = '10px';
  div.style.right = '10px'; // ÁßªÂà∞Âè≥‰∏äËßíÔºåÈÅøÂÖçÂΩ±Âìç‰∏ªË¶ÅÂÜÖÂÆπ
  div.style.width = '100px';
  div.style.zIndex = '9999';
  div.style.borderRadius = '4px';
  div.style.transition = 'height 0.3s ease'; // Ê∑ªÂä†ËøáÊ∏°Âä®ÁîªÔºåÂáèÂ∞ëÁ™ÅÂÖÄÊÑü
  document.body.appendChild(div);

  setTimeout(() => {
    div.style.height = '80px'; // ÂáèÂ∞ëÈ´òÂ∫¶ÂèòÂåñÂπÖÂ∫¶
    setTimeout(() => {
      div.style.opacity = '0'; // Ê∑°Âá∫ÊïàÊûú
      setTimeout(() => {
        if (document.body.contains(div)) {
          document.body.removeChild(div);
        }
      }, DEV_TOOLS_CONSTANTS.DIAGNOSTICS.DIAGNOSTIC_DELAY); // Á≠âÂæÖÊ∑°Âá∫Âä®ÁîªÂÆåÊàê
    }, PERFORMANCE_CONSTANTS.SIMULATION_DELAY);
  }, PERFORMANCE_CONSTANTS.BLOCKING_DURATION);

  // Ê®°Êãü‰∏ªÁ∫øÁ®ãÈòªÂ°û - ÂáèÂ∞ëÈòªÂ°ûÊó∂Èó¥
  const start = performance.now();
  const reducedBlockingTime = Math.min(
    PERFORMANCE_CONSTANTS.BLOCKING_DURATION,
    DEV_TOOLS_CONSTANTS.LAYOUT.DEFAULT_OFFSET,
  ); // ÊúÄÂ§öÈòªÂ°û50ms
  while (performance.now() - start < reducedBlockingTime) {
    // ÈòªÂ°û‰∏ªÁ∫øÁ®ã
  }
}

