  cls: number;
  /** ÊúÄÂ§ßÂÜÖÂÆπÁªòÂà∂ (Largest Contentful Paint) */
  lcp: number;
  /** È¶ñÊ¨°ËæìÂÖ•Âª∂Ëøü (First Input Delay) */
  fid: number;
  /** È¶ñÊ¨°ÂÜÖÂÆπÁªòÂà∂ (First Contentful Paint) */
  fcp: number;
  /** È¶ñÂ≠óËäÇÊó∂Èó¥ (Time to First Byte) */
  ttfb: number;
  /** ÁªºÂêàËØÑÂàÜ */
  score: number;
