/**
 * React Scan Áä∂ÊÄÅÊòæÁ§∫ÁªÑ‰ª∂
 */
export function ReactScanStats() {
  const [stats, setStats] = useState<ReactScanStats | null>(null);

  const updateStats = useCallback(() => {
    if (typeof window !== 'undefined') {
      try {
        // Ê£ÄÊü•Â§öÁßçÂèØËÉΩÁöÑ React Scan Áä∂ÊÄÅ
        const reactScan = (window as ReactScanWindow).__REACT_SCAN__;
        const reactScanInternals = reactScan?.ReactScanInternals;

        // Â∞ùËØï‰ªéÂ§ö‰∏™‰ΩçÁΩÆËé∑ÂèñÁä∂ÊÄÅ
        let isEnabled = false;
        let totalRenders = 0;
        let componentsTracked = 0;

        if (reactScanInternals) {
          isEnabled = reactScanInternals.enabled ?? false;
          totalRenders = reactScanInternals.totalRenders ?? 0;

          // Ëé∑ÂèñË∑üË∏™ÁªÑ‰ª∂Êï∞
          const fiberRoots = reactScanInternals?.fiberRoots || {};
          componentsTracked = Object.keys(fiberRoots).length;
        }

        setStats({
          enabled: isEnabled,
          totalRenders,
          componentsTracked,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      } catch (error) {
        console.warn('Êó†Ê≥ïËé∑Âèñ React Scan Áä∂ÊÄÅ:', error);
        setStats({
          enabled: false,
          totalRenders: 0,
          componentsTracked: 0,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      }
    }
  }, []);

  useEffect(() => {
    updateStats();

    // Êõ¥È¢ëÁπÅÁöÑËΩÆËØ¢‰ª•Âø´ÈÄüÂìçÂ∫îÁä∂ÊÄÅÂèòÂåñ
    const interval = setInterval(
      updateStats,
      REACT_SCAN_CONFIG.STATS_UPDATE_INTERVAL,
    );

    // ÁõëÂê¨ÈîÆÁõò‰∫ã‰ª∂‰ª•Á´ãÂç≥Êõ¥Êñ∞Áä∂ÊÄÅ
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.ctrlKey && event.shiftKey && event.key.toLowerCase() === 'x') {
        // Âª∂Ëøü‰∏ÄÁÇπÊõ¥Êñ∞‰ª•Á°Æ‰øù React Scan Áä∂ÊÄÅÂ∑≤ÁªèÊîπÂèò
        setTimeout(updateStats, 100);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(interval);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [updateStats]);

  if (!stats) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>React Scan Áä∂ÊÄÅ</CardTitle>
        </CardHeader>
        <CardContent>
          <p>Ê≠£Âú®Âä†ËΩΩÁä∂ÊÄÅ...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className='flex items-center gap-2'>
          React Scan Áä∂ÊÄÅ
          <Badge variant={stats.enabled ? 'default' : 'secondary'}>
            {stats.enabled ? 'Â∑≤ÂêØÁî®' : 'Â∑≤Á¶ÅÁî®'}
          </Badge>
        </CardTitle>
        <CardDescription>
          ÂÆûÊó∂ÊòæÁ§∫ React Scan ÁöÑËøêË°åÁä∂ÊÄÅÂíåÁªüËÆ°‰ø°ÊÅØ
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className='grid grid-cols-2 gap-4'>
          <div>
            <p className='text-sm font-medium'>ÊÄªÊ∏≤ÊüìÊ¨°Êï∞</p>
            <p className='text-2xl font-bold'>{stats.totalRenders}</p>
          </div>
          <div>
            <p className='text-sm font-medium'>Ë∑üË∏™ÁªÑ‰ª∂Êï∞</p>
            <p className='text-2xl font-bold'>{stats.componentsTracked}</p>
          </div>
