/**
 * React Scan ÊÄßËÉΩÊï∞ÊçÆÊòæÁ§∫ÁªÑ‰ª∂
 */
export function ReactScanStatsComponent() {
  const [stats, setStats] = useState<ReactScanStats | null>(null);

  const updateStats = useCallback(() => {
    if (typeof window !== 'undefined') {
      try {
        const reactScanWindow = window as ReactScanWindow;
        const reactScanData =
          reactScanWindow.__REACT_SCAN__?.ReactScanInternals;

        if (reactScanData) {
          setStats({
            enabled: reactScanData.enabled || false,
            totalRenders: reactScanData.totalRenders || 0,
            componentsTracked: reactScanData.componentsScanned || 0,
            lastUpdate: new Date().toLocaleTimeString(),
          });
        } else {
          setStats({
            enabled: false,
            totalRenders: 0,
            componentsTracked: 0,
            lastUpdate: new Date().toLocaleTimeString(),
          });
        }
      } catch (error) {
        console.warn('Failed to get React Scan stats:', error);
        setStats({
          enabled: false,
          totalRenders: 0,
          componentsTracked: 0,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      }
    }
  }, []);

  useEffect(() => {
    updateStats();
    const interval = setInterval(updateStats, 1000);
    return () => clearInterval(interval);
  }, [updateStats]);

  const toggleReactScan = useCallback(() => {
    if (typeof window !== 'undefined') {
      try {
        const reactScanWindow = window as ReactScanWindow;
        if (reactScanWindow.__REACT_SCAN__?.ReactScanInternals) {
          const currentState =
            reactScanWindow.__REACT_SCAN__.ReactScanInternals.enabled;
          reactScanWindow.__REACT_SCAN__.ReactScanInternals.enabled =
            !currentState;
          updateStats();
        }
      } catch (error) {
        console.warn('Failed to toggle React Scan:', error);
      }
    }
  }, [updateStats]);

  const resetStats = useCallback(() => {
    if (typeof window !== 'undefined') {
      try {
        const reactScanWindow = window as ReactScanWindow;
        if (reactScanWindow.__REACT_SCAN__?.ReactScanInternals) {
          reactScanWindow.__REACT_SCAN__.ReactScanInternals.totalRenders = 0;
          reactScanWindow.__REACT_SCAN__.ReactScanInternals.componentsScanned = 0;
          updateStats();
        }
      } catch (error) {
        console.warn('Failed to reset React Scan stats:', error);
      }
    }
  }, [updateStats]);

  if (!stats) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>React Scan ÁªüËÆ°</CardTitle>
          <CardDescription>Âä†ËΩΩ‰∏≠...</CardDescription>
        </CardHeader>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className='flex items-center justify-between'>
          React Scan ÁªüËÆ°
          <Badge variant={stats.enabled ? 'default' : 'secondary'}>
            {stats.enabled ? 'Â∑≤ÂêØÁî®' : 'Â∑≤Á¶ÅÁî®'}
          </Badge>
        </CardTitle>
        <CardDescription>ÂÆûÊó∂ÁõëÊéß React ÁªÑ‰ª∂Ê∏≤ÊüìÊÄßËÉΩ</CardDescription>
      </CardHeader>
      <CardContent>
        <div className='space-y-4'>
          <div className='grid gap-4 md:grid-cols-3'>
            <div className='rounded-lg border p-4 text-center'>
              <div className='text-2xl font-bold text-blue-600'>
                {stats.totalRenders}
              </div>
              <div className='text-muted-foreground text-sm'>ÊÄªÊ∏≤ÊüìÊ¨°Êï∞</div>
            </div>
            <div className='rounded-lg border p-4 text-center'>
              <div className='text-2xl font-bold text-green-600'>
                {stats.componentsTracked}
              </div>
              <div className='text-muted-foreground text-sm'>Ë∑üË∏™ÁªÑ‰ª∂Êï∞</div>
            </div>
            <div className='rounded-lg border p-4 text-center'>
              <div className='text-sm font-medium text-purple-600'>
                {stats.lastUpdate}
              </div>
              <div className='text-muted-foreground text-sm'>ÊúÄÂêéÊõ¥Êñ∞</div>
            </div>
          </div>

          <div className='flex gap-2'>
            <Button
              onClick={toggleReactScan}
              variant='outline'
              size='sm'
            >
              {stats.enabled ? 'Á¶ÅÁî®' : 'ÂêØÁî®'} React Scan
            </Button>
            <Button
              onClick={resetStats}
              variant='outline'
              size='sm'
            >
              ÈáçÁΩÆÁªüËÆ°
            </Button>
            <Button
              onClick={updateStats}
              variant='outline'
              size='sm'
            >
              Âà∑Êñ∞Êï∞ÊçÆ
            </Button>
          </div>

          <div className='bg-muted rounded-lg p-4'>
            <h4 className='mb-2 font-semibold'>ÈÖçÁΩÆ‰ø°ÊÅØ</h4>
            <div className='space-y-1 text-sm'>
              <div>ÂêØÁî®Áä∂ÊÄÅ: {REACT_SCAN_CONFIG.enabled ? 'ÊòØ' : 'Âê¶'}</div>
              <div>
                ÊòæÁ§∫Ë¶ÜÁõñÂ±Ç: {REACT_SCAN_CONFIG.showOverlay ? 'ÊòØ' : 'Âê¶'}
              </div>
              <div>
                Ë∑üË∏™Ê∏≤Êüì: {REACT_SCAN_CONFIG.trackRenders ? 'ÊòØ' : 'Âê¶'}
              </div>
              <div>
                ÊéßÂà∂Âè∞Êó•Âøó: {REACT_SCAN_CONFIG.logToConsole ? 'ÊòØ' : 'Âê¶'}
              </div>
            </div>
          </div>

          <div className='text-muted-foreground text-xs'>
            <p>
              üí° ÊèêÁ§∫: React Scan ‰ºöÈ´ò‰∫ÆÊòæÁ§∫‰∏çÂøÖË¶ÅÁöÑÈáçÊñ∞Ê∏≤Êüì„ÄÇ
              Á∫¢Ëâ≤Ë°®Á§∫ÂèØËÉΩÁöÑÊÄßËÉΩÈóÆÈ¢òÔºåÁªøËâ≤Ë°®Á§∫Ê≠£Â∏∏Ê∏≤Êüì„ÄÇ
            </p>
