          <Button
            onClick={isRecording ? stopRecording : startRecording}
            variant={isRecording ? 'destructive' : 'default'}
          >
            {isRecording ? 'ÂÅúÊ≠¢ËÆ∞ÂΩï' : 'ÂºÄÂßãËÆ∞ÂΩï'}
          </Button>
          <Button
            onClick={clearData}
            variant='outline'
          >
            Ê∏ÖÈô§Êï∞ÊçÆ
          </Button>
          <Button
            onClick={exportData}
            variant='outline'
            disabled={!componentStats.length}
          >
            ÂØºÂá∫Êï∞ÊçÆ
          </Button>
        </div>

        {/* Êï∞ÊçÆË°®Ê†º */}
        <Tabs
          defaultValue='stats'
