      const { rerender } = render(
        <ThemeMenuItem
          {...defaultProps}
          icon={Moon}
        />,
      );
      expect(screen.getByRole('menuitem')).toBeInTheDocument();

      rerender(
        <ThemeMenuItem
          {...defaultProps}
          icon={Monitor}
        />,
      );
      expect(screen.getByRole('menuitem')).toBeInTheDocument();
