  describe('View Transitions Support', () => {
    it('shows view transitions indicator when supportsViewTransitions is true', () => {
      render(
        <ThemeMenuItem
          {...defaultProps}
          supportsViewTransitions={true}
        />,
      );

      const transitionsIndicator = screen.getByText('‚ú®');
