  validateAndSanitizeOptions,
} from '@/hooks/performance-monitor-utils';

/**
 * ÊÄßËÉΩÁõëÊéß Hook
 *
 * Êèê‰æõÂÖ®Èù¢ÁöÑÊÄßËÉΩÁõëÊéßÂäüËÉΩÔºåÂåÖÊã¨Ôºö
 * - Âä†ËΩΩÊó∂Èó¥ÁõëÊéß
 * - Ê∏≤ÊüìÊó∂Èó¥ÁõëÊéß
 * - ÂÜÖÂ≠ò‰ΩøÁî®ÁõëÊéß
 * - ÊÄßËÉΩË≠¶ÂëäÁ≥ªÁªü
 * - ÂÆûÊó∂ÊÄßËÉΩÊåáÊ†á
 */
export function usePerformanceMonitor(
  options: UsePerformanceMonitorOptions = {},
): UsePerformanceMonitorReturn {
  // È™åËØÅÂíåÊ∏ÖÁêÜÈÄâÈ°π
  const state = validateAndSanitizeOptions(options);
  const {
    enableAlerts,
    alertThresholds,
    monitoringInterval,
    enableMemoryMonitoring,
    enableRenderTimeMonitoring,
    enableLoadTimeMonitoring,
    maxAlerts,
  } = state;

  // Áä∂ÊÄÅÁÆ°ÁêÜ
  const [isMonitoring, setIsMonitoring] = useState(false);
  const [metrics, setMetrics] = useState<PerformanceMetrics | null>(null);
  const [alerts, setAlerts] = useState<PerformanceAlert[]>([]);
  const [error, setError] = useState<string | null>(null);

  // Refs
  const startTime = useRef<number | null>(null);
  const alertHistory = useRef<PerformanceAlert[]>([]);
  const monitoringIntervalRef = useRef<NodeJS.Timeout | null>(null);

  // Ê∑ªÂä†Ë≠¶ÂëäÁöÑÂáΩÊï∞
  const addAlert = useCallback(
    (alert: Omit<PerformanceAlert, 'id' | 'timestamp'>) => {
      const newAlert: PerformanceAlert = {
        ...alert,
        id: generateAlertId(),
        timestamp: Date.now(),
      };

      setAlerts((prev) => {
        const updated = [...prev, newAlert];
        // ÈôêÂà∂Ë≠¶ÂëäÊï∞Èáè
        if (updated.length > maxAlerts) {
          return updated.slice(-maxAlerts);
        }
        return updated;
      });

      // Ê∑ªÂä†Âà∞ÂéÜÂè≤ËÆ∞ÂΩï
      alertHistory.current.push(newAlert);
      if (alertHistory.current.length > maxAlerts * COUNT_PAIR) {
        alertHistory.current = alertHistory.current.slice(-maxAlerts * COUNT_PAIR);
      }
    },
    [maxAlerts],
  );

  // ÊÄßËÉΩÊµãÈáèÂáΩÊï∞
  const measurements = usePerformanceMeasurements({
    enableAlerts,
    alertThresholds,
    addAlert,
    setMetrics,
    startTime,
  });

  // ÁõëÊéßÊéßÂà∂ÂáΩÊï∞
  const controls = createMonitoringControls({
    setIsMonitoring,
    setError,
    startTime,
    setMetrics,
    monitoringInterval,
    refreshMetrics: () => {
      if (enableLoadTimeMonitoring) measurements.measureLoadTime();
      if (enableRenderTimeMonitoring) measurements.measureRenderTime();
      if (enableMemoryMonitoring) measurements.measureMemoryUsage();
    },
    monitoringIntervalRef,
  });

  // ÊÄßËÉΩË≠¶ÂëäÁ≥ªÁªü
  const performanceAlertSystem = createPerformanceAlertSystem({
    alerts,
    alertHistory,
    addAlert,
    setAlerts,
  });

  // Ëé∑ÂèñÂΩìÂâçÊåáÊ†á
  const getMetrics = useCallback(() => metrics, [metrics]);

  // Âà∑Êñ∞ÊåáÊ†á
  const refreshMetrics = useCallback(() => {
    if (enableLoadTimeMonitoring) {
      measurements.measureLoadTime();
    }
    if (enableRenderTimeMonitoring) {
      measurements.measureRenderTime();
    }
    if (enableMemoryMonitoring) {
      measurements.measureMemoryUsage();
    }
  }, [
    enableLoadTimeMonitoring,
    enableRenderTimeMonitoring,
    enableMemoryMonitoring,
    measurements,
  ]);

  // Ëá™Âä®ÁõëÊéßÈÄªËæëÂ∑≤ÁßªÂä®Ëá≥ controls.startMonitoring/stopMonitoring

  // ÁªÑ‰ª∂Âç∏ËΩΩÊó∂Ê∏ÖÁêÜ
  useEffect(() => {
    return () => {
      if (monitoringIntervalRef.current) {
        clearInterval(monitoringIntervalRef.current);
      }
    };
  }, []);

  // ÂàùÂßãÂåñÊó∂ÊµãÈáèÂä†ËΩΩÊó∂Èó¥
  useEffect(() => {
    if (enableLoadTimeMonitoring) {
      // Âª∂ËøüÊµãÈáè‰ª•Á°Æ‰øùÈ°µÈù¢ÂÆåÂÖ®Âä†ËΩΩ
      const timer = setTimeout(() => {
        measurements.measureLoadTime();
      }, PERCENTAGE_FULL);

      return () => clearTimeout(timer);
    }
    return undefined;
  }, [enableLoadTimeMonitoring, measurements]);

  // ËøîÂõûÊÄßËÉΩÁõëÊéßÊé•Âè£
  return createPerformanceMonitorReturn({
    isMonitoring,
    metrics,
    alerts,
    error,
    getMetrics,
    startMonitoring: controls.startMonitoring,
    stopMonitoring: controls.stopMonitoring,
    resetMetrics: controls.resetMetrics,
    measureLoadTime: measurements.measureLoadTime,
    measureRenderTime: measurements.measureRenderTime,
    refreshMetrics,
    clearAlerts: performanceAlertSystem.clearAlerts,
    performanceAlertSystem,
  });
}

