        const messages = await mockGetCachedMessages(locale);
        if (namespace) {
          return messages[namespace] || {};
        }
        return messages;
      },
    );
  });

  afterEach(() => {
    vi.useRealTimers();
  });

  describe('Ê†∏ÂøÉÂáΩÊï∞ÂØºÂá∫È™åËØÅ', () => {
