import { DEFAULT_LOCALE, SUPPORTED_LOCALES } from '@/lib/locale-constants';
import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, SECONDS_PER_MINUTE } from '@/constants';

import type { Locale } from '@/types/i18n';
import {
  COUNTRY_CODE_TO_LOCALE_MAP,
  DETECTION_TIMEOUTS,
  GEO_API_CONFIG,
  LANGUAGE_CODE_TO_LOCALE_MAP,
  TIMEZONE_TO_LOCALE_MAP,
} from '@/lib/locale-detector-constants';

/**
 * Âü∫Á°ÄËØ≠Ë®ÄÊ£ÄÊµãÂô®
 * Base locale detector with fundamental detection methods
 */
export class BaseLocaleDetector {
  /**
   * ÂÆâÂÖ®Âú∞‰ªéËØ≠Ë®ÄÊò†Â∞Ñ‰∏≠Ëé∑ÂèñËØ≠Ë®Ä
   * Safely get locale from language mapping
   * ‰ΩøÁî®ÁôΩÂêçÂçïÈ™åËØÅÔºåÈÅøÂÖç Object Injection Sink
   */
  protected getLocaleFromLanguageCode(
    normalizedLang: string,
  ): Locale | undefined {
    if (!normalizedLang || typeof normalizedLang !== 'string') {
      return undefined;
    }

    const locale = LANGUAGE_CODE_TO_LOCALE_MAP.get(
      normalizedLang.toLowerCase(),
    );
    return locale && SUPPORTED_LOCALES.includes(locale) ? locale : undefined;
  }

  /**
   * ÂÆâÂÖ®Âú∞‰ªéÂú∞ÁêÜ‰ΩçÁΩÆÊò†Â∞Ñ‰∏≠Ëé∑ÂèñËØ≠Ë®Ä
   * Safely get locale from geo mapping
   * ‰ΩøÁî®ÁôΩÂêçÂçïÈ™åËØÅÔºåÈÅøÂÖç Object Injection Sink
   */
  protected getLocaleFromCountryCode(
    countryCode: string | undefined,
  ): Locale | undefined {
    if (!countryCode || typeof countryCode !== 'string') {
      return undefined;
    }

    const locale = COUNTRY_CODE_TO_LOCALE_MAP.get(countryCode.toUpperCase());
    return locale && SUPPORTED_LOCALES.includes(locale) ? locale : undefined;
  }

  /**
   * ÂÆâÂÖ®Âú∞‰ªéÊó∂Âå∫Êò†Â∞Ñ‰∏≠Ëé∑ÂèñËØ≠Ë®Ä
   * Safely get locale from timezone mapping
   */
  protected getLocaleFromTimeZone(
    timeZone: string | undefined,
  ): Locale | undefined {
    if (!timeZone || typeof timeZone !== 'string') {
      return undefined;
    }

    const locale = TIMEZONE_TO_LOCALE_MAP.get(timeZone);
    return locale && SUPPORTED_LOCALES.includes(locale) ? locale : undefined;
  }

  /**
   * ‰ªéÊµèËßàÂô®Ê£ÄÊµãËØ≠Ë®Ä
   * Detect locale from browser
   */
  detectFromBrowser(): Locale {
    try {
      if (typeof navigator === 'undefined') {
        return DEFAULT_LOCALE;
      }

      const languages = navigator.languages || [navigator.language];

      for (const lang of languages) {
        const normalizedLang = lang.toLowerCase();
        const detectedLocale = this.getLocaleFromLanguageCode(normalizedLang);

        if (detectedLocale) {
          return detectedLocale;
        }

        // Â∞ùËØïÊèêÂèñ‰∏ªË¶ÅËØ≠Ë®Ä‰ª£Á†Å (‰æãÂ¶Ç: 'zh-CN' -> 'zh')
        const [primaryLang] = normalizedLang.split('-');
        if (typeof primaryLang === 'string' && primaryLang.length > 0) {
          const primaryLocale = this.getLocaleFromLanguageCode(primaryLang);

          if (primaryLocale) {
            return primaryLocale;
          }
        }
      }

      return DEFAULT_LOCALE;
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Browser locale detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * ‰ªéÂú∞ÁêÜ‰ΩçÁΩÆÊ£ÄÊµãËØ≠Ë®Ä
   * Detect locale from geolocation
   */
  async detectFromGeolocation(): Promise<Locale> {
    try {
      if (typeof navigator === 'undefined' || !navigator.geolocation) {
        return DEFAULT_LOCALE;
      }

      return await new Promise<Locale>((resolve) => {
        const timeoutId = setTimeout(() => {
          resolve(DEFAULT_LOCALE);
        }, DETECTION_TIMEOUTS.GEOLOCATION);

        navigator.geolocation.getCurrentPosition(
          async (position) => {
            clearTimeout(timeoutId);
            try {
              const countryCode = await this.getCountryFromCoordinates(
                position.coords.latitude,
                position.coords.longitude,
              );

              const detectedLocale = this.getLocaleFromCountryCode(countryCode);
              resolve(detectedLocale || DEFAULT_LOCALE);
            } catch (error) {
              if (process.env.NODE_ENV === 'development') {
                console.warn('Geolocation API failed:', error);
              }
              resolve(DEFAULT_LOCALE);
            }
          },
          (error) => {
            clearTimeout(timeoutId);
            if (process.env.NODE_ENV === 'development') {
              console.warn('Geolocation permission denied or failed:', error);
            }
            resolve(DEFAULT_LOCALE);
          },
          {
            timeout: DETECTION_TIMEOUTS.GEOLOCATION,
            enableHighAccuracy: false,
            maximumAge: COUNT_TEN * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // 10ÂàÜÈíüÁºìÂ≠ò
          },
        );
      });
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Geolocation detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * ‰ªéÊó∂Âå∫Ê£ÄÊµãËØ≠Ë®Ä
   * Detect locale from timezone
   */
  detectFromTimeZone(): Locale {
    try {
      if (typeof Intl === 'undefined' || !Intl.DateTimeFormat) {
        return DEFAULT_LOCALE;
      }

      const { timeZone } = Intl.DateTimeFormat().resolvedOptions();
      const detectedLocale = this.getLocaleFromTimeZone(timeZone);

      return detectedLocale || DEFAULT_LOCALE;
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Timezone detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * ‰ªéIPÂú∞ÂùÄÊ£ÄÊµãËØ≠Ë®Ä
   * Detect locale from IP address
   */
  async detectFromIP(): Promise<Locale> {
    try {
      const countryCode = await this.getCountryFromIP();
      const detectedLocale = this.getLocaleFromCountryCode(countryCode);

      return detectedLocale || DEFAULT_LOCALE;
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('IP-based detection failed:', error);
      }
      return DEFAULT_LOCALE;
    }
  }

  /**
   * Ê†πÊçÆÂùêÊ†áËé∑ÂèñÂõΩÂÆ∂‰ª£Á†Å
   * Get country code from coordinates
   */
  private async getCountryFromCoordinates(
    lat: number,
    lng: number,
  ): Promise<string | undefined> {
    const controller = new AbortController();
    const timeoutId = setTimeout(
      () => controller.abort(),
      DETECTION_TIMEOUTS.NETWORK_REQUEST,
    );

    try {
      // Âú®ÂÆûÈôÖÂ∫îÁî®‰∏≠ÔºåËøôÈáåÂ∫îËØ•Ë∞ÉÁî®ÁúüÂÆûÁöÑÂú∞ÁêÜ‰ΩçÁΩÆAPI
      // ‰æãÂ¶Ç: Google Geocoding API, OpenStreetMap Nominatim, Á≠â
      const response = await fetch(
        `https://api.example.com/geo?lat=${lat}&lng=${lng}`,
        {
          signal: controller.signal,
          headers: {
            Accept: 'application/json',
          },
        },
      );

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }

      const data = await response.json();
      return data.country || data.country_code || data.countryCode;
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new Error('Geolocation API request timeout');
      }
      throw error;
    } finally {
      clearTimeout(timeoutId);
    }
  }

  /**
   * Ê†πÊçÆIPÂú∞ÂùÄËé∑ÂèñÂõΩÂÆ∂‰ª£Á†Å
   * Get country code from IP address
   */
  private async getCountryFromIP(): Promise<string | undefined> {
    const controller = new AbortController();
    const timeoutId = setTimeout(
      () => controller.abort(),
      DETECTION_TIMEOUTS.NETWORK_REQUEST,
    );

    try {
      // Â∞ùËØïÂ§ö‰∏™IPÂú∞ÁêÜ‰ΩçÁΩÆAPI
      for (const endpoint of GEO_API_CONFIG.ENDPOINTS) {
        const cc = await this.fetchCountryCode(endpoint, controller.signal);
        if (cc) return cc;
      }

      throw new Error('All IP geolocation endpoints failed');
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new Error('IP geolocation request timeout');
      }
      throw error;
    } finally {
      clearTimeout(timeoutId);
    }
  }

  /**
   * Âçï‰∏™Á´ØÁÇπËé∑ÂèñÂõΩÂÆ∂‰ª£Á†ÅÔºàÊó©ËøîÂõûÔºåÈôç‰ΩéÂµåÂ•óÔºâ
   */
  private async fetchCountryCode(
    endpoint: string,
    signal: AbortSignal,
  ): Promise<string | undefined> {
    try {
      const response = await fetch(endpoint, {
        signal,
        headers: { Accept: 'application/json' },
      });
      if (!response.ok) return undefined;
      const data = await response.json();
      const cc = data.country || data.country_code || data.countryCode;
      return typeof cc === 'string' ? cc : undefined;
    } catch {
      return undefined;
    }
  }

  /**
   * È™åËØÅÊ£ÄÊµãÂà∞ÁöÑËØ≠Ë®ÄÊòØÂê¶ÂèóÊîØÊåÅ
   * Validate if detected locale is supported
   */
  protected validateLocale(locale: Locale | undefined): Locale {
    return locale && SUPPORTED_LOCALES.includes(locale)
      ? locale
      : DEFAULT_LOCALE;
  }

  /**
   * Ëé∑ÂèñÊµèËßàÂô®ÊîØÊåÅÁöÑËØ≠Ë®ÄÂàóË°®
   * Get browser supported languages
   */
  getBrowserLanguages(): string[] {
    try {
      if (typeof navigator === 'undefined') {
        return [];
      }

      return navigator.languages
        ? Array.from(navigator.languages)
        : [navigator.language];
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Failed to get browser languages:', error);
      }
      return [];
    }
  }

  /**
   * Ëé∑ÂèñÂΩìÂâçÊó∂Âå∫‰ø°ÊÅØ
   * Get current timezone info
   */
  getTimeZoneInfo(): { timeZone: string; offset: number } | null {
    try {
      if (typeof Intl === 'undefined' || !Intl.DateTimeFormat) {
        return null;
      }

      const { timeZone } = Intl.DateTimeFormat().resolvedOptions();
      const offset = new Date().getTimezoneOffset();

      return { timeZone, offset };
    } catch (error) {
      if (process.env.NODE_ENV === 'development') {
        console.warn('Failed to get timezone info:', error);
      }
      return null;
    }
  }
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®ÂàÜÊûêÊ†∏ÂøÉÂäüËÉΩ
 * Locale Storage Analytics Core Functions
 *
 * Ë¥üË¥£Ê†∏ÂøÉÁªüËÆ°‰ø°ÊÅØÊî∂ÈõÜÂíåÂÅ•Â∫∑Ê£ÄÊü•ÂäüËÉΩ
 */

'use client';

import { DEC_0_4, MAGIC_0_1, MAGIC_0_2, MAGIC_0_3, MAGIC_0_5, MAGIC_0_6, MAGIC_0_7, MAGIC_0_8 } from "@/constants/decimal";
import { ANIMATION_DURATION_VERY_SLOW, BYTES_PER_KB, COUNT_FIVE, HOURS_PER_DAY, ONE, PERCENTAGE_FULL, SECONDS_PER_MINUTE, ZERO } from '@/constants';

import { DAYS_PER_WEEK } from "@/constants/time";
import { LocalStorageManager } from '@/lib/locale-storage-local';
import { estimateStorageSize, type ErrorType, type PriorityLevel, type StorageHealthCheck, type StorageOperationResult, type StorageStats } from '@/lib/locale-storage-types';

// ==================== Ê†∏ÂøÉÁªüËÆ°ÂäüËÉΩ ====================

/**
 * ËÆ°ÁÆóÂ≠òÂÇ®ÁªüËÆ°‰ø°ÊÅØ
 * Calculate storage statistics
 */
interface UserPreferencePartial {
  locale?: string;
  confidence?: number;
  lastUpdated?: number;
  source?: string;
}

interface DetectionHistoryPartial {
  history?: Array<{ detectedLocale?: string; locale?: string; timestamp?: number }>;
  lastUpdated?: number;
}

export function calculateStorageStats(): StorageStats {
  const now = Date.now();

  // Ëé∑ÂèñÊâÄÊúâÂ≠òÂÇ®ÁöÑÊï∞ÊçÆ
  const userPreference = LocalStorageManager.get('user-locale-preference') as UserPreferencePartial | null;
  const detectionHistory = (LocalStorageManager.get('locale-detection-history') || { history: [], lastUpdated: ZERO }) as DetectionHistoryPartial;
  const fallbackLocale = LocalStorageManager.get('fallback-locale');

  // ËÆ°ÁÆóÂ≠òÂÇ®Â§ßÂ∞è
  const userPreferenceSize = estimateStorageSize(userPreference);
  const historySize = estimateStorageSize(detectionHistory);
  const fallbackSize = estimateStorageSize(fallbackLocale);
  const totalSize = userPreferenceSize + historySize + fallbackSize;

  // ËÆ°ÁÆóÂéÜÂè≤ËÆ∞ÂΩïÁªüËÆ°
  const historyCount = Array.isArray(detectionHistory?.history) ? detectionHistory.history.length : ZERO;
  const uniqueLocales = Array.isArray(detectionHistory?.history)
    ? new Set(detectionHistory.history.map((h) => h.detectedLocale ?? h.locale ?? 'unknown')).size
    : ZERO;

  // ËÆ°ÁÆóÊúÄËøëÊ¥ªÂä®
  const fallbackLastUpdated =
    typeof fallbackLocale === 'object' && fallbackLocale !== null && 'lastUpdated' in (fallbackLocale as Record<string, unknown>)
      ? Number((fallbackLocale as { lastUpdated?: number }).lastUpdated ?? ZERO)
      : ZERO;
  const lastActivity = Math.max(
    userPreference?.lastUpdated ?? ZERO,
    detectionHistory?.lastUpdated ?? ZERO,
    fallbackLastUpdated,
  );

  // ËÆ°ÁÆóÊï∞ÊçÆÊñ∞È≤úÂ∫¶ (0-1, 1Ë°®Á§∫ÊúÄÊñ∞)
  const maxAge = DAYS_PER_WEEK * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // 7Â§©
  const dataAge = now - lastActivity;
  const freshness = Math.max(ZERO, ONE - dataAge / maxAge);

  return {
    totalEntries:
      (userPreference ? ONE : ZERO) +
      (detectionHistory ? ONE : ZERO) +
      (fallbackLocale ? ONE : ZERO),
    totalSize,
    lastAccessed: lastActivity,
    lastModified: lastActivity,
    accessCount: ZERO, // ÈúÄË¶Å‰ªéÂÖ∂‰ªñÂú∞ÊñπËé∑Âèñ
    errorCount: ZERO, // ÈúÄË¶Å‰ªéÂÖ∂‰ªñÂú∞ÊñπËé∑Âèñ
    freshness,
    hasOverride: userPreference?.source === 'user_override' || false,
    historyStats: {
      totalEntries: historyCount,
      uniqueLocales,
      oldestEntry:
        detectionHistory?.history?.at(-ONE)?.timestamp || ZERO,
      newestEntry: detectionHistory?.history?.at(ZERO)?.timestamp || ZERO,
    },
  };
}

/**
 * ËÆ°ÁÆóËØ≠Ë®ÄÂàÜÂ∏É
 * Calculate locale distribution
 */
// Ê≥®Ôºö‰øùÁïôÊåâÈúÄÂÆûÁé∞ÁöÑ locale ÂàÜÂ∏ÉËÆ°ÁÆóÔºõÂΩìÂâçÊú™‰ΩøÁî®‰ª•ÈÅøÂÖçÊó†Áî®‰ª£Á†ÅÂëäË≠¶

/**
 * Ëé∑ÂèñÂ≠òÂÇ®ÁªüËÆ°‰ø°ÊÅØ
 * Get storage statistics
 */
export function getStorageStats(): StorageOperationResult<StorageStats> {
  const startTime = Date.now();

  try {
    const stats = calculateStorageStats();

    return {
      success: true,
      data: stats,
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      source: 'localStorage',
      timestamp: Date.now(),
      responseTime: Date.now() - startTime,
    };
  }
}

// ==================== ÂÅ•Â∫∑Ê£ÄÊü•ÂäüËÉΩ ====================

/**
 * ËÆ°ÁÆóÂÅ•Â∫∑Ê£ÄÊü•ÁªìÊûú
 * Calculate health check results
 */
export function calculateHealthCheck(): StorageHealthCheck {
  const stats = calculateStorageStats();
  const availability = checkStorageAvailability();
  type Issue = { type: ErrorType; severity: PriorityLevel; message: string; timestamp: number };
  let healthScore = ONE;
  const issues: Issue[] = [];

  const apply = (delta: number, newIssues: Issue[]) => {
    healthScore -= delta;
    issues.push(...newIssues);
  };

  apply(...checkAvailabilityIssues(availability));
  apply(...checkFreshnessIssue(stats));
  apply(...checkSizeIssue(stats));
  apply(...checkHistoryIssue(stats));

  // Á°ÆÂÆöÂÅ•Â∫∑Áä∂ÊÄÅ
  let status: 'healthy' | 'warning' | 'error';
  if (healthScore >= MAGIC_0_8) {
    status = 'healthy';
  } else if (healthScore >= MAGIC_0_5) {
    status = 'warning';
  } else {
    status = 'error';
  }

  return {
    isHealthy: healthScore >= MAGIC_0_8,
    status,
    issues,
    performance: {
      readLatency: ZERO, // ÈúÄË¶ÅÂÆûÈôÖÊµãÈáè
      writeLatency: ZERO, // ÈúÄË¶ÅÂÆûÈôÖÊµãÈáè
      errorRate: ZERO, // ÈúÄË¶Å‰ªéÈîôËØØÁªüËÆ°‰∏≠Ëé∑Âèñ
      availability: availability.localStorageAvailable ? ONE : ZERO,
    },
    storage: {
      used: stats.totalSize,
      available: COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB - stats.totalSize, // ÂÅáËÆæ5MBÈôêÂà∂
      quota: COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB,
      utilization: stats.totalSize / (COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB),
    },
    lastCheck: Date.now(),
  };
}

function checkAvailabilityIssues(availability: {
  localStorageAvailable: boolean;
  cookiesAvailable: boolean;
}): [number, Array<{ type: ErrorType; severity: PriorityLevel; message: string; timestamp: number }>] {
  const list: Array<{ type: ErrorType; severity: PriorityLevel; message: string; timestamp: number }> = [];
  let delta = ZERO;
  if (!availability.localStorageAvailable) {
    delta += DEC_0_4;
    list.push({ type: 'access_denied', severity: 'high', message: 'localStorage‰∏çÂèØÁî®', timestamp: Date.now() });
  }
  if (!availability.cookiesAvailable) {
    delta += MAGIC_0_2;
    list.push({ type: 'access_denied', severity: 'medium', message: 'Cookies‰∏çÂèØÁî®', timestamp: Date.now() });
  }
  return [delta, list];
}

function checkFreshnessIssue(stats: StorageStats): [number, Array<{ type: ErrorType; severity: PriorityLevel; message: string; timestamp: number }>] {
  if (stats.freshness < MAGIC_0_5) {
    return [MAGIC_0_2, [{ type: 'validation_error', severity: 'medium', message: 'Êï∞ÊçÆËøáÊúü', timestamp: Date.now() }]];
  }
  return [ZERO, []];
}

function checkSizeIssue(stats: StorageStats): [number, Array<{ type: ErrorType; severity: PriorityLevel; message: string; timestamp: number }>] {
  const maxSize = COUNT_FIVE * BYTES_PER_KB * BYTES_PER_KB; // 5MB
  if (stats.totalSize > maxSize * MAGIC_0_8) {
    return [MAGIC_0_1, [{ type: 'storage_full', severity: 'medium', message: 'Â≠òÂÇ®Á©∫Èó¥‰ΩøÁî®ËøáÂ§ö', timestamp: Date.now() }]];
  }
  return [ZERO, []];
}

function checkHistoryIssue(stats: StorageStats): [number, Array<{ type: ErrorType; severity: PriorityLevel; message: string; timestamp: number }>] {
  if (stats.historyStats.totalEntries > ANIMATION_DURATION_VERY_SLOW) {
    return [MAGIC_0_1, [{ type: 'validation_error', severity: 'low', message: 'ÂéÜÂè≤ËÆ∞ÂΩïËøáÂ§ö', timestamp: Date.now() }]];
  }
  return [ZERO, []];
}

/**
 * Ê£ÄÊü•Â≠òÂÇ®ÂèØÁî®ÊÄß
 * Check storage availability
 */
function checkStorageAvailability(): {
  localStorageAvailable: boolean;
  sessionStorageAvailable: boolean;
  cookiesAvailable: boolean;
  indexedDBAvailable: boolean;
} {
  const result = {
    localStorageAvailable: false,
    sessionStorageAvailable: false,
    cookiesAvailable: false,
    indexedDBAvailable: false,
  };

  // Ê£ÄÊü• localStorage
  try {
    const testKey = '__test_localStorage__';
    localStorage.setItem(testKey, 'test');
    localStorage.removeItem(testKey);
    result.localStorageAvailable = true;
  } catch {
    // localStorage ‰∏çÂèØÁî®
  }

  // Ê£ÄÊü• sessionStorage
  try {
    const testKey = '__test_sessionStorage__';
    sessionStorage.setItem(testKey, 'test');
    sessionStorage.removeItem(testKey);
    result.sessionStorageAvailable = true;
  } catch {
    // sessionStorage ‰∏çÂèØÁî®
  }

  // Ê£ÄÊü• Cookies
  try {
    document.cookie = '__test_cookie__=test; path=/';
    result.cookiesAvailable = document.cookie.includes('__test_cookie__');
    // Ê∏ÖÁêÜÊµãËØï cookie
    document.cookie =
      '__test_cookie__=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/';
  } catch {
    // Cookies ‰∏çÂèØÁî®
  }

  // Ê£ÄÊü• IndexedDB
  try {
    result.indexedDBAvailable = 'indexedDB' in window && indexedDB !== null;
  } catch {
    // IndexedDB ‰∏çÂèØÁî®
  }

  return result;
}

/**
 * ÁîüÊàêÂÅ•Â∫∑Âª∫ËÆÆ
 * Generate health recommendations
 */
// Â§áÊ≥®ÔºöÂÅ•Â∫∑Âª∫ËÆÆÁîüÊàêÂáΩÊï∞ÊåâÈúÄÂÆûÁé∞ÔºåÈÅøÂÖçÊú™‰ΩøÁî®ÂëäË≠¶

/**
