// ==================== Â≠òÂÇ®ÊïàÁéáËÆ°ÁÆó ====================

/**
 * ËÆ°ÁÆóÂ≠òÂÇ®ÊïàÁéá
 * Calculate storage efficiency
 */
export function calculateStorageEfficiency(stats: StorageStats): number {
  // Âü∫‰∫éÂ§ö‰∏™Âõ†Á¥†ËÆ°ÁÆóÊïàÁéáÂàÜÊï∞ (0-1)
  let efficiency = ONE;

  // Êï∞ÊçÆÊñ∞È≤úÂ∫¶ÊùÉÈáç 40%
  efficiency *= MAGIC_0_6 + DEC_0_4 * stats.freshness;

  // Â≠òÂÇ®Âà©Áî®ÁéáÊùÉÈáç 30%
  const maxReasonableSize = BYTES_PER_KB * BYTES_PER_KB; // 1MB
  const sizeEfficiency = Math.min(
    ONE,
    maxReasonableSize / Math.max(stats.totalSize, ONE),
  );
  efficiency *= MAGIC_0_7 + MAGIC_0_3 * sizeEfficiency;

  // ÂéÜÂè≤ËÆ∞ÂΩïË¥®ÈáèÊùÉÈáç 30%
  const maxReasonableEntries = PERCENTAGE_FULL;
  const historyEfficiency = Math.min(
    ONE,
    maxReasonableEntries / Math.max(stats.historyStats.totalEntries, ONE),
  );
  efficiency *= MAGIC_0_7 + MAGIC_0_3 * historyEfficiency;

  return Math.max(ZERO, Math.min(ONE, efficiency));
}

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®Áª¥Êä§Êìç‰Ωú
 * Locale Storage Maintenance Operations
 *
 * Ë¥üË¥£Â≠òÂÇ®Êï∞ÊçÆÁöÑÁª¥Êä§„ÄÅÂéãÁº©Âíå‰ºòÂåñÊìç‰Ωú
 */

'use client';

import { LocalStorageManager } from '@/lib/locale-storage-local';
import { ANIMATION_DURATION_VERY_SLOW, COUNT_TEN, COUNT_TRIPLE, DAYS_PER_MONTH, HOURS_PER_DAY, HTTP_OK, ONE, PERCENTAGE_FULL, PERCENTAGE_HALF, SECONDS_PER_MINUTE, ZERO } from '@/constants';

import { LocaleCleanupManager } from '@/lib/locale-storage-maintenance-cleanup';
import { LocaleValidationManager } from '@/lib/locale-storage-maintenance-validation';
import { STORAGE_KEYS, type LocaleDetectionHistory, type MaintenanceOptions, type StorageOperationResult, type UserLocalePreference } from '@/lib/locale-storage-types';

/**
 * ËØ≠Ë®ÄÂ≠òÂÇ®Áª¥Êä§Êìç‰ΩúÁÆ°ÁêÜÂô®
 * Locale storage maintenance operations manager
 */
export class LocaleMaintenanceOperationsManager {
  /**
   * ÊâßË°åÂ≠òÂÇ®Áª¥Êä§
   * Perform storage maintenance
   */
  static performMaintenance(
    options: MaintenanceOptions = {},
  ): StorageOperationResult<{
    totalOperations: number;
    successfulOperations: number;
    results: string[];
  }> {
    const {
      cleanupExpired = true,
      maxDetectionAge = DAYS_PER_MONTH * HOURS_PER_DAY * SECONDS_PER_MINUTE * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW, // 30Â§©
      validateData = true,
      compactStorage = true,
      fixSyncIssues = true,
      cleanupDuplicates = true,
      cleanupInvalid = true,
    } = options;

    try {
      const results: string[] = [];
      let totalOperations = ZERO;
      let successfulOperations = ZERO;

      const tasks: Array<{ enabled: boolean; run: () => { success: boolean; message: string } }> = [
        {
          enabled: cleanupExpired,
          run: () => this.runCleanupExpired(maxDetectionAge),
        },
        { enabled: cleanupDuplicates, run: () => this.runCleanupDuplicates() },
        { enabled: cleanupInvalid, run: () => this.runCleanupInvalid() },
        { enabled: validateData, run: () => this.runValidation() },
        { enabled: fixSyncIssues, run: () => this.runFixSync() },
        { enabled: compactStorage, run: () => this.runCompact() },
      ];

      for (const task of tasks) {
        if (!task.enabled) continue;
        totalOperations += ONE;
        const { success, message } = task.run();
        if (success) successfulOperations += ONE;
        results.push(message);
      }

      const summary: StorageOperationResult<{
        totalOperations: number;
        successfulOperations: number;
        results: string[];
      }> = {
        success: successfulOperations === totalOperations,
        timestamp: Date.now(),
        data: { totalOperations, successfulOperations, results },
      };

      if (successfulOperations !== totalOperations) {
        summary.error = `Áª¥Êä§ÂÆåÊàê: ${successfulOperations}/${totalOperations} Êìç‰ΩúÊàêÂäü`;
      }

      return summary;
    } catch (error) {
      return {
        success: false,
        error: `ÊâßË°åÁª¥Êä§Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

