  /**
   * ÂéãÁº©Â≠òÂÇ®Á©∫Èó¥
   * Compact storage space
   */
  static compactStorage(): StorageOperationResult {
    try {
      let compactedItems = ZERO;

      // ÈáçÊñ∞Â∫èÂàóÂåñÊâÄÊúâÊï∞ÊçÆ‰ª•ÁßªÈô§Â§ö‰ΩôÁ©∫Ê†º
      Object.values(STORAGE_KEYS).forEach((key) => {
        const data = LocalStorageManager.get(key);
        if (data !== null) {
          // ÈáçÊñ∞ËÆæÁΩÆÊï∞ÊçÆÔºåËøô‰ºöËß¶ÂèëÈáçÊñ∞Â∫èÂàóÂåñ
          LocalStorageManager.set(key, data);
          compactedItems += ONE;
        }
      });

      return {
        success: true,
        error: `Â∑≤ÂéãÁº© ${compactedItems} È°πÂ≠òÂÇ®Êï∞ÊçÆ`,
        timestamp: Date.now(),
        data: { compactedItems },
      };
    } catch (error) {
      return {
        success: false,
        error: `ÂéãÁº©Â≠òÂÇ®Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆ
   * Optimize detection history data
   */
  static optimizeDetectionHistory(): StorageOperationResult {
    try {
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );

      if (!historyData || !Array.isArray(historyData.detections)) {
        return {
          success: true,
          error: 'Ê≤°ÊúâÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÈúÄË¶Å‰ºòÂåñ',
          timestamp: Date.now(),
        };
      }

      const originalCount = historyData.detections.length;

      // ÊåâÊó∂Èó¥ÊéíÂ∫èÔºàÊúÄÊñ∞ÁöÑÂú®ÂâçÔºâ
      const sortedDetections = historyData.detections.sort(
        (a, b) => b.timestamp - a.timestamp,
      );

      // ‰øùÁïôÊúÄËøëÁöÑ100Êù°ËÆ∞ÂΩï
      const maxRecords = PERCENTAGE_FULL;
      const optimizedDetections = sortedDetections.slice(ZERO, maxRecords);

      const removedCount = originalCount - optimizedDetections.length;

      if (removedCount > ZERO) {
        const updatedHistory: LocaleDetectionHistory = {
          detections: optimizedDetections,
          history: optimizedDetections,
          lastUpdated: Date.now(),
          totalDetections: optimizedDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          updatedHistory,
        );

        return {
          success: true,
          error: `Â∑≤‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤ÔºåÁßªÈô§ ${removedCount} Êù°ÊóßËÆ∞ÂΩï`,
          timestamp: Date.now(),
          data: { removedCount, remainingCount: optimizedDetections.length },
        };
      }

      return {
        success: true,
        error: 'Ê£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÂ∑≤ÊòØÊúÄ‰ºòÁä∂ÊÄÅ',
        timestamp: Date.now(),
        data: { removedCount: ZERO, remainingCount: originalCount },
      };
    } catch (error) {
      return {
        success: false,
        error: `‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤Â§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ÈáçÂª∫Â≠òÂÇ®Á¥¢Âºï
   * Rebuild storage index
   */
  static rebuildStorageIndex(): StorageOperationResult {
    try {
      let rebuiltItems = ZERO;
      const actions: string[] = [];

      // ÈáçÂª∫ÂÅèÂ•ΩÊï∞ÊçÆÁ¥¢Âºï
      const preference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (preference) {
        // Á°Æ‰øùÊï∞ÊçÆÁªìÊûÑÂÆåÊï¥
        const rebuiltPreference: UserLocalePreference = {
          locale: preference.locale,
          source: preference.source,
          timestamp: preference.timestamp,
          confidence: preference.confidence,
          ...(preference.metadata && { metadata: preference.metadata }),
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_PREFERENCE,
          rebuiltPreference,
        );
        rebuiltItems += ONE;
        actions.push('ÈáçÂª∫ÂÅèÂ•ΩÊï∞ÊçÆÁ¥¢Âºï');
      }

      // ÈáçÂª∫ÂéÜÂè≤Êï∞ÊçÆÁ¥¢Âºï
      const history = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (history) {
        // Á°Æ‰øùÊï∞ÊçÆÁªìÊûÑÂÆåÊï¥Âπ∂ÊåâÊó∂Èó¥ÊéíÂ∫è
        const filteredDetections = history.detections
          .filter(
            (detection) => detection && detection.locale && detection.source,
          )
          .sort((a, b) => b.timestamp - a.timestamp);

        const rebuiltHistory: LocaleDetectionHistory = {
          detections: filteredDetections,
          history: filteredDetections,
          lastUpdated: Date.now(),
          totalDetections: filteredDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          rebuiltHistory,
        );
        rebuiltItems += ONE;
        actions.push('ÈáçÂª∫ÂéÜÂè≤Êï∞ÊçÆÁ¥¢Âºï');
      }

      return {
        success: true,
        error: `Â∑≤ÈáçÂª∫ ${rebuiltItems} È°πÂ≠òÂÇ®Á¥¢Âºï`,
        timestamp: Date.now(),
        data: { rebuiltItems, actions },
      };
    } catch (error) {
      return {
        success: false,
        error: `ÈáçÂª∫Â≠òÂÇ®Á¥¢ÂºïÂ§±Ë¥•: ${error instanceof Error ? error.message : 'Êú™Áü•ÈîôËØØ'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ÊâßË°åÊ∑±Â∫¶Áª¥Êä§
   * Perform deep maintenance
   */
  static performDeepMaintenance(): StorageOperationResult {
    try {
      const results: string[] = [];
      let total = ZERO;
      let ok = ZERO;

      const tasks: Array<{ name: string; run: () => StorageOperationResult }> = [
        {
          name: 'Ê†áÂáÜÁª¥Êä§',
          run: () =>
            this.performMaintenance({
              cleanupExpired: true,
              validateData: true,
              compactStorage: true,
              fixSyncIssues: true,
              cleanupDuplicates: true,
              cleanupInvalid: true,
            }),
        },
        { name: '‰ºòÂåñÂéÜÂè≤', run: () => this.optimizeDetectionHistory() },
        { name: 'ÈáçÂª∫Á¥¢Âºï', run: () => this.rebuildStorageIndex() },
      ];

      for (const t of tasks) {
        total += ONE;
        const r = t.run();
        if (r.success) {
          ok += ONE;
          results.push(`${t.name}ÂÆåÊàê`);
        } else {
          results.push(`${t.name}Â§±Ë¥•: ${r.error || 'Êú™Áü•ÈîôËØØ'}`);
        }
      }

      return {
        success: ok === total,
        error: `Ê∑±Â∫¶Áª¥Êä§ÂÆåÊàê: ${ok}/${total} Êìç‰ΩúÊàêÂäü`,
        timestamp: Date.now(),
        data: { totalOperations: total, successfulOperations: ok, results },
      };
    } catch {
      return {
        success: false,
        error: 'ÊâßË°åÊ∑±Â∫¶Áª¥Êä§Â§±Ë¥•: Êú™Áü•ÈîôËØØ',
        timestamp: Date.now(),
      };
    }
  }

  /**
   * Ëé∑ÂèñÁª¥Êä§Âª∫ËÆÆ
   * Get maintenance recommendations
   */
  static getMaintenanceRecommendations(): {
    recommendations: string[];
    priority: 'low' | 'medium' | 'high';
    estimatedTime: string;
  } {
    const recommendations: string[] = [];
    const levelsEncountered = new Set<'low' | 'medium' | 'high'>();

    const add = (msg: string, level: 'low' | 'medium' | 'high') => {
      recommendations.push(msg);
      levelsEncountered.add(level);
    };

    try {
      const cleanup = LocaleCleanupManager.getCleanupStats();
      if (cleanup.expiredDetections > PERCENTAGE_HALF) add(`Ê∏ÖÁêÜ ${cleanup.expiredDetections} Êù°ËøáÊúüÊ£ÄÊµãËÆ∞ÂΩï`, 'medium');
      if (cleanup.duplicateDetections > COUNT_TEN) add(`Ê∏ÖÁêÜ ${cleanup.duplicateDetections} Êù°ÈáçÂ§çÊ£ÄÊµãËÆ∞ÂΩï`, 'medium');
      if (cleanup.invalidPreferences > ZERO) add(`‰øÆÂ§ç ${cleanup.invalidPreferences} È°πÊó†ÊïàÂÅèÂ•ΩÊï∞ÊçÆ`, 'high');

      const summary = LocaleValidationManager.getValidationSummary();
      if (summary.invalidKeys > ZERO) add(`‰øÆÂ§ç ${summary.invalidKeys} È°πÊó†ÊïàÊï∞ÊçÆ`, 'high');
      if (summary.syncIssues > ZERO) add(`‰øÆÂ§ç ${summary.syncIssues} ‰∏™ÂêåÊ≠•ÈóÆÈ¢ò`, 'medium');

      const history = LocalStorageManager.get<LocaleDetectionHistory>(STORAGE_KEYS.LOCALE_DETECTION_HISTORY);
      if (history?.detections && history.detections.length > HTTP_OK) {
        const historyLevel: 'medium' | 'high' = levelsEncountered.has('high')
          ? 'high'
          : 'medium';
        add('‰ºòÂåñÊ£ÄÊµãÂéÜÂè≤Êï∞ÊçÆÔºàËÆ∞ÂΩïËøáÂ§öÔºâ', historyLevel);
      }

      if (recommendations.length === ZERO) {
        add('Â≠òÂÇ®Áä∂ÊÄÅËâØÂ•ΩÔºåÊó†ÈúÄÁâπÊÆäÁª¥Êä§', 'low');
      }

      const priority: 'low' | 'medium' | 'high' = levelsEncountered.has('high')
        ? 'high'
        : levelsEncountered.has('medium')
          ? 'medium'
          : 'low';

      let estimatedTime: string;
      switch (priority) {
        case 'high':
          estimatedTime = '2-5ÂàÜÈíü';
          break;
        case 'medium':
          estimatedTime = recommendations.length > COUNT_TRIPLE ? '1-2ÂàÜÈíü' : '< 1ÂàÜÈíü';
          break;
        case 'low':
        default:
          estimatedTime = recommendations.length > COUNT_TRIPLE ? '1-2ÂàÜÈíü' : '< 1ÂàÜÈíü';
          break;
      }

      return { recommendations, priority, estimatedTime };
    } catch {
      return {
        recommendations: ['Ëé∑ÂèñÁª¥Êä§Âª∫ËÆÆÊó∂Âá∫ÈîôÔºåÂª∫ËÆÆÊâßË°åÂü∫Á°ÄÁª¥Êä§'],
        priority: 'medium',
        estimatedTime: '1-2ÂàÜÈíü',
      };
    }
  }
}

/**
 * ÊÄßËÉΩÁõëÊéßÊ†∏ÂøÉÈÖçÁΩÆÁÆ°ÁêÜ
 * Performance Monitoring Core Configuration Management
 *
 * Ë¥üË¥£ÊÄßËÉΩÁõëÊéßÁöÑÈÖçÁΩÆÁÆ°ÁêÜ„ÄÅÈ™åËØÅÂíåÂêàÂπ∂ÂäüËÉΩ
 */

import { PERFORMANCE_CONSTANTS } from '@/constants/performance';
import { ANIMATION_DURATION_NORMAL, COUNT_PAIR, COUNT_TEN, ONE, PERCENTAGE_FULL, ZERO } from '@/constants';

import { MB } from '@/constants/units';
import { logger } from '@/lib/logger';
import {
  type PerformanceConfig,
  generateEnvironmentConfig,
  validateConfig,
} from '@/lib/performance-monitoring-types';

/**
 * ÈÖçÁΩÆÁÆ°ÁêÜÂô®
 * Configuration manager
 */
export class PerformanceConfigManager {
  private config: PerformanceConfig;
  private isInitialized = false;

  constructor(customConfig?: Partial<PerformanceConfig>) {
    this.config = this.initializeConfig(customConfig);
    this.isInitialized = true;
  }

  /**
   * ÂàùÂßãÂåñÈÖçÁΩÆ
   * Initialize configuration
   */
  private initializeConfig(
    customConfig?: Partial<PerformanceConfig>,
  ): PerformanceConfig {
    // ÁîüÊàêÈªòËÆ§ÈÖçÁΩÆ
    const defaultConfig = generateEnvironmentConfig();

    // ÂêàÂπ∂Ëá™ÂÆö‰πâÈÖçÁΩÆ
    const mergedConfig = customConfig
      ? this.mergeConfig(defaultConfig, customConfig)
      : defaultConfig;

    // È™åËØÅÈÖçÁΩÆ
    const validation = validateConfig(mergedConfig);
    if (!validation.isValid) {
      logger.warn('Performance monitoring config validation failed', {
        errors: validation.errors,
      });
      // ‰ΩøÁî®ÈªòËÆ§ÈÖçÁΩÆ‰Ωú‰∏∫ÂêéÂ§á
      return defaultConfig;
    }

    return mergedConfig;
  }

  /**
   * ÂêàÂπ∂ÈÖçÁΩÆ
   * Merge configuration
   */
  mergeConfig(
    defaultConfig: PerformanceConfig,
    customConfig: Partial<PerformanceConfig>,
  ): PerformanceConfig {
    const merged: PerformanceConfig = {
      reactScan: {
        ...defaultConfig.reactScan,
        ...customConfig.reactScan,
      },
      webEvalAgent: {
        ...defaultConfig.webEvalAgent,
        ...customConfig.webEvalAgent,
      },
      bundleAnalyzer: {
        ...defaultConfig.bundleAnalyzer,
        ...customConfig.bundleAnalyzer,
      },
      sizeLimit: {
        ...defaultConfig.sizeLimit,
        ...customConfig.sizeLimit,
      },
      ...(defaultConfig.webVitals && {
        webVitals: customConfig.webVitals
          ? {
              ...defaultConfig.webVitals,
              ...customConfig.webVitals,
            }
          : defaultConfig.webVitals,
      }),
      ...(defaultConfig.component && {
        component: customConfig.component
          ? {
              ...defaultConfig.component,
              ...customConfig.component,
            }
          : defaultConfig.component,
      }),
      ...(defaultConfig.network && {
        network: customConfig.network
          ? {
              ...defaultConfig.network,
              ...customConfig.network,
            }
          : defaultConfig.network,
      }),
      ...(defaultConfig.bundle && {
        bundle: customConfig.bundle
          ? {
              ...defaultConfig.bundle,
              ...customConfig.bundle,
            }
          : defaultConfig.bundle,
      }),
      ...(defaultConfig.global && {
        global: customConfig.global
          ? {
              ...defaultConfig.global,
              ...customConfig.global,
            }
          : defaultConfig.global,
      }),
    };

    return merged;
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆ
   * Get configuration
   */
  getConfig(): PerformanceConfig {
    return { ...this.config };
  }

  /**
   * Êõ¥Êñ∞ÈÖçÁΩÆ
   * Update configuration
   */
  updateConfig(newConfig: Partial<PerformanceConfig>): void {
    this.config = this.mergeConfig(this.config, newConfig);

    // ÈáçÊñ∞È™åËØÅÈÖçÁΩÆ
    const validation = validateConfig(this.config);
    if (!validation.isValid) {
      logger.warn('Updated performance config validation failed', {
        errors: validation.errors,
      });
    }
  }

  /**
   * ÈáçÁΩÆÈÖçÁΩÆ‰∏∫ÈªòËÆ§ÂÄº
   * Reset configuration to defaults
   */
  resetConfig(): void {
    this.config = generateEnvironmentConfig();
  }

  /**
   * Ê£ÄÊü•ÈÖçÁΩÆÊòØÂê¶ÊúâÊïà
   * Check if configuration is valid
   */
  isConfigValid(): boolean {
    const validation = validateConfig(this.config);
    return validation.isValid;
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆÈ™åËØÅÁªìÊûú
   * Get configuration validation result
   */
  validateCurrentConfig(): ReturnType<typeof validateConfig> {
    return validateConfig(this.config);
  }

  /**
   * Ê£ÄÊü•ÊòØÂê¶Â∑≤ÂàùÂßãÂåñ
   * Check if initialized
   */
  isConfigInitialized(): boolean {
    return this.isInitialized;
  }

  /**
   * Ëé∑ÂèñÁâπÂÆöÊ®°ÂùóÁöÑÈÖçÁΩÆ
   * Get specific module configuration
   */
  getModuleConfig<T extends keyof PerformanceConfig>(
    module: T,
  ): PerformanceConfig[T] | undefined {
    // ‰ºòÂÖàÂåπÈÖç‰∏ªË¶ÅÊ®°ÂùóÔºåÈôç‰ΩéÂàÜÊîØÂ§çÊùÇÂ∫¶
    const primary = this.getPrimaryModuleConfig(module);
    if (primary !== undefined) return primary as PerformanceConfig[T];

    switch (module) {
      case 'webVitals':
        return this.config.webVitals
          ? ({ ...this.config.webVitals } as PerformanceConfig[T])
          : undefined;
      case 'component':
        return this.config.component
          ? ({ ...this.config.component } as PerformanceConfig[T])
          : undefined;
      case 'network':
        return this.config.network
          ? ({ ...this.config.network } as PerformanceConfig[T])
          : undefined;
      case 'bundle':
        return this.config.bundle
          ? ({ ...this.config.bundle } as PerformanceConfig[T])
          : undefined;
      case 'debug':
        return this.config.debug as PerformanceConfig[T];
      case 'global':
        return this.config.global
          ? ({ ...this.config.global } as PerformanceConfig[T])
          : undefined;
      default:
        // ÂÖúÂ∫ïÔºöËøîÂõû undefinedÔºà‰∏çÂ∫îËß¶ËææÔºâ
        return undefined;
    }
  }

  private getPrimaryModuleConfig<T extends keyof PerformanceConfig>(
    module: T,
  ): PerformanceConfig[T] | undefined {
    switch (module) {
      case 'reactScan':
        return { ...this.config.reactScan } as PerformanceConfig[T];
      case 'webEvalAgent':
        return { ...this.config.webEvalAgent } as PerformanceConfig[T];
      case 'bundleAnalyzer':
        return { ...this.config.bundleAnalyzer } as PerformanceConfig[T];
      case 'sizeLimit':
        return { ...this.config.sizeLimit } as PerformanceConfig[T];
      default:
        return undefined;
    }
  }

  /**
   * Êõ¥Êñ∞ÁâπÂÆöÊ®°ÂùóÁöÑÈÖçÁΩÆ
   * Update specific module configuration
   */
  updateModuleConfig<T extends keyof PerformanceConfig>(
    module: T,
    config: Partial<PerformanceConfig[T]>,
  ): void {
    const current = this.getModuleConfig(module);
    const isMergeableObject = (value: unknown): value is Record<string, unknown> =>
      typeof value === 'object' && value !== null && !Array.isArray(value);

    let nextValue: PerformanceConfig[T] | undefined;

    if (isMergeableObject(current) && isMergeableObject(config)) {
      nextValue = {
        ...(current as Record<string, unknown>),
        ...(config as Record<string, unknown>),
      } as PerformanceConfig[T];
    } else if (config !== undefined) {
      nextValue = config as PerformanceConfig[T];
    } else {
      nextValue = current;
    }

    switch (module) {
      case 'reactScan':
        this.config.reactScan = (nextValue ?? this.config.reactScan) as PerformanceConfig['reactScan'];
        break;
      case 'webEvalAgent':
        this.config.webEvalAgent = (nextValue ?? this.config.webEvalAgent) as PerformanceConfig['webEvalAgent'];
        break;
      case 'bundleAnalyzer':
        this.config.bundleAnalyzer = (nextValue ?? this.config.bundleAnalyzer) as PerformanceConfig['bundleAnalyzer'];
        break;
      case 'sizeLimit':
        this.config.sizeLimit = (nextValue ?? this.config.sizeLimit) as PerformanceConfig['sizeLimit'];
        break;
      case 'webVitals':
        if (nextValue !== undefined) {
          this.config.webVitals = nextValue as NonNullable<PerformanceConfig['webVitals']>;
        } else {
          delete this.config.webVitals;
        }
        break;
      case 'component':
        if (nextValue !== undefined) {
          this.config.component = nextValue as NonNullable<PerformanceConfig['component']>;
        } else {
          delete this.config.component;
        }
        break;
      case 'network':
        if (nextValue !== undefined) {
          this.config.network = nextValue as NonNullable<PerformanceConfig['network']>;
        } else {
          delete this.config.network;
        }
        break;
      case 'bundle':
        if (nextValue !== undefined) {
          this.config.bundle = nextValue as NonNullable<PerformanceConfig['bundle']>;
        } else {
          delete this.config.bundle;
        }
        break;
      case 'debug':
        if (typeof nextValue === 'boolean') {
          this.config.debug = nextValue;
        } else if (nextValue === undefined) {
          delete this.config.debug;
        }
        break;
      case 'global':
        if (nextValue !== undefined) {
          this.config.global = nextValue as NonNullable<PerformanceConfig['global']>;
        } else {
          delete this.config.global;
        }
        break;
      default:
        break;
    }

    // È™åËØÅÊõ¥Êñ∞ÂêéÁöÑÈÖçÁΩÆ
    const validation = validateConfig(this.config);
    if (!validation.isValid) {
      logger.warn(`Updated ${module} config validation failed`, {
        errors: validation.errors,
      });
    }
  }

  /**
   * ÂØºÂá∫ÈÖçÁΩÆ‰∏∫JSON
   * Export configuration as JSON
   */
  exportConfig(): string {
    return JSON.stringify(this.config, null, COUNT_PAIR);
  }

  /**
   * ‰ªéJSONÂØºÂÖ•ÈÖçÁΩÆ
   * Import configuration from JSON
   */
  importConfig(configJson: string): boolean {
    try {
      const importedConfig = JSON.parse(configJson) as PerformanceConfig;

      // È™åËØÅÂØºÂÖ•ÁöÑÈÖçÁΩÆ
      const validation = validateConfig(importedConfig);
      if (!validation.isValid) {
        logger.error('Imported config validation failed', {
          errors: validation.errors,
        });
        return false;
      }

      this.config = importedConfig;
      return true;
    } catch (error) {
      logger.error('Failed to import config', { error: error as Error });
      return false;
    }
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆÊëòË¶Å
   * Get configuration summary
   */
  getConfigSummary(): {
    isEnabled: boolean;
    enabledModules: string[];
    dataRetentionTime: number;
    maxMetrics: number;
    thresholds: Record<string, number>;
  } {
    const { global, component, network, bundle } = this.config;

    const enabledModules = this.getEnabledModules(component?.enabled, network?.enabled, bundle?.enabled);

    const isEnabled = Boolean(global?.enabled);
    const dataRetentionTime =
      global?.dataRetentionTime ?? PERFORMANCE_CONSTANTS.DEFAULT_RETENTION_TIME;
    const maxMetrics =
      global?.maxMetrics ?? PERFORMANCE_CONSTANTS.DEFAULT_MAX_METRICS;

    return {
      isEnabled,
      enabledModules,
      dataRetentionTime,
      maxMetrics,
      thresholds: this.buildThresholds(
        component?.thresholds?.renderTime,
        network?.thresholds?.responseTime,
        bundle?.thresholds?.size,
      ),
    };
  }

  private getEnabledModules(
    componentEnabled?: boolean,
    networkEnabled?: boolean,
    bundleEnabled?: boolean,
  ): string[] {
    const list: string[] = [];
    if (componentEnabled) list.push('component');
    if (networkEnabled) list.push('network');
    if (bundleEnabled) list.push('bundle');
    return list;
  }

  private buildThresholds(
    componentRenderTime?: number,
    networkResponseTime?: number,
    bundleSize?: number,
  ): Record<string, number> {
    return {
      componentRenderTime: componentRenderTime || PERCENTAGE_FULL,
      networkResponseTime: networkResponseTime || ANIMATION_DURATION_NORMAL,
      bundleSize: bundleSize || MB,
    };
  }

  /**
   * ÊØîËæÉ‰∏§‰∏™ÈÖçÁΩÆ
   * Compare two configurations
   */
  compareConfigs(otherConfig: PerformanceConfig): {
    isDifferent: boolean;
    differences: string[];
  } {
    const differences: string[] = [];
    const current = this.config;

    this.compareGlobalConfig(current, otherConfig, differences);
    this.compareComponentConfig(current, otherConfig, differences);
    this.compareNetworkConfig(current, otherConfig, differences);
    this.compareBundleConfig(current, otherConfig, differences);

    return { isDifferent: differences.length > ZERO, differences };
  }

  private compareGlobalConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.global?.enabled !== otherConfig.global?.enabled) {
      differences.push('global.enabled');
    }
    if (current.global?.dataRetentionTime !== otherConfig.global?.dataRetentionTime) {
      differences.push('global.dataRetentionTime');
    }
    if (current.global?.maxMetrics !== otherConfig.global?.maxMetrics) {
      differences.push('global.maxMetrics');
    }
  }

  private compareComponentConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.component?.enabled !== otherConfig.component?.enabled) {
      differences.push('component.enabled');
    }
    if (
      current.component?.thresholds?.renderTime !==
      otherConfig.component?.thresholds?.renderTime
    ) {
      differences.push('component.thresholds.renderTime');
    }
  }

  private compareNetworkConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.network?.enabled !== otherConfig.network?.enabled) {
      differences.push('network.enabled');
    }
    if (
      current.network?.thresholds?.responseTime !==
      otherConfig.network?.thresholds?.responseTime
    ) {
      differences.push('network.thresholds.responseTime');
    }
  }

  private compareBundleConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.bundle?.enabled !== otherConfig.bundle?.enabled) {
      differences.push('bundle.enabled');
    }
    if (current.bundle?.thresholds?.size !== otherConfig.bundle?.thresholds?.size) {
      differences.push('bundle.thresholds.size');
    }
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆÂèòÊõ¥ÂéÜÂè≤
   * Get configuration change history
   */
  private configHistory: Array<{
    timestamp: number;
    config: PerformanceConfig;
    reason?: string;
  }> = [];

  /**
   * ËÆ∞ÂΩïÈÖçÁΩÆÂèòÊõ¥
   * Record configuration change
   */
  private recordConfigChange(reason?: string): void {
    this.configHistory.push({
      timestamp: Date.now(),
      config: { ...this.config },
      ...(reason !== undefined && { reason }),
    });

    // ÈôêÂà∂ÂéÜÂè≤ËÆ∞ÂΩïÊï∞Èáè
    if (this.configHistory.length > COUNT_TEN) {
      this.configHistory = this.configHistory.slice(-COUNT_TEN);
    }
  }

  /**
   * Ëé∑ÂèñÈÖçÁΩÆÂèòÊõ¥ÂéÜÂè≤
   * Get configuration change history
   */
  getConfigHistory(): typeof this.configHistory {
    return [...this.configHistory];
  }

  /**
   * ÂõûÊªöÂà∞‰πãÂâçÁöÑÈÖçÁΩÆ
   * Rollback to previous configuration
   */
  rollbackConfig(steps = ONE): boolean {
    if (this.configHistory.length < steps + ONE) {
      return false;
    }

    const targetConfig = this.configHistory[
      this.configHistory.length - steps - ONE
    ];
    if (targetConfig?.config) {
      // Áõ¥Êé•ÂõûÊªöÂà∞ÁõÆÊ†áÈÖçÁΩÆÔºåÈÅøÂÖçÂä®ÊÄÅÂØπË±°Á¥¢Âºï
      this.config = { ...targetConfig.config };
    }
    this.recordConfigChange(`Rollback ${steps} steps`);

    return true;
  }
}

/**
 * ÂàõÂª∫ÈÖçÁΩÆÁÆ°ÁêÜÂô®ÂÆû‰æã
 * Create configuration manager instance
 */
export function createConfigManager(
