// ÊÄßËÉΩÂàÜÊûêÂáΩÊï∞
export function analyzePerformance(report: DiagnosticReport | null) {
  if (!report) return;

  console.log('\nüî¨ ÊÄßËÉΩÂàÜÊûêÁªìÊûú:');

  // CLS ÂàÜÊûê
  if (
    report.metrics.cls > WEB_VITALS_CONSTANTS.CLS_NEEDS_IMPROVEMENT_THRESHOLD
  ) {
    console.log('üî¥ CLS ‰∏•ÈáçÈóÆÈ¢ò: Â∏ÉÂ±ÄÂÅèÁßªËøáÂ§ßÔºåÁî®Êà∑‰ΩìÈ™åÂ∑Æ');
  } else if (report.metrics.cls > WEB_VITALS_CONSTANTS.CLS_GOOD_THRESHOLD) {
    console.log('üü° CLS ËΩªÂæÆÈóÆÈ¢ò: Êúâ‰∏Ä‰∫õÂ∏ÉÂ±ÄÂÅèÁßª');
  } else {
    console.log('üü¢ CLS ËâØÂ•Ω: Â∏ÉÂ±ÄÁ®≥ÂÆö');
  }

  // LCP ÂàÜÊûê
  if (
    report.metrics.lcp > WEB_VITALS_CONSTANTS.LCP_NEEDS_IMPROVEMENT_THRESHOLD
  ) {
    console.log('üî¥ LCP ‰∏•ÈáçÈóÆÈ¢ò: ‰∏ªË¶ÅÂÜÖÂÆπÂä†ËΩΩËøáÊÖ¢');
  } else if (report.metrics.lcp > WEB_VITALS_CONSTANTS.LCP_GOOD_THRESHOLD) {
    console.log('üü° LCP ÈúÄË¶ÅÊîπËøõ: ‰∏ªË¶ÅÂÜÖÂÆπÂä†ËΩΩËæÉÊÖ¢');
  } else {
    console.log('üü¢ LCP ËâØÂ•Ω: ‰∏ªË¶ÅÂÜÖÂÆπÂä†ËΩΩÂø´ÈÄü');
  }

  // FID ÂàÜÊûê
  if (
    report.metrics.fid > WEB_VITALS_CONSTANTS.FID_NEEDS_IMPROVEMENT_THRESHOLD
  ) {
    console.log('üî¥ FID ‰∏•ÈáçÈóÆÈ¢ò: ‰∫§‰∫íÂìçÂ∫îÂª∂Ëøü‰∏•Èáç');
  } else if (report.metrics.fid > 100) {
    console.log('üü° FID ÈúÄË¶ÅÊîπËøõ: ‰∫§‰∫íÂìçÂ∫îÊúâÂª∂Ëøü');
  } else {
    console.log('üü¢ FID ËâØÂ•Ω: ‰∫§‰∫íÂìçÂ∫îËøÖÈÄü');
  }

  // ÊÄª‰ΩìËØÑÂàÜÂàÜÊûê
  if (report.analysis.score >= WEB_VITALS_CONSTANTS.SCORE_EXCELLENT_THRESHOLD) {
    console.log('üèÜ ÊÄßËÉΩËØÑÁ∫ß: ‰ºòÁßÄ');
  } else if (
    report.analysis.score >= WEB_VITALS_CONSTANTS.TEST_SCORE_THRESHOLD_GOOD
  ) {
    console.log('ü•á ÊÄßËÉΩËØÑÁ∫ß: ËâØÂ•Ω');
  } else if (
    report.analysis.score >= WEB_VITALS_CONSTANTS.SCORE_AVERAGE_THRESHOLD
  ) {
    console.log('ü•à ÊÄßËÉΩËØÑÁ∫ß: ‰∏ÄËà¨');
  } else {
    console.log('ü•â ÊÄßËÉΩËØÑÁ∫ß: ÈúÄË¶ÅÊîπËøõ');
  }
}

// ÁîüÊàêÊÄßËÉΩÊä•Âëä
export function generatePerformanceReport() {
  const report = testWebVitalsCollection();
  if (report) {
    analyzePerformance(report);

    // ‰øùÂ≠òÂà∞ localStorage Áî®‰∫éÊµãËØï
    try {
      const existingReports = JSON.parse(
        localStorage.getItem('webVitalsDiagnostics') || '[]',
      );
      const updatedReports = [
        report,
        ...existingReports.slice(0, WEB_VITALS_CONSTANTS.REPORT_HISTORY_LIMIT),
      ];
      localStorage.setItem(
        'webVitalsDiagnostics',
        JSON.stringify(updatedReports),
      );
      console.log('‚úÖ Êä•ÂëäÂ∑≤‰øùÂ≠òÂà∞Êú¨Âú∞Â≠òÂÇ®');
    } catch (error) {
      console.warn('‚ö†Ô∏è ‰øùÂ≠òÊä•ÂëäÂ§±Ë¥•:', error);
    }
  }

  return report;
}

// Âú®ÊµèËßàÂô®ÁéØÂ¢É‰∏≠Ëá™Âä®ËøêË°å
if (typeof window !== 'undefined') {
  // È°µÈù¢Âä†ËΩΩÂÆåÊàêÂêéËøêË°åÊµãËØï
  if (document.readyState === 'complete') {
    setTimeout(generatePerformanceReport, 1000);
  } else {
    window.addEventListener('load', () => {
      setTimeout(generatePerformanceReport, 1000);
    });
  }
}

