/**
 * Ê£ÄÊü•È°µÈù¢ÊòØÂê¶Â≠òÂú®Âπ≤Êâ∞ÂÖÉÁ¥†
 */
export async function checkForInterferingElements(page: Page) {
  const interferingElements = [
    '#react-scan-toolbar-root',
    '[data-testid="react-scan-indicator"]',
    '[data-testid="react-scan-control-panel"]',
    '.react-scan-overlay',
  ];

  const foundElements: string[] = [];

  for (const selector of interferingElements) {
    try {
      const element = await page.locator(selector);
      const count = await element.count();
      if (count > 0) {
        foundElements.push(selector);
      }
    } catch (error) {
      // ÂøΩÁï•Êü•ÊâæÈîôËØØ
    }
  }

  if (foundElements.length > 0) {
    console.warn('‚ö†Ô∏è  Found interfering elements:', foundElements);
    return foundElements;
  }

  return [];
}

/**
 * ÁßªÈô§È°µÈù¢‰∏≠ÁöÑÂπ≤Êâ∞ÂÖÉÁ¥†
 */
export async function removeInterferingElements(page: Page) {
  console.log('üßπ Removing interfering elements...');

  const interferingSelectors = [
    '#react-scan-toolbar-root',
    '[data-testid="react-scan-indicator"]',
    '[data-testid="react-scan-control-panel"]',
    '.react-scan-overlay',
    '.react-scan-toolbar',
  ];

  for (const selector of interferingSelectors) {
    try {
      await page.evaluate((sel: string) => {
        const elements = document.querySelectorAll(sel);
        elements.forEach((el) => el.remove());
      }, selector);
    } catch (error) {
      // ÂøΩÁï•ÁßªÈô§ÈîôËØØ
    }
  }

  console.log('‚úÖ Interfering elements removed');
}

/**
 * Á≠âÂæÖÈ°µÈù¢Á®≥ÂÆöÔºàÊó†Âπ≤Êâ∞ÂÖÉÁ¥†Ôºâ
 */
export async function waitForStablePage(page: Page, timeout = 5000) {
  console.log('‚è≥ Waiting for page to stabilize...');

  const startTime = Date.now();

  while (Date.now() - startTime < timeout) {
    const interferingElements = await checkForInterferingElements(page);

    if (interferingElements.length === 0) {
      console.log('‚úÖ Page is stable');
      return true;
    }

    // Â∞ùËØïÁßªÈô§Âπ≤Êâ∞ÂÖÉÁ¥†
    await removeInterferingElements(page);

    // Á≠âÂæÖ‰∏ÄÂ∞èÊÆµÊó∂Èó¥ÂÜçÊ£ÄÊü•
    await page.waitForTimeout(100);
  }

  console.warn('‚ö†Ô∏è  Page did not stabilize within timeout');
  return false;
}

/**
 * ÂÆâÂÖ®ÁÇπÂáªÂÖÉÁ¥†ÔºàÈÅøÂÖçÂπ≤Êâ∞Ôºâ
 */
export async function safeClick(
  page: Page,
  selector: string,
  options: unknown = {},
) {
  console.log(`üñ±Ô∏è  Safe clicking: ${selector}`);

  // È¶ñÂÖàÁßªÈô§Âπ≤Êâ∞ÂÖÉÁ¥†
  await removeInterferingElements(page);

  // Á≠âÂæÖÂÖÉÁ¥†ÂèØËßÅ
  await page.waitForSelector(selector, { state: 'visible', timeout: 5000 });

  // ÊªöÂä®Âà∞ÂÖÉÁ¥†‰ΩçÁΩÆ
  await page.locator(selector).scrollIntoViewIfNeeded();

  // Á≠âÂæÖÂÖÉÁ¥†Á®≥ÂÆö
  await page.waitForTimeout(100);

  // ÂÜçÊ¨°Ê£ÄÊü•Âπ∂ÁßªÈô§Âπ≤Êâ∞ÂÖÉÁ¥†
  await removeInterferingElements(page);

  try {
    // Â∞ùËØïÁÇπÂáª
    await page.locator(selector).click(options);
    console.log(`‚úÖ Successfully clicked: ${selector}`);
    return true;
  } catch (error) {
    console.warn(`‚ö†Ô∏è  Click failed for ${selector}:`, (error as Error).message);

    // Â∞ùËØï‰ΩøÁî® JavaScript ÁÇπÂáª
    try {
      await page.evaluate((sel: string) => {
        const element = document.querySelector(sel);
        if (element) {
          (element as HTMLElement).click();
        }
      }, selector);
      console.log(`‚úÖ JavaScript click succeeded: ${selector}`);
      return true;
    } catch (jsError) {
      console.error(`‚ùå Both click methods failed for ${selector}`);
      return false;
    }
  }
}

