 */
function readCoverageReport() {
  const coveragePath = path.join(
    process.cwd(),
    'coverage',
    'coverage-summary.json',
  );

  if (!fs.existsSync(coveragePath)) {
    console.error('âŒ è¦†ç›–ç‡æŠ¥å‘Šä¸å­˜åœ¨ï¼Œè¯·å…ˆè¿è¡Œ: pnpm test:coverage');
