    this.showProgress(steps);

    try {
      // è¿è¡Œå®Œæ•´çš„éƒ¨ç½²æ£€æŸ¥ï¼ˆAIå±‚çš„å¤æ‚é€»è¾‘ï¼‰
      const output = execSync('pnpm deploy:check', {
