    }
  }

  /**
   * æå–é˜»å¡æ€§é—®é¢˜
   */
  extractBlockers(report) {
    if (!report.deployment.blockers) return [];

    return report.deployment.blockers.map((blocker) => ({
      type: blocker.type,
