      'find src/ -name "*.ts" -o -name "*.tsx" | xargs grep -l "MAGIC_\\|ANIMATION_\\|SIZE_\\|OPACITY_\\|COUNT_\\|PERCENTAGE_\\|OFFSET_\\|HEX_\\|DECIMAL_\\|BYTES_\\|SECONDS_\\|HOURS_\\|DAYS_"',
      {
