const fs = require('fs');
const path = require('path');

// å¸¸é‡æ˜ å°„è¡¨ - æ ¹æ®å½“å‰å¸¸é‡ç³»ç»Ÿå®šä¹‰
const CONSTANT_MAPPINGS = {
  // åŸºç¡€æ•°å€¼
  COUNT_PAIR: '@/constants',
  COUNT_TRIPLE: '@/constants',
  COUNT_QUAD: '@/constants',
  COUNT_FIVE: '@/constants',
  COUNT_TEN: '@/constants',

  // ç™¾åˆ†æ¯”
