#!/usr/bin/env node
/*
 * æ™ºèƒ½å¯¼å…¥ä¿®å¤å™¨
 * - åˆ†ææ–‡ä»¶ä¸­ä½¿ç”¨çš„é­”æ³•æ•°å­—å¸¸é‡
 * - è‡ªåŠ¨æ·»åŠ æˆ–æ›´æ–°å¯¼å…¥è¯­å¥
 * - ä½¿ç”¨Setå»é‡ç¡®ä¿å¯¼å…¥åˆ—è¡¨æ— é‡å¤
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// ä»magic-numbers.tsæå–æ‰€æœ‰å¯ç”¨å¸¸é‡
function getAvailableConstants() {
  const constantsFile = 'src/constants/magic-numbers.ts';
  const content = fs.readFileSync(constantsFile, 'utf8');
  const matches = content.match(/export const (\w+)/g) || [];
  return matches.map((match) => match.replace('export const ', ''));
}

// åˆ†ææ–‡ä»¶ä¸­ä½¿ç”¨çš„å¸¸é‡
function analyzeFileConstants(filePath) {
  if (!fs.existsSync(filePath)) return [];

  const content = fs.readFileSync(filePath, 'utf8');
  const availableConstants = getAvailableConstants();
  const usedConstants = [];

  for (const constant of availableConstants) {
    // æ£€æŸ¥å¸¸é‡æ˜¯å¦åœ¨æ–‡ä»¶ä¸­ä½¿ç”¨ï¼ˆä½†ä¸æ˜¯åœ¨exportè¯­å¥ä¸­å®šä¹‰ï¼‰
    const regex = new RegExp(`\\b${constant}\\b`, 'g');
    if (regex.test(content) && !content.includes(`export const ${constant}`)) {
      usedConstants.push(constant);
    }
  }

  return [...new Set(usedConstants)]; // å»é‡
}

// è·å–ç°æœ‰å¯¼å…¥
function getExistingImports(filePath) {
  const content = fs.readFileSync(filePath, 'utf8');
  const importMatch = content.match(
    /import\s*{\s*([^}]+)\s*}\s*from\s*['"]@\/constants\/magic-numbers['"]/,
  );

  if (!importMatch) return [];

  return importMatch[1]
    .split(',')
    .map((item) => item.trim())
    .filter((item) => item.length > 0);
}

// æ›´æ–°å¯¼å…¥è¯­å¥
function updateImports(filePath, usedConstants) {
  if (usedConstants.length === 0) return false;

  const existingImports = getExistingImports(filePath);
  const allConstants = [
    ...new Set([...existingImports, ...usedConstants]),
  ].sort();

  let content = fs.readFileSync(filePath, 'utf8');

  // ç”Ÿæˆæ–°çš„å¯¼å…¥è¯­å¥
  const newImportStatement = `import { ${allConstants.join(', ')} } from '@/constants/magic-numbers';`;

  // ç§»é™¤æ—§çš„å¯¼å…¥
  content = content.replace(
    /import\s*{\s*[^}]+\s*}\s*from\s*['"]@\/constants\/magic-numbers['"];?\n?/g,
    '',
  );

  // æ·»åŠ æ–°çš„å¯¼å…¥
  if (existingImports.length > 0 || content.includes('import')) {
    // åœ¨ç¬¬ä¸€ä¸ªimportè¯­å¥åæ·»åŠ 
    const firstImportMatch = content.match(/^import .+;$/m);
    if (firstImportMatch) {
      content = content.replace(
        firstImportMatch[0],
        `${firstImportMatch[0]}\n${newImportStatement}`,
      );
    } else {
      // åœ¨æ–‡ä»¶å¼€å¤´æ·»åŠ 
      content = `${newImportStatement}\n\n${content}`;
    }
  } else {
    // åœ¨æ–‡ä»¶å¼€å¤´æ·»åŠ 
    content = `${newImportStatement}\n\n${content}`;
  }

  fs.writeFileSync(filePath, content);
  return true;
}

// è·å–éœ€è¦ä¿®å¤çš„æ–‡ä»¶åˆ—è¡¨
function getFilesToFix() {
  let output = '';
  try {
    output = execSync('pnpm run type-check 2>&1', {
      encoding: 'utf8',
      shell: true,
      maxBuffer: 1024 * 1024 * 10,
    });
  } catch (error) {
    output = error.stdout || error.output?.join('') || '';
  }

  const files = new Set();
  const lines = output.split('\n');

  for (const line of lines) {
    // åŒ¹é… "Cannot find name" é”™è¯¯çš„æ–‡ä»¶è·¯å¾„
    const match = line.match(
      /^([^(]+)\(\d+,\d+\):\s*error\s+TS2304:\s*Cannot find name/,
    );
    if (match) {
      files.add(match[1]);
    }
  }

  return Array.from(files);
}

// ä¸»æ‰§è¡Œå‡½æ•°
function main() {
  console.log('ğŸ” åˆ†æéœ€è¦ä¿®å¤å¯¼å…¥çš„æ–‡ä»¶...');

  const filesToFix = getFilesToFix();
  console.log(`ğŸ“Š å‘ç° ${filesToFix.length} ä¸ªæ–‡ä»¶éœ€è¦ä¿®å¤å¯¼å…¥`);

  if (filesToFix.length === 0) {
    console.log('âœ… æ²¡æœ‰æ–‡ä»¶éœ€è¦ä¿®å¤å¯¼å…¥');
    return;
  }

  let fixedCount = 0;

  for (const filePath of filesToFix) {
    try {
      const usedConstants = analyzeFileConstants(filePath);
      if (usedConstants.length > 0) {
        const updated = updateImports(filePath, usedConstants);
        if (updated) {
          console.log(`âœ… ä¿®å¤: ${filePath} (${usedConstants.length}ä¸ªå¸¸é‡)`);
          fixedCount++;
        }
      }
    } catch (error) {
      console.error(`âŒ å¤„ç†æ–‡ä»¶å¤±è´¥ ${filePath}:`, error.message);
    }
  }

  console.log(`\nğŸ‰ ä¿®å¤å®Œæˆï¼`);
  console.log(`ğŸ“Š æ€»è®¡ä¿®å¤ ${fixedCount} ä¸ªæ–‡ä»¶`);

  if (fixedCount > 0) {
    console.log('\nğŸ’¡ å»ºè®®è¿è¡Œä»¥ä¸‹å‘½ä»¤éªŒè¯ä¿®å¤æ•ˆæœï¼š');
    console.log('pnpm run type-check');
  }
}

if (require.main === module) {
  main();
}

#!/usr/bin/env node

/**
 * è‡ªåŠ¨ä¿®å¤ç›¸å¯¹è·¯å¾„å¯¼å…¥é—®é¢˜
 * å°†ç›¸å¯¹è·¯å¾„å¯¼å…¥è½¬æ¢ä¸º @/ ç»å¯¹è·¯å¾„å¯¼å…¥
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// é¡¹ç›®æ ¹ç›®å½•
const PROJECT_ROOT = process.cwd();
const SRC_DIR = path.join(PROJECT_ROOT, 'src');

/**
 * é€’å½’è·å–æ‰€æœ‰ TypeScript æ–‡ä»¶
 */
function getAllTSFiles(dir) {
  const files = [];

  function traverse(currentDir) {
    const items = fs.readdirSync(currentDir);

    for (const item of items) {
      const fullPath = path.join(currentDir, item);
      const stat = fs.statSync(fullPath);

      if (stat.isDirectory()) {
        // è·³è¿‡ node_modules å’Œå…¶ä»–ä¸éœ€è¦çš„ç›®å½•
        if (
          !['node_modules', '.next', '.git', 'dist', 'build'].includes(item)
        ) {
          traverse(fullPath);
        }
      } else if (item.match(/\.(ts|tsx)$/)) {
        files.push(fullPath);
      }
    }
  }

  traverse(dir);
  return files;
}

/**
 * æ£€æŸ¥æ–‡ä»¶æ˜¯å¦åŒ…å«ç›¸å¯¹è·¯å¾„å¯¼å…¥
 */
function hasRelativeImports(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    return (
      /import\s+.*?\s+from\s+['"][.\/]/m.test(content) ||
      /export\s+.*?\s+from\s+['"][.\/]/m.test(content)
    );
  } catch (error) {
    return false;
  }
}

/**
 * å°†ç›¸å¯¹è·¯å¾„è½¬æ¢ä¸ºç»å¯¹è·¯å¾„
 */
function convertRelativeToAbsolute(filePath, importPath) {
  // å¦‚æœå·²ç»æ˜¯ç»å¯¹è·¯å¾„ï¼Œè·³è¿‡
  if (importPath.startsWith('@/') || !importPath.startsWith('.')) {
    return importPath;
  }

  // è·å–æ–‡ä»¶æ‰€åœ¨ç›®å½•
  const fileDir = path.dirname(filePath);

  // è§£æç›¸å¯¹è·¯å¾„
  const absolutePath = path.resolve(fileDir, importPath);

  // æ£€æŸ¥æ˜¯å¦åœ¨é¡¹ç›®æ ¹ç›®å½•å†…
  if (!absolutePath.startsWith(PROJECT_ROOT)) {
    console.warn(`  è­¦å‘Š: è·¯å¾„è¶…å‡ºé¡¹ç›®èŒƒå›´ ${importPath} -> ${absolutePath}`);
    return importPath; // ä¿æŒåŸæ ·
  }

  // è½¬æ¢ä¸ºç›¸å¯¹äºé¡¹ç›®æ ¹ç›®å½•çš„è·¯å¾„
  let relativePath = path.relative(PROJECT_ROOT, absolutePath);

  // å¦‚æœè·¯å¾„ä¸åœ¨ src ç›®å½•å†…ï¼Œä½¿ç”¨ @/../ å‰ç¼€
  if (!relativePath.startsWith('src/')) {
    return `@/../${relativePath.replace(/\\/g, '/')}`;
  }

  // ç§»é™¤ src/ å‰ç¼€å¹¶è½¬æ¢ä¸º @/ è·¯å¾„
  relativePath = relativePath.substring(4); // ç§»é™¤ 'src/'
  return `@/${relativePath.replace(/\\/g, '/')}`;
}

/**
 * ä¿®å¤å•ä¸ªæ–‡ä»¶çš„å¯¼å…¥
 */
function fixFileImports(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    const lines = content.split('\n');
    let modified = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // åŒ¹é… import è¯­å¥
      const importMatch = line.match(
        /^(\s*import\s+.*?\s+from\s+['"])([^'"]+)(['"].*)/,
      );
      if (importMatch) {
        const [, prefix, importPath, suffix] = importMatch;

        // åªå¤„ç†ç›¸å¯¹è·¯å¾„å¯¼å…¥
        if (importPath.startsWith('.')) {
          const newPath = convertRelativeToAbsolute(filePath, importPath);
          lines[i] = prefix + newPath + suffix;
          modified = true;
          console.log(`  ${importPath} -> ${newPath}`);
        }
      }

      // åŒ¹é… export è¯­å¥
      const exportMatch = line.match(
        /^(\s*export\s+.*?\s+from\s+['"])([^'"]+)(['"].*)/,
      );
      if (exportMatch) {
        const [, prefix, importPath, suffix] = exportMatch;

        // åªå¤„ç†ç›¸å¯¹è·¯å¾„å¯¼å…¥
        if (importPath.startsWith('.')) {
          const newPath = convertRelativeToAbsolute(filePath, importPath);
          lines[i] = prefix + newPath + suffix;
          modified = true;
          console.log(`  ${importPath} -> ${newPath}`);
        }
      }
    }

    if (modified) {
      fs.writeFileSync(filePath, lines.join('\n'));
      return true;
    }

    return false;
  } catch (error) {
    console.error(`ä¿®å¤æ–‡ä»¶å¤±è´¥ ${filePath}:`, error.message);
    return false;
  }
}

/**
 * ä¸»å‡½æ•°
 */
function main() {
  console.log('ğŸ”§ å¼€å§‹ä¿®å¤ç›¸å¯¹è·¯å¾„å¯¼å…¥é—®é¢˜...\n');

  // è·å–æ‰€æœ‰ TypeScript æ–‡ä»¶
  const allFiles = getAllTSFiles(PROJECT_ROOT);
  console.log(`æ‰«æäº† ${allFiles.length} ä¸ª TypeScript æ–‡ä»¶`);

  // ç­›é€‰å‡ºåŒ…å«ç›¸å¯¹è·¯å¾„å¯¼å…¥çš„æ–‡ä»¶
  const filesToFix = allFiles.filter(hasRelativeImports);
  console.log(`å‘ç° ${filesToFix.length} ä¸ªéœ€è¦ä¿®å¤çš„æ–‡ä»¶\n`);

  let fixedCount = 0;

  for (const filePath of filesToFix) {
    console.log(`ä¿®å¤æ–‡ä»¶: ${path.relative(PROJECT_ROOT, filePath)}`);

    if (fixFileImports(filePath)) {
      fixedCount++;
    }

    console.log('');
  }

  console.log(`âœ… ä¿®å¤å®Œæˆï¼å…±ä¿®å¤ ${fixedCount} ä¸ªæ–‡ä»¶`);

  // è¿è¡Œ ESLint æ£€æŸ¥ä¿®å¤ç»“æœ
  console.log('\nğŸ” æ£€æŸ¥ä¿®å¤ç»“æœ...');
  try {
    const result = execSync(
      'npm run lint:check 2>&1 | grep "no-restricted-imports" | wc -l',
      {
        encoding: 'utf8',
      },
    );
    console.log(`å‰©ä½™ no-restricted-imports é”™è¯¯: ${result.trim()}`);
  } catch (error) {
    console.log('æ— æ³•æ£€æŸ¥ä¿®å¤ç»“æœ');
  }
}

if (require.main === module) {
  main();
}

module.exports = { convertRelativeToAbsolute, fixFileImports };

#!/usr/bin/env tsx

/**
 * æ‰¹é‡ä¿®å¤ç›¸å¯¹è·¯å¾„å¯¼å…¥è„šæœ¬
 * å°†æ‰€æœ‰ ../xxx å’Œ ./xxx å¯¼å…¥æ›¿æ¢ä¸º @/xxx ç»å¯¹è·¯å¾„å¯¼å…¥
 */
import { readFileSync, writeFileSync } from 'fs';
import path from 'path';
import { glob } from 'glob';

interface ImportReplacement {
  file: string;
  line: number;
  original: string;
  replacement: string;
}

/**
 * å°†ç›¸å¯¹è·¯å¾„è½¬æ¢ä¸º@/ç»å¯¹è·¯å¾„
 */
function convertRelativeToAbsolute(
  relativePath: string,
  currentFilePath: string,
): string {
  // è·å–å½“å‰æ–‡ä»¶ç›¸å¯¹äºé¡¹ç›®æ ¹ç›®å½•çš„ç›®å½•è·¯å¾„
  const currentDir = path.dirname(currentFilePath);

  // è§£æç›¸å¯¹è·¯å¾„ï¼Œå¾—åˆ°ç»å¯¹è·¯å¾„
  const absolutePath = path.resolve(currentDir, relativePath);

  // å°†ç»å¯¹è·¯å¾„è½¬æ¢ä¸ºç›¸å¯¹äºsrcç›®å½•çš„è·¯å¾„
  const srcRelativePath = path.relative('src', absolutePath);

  // è¿”å›@/æ ¼å¼çš„è·¯å¾„ï¼Œç¡®ä¿ä½¿ç”¨æ­£æ–œæ 
  return `@/${srcRelativePath.replace(/\\/g, '/')}`;
}

/**
 * å¤„ç†å•ä¸ªæ–‡ä»¶çš„å¯¼å…¥æ›¿æ¢
 */
function processFile(filePath: string): ImportReplacement[] {
  const content = readFileSync(filePath, 'utf-8');
  const lines = content.split('\n');
  const replacements: ImportReplacement[] = [];

  let hasChanges = false;
  const newLines = lines.map((line, index) => {
    // åŒ¹é…å„ç§å¯¼å…¥è¯­å¥çš„æ­£åˆ™è¡¨è¾¾å¼
    const importPatterns = [
      // import ... from './xxx' æˆ– '../xxx'
      /^(\s*import\s+.*?\s+from\s+['"])(\.\.?\/[^'"]+)(['"])/,
      // import('./xxx') æˆ– import('../xxx') åŠ¨æ€å¯¼å…¥
      /^(\s*.*?import\s*\(\s*['"])(\.\.?\/[^'"]+)(['"])/,
      // require('./xxx') æˆ– require('../xxx')
      /^(\s*.*?require\s*\(\s*['"])(\.\.?\/[^'"]+)(['"])/,
    ];

    for (const pattern of importPatterns) {
      const match = line.match(pattern);
      if (match) {
        const [, prefix, relativePath, suffix] = match;

        // è·³è¿‡å·²ç»æ˜¯@/æ ¼å¼çš„å¯¼å…¥
        if (relativePath.startsWith('@/')) {
          continue;
        }

        // è½¬æ¢ä¸ºç»å¯¹è·¯å¾„
        const absolutePath = convertRelativeToAbsolute(relativePath, filePath);
        const newLine = `${prefix}${absolutePath}${suffix}`;

        replacements.push({
          file: filePath,
          line: index + 1,
          original: line.trim(),
          replacement: newLine.trim(),
        });

        hasChanges = true;
        return newLine;
      }
    }

    return line;
  });

  // å¦‚æœæœ‰å˜æ›´ï¼Œå†™å›æ–‡ä»¶
  if (hasChanges) {
    writeFileSync(filePath, newLines.join('\n'), 'utf-8');
  }

  return replacements;
}

