#!/usr/bin/env node
/*
 * å¢é‡æ ¼å¼æ£€æŸ¥ï¼ˆä»…é’ˆå¯¹æš‚å­˜æ–‡ä»¶ï¼‰
 * - è¯»å– Git æš‚å­˜åŒºçš„æ–‡ä»¶åˆ—è¡¨
 * - ä»…å¯¹è¿™äº›æ–‡ä»¶è¿è¡Œ Prettier æ ¼å¼æ£€æŸ¥
 * - ä¼˜åŒ–pre-commitæ€§èƒ½ï¼Œé¿å…å…¨é‡æ ¼å¼æ£€æŸ¥
 */

const { execSync, spawnSync } = require('child_process');

function getStagedFiles() {
  try {
    const output = execSync(
      'git diff --name-only --cached --diff-filter=ACMR',
      { stdio: ['ignore', 'pipe', 'ignore'] },
    )
      .toString()
      .trim();
    if (!output) return [];

    // è¿‡æ»¤å‡º Prettier æ”¯æŒçš„æ–‡ä»¶ç±»å‹
    return output
      .split('\n')
      .filter((f) =>
        /\.(js|jsx|ts|tsx|json|md|css|scss|html|yml|yaml)$/i.test(f),
      );
  } catch {
    return [];
  }
}

function runPrettierCheck(files) {
  if (files.length === 0) {
    console.log('[format:check:staged] æ— éœ€æ£€æŸ¥ï¼šæ²¡æœ‰æš‚å­˜çš„æ ¼å¼åŒ–æ–‡ä»¶');
    return 0;
  }

  console.log(`[format:check:staged] æ£€æŸ¥ ${files.length} ä¸ªæ–‡ä»¶çš„æ ¼å¼...`);

  // ä½¿ç”¨ prettier --check å¯¹æŒ‡å®šæ–‡ä»¶è¿›è¡Œæ ¼å¼æ£€æŸ¥
  const result = spawnSync('npx', ['prettier', '--check', ...files], {
    stdio: 'inherit',
    cwd: process.cwd(),
  });

  if (result.status !== 0) {
    console.error('[format:check:staged] ä»£ç æ ¼å¼æ£€æŸ¥å¤±è´¥');
    console.error('è¯·è¿è¡Œ pnpm format:write ä¿®å¤æ ¼å¼é—®é¢˜');
    return result.status;
  }

  console.log('[format:check:staged] âœ… ä»£ç æ ¼å¼æ£€æŸ¥é€šè¿‡');
  return 0;
}

function main() {
  const stagedFiles = getStagedFiles();
  const exitCode = runPrettierCheck(stagedFiles);
  process.exit(exitCode);
}

if (require.main === module) {
  main();
}

module.exports = { getStagedFiles, runPrettierCheck };

#!/usr/bin/env tsx
import { readdirSync, statSync, writeFileSync } from 'node:fs';
import { join, resolve } from 'node:path';
import { Project, SourceFile, ts } from 'ts-morph';
import {
  ensureConstDefined,
  loadEnhancedMapping,
  mergeAndAliasImports,
  shouldSkipNode,
} from './utils';

export interface ReplaceLogEntry {
  file: string;
  changes: Array<{
    start: number;
    end: number;
    raw: string;
    constant: string;
    negated: boolean;
  }>;
  imports: {
    added: string[];
    aliased: Record<string, string>;
  };
}

export interface Options {
  write: boolean;
  files?: string;
  limit?: number;
  dryRun?: boolean;
}

/**
 * é€’å½’æŸ¥æ‰¾åŒ¹é…çš„TypeScriptæ–‡ä»¶
 */
function findTsFiles(dir: string, pattern: RegExp = /\.(ts|tsx)$/): string[] {
  const results: string[] = [];

  try {
    const items = readdirSync(dir);

    for (const item of items) {
      const fullPath = join(dir, item);
      const stat = statSync(fullPath);

      if (stat.isDirectory()) {
        // è·³è¿‡node_moduleså’Œ.gitç­‰ç›®å½•
        if (!item.startsWith('.') && item !== 'node_modules') {
          results.push(...findTsFiles(fullPath, pattern));
        }
      } else if (stat.isFile() && pattern.test(item)) {
        results.push(fullPath);
      }
    }
  } catch (error) {
    console.warn(`âš ï¸ æ— æ³•è¯»å–ç›®å½• ${dir}:`, error);
  }

  return results;
}

/**
 * AST-based magic numbers replacement
 */
export async function run(opts: Options): Promise<ReplaceLogEntry[]> {
  console.log('ğŸš€ ASTé­”æ³•æ•°å­—æ›¿æ¢å™¨å¯åŠ¨...');

  // åˆå§‹åŒ–é¡¹ç›® - ä¿®å¤è·¯å¾„é—®é¢˜
  const rootDir = process.cwd().endsWith('scripts/magic-numbers')
    ? resolve(process.cwd(), '../..')
    : process.cwd();

  const project = new Project({
    tsConfigFilePath: resolve(rootDir, 'tsconfig.json'),
    manipulationSettings: {
      indentationText: '  ',
    },
  });

  // è·å–æ–‡ä»¶åˆ—è¡¨
  let filePaths: string[];
  if (opts.files) {
    // æ£€æŸ¥æ˜¯å¦æ˜¯å•ä¸ªæ–‡ä»¶
    if (opts.files.endsWith('.ts') || opts.files.endsWith('.tsx')) {
      // å•ä¸ªæ–‡ä»¶æ¨¡å¼
      const filePath = opts.files.startsWith('/')
        ? opts.files
        : resolve(rootDir, opts.files);
      filePaths = [filePath];
      console.log(`ğŸ¯ å•ä¸ªæ–‡ä»¶æ¨¡å¼: ${filePath}`);
    } else {
      // ç›®å½•æ¨¡å¼ - å¤„ç†ç›¸å¯¹è·¯å¾„
      let searchDir: string;
      if (opts.files.startsWith('/')) {
        searchDir = opts.files;
      } else {
        // å¤„ç†ç›¸å¯¹è·¯å¾„ï¼Œç¡®ä¿æ­£ç¡®è§£æ
        searchDir = resolve(rootDir, opts.files.replace('/**/*.{ts,tsx}', ''));
      }

      console.log(`ğŸ” æ–‡ä»¶æ¨¡å¼: ${opts.files}`);
      console.log(`ğŸ“ æ ¹ç›®å½•: ${rootDir}`);
      console.log(`ğŸ¯ æœç´¢ç›®å½•: ${searchDir}`);

      // ä½¿ç”¨è‡ªå®šä¹‰æ–‡ä»¶æŸ¥æ‰¾
      filePaths = findTsFiles(searchDir);
      console.log(`ğŸ“„ æ‰¾åˆ°æ–‡ä»¶æ•°é‡: ${filePaths.length}`);

      if (filePaths.length > 0) {
        console.log(`ğŸ“‹ å‰5ä¸ªæ–‡ä»¶ç¤ºä¾‹:`);
        filePaths.slice(0, 5).forEach((file) => {
          console.log(`  ${file.replace(rootDir, '.')}`);
        });
      }
    }
  } else {
    // æ·»åŠ æ‰€æœ‰æºæ–‡ä»¶
    const srcDir = resolve(rootDir, 'src');
    console.log(`ğŸ“ é»˜è®¤æœç´¢ç›®å½•: ${srcDir}`);
    filePaths = findTsFiles(srcDir);
  }

  // æ·»åŠ æ–‡ä»¶åˆ°é¡¹ç›®
  const files = project.addSourceFilesAtPaths(filePaths);

  const targetFiles = files.slice(0, opts.limit ?? files.length);

  // åŠ è½½å¢å¼ºç‰ˆæ˜ å°„
  const mapping = loadEnhancedMapping();
  if (Object.keys(mapping).length === 0) {
    console.error('âŒ æ˜ å°„æ–‡ä»¶ä¸ºç©ºæˆ–åŠ è½½å¤±è´¥');
    return [];
  }

  console.log(`ğŸ“Š å¤„ç† ${targetFiles.length} ä¸ªæ–‡ä»¶...`);
  console.log(`ğŸ“‹ å¯ç”¨å¸¸é‡æ˜ å°„: ${Object.keys(mapping).length} ä¸ª`);

  const logs: ReplaceLogEntry[] = [];
  let totalReplacements = 0;

  for (const sourceFile of targetFiles) {
    const filePath = sourceFile.getFilePath();
    const relativePath = filePath.replace(`${process.cwd()}/`, '');

    const fileLog: ReplaceLogEntry = {
      file: relativePath,
      changes: [],
      imports: { added: [], aliased: {} },
    };

    const newConstants = new Set<string>();
    const constantsWithModules: Array<{ constant: string; module: string }> =
      [];

    // ç›´æ¥è¿›è¡Œæ›¿æ¢ï¼Œä½†å…ˆå¤„ç†å¯¼å…¥
    sourceFile.forEachDescendant((node) => {
      if (ts.isNumericLiteral(node.compilerNode)) {
        const text = node.getText();

        // æ£€æŸ¥æ˜¯å¦åº”è¯¥è·³è¿‡
        if (shouldSkipNode(node)) {
          return;
        }

        const { constantName, isSupported, module } = ensureConstDefined(
          mapping,
          text,
        );

        if (!isSupported) {
          return;
        }

        const parent = node.getParent();

        // å¤„ç†è´Ÿå·å‰ç¼€
        if (
          parent &&
          ts.isPrefixUnaryExpression(parent.compilerNode) &&
          parent.compilerNode.operator === ts.SyntaxKind.MinusToken
        ) {
          fileLog.changes.push({
            start: node.getStart(),
            end: node.getEnd(),
            raw: `-${text}`,
            constant: constantName,
            negated: true,
          });

          // æ›¿æ¢æ“ä½œæ•°ï¼Œä¿ç•™è´Ÿå·
          node.replaceWithText(constantName);
          newConstants.add(constantName);
          constantsWithModules.push({ constant: constantName, module });
          totalReplacements++;
        } else {
          fileLog.changes.push({
            start: node.getStart(),
            end: node.getEnd(),
            raw: text,
            constant: constantName,
            negated: false,
          });

          // ç›´æ¥æ›¿æ¢
          node.replaceWithText(constantName);
          newConstants.add(constantName);
          constantsWithModules.push({ constant: constantName, module });
          totalReplacements++;
        }
      }
    });

    // å¤„ç†å¯¼å…¥ï¼ˆåœ¨æ›¿æ¢ä¹‹åï¼Œä½†åœ¨ä¿å­˜ä¹‹å‰ï¼‰
    if (newConstants.size > 0) {
      mergeAndAliasImports(sourceFile, constantsWithModules, fileLog);
    }

    if (newConstants.size > 0) {
      console.log(`ğŸ”§ ${relativePath}: ${newConstants.size} ä¸ªæ›¿æ¢`);
      newConstants.forEach((constName) => {
        console.log(
          `    ${mapping[Object.keys(mapping).find((k) => mapping[k] === constName) || '']} â†’ ${constName}`,
        );
      });
    }

    // ä¿å­˜æ–‡ä»¶
    if (!opts.dryRun && opts.write && newConstants.size > 0) {
      await sourceFile.save();
    }

    if (fileLog.changes.length > 0) {
      logs.push(fileLog);
    }
  }

  // ç”Ÿæˆæ—¥å¿—
  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const logPath = resolve(__dirname, '../logs', `ast-${timestamp}.json`);

  const logData = {
    timestamp: new Date().toISOString(),
    options: opts,
    summary: {
      filesProcessed: targetFiles.length,
      filesModified: logs.length,
      totalReplacements,
    },
    logs,
  };

  writeFileSync(logPath, JSON.stringify(logData, null, 2));

  console.log(`ğŸ“Š å¤„ç†å®Œæˆ:`);
  console.log(`  æ–‡ä»¶å¤„ç†: ${targetFiles.length}`);
  console.log(`  æ–‡ä»¶ä¿®æ”¹: ${logs.length}`);
  console.log(`  æ€»æ›¿æ¢æ•°: ${totalReplacements}`);
  console.log(`ğŸ“„ æ—¥å¿—ä¿å­˜åˆ°: ${logPath}`);

  if (opts.dryRun) {
    console.log('ğŸ” è¿™æ˜¯å¹²è·‘æ¨¡å¼ï¼Œæœªå®é™…ä¿®æ”¹æ–‡ä»¶');
  }

  return logs;
}

// CLI å…¥å£
if (require.main === module) {
  const args = process.argv.slice(2);

  const options: Options = {
    write: args.includes('--write'),
    dryRun: args.includes('--dry-run'),
    files: undefined,
    limit: undefined,
  };

  // è§£æ --files å‚æ•°
  const filesIndex = args.findIndex((arg) => arg === '--files');
  if (filesIndex !== -1 && filesIndex + 1 < args.length) {
    options.files = args[filesIndex + 1];
  } else {
    const filesArg = args.find((arg) => arg.startsWith('--files='));
    if (filesArg) {
      options.files = filesArg.split('=')[1];
    }
  }

  // è§£æ --limit å‚æ•°
  const limitIndex = args.findIndex((arg) => arg === '--limit');
  if (limitIndex !== -1 && limitIndex + 1 < args.length) {
    options.limit = parseInt(args[limitIndex + 1]);
  } else {
    const limitArg = args.find((arg) => arg.startsWith('--limit='));
    if (limitArg) {
      options.limit = parseInt(limitArg.split('=')[1]);
    }
  }

  // é»˜è®¤ä¸ºå¹²è·‘æ¨¡å¼
  if (!options.write) {
    options.dryRun = true;
  }

  run(options).catch(console.error);
}

#!/usr/bin/env tsx

/**
 * CODEXåˆ†å±‚æ²»ç†ï¼šåˆ†æè¿‡æ»¤åçš„æ•°å­—ï¼Œè¯†åˆ«çœŸæ­£æœ‰ä¸šåŠ¡è¯­ä¹‰çš„å¸¸é‡
 *
 * ç›®æ ‡ï¼šä»162ä¸ªæ•°å­—ä¸­ç­›é€‰å‡º40-60ä¸ªæœ‰æ„ä¹‰çš„ä¸šåŠ¡å¸¸é‡
 */
import { readFileSync } from 'node:fs';
import { resolve } from 'node:path';

interface NumberAnalysis {
  value: string;
  constantName: string;
  fileCount: number;
  category: 'business' | 'config' | 'ui' | 'time' | 'http' | 'test' | 'noise';
  priority: 'high' | 'medium' | 'low';
  reason: string;
}

/**
 * åˆ†æè¿‡æ»¤åçš„æ•°å­—ï¼ŒæŒ‰ä¸šåŠ¡ä»·å€¼åˆ†ç±»
 */
function analyzeFilteredNumbers(): NumberAnalysis[] {
  // ä»é¢„æ£€è¾“å‡ºä¸­æå–çš„æ•°å­—ç»Ÿè®¡ï¼ˆç®€åŒ–ç‰ˆï¼Œå®é™…åº”è¯¥ä»mapping.jsonè¯»å–ï¼‰
  const numbersFromPreflight = [
    { value: '0', name: 'ZERO', count: 171 },
    { value: '1', name: 'ONE', count: 118 },
    { value: '2', name: 'COUNT_PAIR', count: 57 },
    { value: '3', name: 'COUNT_TRIPLE', count: 38 },
    { value: '4', name: 'COUNT_QUAD', count: 17 },
    { value: '5', name: 'COUNT_FIVE', count: 47 },
    { value: '10', name: 'COUNT_TEN', count: 54 },
    { value: '24', name: 'HOURS_PER_DAY', count: 34 },
    { value: '25', name: 'PERCENTAGE_QUARTER', count: 8 },
    { value: '30', name: 'DAYS_PER_MONTH', count: 17 },
    { value: '50', name: 'PERCENTAGE_HALF', count: 36 },
    { value: '60', name: 'SECONDS_PER_MINUTE', count: 50 },
    { value: '90', name: 'ANGLE_90_DEG', count: 11 },
    { value: '100', name: 'PERCENTAGE_FULL', count: 101 },
    { value: '200', name: 'HTTP_OK', count: 25 },
    { value: '300', name: 'ANIMATION_DURATION_NORMAL', count: 20 },
    { value: '360', name: 'ANGLE_360_DEG', count: 4 },
    { value: '400', name: 'HTTP_BAD_REQUEST', count: 20 },
    { value: '401', name: 'HTTP_UNAUTHORIZED', count: 2 },
    { value: '500', name: 'ANIMATION_DURATION_SLOW', count: 39 },
    { value: '640', name: 'BREAKPOINT_SM', count: 2 },
    { value: '768', name: 'BREAKPOINT_MD', count: 4 },
    { value: '1000', name: 'ANIMATION_DURATION_VERY_SLOW', count: 93 },
    { value: '1024', name: 'BYTES_PER_KB', count: 22 },
    { value: '1280', name: 'BREAKPOINT_XL', count: 3 },
    { value: '1920', name: 'BREAKPOINT_FULL_HD', count: 2 },
    { value: '3000', name: 'THREE_SECONDS_MS', count: 13 },
    { value: '5000', name: 'FIVE_SECONDS_MS', count: 15 },
    { value: '10000', name: 'TEN_SECONDS_MS', count: 16 },
    { value: '30000', name: 'THIRTY_SECONDS_MS', count: 11 },
    { value: '60000', name: 'MINUTE_MS', count: 12 },
  ];

  const analyses: NumberAnalysis[] = [];

  for (const item of numbersFromPreflight) {
    const analysis = categorizeNumber(item.value, item.name, item.count);
    analyses.push(analysis);
  }

  return analyses.sort((a, b) => {
    // æŒ‰ä¼˜å…ˆçº§å’Œä½¿ç”¨é¢‘ç‡æ’åº
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
    if (priorityDiff !== 0) return priorityDiff;
    return b.fileCount - a.fileCount;
  });
}

/**
 * å¯¹å•ä¸ªæ•°å­—è¿›è¡Œåˆ†ç±»å’Œä¼˜å…ˆçº§è¯„ä¼°
 */
function categorizeNumber(
  value: string,
  constantName: string,
  fileCount: number,
): NumberAnalysis {
  const num = parseFloat(value);

  // HTTPçŠ¶æ€ç  - é«˜ä¼˜å…ˆçº§ä¸šåŠ¡å¸¸é‡
  if ([200, 201, 400, 401, 403, 404, 429, 500, 502, 503].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'http',
      priority: 'high',
      reason: 'HTTPçŠ¶æ€ç ï¼ŒAPIäº¤äº’æ ¸å¿ƒå¸¸é‡',
    };
  }

  // æ—¶é—´ç›¸å…³ - é«˜ä¼˜å…ˆçº§ä¸šåŠ¡å¸¸é‡
  if ([60, 1000, 3000, 5000, 10000, 30000, 60000].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'time',
      priority: 'high',
      reason: 'æ—¶é—´å¸¸é‡ï¼Œç”¨æˆ·ä½“éªŒå’Œæ€§èƒ½ç›¸å…³',
