  console.log('ğŸ“Š å°†è¢«æ›¿æ¢çš„æ•°å­—ç»Ÿè®¡:');
  const sortedNumbers = [...willUse].sort(
    (a, b) => parseFloat(a) - parseFloat(b),
  );
  sortedNumbers.forEach((num) => {
    const count = [...fileUsage.values()].reduce(
      (acc, set) => acc + (set.has(num) ? 1 : 0),
      0,
    );
