/**
 * æ™ºèƒ½å¸¸é‡ç”Ÿæˆå™¨
 * åŸºäºè¯­ä¹‰åˆ†æç»“æœç”Ÿæˆæœ‰æ„ä¹‰çš„å¸¸é‡å
 */

interface SemanticAnalysis {
  timestamp: string;
  summary: {
    totalNumbers: number;
    categoriesFound: number;
    categoryStats: Record<string, number>;
  };
  analysis: Record<
    string,
    {
      category: string;
