    this.analyzeMemoryUsage();
    this.calculatePerformanceScore();

    // æ£€æµ‹å›å½’
    if (this.options.compareWithBaseline) {
      this.detectRegression();
    }

    // ä¿å­˜åŸºå‡†
    if (this.options.saveBaseline) {
      this.saveBaseline();
    }

    return this.generateReport();
  }
}

