  /**
   * æµ‹è¯•åŸºå‡†æµ‹è¯•
   */
  async benchmarkTest() {
    console.log('ğŸ§ª æµ‹è¯•åŸºå‡†æµ‹è¯•...');

    try {
      const startTime = Date.now();
      execSync('pnpm test --run --reporter=json', {
