  /**
   * æ„å»ºåŸºå‡†æµ‹è¯•
   */
  async benchmarkBuild() {
    console.log('ğŸ—ï¸  æ„å»ºåŸºå‡†æµ‹è¯•...');

    try {
      const startTime = Date.now();
      execSync('pnpm build', { stdio: 'pipe', timeout: 180000 });
      const endTime = Date.now();
      const duration = endTime - startTime;

      console.log(`âœ… æ„å»ºå®Œæˆ: ${duration}ms`);
      return {
        duration,
        status: 'success',
        baseline: this.baselines.buildTime,
        regression: duration / this.baselines.buildTime,
      };
    } catch (error) {
      console.log(`âŒ æ„å»ºå¤±è´¥: ${error.message}`);
      return {
        duration: 0,
        status: 'failed',
        error: error.message,
        baseline: this.baselines.buildTime,
        regression: 0,
      };
    }
  }

  /**
   * åŒ…å¤§å°åˆ†æ
   */
  async analyzeBundleSize() {
    console.log('ğŸ“¦ åŒ…å¤§å°åˆ†æ...');

    try {
      const buildDir = path.join(process.cwd(), '.next');
      if (!fs.existsSync(buildDir)) {
        throw new Error('æ„å»ºç›®å½•ä¸å­˜åœ¨ï¼Œè¯·å…ˆè¿è¡Œæ„å»º');
      }

      const size = this.getDirectorySize(buildDir);
      const regression = size / this.baselines.bundleSize;

      console.log(`ğŸ“Š åŒ…å¤§å°: ${this.formatBytes(size)}`);
      return {
        size,
        sizeFormatted: this.formatBytes(size),
        status: 'success',
        baseline: this.baselines.bundleSize,
        regression,
      };
    } catch (error) {
      console.log(`âŒ åŒ…å¤§å°åˆ†æå¤±è´¥: ${error.message}`);
      return {
        size: 0,
        status: 'failed',
        error: error.message,
        baseline: this.baselines.bundleSize,
        regression: 0,
      };
    }
  }

  /**
   * è·å–ç›®å½•å¤§å°
   */
  getDirectorySize(dirPath) {
    let totalSize = 0;

    if (fs.existsSync(dirPath)) {
      const files = fs.readdirSync(dirPath);
      files.forEach((file) => {
        const filePath = path.join(dirPath, file);
        const stats = fs.statSync(filePath);
        if (stats.isDirectory()) {
          totalSize += this.getDirectorySize(filePath);
        } else {
          totalSize += stats.size;
        }
      });
    }

    return totalSize;
  }

  /**
   * ä¿å­˜åŸºå‡†æ•°æ®
   */
  saveBenchmarkData(benchmarks) {
    const filename = `benchmark-${Date.now()}.json`;
    const filepath = path.join(this.dataDir, filename);
    fs.writeFileSync(filepath, JSON.stringify(benchmarks, null, 2));

    // æ¸…ç†æ—§æ•°æ®ï¼ˆä¿ç•™æœ€è¿‘30å¤©ï¼‰
    this.cleanupOldData();
  }

  /**
   * æ¸…ç†æ—§æ•°æ®
   */
  cleanupOldData() {
    try {
      const files = fs.readdirSync(this.dataDir);
      const cutoffTime = Date.now() - 30 * 24 * 60 * 60 * 1000; // 30å¤©å‰

      files.forEach((file) => {
        if (file.startsWith('benchmark-') && file.endsWith('.json')) {
          const timestamp = parseInt(
            file.replace('benchmark-', '').replace('.json', ''),
          );
          if (timestamp < cutoffTime) {
            fs.unlinkSync(path.join(this.dataDir, file));
          }
        }
      });
    } catch (error) {
      console.warn('âš ï¸  æ¸…ç†æ—§æ•°æ®æ—¶å‡ºé”™:', error.message);
    }
  }

  /**
   * åˆ†ææ€§èƒ½å›å½’
   */
  analyzePerformanceRegression(benchmarks) {
    console.log('ğŸ“Š åˆ†ææ€§èƒ½å›å½’...');

    const regressions = [];
    const improvements = [];
    const alerts = [];

    Object.entries(benchmarks.metrics).forEach(([metric, data]) => {
      if (data.status !== 'success') return;

      const { regression } = data;
      const threshold = this.regressionThresholds[metric] || 1.2;

      if (regression > threshold) {
        const regressionData = {
          metric,
          regression,
          threshold,
          severity: regression > threshold * 1.5 ? 'critical' : 'warning',
          message: `${metric} æ€§èƒ½å›å½’ ${((regression - 1) * 100).toFixed(1)}%`,
          current: data.duration || data.size,
          baseline: data.baseline,
        };

        regressions.push(regressionData);
        alerts.push(regressionData);
      } else if (regression < 0.9) {
        improvements.push({
          metric,
          improvement: 1 - regression,
          message: `${metric} æ€§èƒ½æå‡ ${((1 - regression) * 100).toFixed(1)}%`,
          current: data.duration || data.size,
          baseline: data.baseline,
        });
      }
    });

    // åŠ è½½å†å²æ•°æ®è¿›è¡Œè¶‹åŠ¿åˆ†æ
    const historicalData = this.loadHistoricalData();
    const trends = this.analyzeTrends(historicalData, benchmarks);

    return {
      regressions,
      improvements,
      alerts,
      trends,
      summary: {
        totalMetrics: Object.keys(benchmarks.metrics).length,
        successfulMetrics: Object.values(benchmarks.metrics).filter(
          (m) => m.status === 'success',
        ).length,
        regressionCount: regressions.length,
        improvementCount: improvements.length,
        alertCount: alerts.length,
      },
    };
  }

  /**
   * åŠ è½½å†å²æ•°æ®
   */
  loadHistoricalData() {
    try {
      const files = fs
        .readdirSync(this.dataDir)
        .filter(
          (file) => file.startsWith('benchmark-') && file.endsWith('.json'),
        )
        .sort()
        .slice(-10); // æœ€è¿‘10æ¬¡

      return files.map((file) => {
        const content = fs.readFileSync(path.join(this.dataDir, file), 'utf8');
        return JSON.parse(content);
      });
    } catch (error) {
      console.warn('âš ï¸  åŠ è½½å†å²æ•°æ®æ—¶å‡ºé”™:', error.message);
      return [];
    }
  }

  /**
   * åˆ†æè¶‹åŠ¿
   */
  analyzeTrends(historicalData, current) {
    if (historicalData.length < 2) {
      return { status: 'insufficient-data' };
    }

    const trends = {};
    const metrics = ['typeCheck', 'lint', 'test', 'build', 'bundle'];

    metrics.forEach((metric) => {
      const values = historicalData
        .map((data) => data.metrics[metric])
        .filter((m) => m && m.status === 'success')
        .map((m) => m.duration || m.size);

      if (values.length >= 2) {
        const recent = values.slice(-3); // æœ€è¿‘3æ¬¡
        const average = recent.reduce((a, b) => a + b, 0) / recent.length;
        const currentValue =
          current.metrics[metric]?.duration ||
          current.metrics[metric]?.size ||
          0;

        trends[metric] = {
          average,
          current: currentValue,
          trend:
            currentValue > average * 1.1
              ? 'declining'
              : currentValue < average * 0.9
                ? 'improving'
                : 'stable',
          changePercent: ((currentValue - average) / average) * 100,
        };
      }
    });

    return { status: 'success', trends };
  }

  /**
   * ç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
   */
  generatePerformanceReport(benchmarks, analysis) {
    console.log('\nğŸ“Š æ€§èƒ½åŸºå‡†æµ‹è¯•æŠ¥å‘Š');
    console.log('='.repeat(60));

    console.log(
      `ğŸ“… æµ‹è¯•æ—¶é—´: ${new Date(benchmarks.timestamp).toLocaleString()}`,
    );
    console.log(`ğŸŒ¿ åˆ†æ”¯: ${benchmarks.branch}`);
    console.log(`ğŸ“ æäº¤: ${benchmarks.commit.substring(0, 8)}`);

    console.log('\nğŸ“ˆ æ€§èƒ½æŒ‡æ ‡:');
    Object.entries(benchmarks.metrics).forEach(([metric, data]) => {
      if (data.status === 'success') {
        const value = data.duration
          ? `${data.duration}ms`
          : this.formatBytes(data.size);
        const regressionPercent = ((data.regression - 1) * 100).toFixed(1);
        const emoji =
          data.regression > 1.2 ? 'ğŸ”´' : data.regression < 0.9 ? 'ğŸŸ¢' : 'ğŸŸ¡';
        console.log(
          `  ${emoji} ${metric}: ${value} (${regressionPercent >= 0 ? '+' : ''}${regressionPercent}%)`,
        );
      } else {
        console.log(`  âŒ ${metric}: å¤±è´¥ - ${data.error}`);
      }
    });

    if (analysis.improvements.length > 0) {
      console.log('\nğŸŸ¢ æ€§èƒ½æå‡:');
      analysis.improvements.forEach((improvement) => {
        console.log(`  âœ… ${improvement.message}`);
      });
    }

    if (analysis.regressions.length > 0) {
      console.log('\nğŸ”´ æ€§èƒ½å›å½’:');
      analysis.regressions.forEach((regression) => {
        const emoji = regression.severity === 'critical' ? 'ğŸš¨' : 'âš ï¸';
        console.log(`  ${emoji} ${regression.message}`);
      });
    }

    // ä¿å­˜æŠ¥å‘Š
    const reportPath = path.join(
      this.dataDir,
      `performance-report-${Date.now()}.json`,
    );
    fs.writeFileSync(
      reportPath,
      JSON.stringify({ benchmarks, analysis }, null, 2),
    );
    console.log(`\nğŸ’¾ æŠ¥å‘Šå·²ä¿å­˜: ${reportPath}`);
  }

  /**
   * è·å–ç¯å¢ƒä¿¡æ¯
   */
  getEnvironmentInfo() {
    return {
      nodeVersion: process.version,
      platform: process.platform,
      arch: process.arch,
      cpus: require('os').cpus().length,
      memory: `${Math.round(require('os').totalmem() / 1024 / 1024 / 1024)}GB`,
    };
  }

  getCurrentCommit() {
    try {
      return execSync('git rev-parse HEAD', { encoding: 'utf8' }).trim();
    } catch {
      return 'unknown';
    }
  }

  getCurrentBranch() {
    try {
      return execSync('git rev-parse --abbrev-ref HEAD', {
        encoding: 'utf8',
      }).trim();
    } catch {
      return 'unknown';
    }
  }

  formatBytes(bytes) {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${parseFloat((bytes / k ** i).toFixed(2))} ${sizes[i]}`;
  }
}

// ä¸»æ‰§è¡Œå‡½æ•°
async function main() {
  const monitor = new PerformanceBenchmarkMonitor();

  try {
    const { benchmarks, analysis } = await monitor.runBenchmarks();

    // å¦‚æœæœ‰ä¸¥é‡æ€§èƒ½å›å½’ï¼Œé€€å‡ºç ä¸º1
    if (analysis.alerts.some((alert) => alert.severity === 'critical')) {
      console.log('\nğŸš¨ æ£€æµ‹åˆ°ä¸¥é‡æ€§èƒ½å›å½’ï¼Œè¯·ç«‹å³å¤„ç†ï¼');
      process.exit(1);
    }

    console.log('\nâœ… æ€§èƒ½åŸºå‡†æµ‹è¯•å®Œæˆ');
  } catch (error) {
    console.error('âŒ æ€§èƒ½åŸºå‡†æµ‹è¯•å¤±è´¥:', error.message);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

module.exports = { PerformanceBenchmarkMonitor };

#!/usr/bin/env node

/**
 * é¡¹ç›®å¥åº·æ£€æŸ¥å™¨ - è¶…ç®€å•çš„ç”¨æˆ·ç•Œé¢
 * Project Health Checker - Ultra-simple user interface
 */

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

class ProjectHealthChecker {
  constructor() {
    this.healthStatus = {
      overall: 'unknown',
      score: 0,
      issues: [],
      recommendations: [],
      canDeploy: false,
    };
  }

  /**
   * æ˜¾ç¤ºåŠ è½½åŠ¨ç”»
   */
  showLoading(message) {
    const frames = ['â ‹', 'â ™', 'â ¹', 'â ¸', 'â ¼', 'â ´', 'â ¦', 'â §', 'â ‡', 'â '];
    let i = 0;

    const interval = setInterval(() => {
      process.stdout.write(`\r${frames[i]} ${message}`);
      i = (i + 1) % frames.length;
    }, 100);

    return interval;
  }

  /**
   * åœæ­¢åŠ è½½åŠ¨ç”»
   */
  stopLoading(interval, finalMessage) {
    clearInterval(interval);
    process.stdout.write(`\r${finalMessage}\n`);
  }

  /**
   * è¿è¡Œåå°å¤æ‚æ£€æŸ¥
   */
  async runComplexChecks() {
    const loader = this.showLoading('ğŸ” AIæ­£åœ¨è¿›è¡Œå…¨é¢å¥åº·æ£€æŸ¥...');

    try {
      // è¿è¡Œå®Œæ•´çš„è´¨é‡æ£€æŸ¥ï¼ˆAIå±‚çš„å¤æ‚é€»è¾‘ï¼‰
      const output = execSync('pnpm quality:report', {
        stdio: 'pipe',
        encoding: 'utf8',
        timeout: 120000, // 2åˆ†é’Ÿè¶…æ—¶
      });

      this.stopLoading(loader, 'âœ… å¥åº·æ£€æŸ¥å®Œæˆ');

      // è¯»å–è¯¦ç»†æŠ¥å‘Š
      await this.parseDetailedReport();

      return true;
    } catch (error) {
      this.stopLoading(loader, 'âŒ å¥åº·æ£€æŸ¥é‡åˆ°é—®é¢˜');

      // å³ä½¿å‡ºé”™ä¹Ÿå°è¯•è§£æå·²æœ‰æŠ¥å‘Š
      await this.parseDetailedReport();

      return false;
    }
  }

  /**
   * è§£æè¯¦ç»†æŠ¥å‘Šï¼Œæå–å…³é”®ä¿¡æ¯
   */
  async parseDetailedReport() {
    try {
      const reportPath = path.join(
        process.cwd(),
        'reports',
        'simple-quality-report.json',
      );

      if (fs.existsSync(reportPath)) {
        const report = JSON.parse(fs.readFileSync(reportPath, 'utf8'));

        // æå–å…³é”®ä¿¡æ¯
        this.healthStatus.score = report.summary.overallScore || 0;
        this.healthStatus.issues = this.extractCriticalIssues(report);
        this.healthStatus.recommendations =
          this.extractTopRecommendations(report);
        this.healthStatus.canDeploy = this.assessDeploymentReadiness(report);
        this.healthStatus.overall = this.determineOverallHealth(report);
      }
    } catch (error) {
      console.warn('âš ï¸  æ— æ³•è¯»å–è¯¦ç»†æŠ¥å‘Šï¼Œä½¿ç”¨åŸºç¡€æ£€æŸ¥ç»“æœ');
      this.healthStatus.overall = 'unknown';
    }
  }

  /**
   * æå–å…³é”®é—®é¢˜
   */
  extractCriticalIssues(report) {
    const issues = [];

    // ä»æ£€æŸ¥ç»“æœä¸­æå–å¤±è´¥é¡¹
    if (report.checks) {
      report.checks.forEach((check) => {
        if (check.status === 'FAIL') {
          issues.push({
            type: 'quality',
            message: `${check.name}å¤±è´¥`,
            severity: 'high',
          });
        }
      });
    }

    // ä»å®‰å…¨æŠ¥å‘Šä¸­æå–æ¼æ´
    if (report.security && report.security.vulnerabilities > 0) {
      issues.push({
        type: 'security',
        message: `å‘ç° ${report.security.vulnerabilities} ä¸ªå®‰å…¨æ¼æ´`,
        severity: 'critical',
      });
    }

    // ä»æ€§èƒ½æŠ¥å‘Šä¸­æå–é—®é¢˜
    if (report.performance && report.performance.issues > 0) {
      issues.push({
        type: 'performance',
        message: `å‘ç° ${report.performance.issues} ä¸ªæ€§èƒ½é—®é¢˜`,
        severity: 'medium',
      });
    }

    return issues.slice(0, 3); // åªæ˜¾ç¤ºå‰3ä¸ªæœ€é‡è¦çš„é—®é¢˜
  }

  /**
   * æå–é¡¶çº§å»ºè®®
   */
  extractTopRecommendations(report) {
    if (!report.recommendations) return [];

    return report.recommendations
      .filter((rec) => rec.priority === 'HIGH')
      .slice(0, 2)
      .map((rec) => rec.title);
  }

  /**
   * è¯„ä¼°éƒ¨ç½²å°±ç»ªæ€§
   */
  assessDeploymentReadiness(report) {
    // åŸºäºåˆ†æ•°å’Œå…³é”®é—®é¢˜åˆ¤æ–­
    const score = report.summary.overallScore || 0;
    const criticalIssues = this.healthStatus.issues.filter(
      (issue) => issue.severity === 'critical',
    ).length;

    return score >= 70 && criticalIssues === 0;
  }

  /**
   * ç¡®å®šæ•´ä½“å¥åº·çŠ¶å†µ
   */
  determineOverallHealth(report) {
    const score = report.summary.overallScore || 0;
    const failedChecks = report.summary.failedChecks || 0;

    if (score >= 90 && failedChecks === 0) return 'excellent';
    if (score >= 80 && failedChecks <= 1) return 'good';
    if (score >= 70 && failedChecks <= 2) return 'fair';
    if (score >= 50) return 'poor';
    return 'critical';
  }

  /**
   * æ˜¾ç¤ºç®€å•çš„å¥åº·çŠ¶æ€
   */
  displaySimpleHealth() {
    console.log('\nğŸ¥ é¡¹ç›®å¥åº·çŠ¶å†µ');
    console.log('='.repeat(30));

    // æ˜¾ç¤ºæ•´ä½“çŠ¶æ€
    const statusIcons = {
      excellent: 'ğŸŸ¢ ä¼˜ç§€',
      good: 'ğŸŸ¢ è‰¯å¥½',
      fair: 'ğŸŸ¡ ä¸€èˆ¬',
      poor: 'ğŸŸ  è¾ƒå·®',
      critical: 'ğŸ”´ å±é™©',
      unknown: 'âšª æœªçŸ¥',
    };

    console.log(`çŠ¶æ€: ${statusIcons[this.healthStatus.overall]}`);
    console.log(`è¯„åˆ†: ${this.healthStatus.score}/100`);

    // æ˜¾ç¤ºå…³é”®é—®é¢˜
    if (this.healthStatus.issues.length > 0) {
      console.log('\nğŸš¨ éœ€è¦å…³æ³¨çš„é—®é¢˜:');
      this.healthStatus.issues.forEach((issue, index) => {
        const severityIcon = {
          critical: 'ğŸ”´',
          high: 'ğŸŸ ',
          medium: 'ğŸŸ¡',
          low: 'ğŸŸ¢',
        }[issue.severity];

        console.log(`${index + 1}. ${severityIcon} ${issue.message}`);
      });
    }

    // æ˜¾ç¤ºéƒ¨ç½²çŠ¶æ€
    console.log(
      `\nğŸš€ éƒ¨ç½²çŠ¶æ€: ${this.healthStatus.canDeploy ? 'âœ… å¯ä»¥éƒ¨ç½²' : 'âŒ æš‚ä¸å»ºè®®éƒ¨ç½²'}`,
    );

    // æ˜¾ç¤ºå»ºè®®
    if (this.healthStatus.recommendations.length > 0) {
      console.log('\nğŸ’¡ å»ºè®®ä¼˜å…ˆå¤„ç†:');
      this.healthStatus.recommendations.forEach((rec, index) => {
        console.log(`${index + 1}. ${rec}`);
      });
    }

    // æ˜¾ç¤ºè¯¦ç»†æŠ¥å‘Šæç¤º
    console.log('\nğŸ“Š æŸ¥çœ‹è¯¦ç»†æŠ¥å‘Š: pnpm report');
    console.log('ğŸš€ æ£€æŸ¥éƒ¨ç½²å°±ç»ª: pnpm ready');
  }

  /**
   * æ‰§è¡Œå¥åº·æ£€æŸ¥
   */
  async checkHealth() {
    console.log('ğŸ¯ é¡¹ç›®å¥åº·å¿«é€Ÿæ£€æŸ¥');
    console.log('='.repeat(30));

    const success = await this.runComplexChecks();
    this.displaySimpleHealth();

    // è¿”å›ç®€å•çš„é€€å‡ºç 
    const isHealthy =
      this.healthStatus.overall === 'excellent' ||
      this.healthStatus.overall === 'good';

    return isHealthy;
  }
}

// å‘½ä»¤è¡Œæ¥å£
if (require.main === module) {
  const checker = new ProjectHealthChecker();
  checker
    .checkHealth()
    .then((healthy) => {
      process.exit(healthy ? 0 : 1);
    })
    .catch((error) => {
      console.error(`âŒ å¥åº·æ£€æŸ¥å¤±è´¥: ${error.message}`);
      process.exit(1);
    });
}

module.exports = ProjectHealthChecker;

