  async checkCoverage() {
    console.log('ğŸ“Š æ‰§è¡Œè¦†ç›–ç‡é—¨ç¦æ£€æŸ¥...');

    const gate = {
      name: 'Coverage',
      status: 'unknown',
      checks: {},
      blocking: this.config.gates.coverage.blocking,
      issues: [],
    };

    try {
      // è¿è¡Œè¦†ç›–ç‡æµ‹è¯•
      console.log('ğŸ§ª è¿è¡Œæµ‹è¯•ä»¥ç”Ÿæˆè¦†ç›–ç‡...');
      execSync('pnpm test:coverage --run --reporter=json', {
        stdio: 'pipe',
