  /**
   * å®‰å…¨é—¨ç¦æ£€æŸ¥
   */
  async checkSecurity() {
    console.log('ğŸ”’ æ‰§è¡Œå®‰å…¨é—¨ç¦æ£€æŸ¥...');

    const gate = {
      name: 'Security',
      status: 'unknown',
      checks: {},
