  /**
   * å¤„ç† CI ç¯å¢ƒè¾“å‡º
   */
  handleCIOutput() {
    // GitHub Actions æ³¨è§£
    if (process.env.GITHUB_ACTIONS) {
      Object.values(this.results.gates).forEach((gate) => {
