#!/usr/bin/env node
/*
 * å¿«é€Ÿè´¨é‡æ£€æŸ¥ï¼ˆä»…é’ˆå¯¹æš‚å­˜æ–‡ä»¶ï¼‰
 * - è¯»å– Git æš‚å­˜åŒºçš„æ–‡ä»¶åˆ—è¡¨
 * - è¿‡æ»¤å‡º .js/.jsx/.ts/.tsx æ–‡ä»¶
 * - ä»…å¯¹è¿™äº›æ–‡ä»¶è¿è¡Œ ESLintï¼ˆä½¿ç”¨å½“å‰é¡¹ç›®çš„ eslint.config.mjsï¼‰
 */

const { execSync, spawnSync } = require('child_process');

function getStagedFiles() {
  try {
    const output = execSync(
      'git diff --name-only --cached --diff-filter=ACMR',
      { stdio: ['ignore', 'pipe', 'ignore'] },
    )
      .toString()
      .trim();
    if (!output) return [];
    return output.split('\n').filter((f) => /\.(js|jsx|ts|tsx)$/i.test(f));
  } catch {
    return [];
  }
}

function runESLint(files) {
  if (files.length === 0) {
    console.log('[quality:quick:staged] æ— éœ€æ£€æŸ¥ï¼šæ²¡æœ‰æš‚å­˜çš„ JS/TS æ–‡ä»¶');
    return 0;
  }

  // ä½¿ç”¨pnpm execè°ƒç”¨ESLintï¼Œé¿å…è·¯å¾„è§£æé—®é¢˜
  const args = [
    'exec',
    'eslint',
    ...files,
    '--config',
    'eslint.config.mjs',
    '--max-warnings',
    '0',
  ];

  const res = spawnSync('pnpm', args, { stdio: 'inherit' });
  return res.status ?? 1;
}

const files = getStagedFiles();
const code = runESLint(files);
process.exit(code);

#!/usr/bin/env node
/*
 * å®‰å…¨é­”æ³•æ•°å­—ä¿®å¤å™¨ (åŸºäºCODEXå»ºè®®)
 * - ä¿®å¤åŸè„šæœ¬çš„å¯¼å…¥é€»è¾‘ç¼ºé™·
 * - æ·»åŠ é¢„æ£€æŸ¥éªŒè¯æœºåˆ¶
 * - å®æ–½åˆ†æ‰¹å¤„ç†å’Œè‡ªåŠ¨éªŒè¯
 * - é˜²æ­¢ä¸Šä¸‹æ–‡æ›¿æ¢é”™è¯¯
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// ä»magic-numbers.tsæå–æ‰€æœ‰å¯ç”¨å¸¸é‡
function getAvailableConstants() {
  const constantsFile = 'src/constants/magic-numbers.ts';
  const content = fs.readFileSync(constantsFile, 'utf8');
  const matches = content.match(/export const (\w+)/g) || [];
  return matches.map((match) => match.replace('export const ', ''));
}

// è·å–ç°æœ‰å¯¼å…¥å¸¸é‡
function getExistingImports(filePath) {
  const content = fs.readFileSync(filePath, 'utf8');
  const importMatch = content.match(
    /import\s*{\s*([^}]+)\s*}\s*from\s*['"]@\/constants\/magic-numbers['"]/,
  );

  if (!importMatch) return [];

  return importMatch[1]
    .split(',')
    .map((item) => item.trim())
    .filter((item) => item.length > 0);
}

// æ™ºèƒ½æ›´æ–°å¯¼å…¥è¯­å¥ (ä¿®å¤åŸè„šæœ¬ç¼ºé™·)
function updateImports(filePath, newConstants) {
  if (newConstants.length === 0) return false;

  const existingImports = getExistingImports(filePath);
  // ä½¿ç”¨Setå»é‡å¹¶æŒ‰å­—æ¯é¡ºåºæ’åº (CODEXå»ºè®®)
  const allConstants = [
    ...new Set([...existingImports, ...newConstants]),
  ].sort();

  let content = fs.readFileSync(filePath, 'utf8');

  // ç”Ÿæˆæ–°çš„å¯¼å…¥è¯­å¥
  const newImportStatement = `import { ${allConstants.join(', ')} } from '@/constants/magic-numbers';`;

  // ç§»é™¤æ—§çš„å¯¼å…¥
  content = content.replace(
    /import\s*{\s*[^}]+\s*}\s*from\s*['"]@\/constants\/magic-numbers['"];?\n?/g,
    '',
  );

  // æ™ºèƒ½æ·»åŠ æ–°å¯¼å…¥ (å¤„ç†å„ç§æ–‡ä»¶å¼€å¤´æƒ…å†µ)
  if (content.includes('import')) {
    // åœ¨ç¬¬ä¸€ä¸ªimportè¯­å¥åæ·»åŠ 
    const firstImportMatch = content.match(/^import .+;$/m);
    if (firstImportMatch) {
      content = content.replace(
        firstImportMatch[0],
        `${firstImportMatch[0]}\n${newImportStatement}`,
      );
    }
  } else {
    // åœ¨æ–‡ä»¶å¼€å¤´æ·»åŠ  (å¤„ç†æ²¡æœ‰å…¶ä»–importçš„æƒ…å†µ)
    content = `${newImportStatement}\n\n${content}`;
  }

  fs.writeFileSync(filePath, content);
  return true;
}

// é¢„æ£€æŸ¥éªŒè¯ (CODEXå»ºè®®)
function validateConstants(errors) {
  const availableConstants = getAvailableConstants();
  const missingConstants = errors.filter(
    (e) => !availableConstants.includes(e.constantName),
  );

  if (missingConstants.length > 0) {
    console.error('âŒ é¢„æ£€æŸ¥å¤±è´¥ï¼šä»¥ä¸‹å¸¸é‡æœªå®šä¹‰');
    missingConstants.forEach((c) => {
      console.error(`  - ${c.constantName} (ç”¨äºæ•°å­— ${c.number})`);
    });
    throw new Error(
      `ç¼ºå¤±å¸¸é‡å®šä¹‰: ${missingConstants.map((c) => c.constantName).join(', ')}`,
    );
  }

  console.log('âœ… é¢„æ£€æŸ¥é€šè¿‡ï¼šæ‰€æœ‰å¸¸é‡éƒ½å·²å®šä¹‰');
}

// å®‰å…¨çš„æ•°å­—æ›¿æ¢ (é˜²æ­¢ä¸Šä¸‹æ–‡é”™è¯¯)
function safeNumberReplace(content, number, constantName) {
  // æ„å»ºæ›´å®‰å…¨çš„æ­£åˆ™è¡¨è¾¾å¼
  let regex;

  if (number.startsWith('-')) {
    // è´Ÿæ•°å¤„ç†
    regex = new RegExp(`(?<!\\d)${number.replace('.', '\\.')}(?!\\d)`, 'g');
  } else if (number.startsWith('0x')) {
    // åå…­è¿›åˆ¶å¤„ç†
    regex = new RegExp(`\\b${number}\\b`, 'gi');
  } else {
    // æ™®é€šæ•°å­— - æ·»åŠ æ›´ä¸¥æ ¼çš„è¾¹ç•Œæ£€æŸ¥
    const escapedNumber = number.replace('.', '\\.');
    regex = new RegExp(`(?<![\\.\\d])\\b${escapedNumber}\\b(?![\\d])`, 'g');
  }

  // æ‰§è¡Œæ›¿æ¢å¹¶è®°å½•
  const newContent = content.replace(regex, constantName);
  const replacements = (content.match(regex) || []).length;

  if (replacements > 0) {
    console.log(`    æ›¿æ¢ ${number} â†’ ${constantName} (${replacements}æ¬¡)`);
  }

  return newContent;
}

// æ‰¹å¤„ç†ä¿®å¤ (CODEXå»ºè®®)
function processBatch(errors, batchSize = 5) {
  const fileGroups = new Map();

  // æŒ‰æ–‡ä»¶åˆ†ç»„é”™è¯¯
  for (const error of errors) {
    if (!fileGroups.has(error.file)) {
      fileGroups.set(error.file, []);
    }
    fileGroups.get(error.file).push(error);
  }

  const files = Array.from(fileGroups.keys());
  let processedFiles = 0;

  for (let i = 0; i < files.length; i += batchSize) {
    const batch = files.slice(i, i + batchSize);
    console.log(
      `\nğŸ“¦ å¤„ç†æ‰¹æ¬¡ ${Math.floor(i / batchSize) + 1}/${Math.ceil(files.length / batchSize)}`,
    );

    for (const filePath of batch) {
      try {
        const fileErrors = fileGroups.get(filePath);
        console.log(`ğŸ”§ å¤„ç†æ–‡ä»¶: ${path.relative(process.cwd(), filePath)}`);

        // æ›´æ–°å¯¼å…¥
        const constantNames = fileErrors.map((e) => e.constantName);
        updateImports(filePath, constantNames);

        // å®‰å…¨æ›¿æ¢æ•°å­—
        let content = fs.readFileSync(filePath, 'utf8');
        let modified = false;

        for (const error of fileErrors) {
          const newContent = safeNumberReplace(
            content,
            error.number,
            error.constantName,
          );
          if (newContent !== content) {
            content = newContent;
            modified = true;
          }
        }

        if (modified) {
          fs.writeFileSync(filePath, content);
        }

        processedFiles++;
      } catch (error) {
        console.error(`âŒ å¤„ç†æ–‡ä»¶å¤±è´¥ ${filePath}:`, error.message);
        throw error; // å¿«é€Ÿå¤±è´¥
      }
    }

    // æ‰¹æ¬¡éªŒè¯ (CODEXå»ºè®®)
    console.log('ğŸ” éªŒè¯æ‰¹æ¬¡ç»“æœ...');
    try {
      execSync('pnpm run type-check', { stdio: 'pipe' });
      console.log('âœ… æ‰¹æ¬¡éªŒè¯é€šè¿‡');
    } catch (error) {
      console.error('âŒ æ‰¹æ¬¡éªŒè¯å¤±è´¥ï¼Œåœæ­¢å¤„ç†');
      throw new Error('TypeScriptç¼–è¯‘é”™è¯¯ï¼Œè¯·æ£€æŸ¥ä¿®å¤ç»“æœ');
    }
  }

  return processedFiles;
}

// è·å–é­”æ³•æ•°å­—é”™è¯¯
function getMagicNumberErrors() {
  let output = '';
  try {
    output = execSync('pnpm lint:check 2>&1', {
      encoding: 'utf8',
      shell: true,
      maxBuffer: 1024 * 1024 * 10,
    });
  } catch (error) {
    output = error.stdout || error.output?.join('') || '';
  }

  const errors = [];
  const lines = output.split('\n');
  let currentFile = '';

  for (const line of lines) {
    // æ£€æŸ¥æ–‡ä»¶è·¯å¾„
    const fileMatch = line.match(/^\/.*\.(ts|tsx|js|jsx)$/);
    if (fileMatch) {
      currentFile = line.trim();
      continue;
    }

    // æ£€æŸ¥é­”æ³•æ•°å­—é”™è¯¯
    const errorMatch = line.match(
      /^\s*(\d+):(\d+)\s+(error|warning)\s+No magic number:\s+([-]?(?:0x)?[0-9a-fA-F.]+)\s+no-magic-numbers/,
    );

    if (errorMatch && currentFile) {
      const [, lineNum, colNum, severity, number] = errorMatch;
      const constantName = getConstantName(number);

      errors.push({
        file: currentFile,
        line: parseInt(lineNum),
        column: parseInt(colNum),
        number: number,
        constantName: constantName,
      });
    }
  }

  return errors;
}

// ç”Ÿæˆå¸¸é‡å
function getConstantName(number) {
  const constantMap = {
    0.5: 'MAGIC_0_5',
    0.9: 'MAGIC_0_9',
    1.5: 'MAGIC_1_5',
    131: 'MAGIC_131',
    132: 'MAGIC_132',
    133: 'MAGIC_133',
    136: 'MAGIC_136',
    190: 'MAGIC_190',
    368: 'MAGIC_368',
  };

  return (
    constantMap[number] ||
    `MAGIC_${number.replace(/[.-]/g, '_').replace(/^0x/, 'HEX_')}`
  );
}

// ä¸»å‡½æ•°
function main() {
  console.log('ğŸ” å®‰å…¨é­”æ³•æ•°å­—ä¿®å¤å™¨å¯åŠ¨...');

  const errors = getMagicNumberErrors();
  if (errors.length === 0) {
    console.log('âœ… æœªå‘ç°éœ€è¦ä¿®å¤çš„é­”æ³•æ•°å­—');
    return;
  }

  console.log(`ğŸ“Š å‘ç° ${errors.length} ä¸ªé­”æ³•æ•°å­—é”™è¯¯`);

  try {
    // é¢„æ£€æŸ¥éªŒè¯
    validateConstants(errors);

    // åˆ†æ‰¹å¤„ç†
    const processedFiles = processBatch(errors, 3); // å°æ‰¹æ¬¡ï¼Œæ›´å®‰å…¨

    console.log(`\nğŸ‰ ä¿®å¤å®Œæˆï¼å¤„ç†äº† ${processedFiles} ä¸ªæ–‡ä»¶`);

    // æœ€ç»ˆéªŒè¯
    console.log('ğŸ” æ‰§è¡Œæœ€ç»ˆéªŒè¯...');
    execSync('pnpm run type-check', { stdio: 'inherit' });

    const remainingErrors = getMagicNumberErrors().length;
    console.log(`ğŸ“Š å‰©ä½™é­”æ³•æ•°å­—é”™è¯¯: ${remainingErrors} ä¸ª`);
  } catch (error) {
    console.error('âŒ ä¿®å¤è¿‡ç¨‹ä¸­å‡ºç°é”™è¯¯:', error.message);
    console.log('\nğŸ’¡ å»ºè®®æ£€æŸ¥æœ€è¿‘çš„ä¿®æ”¹å¹¶è¿è¡Œ pnpm run type-check');
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

#!/usr/bin/env node

/**
 * Security Configuration Check Script
 *
 * This script validates the security configuration of the application
 * and provides recommendations for improvement.
 */

const fs = require('fs');
const path = require('path');

class SecurityChecker {
  constructor() {
    this.results = {
      passed: true,
      checks: [],
      warnings: [],
      recommendations: [],
    };
  }

  /**
   * Run all security checks
   */
  async runChecks() {
    console.log('ğŸ”’ Running security configuration checks...\n');

    this.checkEnvironmentVariables();
    this.checkSecurityHeaders();
    this.checkCSPConfiguration();
    this.checkDependencies();
    this.checkFilePermissions();
    this.checkGitIgnore();
    this.checkTurnstileConfiguration();

    this.generateReport();
  }

  /**
   * Check environment variables configuration
   */
  checkEnvironmentVariables() {
    console.log('ğŸ” Checking environment variables...');

    // Check .env.example exists
    this.addCheck(
      'Environment template exists',
      fs.existsSync('.env.example'),
      '.env.example file should exist as a template',
    );

    // Check .env files are not committed
    const envFiles = [
      '.env',
      '.env.local',
      '.env.production',
      '.env.development',
    ];
    envFiles.forEach((file) => {
      if (fs.existsSync(file)) {
        this.addWarning(
          `${file} exists - ensure it's not committed to version control`,
        );
      }
    });

    // Check env.mjs exists
    this.addCheck(
      'Environment validation exists',
      fs.existsSync('env.mjs'),
      'env.mjs file should exist for environment variable validation',
    );

    // Check for sensitive data in .env.example
    if (fs.existsSync('.env.example')) {
      const envExample = fs.readFileSync('.env.example', 'utf8');

      // Look for uncommented lines with sensitive values that don't look like placeholders
      const lines = envExample.split('\n');
      const sensitiveLines = lines.filter((line) => {
        // Skip comments
        if (line.trim().startsWith('#')) return false;

        // Check for sensitive keys with non-placeholder values
        const sensitivePattern = /(password|secret|key|token)=/i;
        const placeholderPattern =
          /(your_|test_|example_|placeholder|changeme|replace)/i;

        if (sensitivePattern.test(line)) {
          const value = line.split('=')[1]?.trim();
          if (value && !placeholderPattern.test(value) && value.length > 10) {
            return true;
          }
        }
        return false;
      });

      this.addCheck(
        'No sensitive data in .env.example',
        sensitiveLines.length === 0,
        '.env.example should not contain real sensitive data',
      );

      if (sensitiveLines.length > 0) {
        console.log(
          `     Found potentially sensitive lines: ${sensitiveLines.length}`,
        );
      }
    }
  }

  /**
   * Check security headers configuration
   */
  checkSecurityHeaders() {
    console.log('ğŸ›¡ï¸  Checking security headers...');

    // Check security config exists
    const securityConfigPath = 'src/config/security.ts';
    this.addCheck(
      'Security configuration exists',
      fs.existsSync(securityConfigPath),
      'Security configuration file should exist',
    );

    // Check next.config.ts for security headers
    if (fs.existsSync('next.config.ts')) {
      const nextConfig = fs.readFileSync('next.config.ts', 'utf8');

      this.addCheck(
        'Security headers configured',
        nextConfig.includes('getSecurityHeaders'),
        'next.config.ts should use getSecurityHeaders function',
      );

      this.addCheck(
        'CSP configured',
        nextConfig.includes('Content-Security-Policy') ||
          nextConfig.includes('getSecurityHeaders'),
        'Content Security Policy should be configured',
      );
    }
  }

  /**
   * Check CSP configuration
   */
  checkCSPConfiguration() {
    console.log('ğŸ” Checking CSP configuration...');

    // Check CSP report endpoint exists
    const cspReportPath = 'src/app/api/csp-report/route.ts';
    this.addCheck(
      'CSP report endpoint exists',
      fs.existsSync(cspReportPath),
      'CSP report endpoint should exist for violation reporting',
    );

    // Check middleware includes security
    if (fs.existsSync('middleware.ts')) {
      const middleware = fs.readFileSync('middleware.ts', 'utf8');

      this.addCheck(
        'Middleware includes security',
        middleware.includes('getSecurityHeaders') ||
          middleware.includes('generateNonce'),
        'Middleware should include security headers',
      );
    }
  }

  /**
   * Check dependencies for security vulnerabilities
   */
  checkDependencies() {
    console.log('ğŸ“¦ Checking dependencies...');

    // Check if security-related packages are installed
    if (fs.existsSync('package.json')) {
      const packageJson = JSON.parse(fs.readFileSync('package.json', 'utf8'));
      const dependencies = {
        ...packageJson.dependencies,
        ...packageJson.devDependencies,
      };

      this.addCheck(
        'Environment validation package installed',
        '@t3-oss/env-nextjs' in dependencies,
        '@t3-oss/env-nextjs should be installed for environment validation',
      );

      this.addCheck(
        'Bot protection package installed',
        '@marsidev/react-turnstile' in dependencies,
        'Turnstile package should be installed for bot protection',
      );

      this.addCheck(
        'Security linting installed',
        'eslint-plugin-security' in dependencies,
        'Security ESLint plugin should be installed',
      );

      // Check for known vulnerable packages (basic check)
      const vulnerablePackages = ['lodash', 'moment', 'request'];
      vulnerablePackages.forEach((pkg) => {
        if (pkg in dependencies) {
          this.addWarning(
            `${pkg} is installed - consider using modern alternatives`,
          );
        }
      });
    }
  }

  /**
   * Check file permissions and sensitive files
   */
  checkFilePermissions() {
    console.log('ğŸ“ Checking file permissions...');

    // Check for sensitive files that shouldn't exist
    const sensitiveFiles = [
      '.env',
      '.env.local',
      '.env.production',
      'private.key',
      'server.key',
      'id_rsa',
      'id_dsa',
    ];

    sensitiveFiles.forEach((file) => {
      if (fs.existsSync(file)) {
        this.addWarning(
          `Sensitive file ${file} exists - ensure it's properly secured`,
        );
      }
    });
  }

  /**
   * Check .gitignore configuration
   */
  checkGitIgnore() {
    console.log('ğŸ“ Checking .gitignore...');

    if (fs.existsSync('.gitignore')) {
      const gitignore = fs.readFileSync('.gitignore', 'utf8');

      const requiredEntries = [
        { pattern: '.env', alternatives: ['.env', '.env*'] },
        { pattern: '.env.local', alternatives: ['.env.local', '.env*'] },
        { pattern: '.env.*.local', alternatives: ['.env.*.local', '.env*'] },
        {
          pattern: 'node_modules',
          alternatives: ['node_modules', '/node_modules'],
        },
      ];

      requiredEntries.forEach(({ pattern, alternatives }) => {
        const isIncluded = alternatives.some((alt) => gitignore.includes(alt));
        this.addCheck(
          `.gitignore includes ${pattern}`,
          isIncluded,
          `${pattern} should be in .gitignore`,
        );
      });
    } else {
      this.addCheck('.gitignore exists', false, '.gitignore file should exist');
    }
  }

  /**
   * Check Turnstile configuration
   */
  checkTurnstileConfiguration() {
    console.log('ğŸ¤– Checking bot protection...');

    // Check Turnstile component exists
    const turnstilePath = 'src/components/security/turnstile.tsx';
    this.addCheck(
      'Turnstile component exists',
      fs.existsSync(turnstilePath),
      'Turnstile component should exist for bot protection',
    );

    // Check Turnstile verification endpoint exists
    const verifyPath = 'src/app/api/verify-turnstile/route.ts';
    this.addCheck(
      'Turnstile verification endpoint exists',
      fs.existsSync(verifyPath),
      'Turnstile verification endpoint should exist',
    );
  }

  /**
   * Add a check result
   */
  addCheck(name, passed, message) {
    this.results.checks.push({
      name,
      passed,
      message,
    });

    if (!passed) {
      this.results.passed = false;
    }

    const status = passed ? 'âœ…' : 'âŒ';
    console.log(`  ${status} ${name}`);
    if (!passed) {
      console.log(`     ${message}`);
    }
  }

  /**
   * Add a warning
   */
  addWarning(message) {
    this.results.warnings.push(message);
    console.log(`  âš ï¸  ${message}`);
  }

  /**
   * Add a recommendation
   */
  addRecommendation(message) {
    this.results.recommendations.push(message);
  }

  /**
   * Generate final report
   */
  generateReport() {
    console.log('\nğŸ“Š Security Check Report');
    console.log('========================');

    const totalChecks = this.results.checks.length;
    const passedChecks = this.results.checks.filter((c) => c.passed).length;
    const failedChecks = totalChecks - passedChecks;

    console.log(`Total checks: ${totalChecks}`);
    console.log(`Passed: ${passedChecks}`);
    console.log(`Failed: ${failedChecks}`);
    console.log(`Warnings: ${this.results.warnings.length}`);

    if (this.results.passed) {
      console.log('\nğŸ‰ All security checks passed!');
    } else {
      console.log(
        '\nâš ï¸  Some security checks failed. Please review and fix the issues above.',
      );
    }

    // Add general recommendations
    this.addRecommendation(
      'Regularly update dependencies to patch security vulnerabilities',
    );
    this.addRecommendation('Use strong, unique secrets in production');
    this.addRecommendation('Enable security monitoring and alerting');
    this.addRecommendation(
      'Regularly review and update security configurations',
    );

    if (this.results.recommendations.length > 0) {
      console.log('\nğŸ’¡ Recommendations:');
      this.results.recommendations.forEach((rec) => {
        console.log(`  â€¢ ${rec}`);
      });
    }

    // Save report to file
    const reportPath = 'reports/security-check-report.json';
    const reportsDir = path.dirname(reportPath);

    if (!fs.existsSync(reportsDir)) {
      fs.mkdirSync(reportsDir, { recursive: true });
    }

    fs.writeFileSync(reportPath, JSON.stringify(this.results, null, 2));
    console.log(`\nğŸ“„ Report saved to ${reportPath}`);

    // Exit with appropriate code
    process.exit(this.results.passed ? 0 : 1);
  }
}

// Run the security check
if (require.main === module) {
  const checker = new SecurityChecker();
  checker.runChecks().catch((error) => {
    console.error('Error running security checks:', error);
    process.exit(1);
  });
}

module.exports = SecurityChecker;

#!/usr/bin/env node

/**
 * æµ‹è¯•æœåŠ¡å™¨å¯åŠ¨è„šæœ¬
 *
 * ä¸“é—¨ä¸º E2E æµ‹è¯•å¯åŠ¨å¼€å‘æœåŠ¡å™¨ï¼Œç¡®ä¿ï¼š
 * 1. React Scan è¢«ç¦ç”¨ä»¥é¿å…æµ‹è¯•å¹²æ‰°
 * 2. å…¶ä»–å¼€å‘å·¥å…·è¢«é€‚å½“é…ç½®
 * 3. æµ‹è¯•ç¯å¢ƒå˜é‡è¢«æ­£ç¡®è®¾ç½®
 */

const { spawn } = require('child_process');
const fs = require('fs');
const path = require('path');

class TestServerManager {
  constructor() {
    this.serverProcess = null;
    this.isShuttingDown = false;
  }

  /**
   * è®¾ç½®æµ‹è¯•ç¯å¢ƒå˜é‡
   */
  setupTestEnvironment() {
    console.log('ğŸ§ª Setting up test environment...');

    // è®¾ç½®æµ‹è¯•ç¯å¢ƒå˜é‡
    process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN = 'true';
    process.env.NEXT_PUBLIC_DISABLE_DEV_TOOLS = 'true';
    process.env.NEXT_PUBLIC_TEST_MODE = 'true';
    process.env.PLAYWRIGHT_TEST = 'true';

    console.log('   âœ… NEXT_PUBLIC_DISABLE_REACT_SCAN=true');
    console.log('   âœ… NEXT_PUBLIC_DISABLE_DEV_TOOLS=true');
    console.log('   âœ… NEXT_PUBLIC_TEST_MODE=true');
    console.log('   âœ… PLAYWRIGHT_TEST=true');
  }

  /**
   * å¯åŠ¨å¼€å‘æœåŠ¡å™¨
   */
  async startServer() {
    return new Promise((resolve, reject) => {
      console.log('ğŸš€ Starting test server...');

      this.setupTestEnvironment();

      // å¯åŠ¨ Next.js å¼€å‘æœåŠ¡å™¨
      this.serverProcess = spawn('pnpm', ['dev'], {
        stdio: ['pipe', 'pipe', 'pipe'],
        env: {
          ...process.env,
          // ç¡®ä¿æµ‹è¯•ç¯å¢ƒå˜é‡è¢«ä¼ é€’
          NEXT_PUBLIC_DISABLE_REACT_SCAN: 'true',
          NEXT_PUBLIC_TEST_MODE: 'true',
        },
      });

      let serverReady = false;
      let output = '';

      this.serverProcess.stdout.on('data', (data) => {
        const text = data.toString();
        output += text;

        // æ£€æŸ¥æœåŠ¡å™¨æ˜¯å¦å‡†å¤‡å°±ç»ª
        if (text.includes('Ready') || text.includes('localhost:3000')) {
          if (!serverReady) {
            serverReady = true;
            console.log('âœ… Test server is ready');
            resolve(this.serverProcess);
          }
        }

        // è¾“å‡ºæœåŠ¡å™¨æ—¥å¿—ï¼ˆè¿‡æ»¤æ‰å™ªéŸ³ï¼‰
        if (!text.includes('webpack') && !text.includes('Compiled')) {
          process.stdout.write(text);
        }
      });

      this.serverProcess.stderr.on('data', (data) => {
        const text = data.toString();

        // è¿‡æ»¤æ‰å·²çŸ¥çš„æ— å®³è­¦å‘Š
        if (
          !text.includes('ExperimentalWarning') &&
          !text.includes('punycode') &&
          !text.includes('deprecated')
        ) {
          process.stderr.write(text);
        }
      });

      this.serverProcess.on('error', (error) => {
        console.error('âŒ Failed to start test server:', error);
        reject(error);
      });

      this.serverProcess.on('exit', (code) => {
        if (!this.isShuttingDown) {
          console.log(`ğŸ”„ Test server exited with code ${code}`);
        }
      });

      // è¶…æ—¶å¤„ç†
      setTimeout(() => {
        if (!serverReady) {
          console.error('âŒ Test server failed to start within timeout');
          reject(new Error('Server startup timeout'));
        }
      }, 30000); // 30ç§’è¶…æ—¶
    });
  }

  /**
   * åœæ­¢æœåŠ¡å™¨
   */
  async stopServer() {
    if (this.serverProcess && !this.isShuttingDown) {
      console.log('ğŸ›‘ Stopping test server...');
      this.isShuttingDown = true;

      this.serverProcess.kill('SIGTERM');

      // ç­‰å¾…è¿›ç¨‹ç»“æŸ
      await new Promise((resolve) => {
        this.serverProcess.on('exit', resolve);

        // å¼ºåˆ¶ç»“æŸè¶…æ—¶
        setTimeout(() => {
          if (this.serverProcess) {
            this.serverProcess.kill('SIGKILL');
          }
          resolve(null);
        }, 5000);
      });

      console.log('âœ… Test server stopped');
    }
  }

  /**
   * éªŒè¯æœåŠ¡å™¨çŠ¶æ€
   */
  async verifyServer(url = 'http://localhost:3000') {
    console.log(`ğŸ” Verifying server at ${url}...`);

    try {
      const response = await fetch(url);
      const isHealthy = response.ok;

      if (isHealthy) {
        console.log('âœ… Server is healthy');

        // æ£€æŸ¥å“åº”ä¸­æ˜¯å¦åŒ…å« React Scan ç›¸å…³å†…å®¹
        const html = await response.text();
        const hasReactScan =
          html.includes('react-scan') || html.includes('React Scan');

        if (hasReactScan) {
          console.warn('âš ï¸  Server response contains React Scan content');
          return { healthy: true, hasInterference: true };
        }
        console.log('âœ… Server response is clean (no React Scan content)');
        return { healthy: true, hasInterference: false };
      }
      console.error(`âŒ Server returned status ${response.status}`);
      return { healthy: false, hasInterference: false };
    } catch (error) {
      console.error('âŒ Server verification failed:', error.message);
      return { healthy: false, hasInterference: false };
    }
  }

  /**
   * å®Œæ•´çš„æµ‹è¯•æœåŠ¡å™¨ç”Ÿå‘½å‘¨æœŸç®¡ç†
   */
  async runWithServer(testFunction) {
    try {
      await this.startServer();

      // ç­‰å¾…æœåŠ¡å™¨å®Œå…¨å¯åŠ¨
      await new Promise((resolve) => setTimeout(resolve, 3000));

      // éªŒè¯æœåŠ¡å™¨çŠ¶æ€
      const status = await this.verifyServer();
      if (!status.healthy) {
        throw new Error('Server is not healthy');
      }

      if (status.hasInterference) {
        console.warn(
          'âš ï¸  Detected potential interference, but proceeding with tests',
        );
      }

      // è¿è¡Œæµ‹è¯•å‡½æ•°
      await testFunction();
    } finally {
      await this.stopServer();
    }
  }
}

// å¦‚æœç›´æ¥è¿è¡Œæ­¤è„šæœ¬
if (require.main === module) {
  const manager = new TestServerManager();

  // å¤„ç†é€€å‡ºä¿¡å·
  process.on('SIGINT', async () => {
    console.log('\nğŸ›‘ Received SIGINT, shutting down...');
    await manager.stopServer();
    process.exit(0);
  });

  process.on('SIGTERM', async () => {
    console.log('\nğŸ›‘ Received SIGTERM, shutting down...');
    await manager.stopServer();
    process.exit(0);
  });

  // å¯åŠ¨æœåŠ¡å™¨å¹¶ä¿æŒè¿è¡Œ
  manager
    .startServer()
    .then(() => {
      console.log('ğŸ¯ Test server is running. Press Ctrl+C to stop.');
    })
    .catch((error) => {
      console.error('âŒ Failed to start test server:', error);
      process.exit(1);
    });
}

module.exports = TestServerManager;

#!/usr/bin/env node
/*
 * Summarize ESLint JSON output (reports/eslint-report.json)
 * Outputs a concise JSON summary to stdout for the CLI to capture.
 */

const fs = require('fs');
const path = require('path');

const reportPath = path.resolve(process.cwd(), 'reports/eslint-report.json');
if (!fs.existsSync(reportPath)) {
  console.error('Missing reports/eslint-report.json');
  process.exit(1);
}

/** @type {Array<{filePath:string,errorCount:number,warningCount:number,fixableErrorCount:number,fixableWarningCount:number,source?:string,messages:Array<{ruleId:string|null,severity:number,message:string,line:number,column:number}>>}> */
const files = JSON.parse(fs.readFileSync(reportPath, 'utf8'));

const total = { errors: 0, warnings: 0 };
const byRule = new Map();
const messageSamples = new Map();

for (const f of files) {
  total.errors += f.errorCount || 0;
  total.warnings += f.warningCount || 0;
  for (const m of f.messages) {
    const key = m.ruleId || 'unknown';
    const entry = byRule.get(key) || { errors: 0, warnings: 0 };
    if (m.severity === 2) entry.errors += 1;
    else entry.warnings += 1;
    byRule.set(key, entry);
    if (!messageSamples.has(key) && m.message)
      messageSamples.set(key, m.message);
  }
}

// Helpers
const get = (ruleId) => byRule.get(ruleId) || { errors: 0, warnings: 0 };
const countMsgIncludes = (substr, filterRulePrefix = null) => {
  let n = 0;
  for (const f of files) {
    for (const m of f.messages) {
      if (filterRulePrefix && !(m.ruleId || '').startsWith(filterRulePrefix))
        continue;
      if ((m.message || '').includes(substr)) n++;
    }
  }
  return n;
};

// Category 1: any ç±»å‹
const anyRule = '@typescript-eslint/no-explicit-any';
const anyStats = get(anyRule);

// Category 2: å®‰å…¨ç›¸å…³ï¼ˆsecurity*ï¼‰
let securityErrors = 0,
  securityWarnings = 0;
for (const [ruleId, stat] of byRule.entries()) {
  if (ruleId.startsWith('security/') || ruleId.startsWith('security-node/')) {
    securityErrors += stat.errors;
    securityWarnings += stat.warnings;
  }
}
const securityTotal = securityErrors + securityWarnings;

// Specific security message splits (best-effort by message text)
const genericObjectInjection = countMsgIncludes(
  'Generic Object Injection Sink',
);
const variableAssignedToInjection = countMsgIncludes(
  'Variable Assigned to Object Injection Sink',
);

// Category 3: console
const consoleStats = get('no-console');

// Category 4: é­”æ³•æ•°å­—
const magicStats = get('no-magic-numbers');
// Breakdown by typical numbers (parse from message text)
const magicNumbers = new Map();
for (const f of files) {
  for (const m of f.messages) {
    if (m.ruleId === 'no-magic-numbers') {
      const match = m.message.match(/No magic number:?\s*(-?\d+)/i);
      if (match) {
        const num = match[1];
        magicNumbers.set(num, (magicNumbers.get(num) || 0) + 1);
      }
    }
  }
}
// sort top items
const magicBreakdown = Array.from(magicNumbers.entries())
  .sort((a, b) => b[1] - a[1])
  .slice(0, 12)
  .map(([num, cnt]) => ({ number: Number(num), count: cnt }));

// Category 5: å¤æ‚åº¦
const complexityStats = {
  complexity: get('complexity'),
  maxDepth: get('max-depth'),
  maxNestedCallbacks: get('max-nested-callbacks'),
  noElseReturn: get('no-else-return'),
  noPlusPlus: get('no-plusplus'),
};

// Category 6: æœªä½¿ç”¨å˜é‡
const unusedCombined = {
  js: get('no-unused-vars'),
  ts: get('@typescript-eslint/no-unused-vars'),
};
// Extract common variable names
const unusedNameCounts = new Map();
for (const f of files) {
  for (const m of f.messages) {
    if (
      m.ruleId === 'no-unused-vars' ||
      m.ruleId === '@typescript-eslint/no-unused-vars'
    ) {
      const mm = m.message.match(/'([^']+)' is defined but never used/);
      if (mm) {
        const name = mm[1];
        unusedNameCounts.set(name, (unusedNameCounts.get(name) || 0) + 1);
      }
    }
  }
}
const unusedTop = Array.from(unusedNameCounts.entries())
  .sort((a, b) => b[1] - a[1])
  .slice(0, 10)
  .map(([name, count]) => ({ name, count }));

// Category 7: å¼€å‘å·¥å…·ç›¸å…³
const tsNoCheck = get('@typescript-eslint/ban-ts-comment');
const tNotDefined = (() => {
  let n = 0;
  for (const f of files) {
    for (const m of f.messages) {
      if (
        m.ruleId === 'no-undef' &&
        (m.message || '').includes("'t' is not defined")
      )
        n++;
    }
  }
  return n;
})();
const nextImg = get('@next/next/no-img-element');

const summary = {
  timestamp: new Date().toISOString(),
  totals: {
    errors: total.errors,
    warnings: total.warnings,
    total: total.errors + total.warnings,
  },
  anyType: { rule: anyRule, ...anyStats },
  security: {
    total: securityTotal,
    errors: securityErrors,
    warnings: securityWarnings,
    breakdown: {
      genericObjectInjection,
      variableAssignedToInjection,
    },
  },
  console: consoleStats,
  magicNumbers: {
    total: magicStats.errors + magicStats.warnings,
    top: magicBreakdown,
  },
  complexity: {
    complexity: complexityStats.complexity,
    maxDepth: complexityStats.maxDepth,
    maxNestedCallbacks: complexityStats.maxNestedCallbacks,
    noElseReturn: complexityStats.noElseReturn,
    noPlusPlus: complexityStats.noPlusPlus,
  },
  unused: {
    total:
      unusedCombined.js.errors +
      unusedCombined.js.warnings +
      unusedCombined.ts.errors +
      unusedCombined.ts.warnings,
    js: unusedCombined.js,
    ts: unusedCombined.ts,
    topNames: unusedTop,
  },
  devTools: {
    tsNoCheck: tsNoCheck,
    tNotDefinedCount: tNotDefined,
    nextImg: nextImg,
  },
};

console.log(JSON.stringify(summary, null, 2));

#!/usr/bin/env tsx
/**
 * ESLinté”™è¯¯ç»Ÿè®¡åˆ†æå·¥å…·
 * è¯»å–ESLint JSONè¾“å‡ºï¼ŒæŒ‰è§„åˆ™èšåˆè®¡æ•°ä¸æ–‡ä»¶åˆ†å¸ƒ
 */
import { readFileSync } from 'fs';
import { join } from 'path';

interface ESLintMessage {
  ruleId: string | null;
  severity: number;
  message: string;
  line: number;
  column: number;
  nodeType?: string;
  messageId?: string;
  endLine?: number;
  endColumn?: number;
}

interface ESLintResult {
  filePath: string;
  messages: ESLintMessage[];
  errorCount: number;
  warningCount: number;
  fixableErrorCount: number;
  fixableWarningCount: number;
  source?: string;
}

interface RuleSummary {
  ruleId: string;
  errorCount: number;
  warningCount: number;
  totalCount: number;
  files: Map<string, number>;
}

function main() {
  try {
    const eslintJsonPath = join(process.cwd(), '.tmp/eslint.json');
    const eslintData: ESLintResult[] = JSON.parse(
      readFileSync(eslintJsonPath, 'utf-8'),
    );

    console.log('ğŸ” ESLinté”™è¯¯ç»Ÿè®¡åˆ†ææŠ¥å‘Š');
    console.log('='.repeat(50));

    // ç»Ÿè®¡æ€»ä½“æƒ…å†µ
    const totalFiles = eslintData.length;
    const filesWithIssues = eslintData.filter(
      (result) => result.messages.length > 0,
    ).length;
    const totalErrors = eslintData.reduce(
      (sum, result) => sum + result.errorCount,
      0,
    );
    const totalWarnings = eslintData.reduce(
      (sum, result) => sum + result.warningCount,
      0,
    );

    console.log(`ğŸ“Š æ€»ä½“ç»Ÿè®¡:`);
    console.log(`  æ£€æŸ¥æ–‡ä»¶: ${totalFiles}`);
    console.log(`  æœ‰é—®é¢˜æ–‡ä»¶: ${filesWithIssues}`);
    console.log(`  é”™è¯¯æ€»æ•°: ${totalErrors}`);
    console.log(`  è­¦å‘Šæ€»æ•°: ${totalWarnings}`);
    console.log(`  é—®é¢˜æ€»è®¡: ${totalErrors + totalWarnings}`);
    console.log();

    // æŒ‰è§„åˆ™èšåˆ
    const ruleSummaries = new Map<string, RuleSummary>();

    for (const result of eslintData) {
      for (const message of result.messages) {
        const ruleId = message.ruleId || 'no-rule';

        if (!ruleSummaries.has(ruleId)) {
          ruleSummaries.set(ruleId, {
            ruleId,
            errorCount: 0,
            warningCount: 0,
            totalCount: 0,
            files: new Map(),
          });
        }

        const summary = ruleSummaries.get(ruleId)!;

        if (message.severity === 2) {
          summary.errorCount++;
        } else {
          summary.warningCount++;
        }
        summary.totalCount++;

        // ç»Ÿè®¡æ–‡ä»¶åˆ†å¸ƒ
        const fileName = result.filePath
          .replace(process.cwd(), '')
          .replace(/^\//, '');
        const currentCount = summary.files.get(fileName) || 0;
        summary.files.set(fileName, currentCount + 1);
      }
    }

    // æŒ‰æ€»æ•°æ’åº
    const sortedRules = Array.from(ruleSummaries.values()).sort(
      (a, b) => b.totalCount - a.totalCount,
    );

    console.log(`ğŸ”¥ Top 10 ESLintè§„åˆ™ (æŒ‰é—®é¢˜æ•°é‡æ’åº):`);
    console.log('-'.repeat(80));
    console.log(
      `${'è§„åˆ™åç§°'.padEnd(40) + 'é”™è¯¯'.padEnd(8) + 'è­¦å‘Š'.padEnd(8)}æ€»è®¡`,
    );
    console.log('-'.repeat(80));

    for (const rule of sortedRules.slice(0, 10)) {
      console.log(
        rule.ruleId.padEnd(40) +
