#!/usr/bin/env node

/**
 * Export * è½¬æ¢ Codemod è„šæœ¬
 *
 * åŠŸèƒ½ï¼š
 * - è‡ªåŠ¨å°† export * è½¬æ¢ä¸ºå‘½åå¯¼å‡º
 * - ä¿æŒåŠŸèƒ½ç­‰ä»·æ€§
 * - æ”¯æŒæ‰¹é‡å¤„ç†å’Œå¢é‡è½¬æ¢
 * - ç”Ÿæˆè½¬æ¢æŠ¥å‘Š
 * - æ”¯æŒå›æ»šæ“ä½œ
 */

const fs = require('fs');
const path = require('path');
const { parse } = require('@babel/parser');
const traverse = require('@babel/traverse').default;
const generate = require('@babel/generator').default;
const glob = require('glob');

// è½¬æ¢é…ç½®
const TRANSFORM_CONFIG = {
  // æ‰«ææ¨¡å¼
  scanPatterns: [
    'src/**/*.{ts,tsx,js,jsx}',
    '!src/**/*.{test,spec}.{ts,tsx,js,jsx}',
    '!src/**/*.d.ts',
  ],

  // è¾“å‡ºç›®å½•
  outputDir: path.join(process.cwd(), 'reports', 'transforms'),

  // å¤‡ä»½ç›®å½•
  backupDir: path.join(process.cwd(), 'backups', 'barrel-exports'),

  // è§£æå™¨é€‰é¡¹
  parserOptions: {
    sourceType: 'module',
    allowImportExportEverywhere: true,
    allowReturnOutsideFunction: true,
    plugins: [
      'typescript',
      'jsx',
      'decorators-legacy',
      'classProperties',
      'objectRestSpread',
      'asyncGenerators',
      'functionBind',
      'exportDefaultFrom',
      'exportNamespaceFrom',
      'dynamicImport',
    ],
  },

  // è½¬æ¢é€‰é¡¹
  transformOptions: {
    createBackup: true,
    dryRun: false,
    verbose: true,
    preserveComments: true,
  },
};

class BarrelExportTransformer {
  constructor(options = {}) {
    this.options = { ...TRANSFORM_CONFIG.transformOptions, ...options };
    this.outputDir = TRANSFORM_CONFIG.outputDir;
    this.backupDir = TRANSFORM_CONFIG.backupDir;

    // ç¡®ä¿transformStatsåœ¨æ‰€æœ‰æƒ…å†µä¸‹éƒ½æ­£ç¡®åˆå§‹åŒ–
    this.initializeStats();

    this.ensureDirectories();
  }

  ensureDirectories() {
    [this.outputDir, this.backupDir].forEach((dir) => {
      if (!fs.existsSync(dir)) {
        fs.mkdirSync(dir, { recursive: true });
      }
    });
  }

  /**
   * åˆå§‹åŒ–ç»Ÿè®¡ä¿¡æ¯
   */
  initializeStats() {
    this.transformStats = {
      filesProcessed: 0,
      filesTransformed: 0,
      exportStarRemoved: 0,
      namedExportsAdded: 0,
      errors: [],
    };
  }

  /**
   * åˆ†ææ–‡ä»¶ä¸­çš„export *è¯­å¥
   */
  analyzeExportStar(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const ast = parse(content, TRANSFORM_CONFIG.parserOptions);

      const exportStarNodes = [];
      const existingExports = new Set();

      traverse(ast, {
        ExportAllDeclaration(nodePath) {
          exportStarNodes.push({
            node: nodePath.node,
            source: nodePath.node.source.value,
            line: nodePath.node.loc?.start.line,
          });
        },
        ExportNamedDeclaration(nodePath) {
          if (nodePath.node.specifiers) {
            nodePath.node.specifiers.forEach((spec) => {
              if (spec.exported) {
                existingExports.add(spec.exported.name);
              }
            });
          }
        },
      });

      return { exportStarNodes, existingExports, ast, content };
    } catch (error) {
      // è®°å½•é”™è¯¯ä½†ä¸è¦åœ¨å¤„ç†ä¸­é‡ç½®ç»Ÿè®¡å¯¹è±¡ï¼Œé¿å…å¼•ç”¨ä¸¢å¤±
      (
        this.transformStats ||
        (this.transformStats = {
          filesProcessed: 0,
          filesTransformed: 0,
          exportStarRemoved: 0,
          namedExportsAdded: 0,
          errors: [],
        })
      ).errors.push({
        file: filePath,
        error: error.message,
        type: 'parse_error',
      });

      console.error(`âŒ è§£æå¤±è´¥: ${filePath} - ${error.message}`);
      return null;
    }
  }

  /**
   * è§£æç›®æ ‡æ–‡ä»¶çš„å¯¼å‡º
   */
  async resolveExportsFromModule(modulePath, currentFilePath) {
    try {
      // è§£æç›¸å¯¹è·¯å¾„
      const resolvedPath = this.resolveModulePath(modulePath, currentFilePath);

      if (!resolvedPath || !fs.existsSync(resolvedPath)) {
        return [];
      }

      const content = fs.readFileSync(resolvedPath, 'utf8');
      const ast = parse(content, TRANSFORM_CONFIG.parserOptions);

      const exports = [];

      traverse(ast, {
        ExportNamedDeclaration(nodePath) {
          const { node } = nodePath;

          // å¤„ç† export { a, b }
          if (node.specifiers && node.specifiers.length > 0) {
            node.specifiers.forEach((spec) => {
              if (spec.exported) {
                exports.push(spec.exported.name);
              }
            });
          }

          // å¤„ç† export const/function/class
          if (node.declaration) {
            if (node.declaration.id) {
              exports.push(node.declaration.id.name);
            } else if (node.declaration.declarations) {
              node.declaration.declarations.forEach((decl) => {
                if (decl.id && decl.id.name) {
                  exports.push(decl.id.name);
                }
              });
            }
          }
        },

        ExportDefaultDeclaration(nodePath) {
          exports.push('default');
        },
      });

      return exports;
    } catch (error) {
      if (this.options.verbose) {
        console.warn(`âš ï¸ æ— æ³•è§£ææ¨¡å—å¯¼å‡º: ${modulePath} - ${error.message}`);
      }
      return [];
    }
  }

  /**
   * è§£ææ¨¡å—è·¯å¾„
   */
  resolveModulePath(modulePath, currentFilePath) {
    if (!modulePath.startsWith('.')) {
      return null; // è·³è¿‡ node_modules
    }

    const currentDir = path.dirname(currentFilePath);
    const resolvedPath = path.resolve(currentDir, modulePath);

    // å°è¯•ä¸åŒçš„æ‰©å±•å
    const extensions = [
      '.ts',
      '.tsx',
      '.js',
      '.jsx',
      '/index.ts',
      '/index.tsx',
      '/index.js',
      '/index.jsx',
    ];

    for (const ext of extensions) {
      const testPath = resolvedPath + ext;
      if (fs.existsSync(testPath)) {
        return testPath;
      }
    }

    return null;
  }

  /**
   * è½¬æ¢å•ä¸ªæ–‡ä»¶
   */
  async transformFile(filePath) {
    try {
      if (this.options.verbose) {
        console.log(`ğŸ”„ å¤„ç†æ–‡ä»¶: ${filePath}`);
      }

      // æ•è·ç¨³å®šå¼•ç”¨ï¼Œé¿å…åœ¨ traverse å›è°ƒä¸­ä¸¢å¤± this ä¸Šä¸‹æ–‡
      const stats =
        this.transformStats ||
        (this.transformStats = {
          filesProcessed: 0,
          filesTransformed: 0,
          exportStarRemoved: 0,
          namedExportsAdded: 0,
          errors: [],
        });

      stats.filesProcessed++;

      const analysis = this.analyzeExportStar(filePath);
      if (!analysis || analysis.exportStarNodes.length === 0) {
        return false; // æ²¡æœ‰éœ€è¦è½¬æ¢çš„å†…å®¹
      }

      const { exportStarNodes, existingExports, ast, content } = analysis;
      let hasChanges = false;

      // åˆ›å»ºå¤‡ä»½
      if (this.options.createBackup && !this.options.dryRun) {
        this.createBackup(filePath, content);
      }

      // å¤„ç†æ¯ä¸ª export * è¯­å¥
      for (const exportInfo of exportStarNodes) {
        try {
          const { node, source } = exportInfo;

          // è·å–ç›®æ ‡æ¨¡å—çš„å¯¼å‡º
          const moduleExports = await this.resolveExportsFromModule(
            source,
            filePath,
          );

          if (moduleExports.length === 0) {
            if (this.options.verbose) {
              console.warn(`âš ï¸ æ— æ³•è§£ææ¨¡å—å¯¼å‡º: ${source}`);
            }
            continue;
          }

          // è¿‡æ»¤æ‰å·²å­˜åœ¨çš„å¯¼å‡º
          const newExports = moduleExports.filter(
            (exp) => !existingExports.has(exp),
          );

          if (newExports.length === 0) {
            continue;
          }

          // è½¬æ¢ AST
          traverse(ast, {
            // ä½¿ç”¨ç®­å¤´å‡½æ•°ä»¥æ•è·å¤–å±‚ä½œç”¨åŸŸï¼Œé¿å… this å˜æ›´
            ExportAllDeclaration: (nodePath) => {
              if (nodePath.node === node) {
                // åˆ›å»ºå‘½åå¯¼å‡ºèŠ‚ç‚¹
                const specifiers = newExports.map((exportName) => {
                  return {
                    type: 'ExportSpecifier',
                    local: { type: 'Identifier', name: exportName },
                    exported: { type: 'Identifier', name: exportName },
                  };
                });

                const namedExportNode = {
                  type: 'ExportNamedDeclaration',
                  declaration: null,
                  specifiers: specifiers,
                  source: node.source,
                };

                // æ›¿æ¢èŠ‚ç‚¹
                nodePath.replaceWith(namedExportNode);
                hasChanges = true;

                // ç»Ÿè®¡ä¿¡æ¯æ›´æ–°ä½¿ç”¨ç¨³å®šå¼•ç”¨
                stats.exportStarRemoved++;
                stats.namedExportsAdded += newExports.length;
              }
            },
          });
        } catch (exportError) {
          // å¤„ç†å•ä¸ª export å¤„ç†é”™è¯¯ï¼ˆä¸é‡ç½®ç»Ÿè®¡å¯¹è±¡ï¼‰
          stats.errors.push({
            file: filePath,
            error: `Export processing error: ${exportError.message}`,
            type: 'export_processing_error',
          });

          if (this.options.verbose) {
            console.warn(
              `âš ï¸ å¤„ç†exportæ—¶å‡ºé”™: ${filePath} - ${exportError.message}`,
            );
          }
          continue; // ç»§ç»­å¤„ç†å…¶ä»–export
        }
      }

      // ç”Ÿæˆæ–°ä»£ç 
      if (hasChanges && !this.options.dryRun) {
        const result = generate(ast, {
          retainLines: true,
          comments: this.options.preserveComments,
        });

        fs.writeFileSync(filePath, result.code);

        stats.filesTransformed++;

        if (this.options.verbose) {
          console.log(`âœ… å·²è½¬æ¢: ${filePath}`);
        }
      }

      return hasChanges;
    } catch (error) {
      // æ•´ä¸ªæ–¹æ³•çš„é”™è¯¯å¤„ç†ï¼ˆä¸é‡ç½®ç»Ÿè®¡å¯¹è±¡ï¼‰
      const stats =
        this.transformStats ||
        (this.transformStats = {
          filesProcessed: 0,
          filesTransformed: 0,
          exportStarRemoved: 0,
          namedExportsAdded: 0,
          errors: [],
        });

      stats.errors.push({
        file: filePath,
        error: `Transform file error: ${error.message}`,
        type: 'transform_file_error',
      });

      if (this.options.verbose) {
        console.error(`âŒ è½¬æ¢æ–‡ä»¶å¤±è´¥: ${filePath} - ${error.message}`);
      }

      return false;
    }
  }

  /**
   * åˆ›å»ºæ–‡ä»¶å¤‡ä»½
   */
  createBackup(filePath, content) {
    const relativePath = path.relative(process.cwd(), filePath);
    const backupPath = path.join(this.backupDir, relativePath);
    const backupDir = path.dirname(backupPath);

    if (!fs.existsSync(backupDir)) {
      fs.mkdirSync(backupDir, { recursive: true });
    }

    fs.writeFileSync(backupPath, content);
  }

  /**
   * æ‰¹é‡è½¬æ¢æ–‡ä»¶
   */
  async transformFiles(patterns = TRANSFORM_CONFIG.scanPatterns) {
    console.log('ğŸš€ å¼€å§‹ Export * è½¬æ¢...\n');

    // è·å–è¦å¤„ç†çš„æ–‡ä»¶
    const files = [];
    for (const pattern of patterns) {
      const matchedFiles = glob.sync(pattern, { cwd: process.cwd() });
      files.push(...matchedFiles);
    }

    console.log(`ğŸ“ æ‰¾åˆ° ${files.length} ä¸ªæ–‡ä»¶å¾…å¤„ç†\n`);

    // å¤„ç†æ–‡ä»¶
    for (const file of files) {
      try {
        await this.transformFile(file);
      } catch (error) {
        (
          this.transformStats ||
          (this.transformStats = {
            filesProcessed: 0,
            filesTransformed: 0,
            exportStarRemoved: 0,
            namedExportsAdded: 0,
            errors: [],
          })
        ).errors.push({
          file,
          error: error.message,
          type: 'transform_error',
        });
        console.error(`âŒ è½¬æ¢å¤±è´¥: ${file} - ${error.message}`);
      }
    }

    // ç”ŸæˆæŠ¥å‘Š
    await this.generateReport();

    console.log('\nğŸ“Š è½¬æ¢å®Œæˆ!');
    console.log(`å¤„ç†æ–‡ä»¶: ${this.transformStats.filesProcessed}`);
    console.log(`è½¬æ¢æ–‡ä»¶: ${this.transformStats.filesTransformed}`);
    console.log(`ç§»é™¤ export *: ${this.transformStats.exportStarRemoved}`);
    console.log(`æ·»åŠ å‘½åå¯¼å‡º: ${this.transformStats.namedExportsAdded}`);

    if (this.transformStats.errors.length > 0) {
      console.log(`é”™è¯¯æ•°é‡: ${this.transformStats.errors.length}`);
    }

    return this.transformStats;
  }

  /**
   * ç”Ÿæˆè½¬æ¢æŠ¥å‘Š
   */
  async generateReport() {
    const report = {
      timestamp: new Date().toISOString(),
      options: this.options,
      stats: this.transformStats,
      summary: {
        success: this.transformStats.errors.length === 0,
        filesProcessed: this.transformStats.filesProcessed,
        filesTransformed: this.transformStats.filesTransformed,
        transformationRate:
          this.transformStats.filesProcessed > 0
            ? `${(
                (this.transformStats.filesTransformed /
                  this.transformStats.filesProcessed) *
                100
              ).toFixed(2)}%`
            : '0%',
      },
    };

    const reportPath = path.join(
      this.outputDir,
      `barrel-transform-${Date.now()}.json`,
    );
    fs.writeFileSync(reportPath, JSON.stringify(report, null, 2));

    console.log(`ğŸ“„ æŠ¥å‘Šå·²ç”Ÿæˆ: ${reportPath}`);
    return report;
  }
}

// å‘½ä»¤è¡Œæ¥å£
async function main() {
  const args = process.argv.slice(2);
  const options = {};

  // è§£æå‘½ä»¤è¡Œå‚æ•°
  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--dry-run':
        options.dryRun = true;
        break;
      case '--no-backup':
        options.createBackup = false;
        break;
      case '--quiet':
        options.verbose = false;
        break;
      case '--no-comments':
        options.preserveComments = false;
        break;
    }
  }

  const transformer = new BarrelExportTransformer(options);

  try {
    await transformer.transformFiles();
  } catch (error) {
    console.error('âŒ è½¬æ¢è¿‡ç¨‹ä¸­å‡ºç°é”™è¯¯:', error);
    process.exit(1);
  }
}

// å¦‚æœç›´æ¥è¿è¡Œæ­¤è„šæœ¬
if (require.main === module) {
  main();
}

module.exports = BarrelExportTransformer;

#!/usr/bin/env node

/**
 * ç¿»è¯‘é”®è‡ªåŠ¨æ‰«æå·¥å…·
 * ä½¿ç”¨ASTè§£ææ‰«æä»£ç ä¸­çš„ç¿»è¯‘é”®ï¼Œç¡®ä¿ç¿»è¯‘å®Œæ•´æ€§
 */

const fs = require('fs');
const path = require('path');
const { parse } = require('@babel/parser');
const traverse = require('@babel/traverse').default;
const glob = require('glob');

console.log('ğŸ” å¼€å§‹ç¿»è¯‘é”®æ‰«æ...\n');

// é…ç½®
const CONFIG = {
  // æ‰«æçš„æ–‡ä»¶æ¨¡å¼
  SCAN_PATTERNS: [
    'src/**/*.{ts,tsx,js,jsx}',
    'app/**/*.{ts,tsx,js,jsx}',
    '!src/**/*.test.{ts,tsx,js,jsx}',
    '!src/**/*.spec.{ts,tsx,js,jsx}',
    '!**/*.d.ts',
  ],

  // ç¿»è¯‘å‡½æ•°å
  TRANSLATION_FUNCTIONS: ['t', 'useTranslations', 'getTranslations'],

  // è¾“å‡ºç›®å½•
  OUTPUT_DIR: path.join(process.cwd(), 'reports'),

  // ç¿»è¯‘æ–‡ä»¶ç›®å½•
  MESSAGES_DIR: path.join(process.cwd(), 'messages'),

  // æ”¯æŒçš„è¯­è¨€
  LOCALES: ['en', 'zh'],
};

const scanResults = {
  totalFiles: 0,
  scannedFiles: 0,
  translationKeys: new Set(),
  keyUsages: new Map(),
  errors: [],
  warnings: [],
  statistics: {
    totalKeys: 0,
    uniqueKeys: 0,
    missingKeys: [],
    unusedKeys: [],
  },
};

/**
 * æ‰«ææ–‡ä»¶ä¸­çš„ç¿»è¯‘é”®
 */
function scanFile(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    const ast = parse(content, {
      sourceType: 'module',
      plugins: [
        'typescript',
        'jsx',
        'decorators-legacy',
        'classProperties',
        'objectRestSpread',
        'asyncGenerators',
        'functionBind',
        'exportDefaultFrom',
        'exportNamespaceFrom',
        'dynamicImport',
        'nullishCoalescingOperator',
        'optionalChaining',
      ],
    });

    const fileKeys = new Set();

    traverse(ast, {
      // æ‰«æ useTranslations hook
      CallExpression(nodePath) {
        const { node } = nodePath;

        if (
          node.callee.name === 'useTranslations' &&
          node.arguments.length > 0 &&
          node.arguments[0].type === 'StringLiteral'
        ) {
          const namespace = node.arguments[0].value;
          fileKeys.add(namespace);
          recordKeyUsage(namespace, filePath, node.loc);
        }
      },

      // æ‰«æ t() å‡½æ•°è°ƒç”¨
      MemberExpression(nodePath) {
        const { node } = nodePath;

        if (
          node.property &&
          node.property.type === 'Identifier' &&
          node.property.name === 't' &&
          nodePath.parent.type === 'CallExpression'
        ) {
          const callExpression = nodePath.parent;
          if (
            callExpression.arguments.length > 0 &&
            callExpression.arguments[0].type === 'StringLiteral'
          ) {
            const key = callExpression.arguments[0].value;
            fileKeys.add(key);
            recordKeyUsage(key, filePath, callExpression.loc);
          }
        }
      },

      // æ‰«æç›´æ¥çš„ t() è°ƒç”¨
      CallExpression(nodePath) {
        const { node } = nodePath;

        if (
          node.callee.type === 'Identifier' &&
          node.callee.name === 't' &&
          node.arguments.length > 0 &&
          node.arguments[0].type === 'StringLiteral'
        ) {
          const key = node.arguments[0].value;
          fileKeys.add(key);
          recordKeyUsage(key, filePath, node.loc);
        }
      },
    });

    scanResults.scannedFiles++;
    fileKeys.forEach((key) => scanResults.translationKeys.add(key));

    console.log(`âœ… æ‰«æå®Œæˆ: ${filePath} (å‘ç° ${fileKeys.size} ä¸ªç¿»è¯‘é”®)`);
  } catch (error) {
    scanResults.errors.push({
      file: filePath,
      error: error.message,
      type: 'parse_error',
    });
    console.error(`âŒ æ‰«æå¤±è´¥: ${filePath} - ${error.message}`);
  }
}

/**
 * è®°å½•ç¿»è¯‘é”®ä½¿ç”¨æƒ…å†µ
 */
function recordKeyUsage(key, filePath, location) {
  if (!scanResults.keyUsages.has(key)) {
    scanResults.keyUsages.set(key, []);
  }

  scanResults.keyUsages.get(key).push({
    file: filePath,
    line: location ? location.start.line : null,
    column: location ? location.start.column : null,
  });
}

/**
 * åŠ è½½ç°æœ‰ç¿»è¯‘æ–‡ä»¶
 */
function loadExistingTranslations() {
  const translations = {};

  for (const locale of CONFIG.LOCALES) {
