#!/usr/bin/env node
/*
 * å¢é‡TypeScriptç±»å‹æ£€æŸ¥ï¼ˆä»…é’ˆå¯¹æš‚å­˜æ–‡ä»¶ï¼‰
 * - è¯»å– Git æš‚å­˜åŒºçš„ TypeScript æ–‡ä»¶åˆ—è¡¨
 * - ä»…å¯¹è¿™äº›æ–‡ä»¶è¿è¡Œ tsc --noEmit
 * - ä¼˜åŒ–pre-commitæ€§èƒ½ï¼Œé¿å…å…¨é‡ç±»å‹æ£€æŸ¥
 */

const { execSync, spawnSync } = require('child_process');
const path = require('path');

function getStagedTSFiles() {
  try {
    const output = execSync(
      'git diff --name-only --cached --diff-filter=ACMR',
      { stdio: ['ignore', 'pipe', 'ignore'] },
    )
      .toString()
      .trim();
    if (!output) return [];
    return output.split('\n').filter((f) => /\.(ts|tsx)$/i.test(f));
  } catch {
    return [];
  }
}

function runTypeCheck(files) {
  if (files.length === 0) {
    console.log('[type-check:staged] æ— éœ€æ£€æŸ¥ï¼šæ²¡æœ‰æš‚å­˜çš„ TypeScript æ–‡ä»¶');
    return 0;
  }

  console.log(`[type-check:staged] æ£€æŸ¥ ${files.length} ä¸ª TypeScript æ–‡ä»¶...`);

  // ä½¿ç”¨ tsc --noEmit å¯¹æŒ‡å®šæ–‡ä»¶è¿›è¡Œç±»å‹æ£€æŸ¥
  // æ³¨æ„ï¼štsc ä¸æ”¯æŒç›´æ¥æŒ‡å®šæ–‡ä»¶åˆ—è¡¨è¿›è¡Œ --noEmit æ£€æŸ¥
  // æ‰€ä»¥æˆ‘ä»¬ä½¿ç”¨é¡¹ç›®é…ç½®ï¼Œä½†åªåœ¨æœ‰TSæ–‡ä»¶å˜æ›´æ—¶è¿è¡Œ
  const result = spawnSync('npx', ['tsc', '--noEmit', '--skipLibCheck'], {
    stdio: 'inherit',
    cwd: process.cwd(),
  });

  if (result.status !== 0) {
    console.error('[type-check:staged] TypeScript ç±»å‹æ£€æŸ¥å¤±è´¥');
    console.error('è¯·ä¿®å¤ç±»å‹é”™è¯¯åé‡æ–°æäº¤');
    return result.status;
  }

  console.log('[type-check:staged] âœ… TypeScript ç±»å‹æ£€æŸ¥é€šè¿‡');
  return 0;
}

function main() {
  const stagedFiles = getStagedTSFiles();
  const exitCode = runTypeCheck(stagedFiles);
  process.exit(exitCode);
}

if (require.main === module) {
  main();
}

module.exports = { getStagedTSFiles, runTypeCheck };

