  }

  // ç”Ÿæˆä¸‹ä¸€æ­¥å»ºè®®
  if (errors.length === 0) {
    report.next_steps.push('ğŸ‰ æ‰€æœ‰é­”æ³•æ•°å­—é”™è¯¯å·²ä¿®å¤ï¼');
  } else {
