export function simulatePerformanceIssues(): void {
  // æ¨¡æ‹Ÿå¸ƒå±€åç§» - ä½¿ç”¨æ›´æ¸©å’Œçš„æ–¹å¼ï¼Œé¿å…ä¸¥é‡çš„ CLS
  const div = document.createElement('div');
