export function PerformanceTestComponent({
  iterations = 1000,
}: {
  iterations?: number;
}) {
  const [isRunning, setIsRunning] = useState(false);
  const [results, setResults] = useState<{
    inefficient: number;
    optimized: number;
  } | null>(null);

  const runPerformanceTest = useCallback(async () => {
    setIsRunning(true);
    setResults(null);

    // æµ‹è¯•ä½æ•ˆç»„ä»¶
    const inefficientStart = performance.now();
    for (let i = 0; i < iterations; i++) {
      // æ¨¡æ‹Ÿä½æ•ˆç»„ä»¶çš„è®¡ç®—ï¼ˆä¸¢å¼ƒä¸­é—´ç»“æœä»¥é¿å…æœªä½¿ç”¨å˜é‡ï¼‰
      for (let j = 0; j < 1000; j++) {
        Math.random();
      }
    }
    const inefficientTime = performance.now() - inefficientStart;

    // æµ‹è¯•ä¼˜åŒ–ç»„ä»¶ï¼ˆæ¨¡æ‹Ÿç¼“å­˜ï¼‰
    const optimizedStart = performance.now();
    let cachedResult = 0;
    for (let j = 0; j < 1000; j++) {
      cachedResult += Math.random();
    }
    for (let i = 0; i < iterations; i++) {
      // æ¨¡æ‹Ÿä½¿ç”¨ç¼“å­˜çš„ç»“æœï¼ˆæ˜¾å¼ä¸¢å¼ƒï¼‰
      void cachedResult;
    }
    const optimizedTime = performance.now() - optimizedStart;

    setResults({
      inefficient: inefficientTime,
      optimized: optimizedTime,
    });
    setIsRunning(false);
  }, [iterations]);

  return (
    <Card>
      <CardHeader>
        <CardTitle>æ€§èƒ½æµ‹è¯•</CardTitle>
        <CardDescription>æ¯”è¾ƒä¼˜åŒ–å’Œæœªä¼˜åŒ–ç»„ä»¶çš„æ€§èƒ½å·®å¼‚</CardDescription>
      </CardHeader>
      <CardContent>
        <div className='space-y-4'>
          <Button
            onClick={runPerformanceTest}
            disabled={isRunning}
