/**
 * React Scan çŠ¶æ€æ˜¾ç¤ºç»„ä»¶
 */
export function ReactScanStats() {
  const [stats, setStats] = useState<ReactScanStats | null>(null);

  const updateStats = useCallback(() => {
    if (typeof window !== 'undefined') {
      try {
        // æ£€æŸ¥å¤šç§å¯èƒ½çš„ React Scan çŠ¶æ€
        const reactScan = (window as ReactScanWindow).__REACT_SCAN__;
        const reactScanInternals = reactScan?.ReactScanInternals;

        // å°è¯•ä»å¤šä¸ªä½ç½®è·å–çŠ¶æ€
        let isEnabled = false;
        let totalRenders = 0;
        let componentsTracked = 0;

        if (reactScanInternals) {
          isEnabled = reactScanInternals.enabled ?? false;
          totalRenders = reactScanInternals.totalRenders ?? 0;

          // è·å–è·Ÿè¸ªç»„ä»¶æ•°
          const fiberRoots = reactScanInternals?.fiberRoots || {};
          componentsTracked = Object.keys(fiberRoots).length;
        }

        setStats({
          enabled: isEnabled,
          totalRenders,
          componentsTracked,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      } catch (error) {
        console.warn('æ— æ³•è·å– React Scan çŠ¶æ€:', error);
        setStats({
          enabled: false,
          totalRenders: 0,
          componentsTracked: 0,
          lastUpdate: new Date().toLocaleTimeString(),
        });
      }
    }
  }, []);

  useEffect(() => {
    updateStats();

    // æ›´é¢‘ç¹çš„è½®è¯¢ä»¥å¿«é€Ÿå“åº”çŠ¶æ€å˜åŒ–
    const interval = setInterval(
      updateStats,
      REACT_SCAN_CONFIG.STATS_UPDATE_INTERVAL,
    );

    // ç›‘å¬é”®ç›˜äº‹ä»¶ä»¥ç«‹å³æ›´æ–°çŠ¶æ€
    const handleKeyDown = (event: KeyboardEvent) => {
      if (event.ctrlKey && event.shiftKey && event.key.toLowerCase() === 'x') {
        // å»¶è¿Ÿä¸€ç‚¹æ›´æ–°ä»¥ç¡®ä¿ React Scan çŠ¶æ€å·²ç»æ”¹å˜
        setTimeout(updateStats, 100);
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      clearInterval(interval);
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [updateStats]);

  if (!stats) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>React Scan çŠ¶æ€</CardTitle>
        </CardHeader>
        <CardContent>
          <p>æ­£åœ¨åŠ è½½çŠ¶æ€...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className='flex items-center gap-2'>
          React Scan çŠ¶æ€
          <Badge variant={stats.enabled ? 'default' : 'secondary'}>
            {stats.enabled ? 'å·²å¯ç”¨' : 'å·²ç¦ç”¨'}
          </Badge>
        </CardTitle>
        <CardDescription>
          å®æ—¶æ˜¾ç¤º React Scan çš„è¿è¡ŒçŠ¶æ€å’Œç»Ÿè®¡ä¿¡æ¯
        </CardDescription>
      </CardHeader>
      <CardContent>
        <div className='grid grid-cols-2 gap-4'>
          <div>
            <p className='text-sm font-medium'>æ€»æ¸²æŸ“æ¬¡æ•°</p>
            <p className='text-2xl font-bold'>{stats.totalRenders}</p>
          </div>
          <div>
            <p className='text-sm font-medium'>è·Ÿè¸ªç»„ä»¶æ•°</p>
            <p className='text-2xl font-bold'>{stats.componentsTracked}</p>
          </div>
        </div>
        <div className='border-_t mt-4 pt-4'>
          <p className='text-muted-foreground text-xs'>
            æœ€åæ›´æ–°: {stats.lastUpdate}
          </p>
        </div>
      </CardContent>
    </Card>
  );
}

