import { useCallback } from 'react';

export interface ReactScanData {
  components: Array<{
    name: string;
    renderCount: number;
    renderTime: number;
    lastRenderTime: number;
  }>;
  totalRenderTime: number;
  totalRenderCount: number;
}

// React Scan å…¨å±€å¯¹è±¡ç±»å‹å®šä¹‰
interface ReactScanGlobalData {
  renderCount: number;
  renderTime: number;
  lastRenderTime: number;
}

interface ReactScanGlobal {
  components: Record<string, ReactScanGlobalData>;
}

// æ‰©å±• Window æ¥å£ä»¥åŒ…å« React Scan å…¨å±€å¯¹è±¡
declare global {
  interface Window {
    __REACT_SCAN__?: ReactScanGlobal;
  }
}

export interface ComponentStats {
  name: string;
  renderCount: number;
  renderTime: number;
  lastRenderTime: number;
  avgRenderTime: number;
  efficiency: number;
  timestamp: number;
}

/**
 * React Scan åŸå§‹ç»„ä»¶æ•°æ®æ¥å£
