import { ThemeMenuItem } from '@/components/theme/theme-menu-item';

// Mock DropdownMenuItem
vi.mock('@/components/ui/dropdown-menu', () => ({
  DropdownMenuItem: ({
    children,
    className,
    onClick,
    onKeyDown,
    role,
    ...props
  }: React.ComponentProps<'div'>) => (
    <div
      role={role}
      className={className}
      onClick={onClick}
      onKeyDown={onKeyDown}
      {...props}
    >
      {children}
    </div>
  ),
}));

describe('ThemeMenuItem', () => {
