  return { getPerformanceTrends, getPageComparison };
}

/**
 * æ•°æ®ç®¡ç†åŠŸèƒ½Hook
 */
function useDataManagement(
  setState: React.Dispatch<React.SetStateAction<WebVitalsDiagnosticsState>>,
) {
  const clearHistory = useCallback(() => {
    setState((prev) => ({
      ...prev,
      historicalReports: [],
