  const mergedConnection: ConnectionInfo = overrides.connection
    ? { ...baseConnection, ...overrides.connection }
    : baseConnection;

  const mergedDevice: DetailedWebVitals['device'] = {
    ...base.device,
    ...(overrides.device ?? {}),
  };

  const mergedPage: DetailedWebVitals['page'] = {
    ...base.page,
    ...(overrides.page ?? {}),
