/**
 * æ£€æŸ¥å¼€å‘å·¥å…·æ˜¯å¦åº”è¯¥é»˜è®¤æŠ˜å 
 */
export function shouldDefaultCollapse(toolId: string): boolean {
  const config = DEV_TOOLS_CONFIG[toolId as keyof typeof DEV_TOOLS_CONFIG];
  return config?.defaultCollapsed || false;
