/**
 * è·å–å¼€å‘å·¥å…·çš„ä¼˜å…ˆçº§
 */
export function getDevToolPriority(toolId: string): number {
  const config = DEV_TOOLS_CONFIG[toolId as keyof typeof DEV_TOOLS_CONFIG];
  return config?.priority || 1;
}

/**
 * å¼€å‘å·¥å…·å¸ƒå±€ç®¡ç†å™¨
 */
export class DevToolsLayoutManager {
  private static instance: DevToolsLayoutManager;
  private activeTools: Set<string> = new Set();

  static getInstance(): DevToolsLayoutManager {
    if (!DevToolsLayoutManager.instance) {
      DevToolsLayoutManager.instance = new DevToolsLayoutManager();
    }
    return DevToolsLayoutManager.instance;
  }

  /**
   * æ³¨å†Œå¼€å‘å·¥å…·
   */
  registerTool(toolId: string): void {
    this.activeTools.add(toolId);
    this.optimizeLayout();
  }

  /**
   * æ³¨é”€å¼€å‘å·¥å…·
   */
  unregisterTool(toolId: string): void {
    this.activeTools.delete(toolId);
    this.optimizeLayout();
  }

  /**
   * ä¼˜åŒ–å¸ƒå±€ï¼Œé¿å…é‡å 
   */
  private optimizeLayout(): void {
    // æŒ‰ä¼˜å…ˆçº§æ’åºæ´»è·ƒå·¥å…·
    const sortedTools = Array.from(this.activeTools).sort((a, b) => {
      const priorityA = getDevToolPriority(a);
      const priorityB = getDevToolPriority(b);
      return priorityB - priorityA; // é™åºæ’åˆ—
    });

    // æ£€æµ‹ä½ç½®å†²çªå¹¶è°ƒæ•´
    const usedPositions = new Set<DevToolPosition>();

    sortedTools.forEach((toolId) => {
      const config = DEV_TOOLS_CONFIG[toolId as keyof typeof DEV_TOOLS_CONFIG];
      if (!config) return;

      if (usedPositions.has(config.position)) {
        // ä½ç½®å†²çªï¼Œå¯»æ‰¾æ›¿ä»£ä½ç½®
        const alternativePosition = this.findAlternativePosition(
          config.position,
          usedPositions,
        );
        if (alternativePosition) {
          // ä¸´æ—¶è°ƒæ•´ä½ç½®ï¼ˆåœ¨å®é™…åº”ç”¨ä¸­å¯èƒ½éœ€è¦æ›´å¤æ‚çš„çŠ¶æ€ç®¡ç†ï¼‰
          console.warn(
            `Dev tool ${toolId} position conflict, using alternative: ${alternativePosition}`,
          );
        }
      } else {
        usedPositions.add(config.position);
      }
    });
  }

  /**
   * å¯»æ‰¾æ›¿ä»£ä½ç½®
   */
  private findAlternativePosition(
    preferredPosition: DevToolPosition,
    usedPositions: Set<DevToolPosition>,
  ): DevToolPosition | null {
    // ä½ç½®ä¼˜å…ˆçº§æ˜ å°„
    const alternatives: Record<DevToolPosition, DevToolPosition[]> = {
      'bottom-right': ['right-center', 'bottom-center', 'top-right'],
      'bottom-left': ['left-center', 'bottom-center', 'top-left'],
      'top-right': ['right-center', 'top-center', 'bottom-right'],
      'top-left': ['left-center', 'top-center', 'bottom-left'],
      'top-center': ['top-right', 'top-left', 'bottom-center'],
      'bottom-center': ['bottom-right', 'bottom-left', 'top-center'],
      'left-center': ['bottom-left', 'top-left', 'right-center'],
      'right-center': ['bottom-right', 'top-right', 'left-center'],
    };

    const alternativeList =
      alternatives[preferredPosition as keyof typeof alternatives] || [];

    for (const alternative of alternativeList) {
      if (!usedPositions.has(alternative)) {
        return alternative;
      }
    }

    return null;
  }

  /**
   * è·å–æ‰€æœ‰æ´»è·ƒå·¥å…·çš„å¸ƒå±€ä¿¡æ¯
   */
  getLayoutInfo(): Array<{ toolId: string; config: DevToolConfig }> {
    return Array.from(this.activeTools)
      .map((toolId) => ({
        toolId,
        config: DEV_TOOLS_CONFIG[toolId as keyof typeof DEV_TOOLS_CONFIG],
      }))
      .filter((item): item is { toolId: string; config: DevToolConfig } =>
        Boolean(item.config),
      )
      .sort((a, b) => b.config.priority - a.config.priority);
  }
}

/**
 * React Hook for dev tools positioning
 */
export function useDevToolsLayout() {
  const layoutManager = DevToolsLayoutManager.getInstance();

  return {
    registerTool: (toolId: string) => layoutManager.registerTool(toolId),
    unregisterTool: (toolId: string) => layoutManager.unregisterTool(toolId),
    getClasses: (toolId: string) => getDevToolClasses(toolId),
    getZIndex: (toolId: string) => getDevToolZIndex(toolId),
    shouldCollapse: (toolId: string) => shouldDefaultCollapse(toolId),
    getLayoutInfo: () => layoutManager.getLayoutInfo(),
  };
}

/**
 * å›½é™…åŒ–æ€§èƒ½æŒ‡æ ‡æ”¶é›†å™¨
 *
 * è´Ÿè´£æ”¶é›†å’Œç®¡ç† i18n ç³»ç»Ÿçš„æ€§èƒ½æŒ‡æ ‡ï¼ŒåŒ…æ‹¬ç¼“å­˜å‘½ä¸­ç‡ã€åŠ è½½æ—¶é—´ã€é”™è¯¯ç‡ç­‰
 */

import type { I18nMetrics, Locale } from '@/types/i18n';
import type {
  CacheEvent,
  CacheEventListener,
  CacheEventType,
  MetricsCollector,
} from '@/lib/i18n-cache-types';
import { logger } from '@/lib/logger';
import {
  ANGLE_90_DEG,
  COUNT_PAIR,
  COUNT_TEN,
  DAYS_PER_MONTH,
  HTTP_OK,
  ONE,
  PERCENTAGE_FULL,
  PERCENTAGE_HALF,
  PERCENTAGE_QUARTER,
  SECONDS_PER_MINUTE,
  ZERO,
} from '@/constants';
import {
  COUNT_35,
  MAGIC_20,
  MAGIC_40,
  MAGIC_70,
  MAGIC_80,
  MAGIC_95,
} from '@/constants/count';
import {
  MAGIC_0_1,
  MAGIC_0_5,
  MAGIC_0_9,
  MAGIC_0_95,
  MAGIC_0_99,
} from '@/constants/decimal';
import { MINUTE_MS } from '@/constants/time';

// æ€§èƒ½æŒ‡æ ‡æ”¶é›†å™¨å®ç°
export class I18nMetricsCollector implements MetricsCollector {
  private metrics: I18nMetrics = {
    loadTime: ZERO,
    cacheHitRate: ZERO,
    errorRate: ZERO,
    translationCoverage: ZERO,
    localeUsage: { en: ZERO, zh: ZERO },
  };

  private totalRequests = ZERO;
  private cacheHits = ZERO;
  private errors = ZERO;
  private loadTimes: number[] = [];
  private localeUsageCount: { en: number; zh: number } = { en: ZERO, zh: ZERO };
  private eventListeners: Map<CacheEventType | '*', CacheEventListener[]> =
    new Map();

  private static isAllowedEventType(
    type: string,
  ): type is CacheEventType | '*' {
    switch (type) {
      case 'hit':
      case 'miss':
      case 'set':
      case 'delete':
      case 'clear':
      case 'expire':
      case 'preload_start':
      case 'preload_complete':
      case 'preload_error':
      case '*':
        return true;
      default:
        return false;
    }
  }
  private startTime = Date.now();

  // è®°å½•åŠ è½½æ—¶é—´
  recordLoadTime(time: number): void {
    this.loadTimes.push(time);

    // ä¿æŒæœ€è¿‘ 100 æ¬¡è®°å½•ä»¥è®¡ç®—å¹³å‡å€¼
    if (this.loadTimes.length > PERCENTAGE_FULL) {
      this.loadTimes = this.loadTimes.slice(-PERCENTAGE_FULL);
    }

    this.metrics.loadTime =
      this.loadTimes.reduce((a, b) => a + b, ZERO) / this.loadTimes.length;

    this.emitEvent({
      type: 'hit',
      timestamp: Date.now(),
      metadata: { loadTime: time },
    });
  }

  // è®°å½•ç¼“å­˜å‘½ä¸­
  recordCacheHit(): void {
    this.cacheHits += ONE;
    this.totalRequests += ONE;
    this.updateCacheHitRate();

    this.emitEvent({
      type: 'hit',
      timestamp: Date.now(),
      metadata: {
        totalHits: this.cacheHits,
        totalRequests: this.totalRequests,
        hitRate: this.metrics.cacheHitRate,
      },
    });
  }

  // è®°å½•ç¼“å­˜æœªå‘½ä¸­
  recordCacheMiss(): void {
    this.totalRequests += ONE;
    this.updateCacheHitRate();

    this.emitEvent({
      type: 'miss',
      timestamp: Date.now(),
      metadata: {
        totalRequests: this.totalRequests,
        hitRate: this.metrics.cacheHitRate,
      },
    });
  }

  // è®°å½•é”™è¯¯
  recordError(): void {
    this.errors += ONE;
    this.updateErrorRate();

    this.emitEvent({
      type: 'preload_error',
      timestamp: Date.now(),
      metadata: {
        totalErrors: this.errors,
        errorRate: this.metrics.errorRate,
      },
    });
  }

  // è®°å½•è¯­è¨€ä½¿ç”¨æƒ…å†µ
  recordLocaleUsage(locale: Locale): void {
    if (locale === 'en') {
      this.localeUsageCount.en += ONE;
    } else if (locale === 'zh') {
      this.localeUsageCount.zh += ONE;
    }
    this.updateLocaleUsage();

    this.emitEvent({
      type: 'hit',
      timestamp: Date.now(),
      metadata: {
        locale,
        usageCount:
          locale === 'en' ? this.localeUsageCount.en : this.localeUsageCount.zh,
        totalUsage: this.getTotalLocaleUsage(),
      },
    });
  }

  // è®°å½•ç¿»è¯‘è¦†ç›–ç‡
  recordTranslationCoverage(coverage: number): void {
    const safeCoverage = Math.min(Math.max(coverage, ZERO), ONE);
    this.metrics.translationCoverage = safeCoverage;

    this.emitEvent({
      type: 'set',
      timestamp: Date.now(),
      metadata: {
        translationCoverage: safeCoverage,
      },
    });
  }

  // è·å–å½“å‰æŒ‡æ ‡
  getMetrics(): I18nMetrics {
    return { ...this.metrics };
  }

  // é‡ç½®æŒ‡æ ‡
  reset(): void {
    this.metrics = {
      loadTime: ZERO,
      cacheHitRate: ZERO,
      errorRate: ZERO,
      translationCoverage: ZERO,
      localeUsage: { en: ZERO, zh: ZERO },
    };

    this.totalRequests = ZERO;
    this.cacheHits = ZERO;
    this.errors = ZERO;
    this.loadTimes = [];
    this.localeUsageCount = { en: ZERO, zh: ZERO };
    this.startTime = Date.now();

    this.emitEvent({
      type: 'clear',
      timestamp: Date.now(),
      metadata: { reason: 'metrics_reset' },
    });
  }

  // è·å–è¯¦ç»†ç»Ÿè®¡ä¿¡æ¯
  getDetailedStats() {
    const uptime = Date.now() - this.startTime;
    const requestsPerMinute =
      this.totalRequests > ZERO
        ? this.totalRequests / (uptime / MINUTE_MS)
        : ZERO;

    return {
      uptime,
      totalRequests: this.totalRequests,
      cacheHits: this.cacheHits,
      cacheMisses: this.totalRequests - this.cacheHits,
      errors: this.errors,
      requestsPerMinute,
      averageLoadTime: this.metrics.loadTime,
      loadTimePercentiles: this.calculateLoadTimePercentiles(),
      localeDistribution: this.getLocaleDistribution(),
      performanceGrade: this.calculatePerformanceGrade(),
    };
  }

  // æ·»åŠ äº‹ä»¶ç›‘å¬å™¨
  addEventListener(
    eventType: CacheEventType | '*',
    listener: CacheEventListener,
  ): void {
    if (!I18nMetricsCollector.isAllowedEventType(eventType)) return;
    if (!this.eventListeners.has(eventType)) {
      this.eventListeners.set(eventType, []);
    }
    this.eventListeners.get(eventType)!.push(listener);
  }

  // ç§»é™¤äº‹ä»¶ç›‘å¬å™¨
  removeEventListener(
    eventType: CacheEventType | '*',
    listener: CacheEventListener,
  ): void {
    if (!I18nMetricsCollector.isAllowedEventType(eventType)) return;
    const listeners = this.eventListeners.get(eventType);
    if (listeners) {
      const index = listeners.indexOf(listener);
      if (index > -ONE) {
        listeners.splice(index, ONE);
      }
    }
  }

  // å‘å‡ºäº‹ä»¶
  private emitEvent(event: CacheEvent): void {
    const listeners = I18nMetricsCollector.isAllowedEventType(event.type)
      ? this.eventListeners.get(event.type)
      : undefined;
    if (listeners) {
      listeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in cache event listener:', error);
        }
      });
    }

    // ä¹Ÿå‘é€ç»™é€šç”¨ç›‘å¬å™¨
    const allListeners = this.eventListeners.get('*');
    if (allListeners) {
      allListeners.forEach((listener) => {
        try {
          listener(event);
        } catch (error) {
          logger.error('Error in cache event listener:', error);
        }
      });
    }
  }

  // æ›´æ–°ç¼“å­˜å‘½ä¸­ç‡
  private updateCacheHitRate(): void {
    this.metrics.cacheHitRate =
      this.totalRequests > ZERO
        ? this.cacheHits / this.totalRequests
        : ZERO;
  }

  // æ›´æ–°é”™è¯¯ç‡
  private updateErrorRate(): void {
    this.metrics.errorRate =
      this.totalRequests > ZERO ? this.errors / this.totalRequests : ZERO;
  }

  // æ›´æ–°è¯­è¨€ä½¿ç”¨æƒ…å†µ
  private updateLocaleUsage(): void {
    const total = this.getTotalLocaleUsage();
    const enCount = this.localeUsageCount.en;
    const zhCount = this.localeUsageCount.zh;
    this.metrics.localeUsage = {
      en: enCount,
      zh: zhCount,
    };
  }

  // è·å–æ€»è¯­è¨€ä½¿ç”¨æ¬¡æ•°
  private getTotalLocaleUsage(): number {
    return Object.values(this.localeUsageCount).reduce(
      (sum, count) => sum + count,
      ZERO,
    );
  }

  // è®¡ç®—åŠ è½½æ—¶é—´ç™¾åˆ†ä½æ•°
  private calculateLoadTimePercentiles() {
    if (this.loadTimes.length === ZERO) {
      return { p50: ZERO, p90: ZERO, p95: ZERO, p99: ZERO };
    }

    const sorted = [...this.loadTimes].sort((a, b) => a - b);
    const len = sorted.length;

    return {
      p50: sorted.at(Math.floor(len * MAGIC_0_5)) ?? ZERO,
      p90: sorted.at(Math.floor(len * MAGIC_0_9)) ?? ZERO,
      p95: sorted.at(Math.floor(len * MAGIC_0_95)) ?? ZERO,
      p99: sorted.at(Math.floor(len * MAGIC_0_99)) ?? ZERO,
    };
  }

  // è·å–è¯­è¨€åˆ†å¸ƒ
  private getLocaleDistribution() {
    const total = this.getTotalLocaleUsage();
    return Object.entries(this.localeUsageCount).map(([locale, count]) => ({
      locale,
      count,
      percentage: total > ZERO ? (count / total) * PERCENTAGE_FULL : ZERO,
    }));
  }

  // è®¡ç®—æ€§èƒ½ç­‰çº§
  private calculatePerformanceGrade(): 'A' | 'B' | 'C' | 'D' | 'F' {
    const hitRatePercent =
      this.metrics.cacheHitRate * PERCENTAGE_FULL;
    const errorRatePercent =
      this.metrics.errorRate * PERCENTAGE_FULL;
    const avgLoadTime = this.metrics.loadTime;

    // åŸºäºå¤šä¸ªæŒ‡æ ‡è®¡ç®—ç»¼åˆè¯„åˆ†
    let score = ZERO;

    // ç¼“å­˜å‘½ä¸­ç‡è¯„åˆ† (40%)
    if (hitRatePercent >= MAGIC_95) score += MAGIC_40;
    else if (hitRatePercent >= ANGLE_90_DEG) score += COUNT_35;
    else if (hitRatePercent >= MAGIC_80) score += DAYS_PER_MONTH;
    else if (hitRatePercent >= MAGIC_70) score += MAGIC_20;
    else score += COUNT_TEN;

    // é”™è¯¯ç‡è¯„åˆ† (30%)
    if (errorRatePercent <= MAGIC_0_1) score += DAYS_PER_MONTH;
    else if (errorRatePercent <= MAGIC_0_5) score += PERCENTAGE_QUARTER;
    else if (errorRatePercent <= ONE) score += MAGIC_20;
    else if (errorRatePercent <= COUNT_PAIR) score += COUNT_TEN;
    else score += ZERO;

    // å¹³å‡åŠ è½½æ—¶é—´è¯„åˆ† (30%)
    if (avgLoadTime <= COUNT_TEN) score += DAYS_PER_MONTH;
    else if (avgLoadTime <= PERCENTAGE_HALF) score += PERCENTAGE_QUARTER;
    else if (avgLoadTime <= PERCENTAGE_FULL) score += MAGIC_20;
    else if (avgLoadTime <= HTTP_OK) score += COUNT_TEN;
    else score += ZERO;

    // æ ¹æ®æ€»åˆ†ç¡®å®šç­‰çº§
    if (score >= ANGLE_90_DEG) return 'A';
    if (score >= MAGIC_80) return 'B';
    if (score >= MAGIC_70) return 'C';
    if (score >= SECONDS_PER_MINUTE) return 'D';
    return 'F';
  }

  // ç”Ÿæˆæ€§èƒ½æŠ¥å‘Š
  generatePerformanceReport() {
    const stats = this.getDetailedStats();
    const percentiles = this.calculateLoadTimePercentiles();
    const grade = this.calculatePerformanceGrade();

    return {
      summary: {
        grade,
        uptime: stats.uptime,
        totalRequests: stats.totalRequests,
        averageLoadTime: stats.averageLoadTime,
        cacheHitRate: this.metrics.cacheHitRate,
        errorRate: this.metrics.errorRate,
      },
      performance: {
        requestsPerMinute: stats.requestsPerMinute,
        loadTimePercentiles: percentiles,
        cacheEfficiency: {
          hits: stats.cacheHits,
          misses: stats.cacheMisses,
          hitRate: this.metrics.cacheHitRate,
        },
      },
      usage: {
        localeDistribution: stats.localeDistribution,
        totalErrors: stats.errors,
      },
      recommendations: this.generateRecommendations(stats, grade),
    };
  }

  // ç”Ÿæˆæ€§èƒ½å»ºè®®
  private generateRecommendations(
    stats: Record<string, unknown>,
    grade: string,
  ): string[] {
    const recommendations: string[] = [];

    const hitRatePercent = this.metrics.cacheHitRate * PERCENTAGE_FULL;
    const errorRatePercent = this.metrics.errorRate * PERCENTAGE_FULL;

    if (hitRatePercent < MAGIC_80) {
      recommendations.push('è€ƒè™‘å¢åŠ ç¼“å­˜å¤§å°æˆ–è°ƒæ•´ TTL ä»¥æé«˜ç¼“å­˜å‘½ä¸­ç‡');
    }

    if (errorRatePercent > ONE) {
      recommendations.push('é”™è¯¯ç‡è¾ƒé«˜ï¼Œå»ºè®®æ£€æŸ¥ç½‘ç»œè¿æ¥å’Œç¿»è¯‘æ–‡ä»¶å®Œæ•´æ€§');
    }

    if (this.metrics.loadTime > PERCENTAGE_FULL) {
      recommendations.push(
        'å¹³å‡åŠ è½½æ—¶é—´è¾ƒé•¿ï¼Œè€ƒè™‘å¯ç”¨é¢„åŠ è½½æˆ–ä¼˜åŒ–ç¿»è¯‘æ–‡ä»¶å¤§å°',
      );
    }

    const requestsPerMinute =
      typeof stats.requestsPerMinute === 'number'
        ? stats.requestsPerMinute
        : ZERO;
    if (requestsPerMinute > PERCENTAGE_FULL) {
      recommendations.push('è¯·æ±‚é¢‘ç‡è¾ƒé«˜ï¼Œå»ºè®®å¢åŠ ç¼“å­˜å®¹é‡');
    }

    if (grade === 'D' || grade === 'F') {
      recommendations.push('æ•´ä½“æ€§èƒ½éœ€è¦æ”¹è¿›ï¼Œå»ºè®®å…¨é¢ä¼˜åŒ–ç¼“å­˜ç­–ç•¥');
    }

    return recommendations;
  }
}

// åˆ›å»ºé»˜è®¤æŒ‡æ ‡æ”¶é›†å™¨å®ä¾‹
export const defaultMetricsCollector = new I18nMetricsCollector();

// å¯¼å‡ºå·¥å…·å‡½æ•°
