  return new I18nMetricsCollector();
}

export function formatMetrics(metrics: I18nMetrics): string {
  return `
ç¼“å­˜å‘½ä¸­ç‡: ${(metrics.cacheHitRate * PERCENTAGE_FULL).toFixed(COUNT_PAIR)}%
å¹³å‡åŠ è½½æ—¶é—´: ${metrics.loadTime.toFixed(COUNT_PAIR)}ms
é”™è¯¯ç‡: ${(metrics.errorRate * PERCENTAGE_FULL).toFixed(COUNT_PAIR)}%
ç¿»è¯‘è¦†ç›–ç‡: ${metrics.translationCoverage.toFixed(COUNT_PAIR)}%
è¯­è¨€ä½¿ç”¨åˆ†å¸ƒ: ${Object.entries(metrics.localeUsage)
    .map(([locale, usage]) => `${locale}: ${usage.toFixed(ONE)}%`)
    .join(', ')}
  `.trim();
}

// å¯¼å‡ºç±»å‹åˆ«å
