/**
 * ç¿»è¯‘é¢„åŠ è½½å™¨æ ¸å¿ƒå®ç°
 * Translation Preloader Core Implementation
 *
 * æä¾›ç¿»è¯‘é¢„åŠ è½½çš„æ ¸å¿ƒåŠŸèƒ½å’Œä¸»è¦ç±»å®ç°
 */

import type { Locale, Messages } from '@/types/i18n';
import type {
  CacheOperationResult,
  CacheStorage,
  MetricsCollector,
  Preloader,
} from '@/lib/i18n-cache-types';
import {
  PreloaderError,
  PreloaderNetworkError,
  PreloaderTimeoutError,
  type IPreloader,
  type PreloaderConfig,
  type PreloadOptions,
  type PreloadState,
  type PreloadStats,
} from '@/lib/i18n-preloader-types';
import { logger } from '@/lib/logger';
import {
  ANIMATION_DURATION_VERY_SLOW,
  BYTES_PER_KB,
  COUNT_FIVE,
  COUNT_TRIPLE,
  HOURS_PER_DAY,
  ONE,
  PERCENTAGE_FULL,
  PERCENTAGE_HALF,
  SECONDS_PER_MINUTE,
  TEN_SECONDS_MS,
  ZERO,
} from '@/constants';
import { MAGIC_0_1 } from '@/constants/decimal';
import { MINUTE_MS } from '@/constants/time';
import enStaticMessages from '../../messages/en.json';
import zhStaticMessages from '../../messages/zh.json';

const localeModuleLoaders: Partial<Record<Locale, () => Promise<Messages>>> = {
  en: async () => enStaticMessages as unknown as Messages,
  zh: async () => zhStaticMessages as unknown as Messages,
};

/**
 * ç¿»è¯‘é¢„åŠ è½½å™¨æ ¸å¿ƒå®ç°
 * Translation preloader core implementation
 */
export class TranslationPreloader implements Preloader, IPreloader {
  private cache: CacheStorage<Messages>;
  private metricsCollector: MetricsCollector;
  private preloadState: PreloadState = {
    isPreloading: false,
    progress: ZERO,
    totalLocales: ZERO,
    completedLocales: ZERO,
    errors: [],
  };
  private preloadConfig: PreloaderConfig;
  private abortController?: AbortController;

  constructor(
    cache: CacheStorage<Messages>,
    metricsCollector: MetricsCollector,
    config?: Partial<PreloaderConfig>,
  ) {
    this.cache = cache;
    this.metricsCollector = metricsCollector;
    this.preloadConfig = {
      enablePreload: true,
      preloadLocales: ['en', 'zh'],
      batchSize: COUNT_TRIPLE,
      delayBetweenBatches: PERCENTAGE_FULL,
      maxConcurrency: COUNT_FIVE,
      timeout: TEN_SECONDS_MS,
      retryCount: COUNT_TRIPLE,
      retryDelay: ANIMATION_DURATION_VERY_SLOW,
      smartPreload: {
        enabled: false,
        maxLocales: COUNT_TRIPLE,
        minUsageThreshold: MAGIC_0_1,
        usageWindow: HOURS_PER_DAY,
        preloadTrigger: 'idle',
      },
      memoryLimit: PERCENTAGE_HALF * BYTES_PER_KB * BYTES_PER_KB,
      networkThrottling: false,
      priorityQueue: false,
      cacheStrategy: 'adaptive',
      cacheTTL:
        HOURS_PER_DAY *
        SECONDS_PER_MINUTE *
        SECONDS_PER_MINUTE *
        ANIMATION_DURATION_VERY_SLOW,
      maxCacheSize: PERCENTAGE_FULL,
      enableMetrics: true,
      metricsInterval: MINUTE_MS,
      enableLogging: false,
      logLevel: 'info',
      ...config,
    };
  }

  /**
   * é¢„åŠ è½½æŒ‡å®šè¯­è¨€
   * Preload specific locale
   */
  async preloadLocale(
    locale: Locale,
    options?: PreloadOptions,
  ): Promise<Messages> {
    const startTime = Date.now();

    try {
      // é¦–å…ˆæ£€æŸ¥ç¼“å­˜
      const cached = this.cache.get(locale);
      if (cached) {
        this.metricsCollector.recordLoadTime(Date.now() - startTime);
        options?.onSuccess?.(locale, cached);
        return cached;
      }

      // ä»æœåŠ¡å™¨åŠ è½½
      const messages = await this.loadMessagesFromServer(locale, options);

      // å­˜å‚¨åˆ°ç¼“å­˜
      this.cache.set(locale, messages);
      this.metricsCollector.recordLoadTime(Date.now() - startTime);

      options?.onSuccess?.(locale, messages);
      return messages;
    } catch (error) {
      if (error instanceof PreloaderError) {
        options?.onError?.(error, locale);
        throw error;
      }

      const networkError =
        error instanceof Error
          ? new PreloaderNetworkError(locale, error)
          : new PreloaderError({
              message: `Failed to preload locale ${locale}`,
              locale,
            });

      options?.onError?.(networkError, locale);
      throw networkError;
    }
  }

  /**
   * é¢„çƒ­ç¼“å­˜
   * Warmup cache
   */
  warmupCache(): void {
    if (!this.preloadConfig.enablePreload || this.preloadState.isPreloading) {
      return;
    }

    this.preloadMultipleLocales(this.preloadConfig.preloadLocales);
  }

  /**
   * æ£€æŸ¥æ˜¯å¦æ­£åœ¨é¢„åŠ è½½
   * Check if preloading
   */
  isPreloading(): boolean {
    return this.preloadState.isPreloading;
  }

  /**
   * è·å–é¢„åŠ è½½è¿›åº¦
   * Get preload progress
   */
  getPreloadProgress(): number {
    return this.preloadState.progress;
  }

  /**
   * è·å–é¢„åŠ è½½çŠ¶æ€
   * Get preload state
   */
  getPreloadState(): PreloadState {
    return { ...this.preloadState };
  }

  /**
   * æ‰¹é‡é¢„åŠ è½½å¤šä¸ªè¯­è¨€
   * Preload multiple locales in batches
   */
  async preloadMultipleLocales(
    locales: Locale[],
    options?: PreloadOptions,
  ): Promise<CacheOperationResult<Messages>[]> {
    if (this.preloadState.isPreloading) {
      throw new PreloaderError({
        message: 'Preloading is already in progress',
      });
    }

    this.preloadState = {
      isPreloading: true,
      progress: ZERO,
      totalLocales: locales.length,
      completedLocales: ZERO,
      errors: [],
      startTime: Date.now(),
    };

    this.abortController = new AbortController();
    const results: CacheOperationResult<Messages>[] = [];

    try {
      // åˆ†æ‰¹å¤„ç†
      const batches = this.createBatches(locales, this.preloadConfig.batchSize);

      for (const [i, batch] of batches.entries()) {
        if (this.abortController.signal.aborted) {
          break;
        }

        if (!batch) continue;
        const batchResults = await this.processBatch(batch, options);
        results.push(...batchResults);

        this.updateProgress(batch.length, options);
        await this.delayBetweenBatchesIfNeeded(i, batches.length);
      }
    } catch (error) {
      logger.error('Preloading failed:', error);
    } finally {
      this.preloadState.isPreloading = false;
    }

    return results;
  }

  private updateProgress(
    completedDelta: number,
    options?: PreloadOptions,
  ): void {
    this.preloadState.completedLocales += completedDelta;
    this.preloadState.progress =
      (this.preloadState.completedLocales / this.preloadState.totalLocales) *
      PERCENTAGE_FULL;
    options?.onProgress?.(this.preloadState.progress);
  }

  private async delayBetweenBatchesIfNeeded(
    index: number,
    total: number,
  ): Promise<void> {
    if (index < total - 1 && this.preloadConfig.delayBetweenBatches > ZERO) {
      await this.delay(this.preloadConfig.delayBetweenBatches);
    }
  }

  /**
   * åœæ­¢é¢„åŠ è½½
   * Stop preloading
   */
  stopPreloading(): void {
    if (this.abortController) {
      this.abortController.abort();
    }
    this.preloadState.isPreloading = false;
  }

  /**
   * æš‚åœé¢„åŠ è½½
   * Pause preloading
   */
  pausePreloading(): void {
    // å®ç°æš‚åœé€»è¾‘
    this.stopPreloading();
  }

  /**
   * æ¢å¤é¢„åŠ è½½
   * Resume preloading
   */
  resumePreloading(): void {
    // å®ç°æ¢å¤é€»è¾‘
    if (!this.preloadState.isPreloading) {
      this.warmupCache();
    }
  }

  /**
   * æ™ºèƒ½é¢„åŠ è½½ï¼ˆåŸºäºä½¿ç”¨æ¨¡å¼ï¼‰
   * Smart preload based on usage patterns
   */
  async smartPreload(): Promise<void> {
    // è·å–ä½¿ç”¨ç»Ÿè®¡
    const metrics = this.metricsCollector.getMetrics();
    const { localeUsage } = metrics;

    // æŒ‰ä½¿ç”¨é¢‘ç‡æ’åº
    const sortedLocales = Object.entries(localeUsage)
      .sort(([, a], [, b]) => b - a)
      .map(([locale]) => locale as Locale)
      .slice(ZERO, COUNT_TRIPLE); // åªé¢„åŠ è½½å‰3ä¸ªæœ€å¸¸ç”¨çš„è¯­è¨€

    if (sortedLocales.length > ZERO) {
      await this.preloadMultipleLocales(sortedLocales);
    }
  }

  /**
   * é¢„åŠ è½½ç›¸å…³è¯­è¨€
   * Preload related locales
   */
  async preloadRelatedLocales(currentLocale: Locale): Promise<void> {
    const relatedLocales = this.getRelatedLocales(currentLocale);
    if (relatedLocales.length > ZERO) {
      await this.preloadMultipleLocales(relatedLocales);
    }
  }

  /**
   * é¢„åŠ è½½ç¼ºå¤±çš„ç¿»è¯‘
   * Preload missing translations
   */
  async preloadMissingTranslations(
    locale: Locale,
    keys: string[],
  ): Promise<void> {
    try {
      const messages = await this.loadSpecificKeys(locale, keys);

      // åˆå¹¶åˆ°ç°æœ‰ç¼“å­˜
      const existing = this.cache.get(locale) || {};
      const merged = { ...existing, ...messages } as Messages;
      this.cache.set(locale, merged);
    } catch (error) {
      logger.error(
        `Failed to preload missing translations for ${locale}:`,
        error,
      );
    }
  }

  /**
   * è·å–é¢„åŠ è½½ç»Ÿè®¡
   * Get preload statistics
   */
  getPreloadStats(): PreloadStats {
    const state = this.preloadState;
    const duration = state.startTime ? Date.now() - state.startTime : ZERO;

    return {
      isActive: state.isPreloading,
      progress: state.progress,
      totalLocales: state.totalLocales,
      completedLocales: state.completedLocales,
      errorCount: state.errors.length,
      duration,
      averageLoadTime: duration / Math.max(state.completedLocales, ONE),
      successRate:
        state.totalLocales > ZERO
          ? (state.completedLocales - state.errors.length) / state.totalLocales
          : ZERO,
    };
  }

  /**
   * æ¸…é™¤ç¼“å­˜
   * Clear cache
   */
  clearCache(): void {
    this.cache.clear();
  }

  /**
   * è·å–ç¼“å­˜ç»Ÿè®¡
   * Get cache statistics
   */
  getCacheStats(): { size: number; keys: string[] } {
    return {
      size: this.cache.size(),
      keys: Array.from(this.cache.keys()),
      // å…¶ä»–ç¼“å­˜ç»Ÿè®¡ä¿¡æ¯
    };
  }

  /**
   * ä»æœåŠ¡å™¨åŠ è½½æ¶ˆæ¯
   * Load messages from server
   */
  private async loadMessagesFromServer(
    locale: Locale,
    options?: PreloadOptions,
  ): Promise<Messages> {
    const isProcessDefined = typeof process !== 'undefined';
    const isTestEnv = isProcessDefined && process.env?.NODE_ENV === 'test';
    if (isTestEnv) {
      console.debug('i18n-preloader: detected test environment', {
        isProcessDefined,
        nodeEnv: process.env?.NODE_ENV,
        hasWindow: typeof window !== 'undefined',
      });
    }

    if (typeof window === 'undefined' || isTestEnv) {
      const moduleMessages = await this.loadMessagesFromModule(locale);
      if (moduleMessages) {
        return moduleMessages;
      }

      if (isTestEnv) {
        console.warn('i18n-preloader: missing module messages', { locale });
      }

      throw new PreloaderError({
        message: `Failed to load messages for locale: ${locale}`,
        locale,
      });
    }

    try {

      // è¿™é‡Œåº”è¯¥æ˜¯å®é™…çš„ API è°ƒç”¨
      const controller = new AbortController();
      const timeoutId = setTimeout(
        () => controller.abort(),
        options?.timeout || this.preloadConfig.timeout,
      );

      const response = await fetch(`/api/messages/${locale}`, {
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(
          `Failed to load messages for ${locale}: ${response.statusText}`,
        );
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new PreloaderTimeoutError(
          locale,
          options?.timeout || this.preloadConfig.timeout,
        );
      }
      throw new PreloaderNetworkError(
        locale,
        error instanceof Error ? error : new Error(String(error)),
      );
    }
  }

  /**
   * åŠ è½½ç‰¹å®šé”®çš„ç¿»è¯‘
   * Load specific translation keys
   */
  private async loadSpecificKeys(
    locale: Locale,
    keys: string[],
  ): Promise<Partial<Messages>> {
    const isTestEnv =
      typeof process !== 'undefined' && process.env?.NODE_ENV === 'test';

    if (typeof window === 'undefined' || isTestEnv) {
      throw new PreloaderError({
        message: `Partial key loading is not supported for locale ${locale} in test environment`,
        locale,
      });
    }

    try {
      const response = await fetch(`/api/messages/${locale}/keys`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ keys }),
        signal: this.abortController?.signal,
      } as RequestInit);

      if (!response.ok) {
        throw new Error(
          `Failed to load specific keys for ${locale}: ${response.statusText}`,
        );
      }

      return await response.json();
    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        throw new PreloaderError({ message: 'Load was cancelled', locale });
      }
      throw error;
    }
  }

  private async loadMessagesFromModule(
    locale: Locale,
  ): Promise<Messages | null> {
    const loader = localeModuleLoaders[locale];
    if (!loader) {
      return null;
    }

    try {
      const messages = await loader();
      return messages ?? null;
    } catch (error) {
      logger.warn('Failed to load locale messages from module', {
        locale,
        error: error instanceof Error ? error.message : error,
      });
      return null;
    }
  }

  /**
   * åˆ›å»ºæ‰¹æ¬¡
   * Create batches
   */
  private createBatches<T>(items: T[], batchSize: number): T[][] {
    const batches: T[][] = [];
    for (let i = ZERO; i < items.length; i += batchSize) {
      batches.push(items.slice(i, i + batchSize));
    }
    return batches;
  }

  /**
   * å¤„ç†æ‰¹æ¬¡
   * Process batch
   */
  private async processBatch(
    batch: Locale[],
    options?: PreloadOptions,
  ): Promise<CacheOperationResult<Messages>[]> {
    const promises = batch.map(
      async (locale): Promise<CacheOperationResult<Messages>> => {
        try {
          const messages = await this.preloadLocale(locale, options);
          return {
            success: true,
            data: messages,
            fromCache: false,
            loadTime: Date.now(),
          };
        } catch (error) {
          this.preloadState.errors.push({
            locale,
            error: error instanceof Error ? error.message : 'Unknown error',
          });

          return {
            success: false,
            error: error instanceof Error ? error.message : 'Unknown error',
            fromCache: false,
          };
        }
      },
    );

    const results = await Promise.all(promises);
    return results;
  }

  /**
   * è·å–ç›¸å…³è¯­è¨€
   * Get related locales
   */
  private getRelatedLocales(locale: Locale): Locale[] {
    if (locale === 'en') return ['zh'];
    if (locale === 'zh') return ['en'];
    return [];
  }

  /**
   * å»¶è¿Ÿå‡½æ•°
   * Delay function
   */
  private delay(ms: number): Promise<void> {
    return new Promise((resolve) => setTimeout(resolve, ms));
  }

  /**
   * æ¸…ç†èµ„æº
   * Cleanup resources
   */
  cleanup(): void {
    this.stopPreloading();
    this.preloadState = {
      isPreloading: false,
      progress: ZERO,
      totalLocales: ZERO,
      completedLocales: ZERO,
      errors: [],
    };
  }

  /**
   * è®¾ç½®é¢„åŠ è½½é…ç½®
   * Set preload configuration
   */
  setConfig(config: Partial<PreloaderConfig>): void {
    this.preloadConfig = { ...this.preloadConfig, ...config };
  }

  /**
   * è·å–é¢„åŠ è½½é…ç½®
   * Get preload configuration
   */
  getConfig(): PreloaderConfig {
    return { ...this.preloadConfig };
  }
}

